INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(114, '2003-12-22 00:00:00', 'Servizio di Ficiesse', 'LA LETTERA DEL PRESIDENTE DEL COCER AL MINISTRO DELLA GIUSTIZIA', '', 'ALLA PRESA DI POSIZIONE DEL COCER SEGUE UNA LETTERA AL MINISTRO DELLA GIUSTIZIA', '<P class=MsoDate style=\"MARGIN: 0cm 0cm 11pt 229.5pt\"><FONT face=\"arial, helvetica, sans-serif\" size=3>19 dicembre 2003</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 1cm 0pt 1.15pt\"><SPAN style=\"mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>Sen.<?xml:namespace prefix = o /><o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 1cm 0pt 1.15pt\"><SPAN style=\"mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>Roberto Castelli<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 1cm 0pt 1.15pt\"><SPAN style=\"mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>Ministro della giustizia<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 1cm 0pt 1.15pt\"><SPAN style=\"mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>Via Arenula, 70<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 1cm 12pt 1.2pt\"><SPAN style=\"mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>00186 Roma<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 1cm 12pt 1.2pt\"><SPAN style=\"mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>&nbsp;<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 1cm 12pt 1.2pt\"><SPAN style=\"mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>&nbsp;<o:p></o:p></FONT></FONT></SPAN></P><PRE style=\"MARGIN-BOTTOM: 12pt; TEXT-INDENT: 35.45pt\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3><SPAN style=\"FONT-FAMILY: Garamond; mso-bidi-font-family: Arial\">mi rivolgo alla S.V. quale Autorità competente per gli esami di avvocato, ai sensi dell’art. 22 del R.D.L. 27 novembre 1933, n. 1578,</SPAN><SPAN style=\"FONT-FAMILY: Garamond\"> convertito, con modificazioni, dalla legge 22 gennaio 1934, n. 36, come modificato, da ultimo, dall’art. 1 bis decreto-legge 21 maggio 2003, n. 112, convertito dalla legge di conversione 18 luglio 2003, n. 180.<o:p></o:p></SPAN></FONT></FONT></PRE><PRE style=\"MARGIN-BOTTOM: 12pt; TEXT-INDENT: 35.45pt\"><SPAN style=\"FONT-FAMILY: Garamond\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>Il giorno 17 u.s., si è tenuta la prova scritta in materia penale degli esami per avvocato, indetti con D.M. 23 luglio 2003.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>La traccia proposta è stata la seguente:<o:p></o:p></FONT></FONT></SPAN></PRE>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 24.45pt 12pt 34.8pt\"><SPAN style=\"mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>“”” Tizio veniva avvicinato da Caio, Maresciallo della Guardia di Finanza che gli riferiva di un esposto anonimo in merito a delle irregolarità commesse dalle aziende Alfa e Beta, per le quali Tizio svolgeva la propria prestazione professionale in qualità di dottore Commercialista, facendogli intendere che avrebbe potuto occultare l’esposto evitando, in tal modo, delle conseguenze negative a Tizio stesso ed alla società in<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>questione.<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 24.45pt 12pt 34.8pt\"><SPAN style=\"mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>Tizio e Caio si incontravano tre volte presso l’abitazione del dottore Commercialista: nel corso della 1^ riunione si parlava del possibile intervento del Maresciallo per definire la vicenda; nel corso della 2^ era quantificata la somma pretesa dal sottufficiale; durante la 3^ vi era la consegna di una parte della somma.<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 24.45pt 12pt 34.8pt\"><SPAN style=\"mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>Caio si reca da un legale per conoscere quali possono essere le conseguenze della sua condotta.<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 24.45pt 12pt 34.8pt\"><SPAN style=\"mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>Il candidato, assunte le vesti del legale, premessi brevi cenni sui delitti dei pubblici ufficiali contro la Pubblica Amministrazione, rediga motivato parere soffermandosi sulla problematica sottesa al caso in esame.””””<o:p></o:p></FONT></FONT></SPAN></P><PRE style=\"MARGIN-BOTTOM: 12pt; TEXT-INDENT: 35.45pt\"><SPAN style=\"FONT-FAMILY: Garamond; mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>L’aver indicato la specie del pubblico ufficiale, elaborando la traccia in modo da far intendere che le presunte irregolarità si riferissero a fattispecie riconducibili alla competenza della Guardia di finanza, appare del tutto inopportuno, lesivo dell’immagine e del prestigio di quest’ultima.<o:p></o:p></FONT></FONT></SPAN></PRE><PRE style=\"MARGIN-BOTTOM: 12pt; TEXT-INDENT: 35.45pt\"><SPAN style=\"FONT-FAMILY: Garamond; mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>Forse che il proporre il tema nei termini più generici del “pubblico ufficiale” quale soggetto attivo, avrebbe tolto valore alla prova d’esame?<o:p></o:p></FONT></FONT></SPAN></PRE><PRE style=\"MARGIN-BOTTOM: 12pt; TEXT-INDENT: 35.45pt\"><SPAN style=\"FONT-FAMILY: Garamond; mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>Oltretutto, l’ulteriore specificazione del “maresciallo”, appare direttamente denigratoria di una categoria che invece assicura, tra molte difficoltà, l’ordine economico finanziario, con dichiarato apprezzamento del Governo e dell’autorità giudiziaria.<o:p></o:p></FONT></FONT></SPAN></PRE><PRE style=\"MARGIN-BOTTOM: 12pt; TEXT-INDENT: 35.45pt\"><SPAN style=\"FONT-FAMILY: Garamond; mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>Intravedo, pertanto, il rischio che si inneschi nella platea dei giovani candidati, un meccanismo di macchiettistica prevenzione verso una categoria di tutori dell’ordine, rafforzato dall’ufficialità della sede in cui la questione è stata proposta.<o:p></o:p></FONT></FONT></SPAN></PRE><PRE style=\"MARGIN-BOTTOM: 12pt; TEXT-INDENT: 35.45pt\"><SPAN style=\"FONT-FAMILY: Garamond; mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>La qualità di “militare” portata dal maresciallo della Guardia di finanza, dovrebbe<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>portare il legale evocato nella traccia, a valutare le conseguenze della condotta anche sul piano del Diritto penale militare, che non mi sembra ricompresso tra le materie d’esame.<o:p></o:p></FONT></FONT></SPAN></PRE><PRE style=\"MARGIN-BOTTOM: 12pt; TEXT-INDENT: 35.45pt\"><SPAN style=\"FONT-FAMILY: Garamond; mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>Mi si consenta, inoltre, di rappresentare che anche la categoria dei dottori commercialisti,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>appare lesa dalla diretta attribuzione di un discutibile comportamento professionale.<o:p></o:p></FONT></FONT></SPAN></PRE><PRE style=\"MARGIN-BOTTOM: 12pt; TEXT-INDENT: 35.45pt\"><SPAN style=\"FONT-FAMILY: Garamond; mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>Mi resta quindi difficile comprendere perché lo Stato finisca per evidente sottovalutazione, per denigrare se stesso.<o:p></o:p></FONT></FONT></SPAN></PRE><PRE style=\"MARGIN-BOTTOM: 12pt; TEXT-INDENT: 35.45pt\"><SPAN style=\"FONT-FAMILY: Garamond; mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>Signor Ministro, attendo un Suo intervento che possa dirimere gli evidenti equivoci, ripristinando un clima di fiducia e serenità.<o:p></o:p></FONT></FONT></SPAN></PRE><PRE style=\"MARGIN-BOTTOM: 12pt; TEXT-INDENT: 35.45pt\"><SPAN style=\"FONT-FAMILY: Garamond; mso-bidi-font-family: Arial\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3>&nbsp;<o:p></o:p></FONT></FONT></SPAN></PRE><PRE style=\"MARGIN-BOTTOM: 12pt; TEXT-INDENT: 35.45pt\"><FONT face=\"arial, helvetica, sans-serif\" size=3>                                                               Il Presidente del COCER</FONT></PRE>\r\n<P class=Firmaposizione style=\"MARGIN: 0cm 0cm 0pt 236.4pt\"><FONT face=\"arial, helvetica, sans-serif\"><FONT size=3><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>Gen.B. Paolo Poletti</FONT></FONT></P>\r\n<DIV style=\"mso-element: frame; mso-element-wrap: no-wrap-beside\">\r\n<TABLE height=181 cellSpacing=0 cellPadding=0 width=883 align=center vspace=\"0\" hspace=\"0\">\r\n<TBODY>\r\n<TR>\r\n<TD style=\"BORDER-RIGHT: #c0c0c0; PADDING-RIGHT: 9.35pt; BORDER-TOP: #c0c0c0; PADDING-LEFT: 9.35pt; PADDING-BOTTOM: 9.35pt; BORDER-LEFT: #c0c0c0; PADDING-TOP: 9.35pt; BORDER-BOTTOM: #c0c0c0; BACKGROUND-COLOR: transparent\" vAlign=top align=left height=181><FONT face=Arial size=3></FONT></TD></TR></TBODY></TABLE></DIV><BR clear=all>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"arial, helvetica, sans-serif\">&nbsp;</FONT></P>', 0, '', '', '', 2818, 1, 0, '2026-01-09 18:22:46', '0000-00-00'),
(115, '2003-12-22 00:00:00', 'Dalla rappresentanza militare', 'LA RISPOSTA DEL MINISTERO DELLA GIUSTIZIA', '', 'si è trattato di uno \" spiacevole e certamente non voluto equivoco\".', '<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">MINISTERO DELLA GIUSTIZIA</SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">Il vice capo di Gabinetto&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Roma&nbsp; 19 dicembre 2003</SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">Al Sig. G.B.&nbsp; dott.</SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"> </SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">Paolo&nbsp; Poletti</SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">Presidente del COCER&nbsp;&nbsp;&nbsp; Comando Generale della Guardia di Finanza&nbsp;&nbsp;&nbsp; Roma</SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">Signor&nbsp; Generale</SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">In relazione alla sua nota in data odierna relativa al rincrescimento da Ella rappresentato per l\'oggetto di una delle prove degli ultimi esami per avvocato, voglia consentirmi di esprimerLe il rammarico del Ministero della Giustizia per l\'essersi verificato uno spiacevole e certamente non voluto nè immaginato equivoco.</SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">In particolare, mi permetta di rappresentarLe che le tracce, tutte scelte da una casistica giudiziaria relativa ad episodi realmente verificatisi e predisposte dalla Direzione generale della Giustizia Civile e delle Libere professioni, vengono predisposte cercando di dotare le stesse della veste più realistica possibile, al fine di permettere il miglior espletamento di una prova teorica e pratica nella quale il candidato si cala quanto più è possibile nella veste di un vero avvocato chiamato, nel caso di specie ad assumere una difesa.</SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">Il caso specifico, ed il fatto che si sia fatto riferimento ad un appartenente alla Guardia di Finanza ( nella specie un Maresciallo ) lungi dal voler avere una finalità in alcun modo denigratoria, muoveva unicamente dall\'esigenza di parametrare nella massima verosimiglianza la prova teorico - pratica al caso giudiziario in riferimento.</SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">Se ciò ha potuto ingenerare risentimenti, voglia allora sinceramente gradire scuse non formali ma di vera sostanza, soprattutto alla luce dei rapporti che da sempre ed ancor più nei tempi recenti, vedono assidua collaborazione tra il ministero della Giustizia e l\'onorato Corpo della Guardia di Finanza, specie in relazione alla comune partecipazione in tavoli di studio internazionali ed in iniziative interministeriali.</SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">Mi permetta infine di ritenere inutile soffermarsi poi sull\'antico e consolidato rapporto che lega la Guardia di Finanza tutta, specie attraverso i suoi sottufficiali distaccati presso le Sezioni di Polizia Giudiziaria di tutte le Procure della Repubblica, con la Magistratura italiana.</SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">Sperando così di aver chiarito questo equivoco e sicuro che in nulla potrà risultare modificato il riferito antico e proficuo rapporto, basato proprio ed essenzialmente sul reciproco compito di lotta alle forme di criminalità di carattere fiscale, economico e tributario, voglia pertanto gradire i più cordiali saluti ed auguri per un sereno Natale, nonché i sensi più alti della considerazione e stima per il nobile Corpo Specialistico di Polizia e Militare che Ella, nella sua qualità rappresenta.</SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Suo, mi creda</SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Arial\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Alfonso&nbsp; PAPA</SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT size=5><FONT face=\"Baskerville Old Face\">&nbsp;<o:p></o:p></FONT></FONT></P>', 0, '', '', '', 1882, 1, 0, '2026-01-09 19:45:30', '0000-00-00'),
(116, '2003-12-22 00:00:00', 'Dalla rappresentanza militare', 'LETTERA DI UN DELEGATO AL PRESIDENTE DELLA REPUBBLICA- REPLICA DI ALTRO DELEGATO', '', 'DUE INTERVENTI SUL RIALLINEAMENTO DA DUE PUNTI DI VISTA', '<P>COME E\' NOSTRO COSTUME ABBIAMO RICEVUTO DAL DELEGATO COCER DELLA GUARDIA DI FINANZA CARMINE DI GAETA IL TESTO DI UNA LETTERA&nbsp;DIRETTA DAL DELEGATO COCER DELL\'AERONAUTICA SALVATORE RULLO AL PRESIDENTE DELLA REPUBBLICA, NONCHE\' UNA SUA REPLICA AL CONTENUTO DELLA STESSA. SENZA ENTRARE NEL MERITO DELL\'ARGOMENTO TRATTATO, SUL QUALE ABBIAMO PERALTRO SCRITTO.</P>\r\n<P>CI LIMITIAMO, STAVOLTA,&nbsp;AD OFFRIRE AI NOSTRI VISITATORI I TEMI ENUNCIATI RITENENDOLI COMUNQUE ESPRESSIONE DI POSIZIONI DIVERSE MA TUTTE MOTIVATE.</P>\r\n<P>IL TESTO DELLA LETTERA DI SALVATORE RULLO:</P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Buone feste signor <U>Presidente della Repubblica</U> e… grazie di esistere! grazie per le sue decisioni, grazie per i suoi richiami continui alla democrazia del dialogo e del confronto, per la sua difesa, in nome e per conto di tutti, dei valori fondamentali della costituzione nata dopo il sacrificio estremo di tanti, per quel bel tricolore che sventola sulla sua prestigiosa residenza e che sempre onora e difende, continui così signor Presidente e di fronte ad offese o attacchi al nostro simbolo dell’unità nazionale non ci dovrebbe essere nessuna immunità o.. impunità.<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></B></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Buone feste a tutti i nostri politici, anche a quelli che vorrebbero applicare sempre a metà il motto dei moschettieri di re Artù - tutti per uno - Con la speranza che tutti loro lavorino per tutti noi e che dopo averci \" venduto \" circa 4.000 alloggi della difesa magari si illuminino e si ravvedano programmando un piano di edilizia per tutti i militari bisognosi di alloggi.<o:p></o:p></FONT></SPAN></B></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Buone feste ai nostri GENERALI, quelli illuminati e quelli oscurantisti. ( più avanti vedremo chi sono).<o:p></o:p></FONT></SPAN></B></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Buone feste ai colleghi MARESCIALLI Riallineati dell’ <U>Aeronautica – Esercito – Marina</U> ed a tutti i loro rappresentanti che sono riusciti a sanare una parte della speculazione creata nel 1995. ( La <U>RAPPRESENTANZA MILITARE</U> ottiene qualche risultato? <BR>i <U>CoCeR</U> servono a qualcosa? ebbene si! per ora portiamo all’incasso questo provvedimento e… non mettiamo limiti alla provvidenza ed al nostro impegno con la speranza di ottenerne altri che possano migliorare la nostra condizione di \" lavoratori con le stellette \"). E’ doveroso ricordare che, per quanto riguarda questo provvedimento e vista la nostra costante pressione, i nostri GENERALI si sono comportati da illuminati. <o:p></o:p></FONT></SPAN></B></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Buone feste ai colleghi del CoCeR Carabinieri e Finanza che, lasciandomi molto perplesso, in quanto molti di loro conosco e stimo profondamente per il lavoro e l’impegno comune che spesso portiamo avanti, hanno criticato aspramente il provvedimento che ci Riallinea e ci mette su posizioni simili a quelle che loro avevano raggiunto da anni (1995)!!!<o:p></o:p></FONT></SPAN></B></P>\r\n<P><FONT size=3><B><U><SPAN style=\"FONT-FAMILY: Arial\">A PROPOSITO DI RIALLINEAMENTO DEI MARESCIALLI</SPAN></U></B><B><SPAN style=\"FONT-FAMILY: Arial\"> dell’Aeronautica - Esercito – Marina ; Ai colleghi dei CoCeR Carabinieri e Finanza vorrei fare la seguente domanda: \" Secondo voi, se io incontro per la strada un mio fratello – militare – Carabiniere – Finanziere e gli chiedo :\" caro maresciallo, io mi sono arruolato nel 1979 come te, sto per essere riallineato e mi danno lo stesso grado che tu indossi già da qualche anno, ti senti penalizzato da questo fatto? \" ; <o:p></o:p></SPAN></B></FONT></P>\r\n<P><FONT size=3><B><U><SPAN style=\"FONT-FAMILY: Arial\">Secondo me risponde di NO</SPAN></U></B><B><SPAN style=\"FONT-FAMILY: Arial\"> .<o:p></o:p></SPAN></B></FONT></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Ti ricordo anche, caro fratello, che la decorrenza economica sarà dal gennaio 2003 e che tutto il danno economico che abbiamo subito prima di quella data non sarà restituito (dal 1995)!<o:p></o:p></FONT></SPAN></B></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Caro fratello, aggiungo anche che molti avranno solo un’anzianità retroattiva nel grado che rivestono. Il Riallineamento è anche questo ed i tuoi rappresentanti lo hanno contrasto!.<o:p></o:p></FONT></SPAN></B></P>\r\n<P><B><U><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Secondo me risponde che era solo una cosa logica e non si capisce perché abbiamo dovuto aspettare tanto per essere come lui!. <o:p></o:p></FONT></SPAN></U></B></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>E se a questo fratello militare Carabiniere o finanziere dico: \" dopo il riallineamento si farà insieme un <U>riordino delle carriere che riguarda tutto il personale non direttivo</U>, e si fa anche sulla scia di questo provvedimento, sei contrario a questo?\".<o:p></o:p></FONT></SPAN></B></P>\r\n<P><B><U><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Secondo me risponde di NO e mi dice che questa è la strada giusta da percorrere!.<o:p></o:p></FONT></SPAN></U></B></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Allora se c’è qualcuno che pensa che indossare una divisa di un colore consenta di sentirsi superiori o \" migliori \" rispetto ad altri che indossano un colore diverso, sicuramente ha sbagliato considerazione e tattica dimenticando che la formula del giuramento che abbiamo pronunciato è uguale per tutti i militari….. sarebbe anche molto interessante un confronto tra tutti i rappresentanti di tutti i livelli di Esercito – Aeronautica – Marina – Carabinieri – Finanza, per verificare quanto e come, i rappresentanti a livello centrale, le \" teste pensanti di Roma\" rappresentano effettivamente gli interessi della base. <o:p></o:p></FONT></SPAN></B></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Buone feste a tutti quelli che lottano per migliorare i diritti dei lavoratori con le stellette e solo per questo sono considerati, dagli oscurantisti, dei nemici delle istituzioni.<o:p></o:p></FONT></SPAN></B></P>\r\n<P><B><U><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>E’ incredibile come non si capisce che dare più competenze alla Rappresentanza ed alle associazioni riconosciute ( senza pregiudizi ), vuol dire anche migliorare l’intera organizzazione delle forze armate.<o:p></o:p></FONT></SPAN></U></B></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Buone feste agli <U>oscurantisti che si oppongono in modo fermo</U> ad una nuova legge di riforma della Rappresentanza Militare e che non vorrebbero consentirci di migliorare neanche di una virgola il nostro sistema di tutela dei militari. <o:p></o:p></FONT></SPAN></B></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Buone feste anche a quei pochi che vorrebbero ritornare al motto di: <U>Credere, Obbedire</U>, <U>Combattere</U>, proprio il motto che calza perfettamente ai terroristi che seminano morte e distruzioni nel mondo.<o:p></o:p></FONT></SPAN></B></P>\r\n<P><FONT size=3><B><U><SPAN style=\"FONT-FAMILY: Arial\">Credendo</SPAN></U></B><B><SPAN style=\"FONT-FAMILY: Arial\"> ( in modo distorto a principi religiosi ), <U>Obbedendo</U> ( in nome solo di una fede cieca e senza nessun’umana riflessione), <U>Combattendo</U> ( uccidendo uomini e donne innocenti che pagano le colpe di chi dovrebbe risolvere i problemi internazionali).<o:p></o:p></SPAN></B></FONT></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Un abbraccio a tutti i parenti di tutti i caduti nell’assolvimento del loro dovere e della loro missione, senza distinzione di appartenenza, grado o impegno umanitario. <o:p></o:p></FONT></SPAN></B></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Buone feste ai potenti del mondo con la speranza che la soluzione delle controversie non sia solo quella dei conflitti e delle guerre ma il confronto ed il dialogo.<o:p></o:p></FONT></SPAN></B></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Visto che questa serie di \" buone feste \" che state leggendo si riferisce soprattutto ad una festa che non è solo consumistica ma, principalmente religiosa, al Natale, vi voglio salutare con un passaggio della preghiera dei fedeli recitata nella chiesa più grande di Roma qualche giorno fa: <o:p></o:p></FONT></SPAN></B></P>\r\n<P><B><U><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>- Per la pace e l’unità dei popoli: perché lo Spirito Santo solleciti i governanti d’ogni nazione e tutti gli uomini di buona volontà ad unire i loro sforzi per costruire un mondo più giusto, nel quale la dignità dell’uomo, le risorse naturali ed il progresso scientifico si sviluppino in pace e in armonioso equilibrio a vantaggio di tutti. -<o:p></o:p></FONT></SPAN></U></B></P>\r\n<P><FONT size=3><B><U><SPAN style=\"FONT-FAMILY: Arial\">Vi saluto fratelli militari, con la speranza che le armi del rancore e della vendetta siano deposte DEFINITIVAMENTE</SPAN></U></B><B><SPAN style=\"FONT-FAMILY: Arial\">.<o:p></o:p></SPAN></B></FONT></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Buone feste a tutti i militari ed alle loro famiglie, soprattutto alle nostre donne ( madri, mogli, compagne, fidanzate che condividono con noi la condizione di lavoratori con le stellette che molte volte le costringe a rinunce affettive ed economiche).<o:p></o:p></FONT></SPAN></B></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Salvatore RULLO</FONT></SPAN></B></P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3></FONT></SPAN></B>&nbsp;</P>\r\n<P><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3><o:p>IL TESTO DELLA REPLICA:</o:p></FONT></SPAN></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P>\r\n<P class=MsoTitle style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=5>IL SALVATORE E’ UNO SOLO E NON SI CHIAMA DI CERTO RULLO ! </FONT></P>\r\n<P class=MsoTitle style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"mso-spacerun: yes\"><FONT face=\"Times New Roman\" size=5>&nbsp;</FONT></SPAN></P>\r\n<P class=MsoTitle style=\"MARGIN: 0cm 0cm 0pt\"><I><FONT size=5><FONT face=\"Times New Roman\">Libera manifestazione di pensiero<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">di Carmine Di Gaeta <o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Devo una replica, agli imposti voti augurali, elargiti con <I>perplessità,</I> dell’<I>asceta</I> delegato Cocer: M.c. Salvatore RULLO, Comparto Difesa, che non è mio <I>fratello</I>. <o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Caro Salvatore,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>nel tuo vergato ti produci in una commistione di:<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">-<SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">predicatore, prodigo di voti augurali a tutti<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>(anche a quelli cattivi,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>naturalmente da te individuati: <I>i politici, </I><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN><I>gli oscurantisti…</I>a proposito, chi sono?);<o:p></o:p></SPAN></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">-<SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">veggente, di marzulliana memoria ( incontrando per strada i marescialli della Guardia di Finanza e dei Carabinieri, poni loro domande a cui dai tu stesso risposte);<o:p></o:p></SPAN></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">-<SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">profeta, …<I>la strada giusta da percorrere.</I><o:p></o:p></SPAN></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><I><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Per non parlare delle citazioni, il tuo pezzo forte: “… </FONT></SPAN><FONT size=3><I><SPAN style=\"FONT-FAMILY: Arial\">applicare sempre a metà il motto dei <U>moschettieri di re Artù</U> - tutti per uno -</SPAN></I><B><SPAN style=\"FONT-FAMILY: Arial\"> ….”.</SPAN><o:p></o:p></B></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Alexandre Dumas si sta rivoltando nella tomba; Aramis, Porthos e Athos ( “I tre moschettieri”) stanno cercando d’Artagnan<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>mentre re Artù ha convocato una seduta straordinaria con i suoi Cavalieri: tutti per decidere come salvare l’onore!<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Ma è il tono sentenziale, l’oggetto vero della lettera (<I>il riallineamento) </I>e l’uso spropositato di certi vocaboli (<I>sperequazione</I>)<I> </I>che m’impone di fare un ragionamento con te, delegato del Comparto Difesa.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=5>Nel 1995 tutte le componenti dei<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>distinti Comparti hanno ricevuto, in maniera proporzionale, le risorse per adeguare, riassettare e riordinare i ruoli di ciascun Corpo o Arma. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Quindi, anche il Comparto Difesa ha avuto il suo spettato,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN><U>solo che è stato utilizzato in maniera diversa.</U><o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Pertanto, dove sta la sperequazione ? A chi vuol ricevere due, tre o quattro volte?<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Nella Guardia di Finanza ci sono attualmente diversi Marescialli Capo, arruolati come te nel 1979 che rivestono tale grado.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Chi va a dirgli che per una presunta “sperequazione” sono stati scavalcati ? Dov’è l’equità di tale provvedimento?<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Ma la vera sperequazione è stata nel 1995: includere tutti nel calderone del riordino, anche<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>quei Corpi che fino a quel momento inquadravano, <U>nel servizio permanente effettivo</U>, solo sottufficiali e ufficiali.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\"><B><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">Se dovesse davvero passare il riallineamento, converrà sicuramente che tutti gli Appuntati e Sovrintendenti del Comparto Sicurezza chiedano di passare nel Comparto Difesa per vedersi ricostruire la carriera, per un diritto di equità</SPAN></B><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">.<o:p></o:p></SPAN></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Sicuramente mi risponderai che quanto da me esposto è una cosa diversa.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Sono d’accordo con te! <U>Siamo</U> diversi!<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Vedi, il riordino è nato da un ricorso collettivo di Carabinieri e Finanzieri che reclamavano un trattamento equo nei confronti dei colleghi della Polizia di Stato, i quali, a parità di competenza di polizia giudiziaria o tributaria, rivestivano un grado del ruolo superiore. <o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Mi chiedo dove siano le tue paritetiche competenze di polizia giudiziaria o tributaria, senza per questo polemizzare o dare<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>percezione di onniscienza.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">E in guerra ci vanno ogni giorno anche migliaia di poliziotti, carabinieri e finanzieri sulle nostre strade.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Questa, purtroppo, è una guerra tra poveri che si poteva risolvere in maniera più armonica e ragionata senza l’imposizione dell’atto in finanziaria.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Mi duole anche la certezza che è stato partorito in un periodo di alta emotività, senza avere la possibilità e il tempo necessario per un giusto approfondimento.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Avreste realizzato un riallineamento, sicuramente confacente alle vostre aspettative, se aveste avuto la pazienza e l’intenzione di separare prima i comparti; in questo modo, invece, dovrete concertarlo anche con noi. <o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Un’ultima cosa.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Nei tuoi scritti sentenzi spesso valutazioni e giudizi sulle persone, a volte individuandoli nominalmente.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Non credo tu possegga il dono del Verbo, tanto meno la Potestà Divina per giudicare uomini e cose,… non ti confondere, il Salvatore è uno solo.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Pertanto, se posso esprimere un consiglio: smetti i panni Celesti ed indossa invece l’abito dell’umiltà, accettando il contraddittorio anche quando non gradito: è un principio elementare di democrazia.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">BUONE FESTE.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><I><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>M.O. Carmine Di Gaeta,<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><I><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>delegato Cocer G. di F., Comparto Sicurezza.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P>&nbsp;</P></P>', 0, '', '', '', 2457, 1, 0, '2026-01-13 13:11:50', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(118, '2004-01-07 00:00:00', 'Dal Parlamento', 'D.P.R.19 novembre 2003 n. 348- Recepimento dell\'accordo sindacale e del provvedimento di concertazione integrativa. (G.U. n. 298 del 24 dicembre 2003).\r\nIniziamo con il D.P.R. in oggetto la pubblicazione dei provvedimenti che interessano il personale ...', '', 'D.P.R.19 novembre 2003 n. 348', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><B><SPAN style=\"FONT-FAMILY: TimesNewRoman,Bold\"><FONT size=3><FONT face=\"Times New Roman\">DECRETO DEL PRESIDENTE DELLA REPUBBLICA<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>19 novembre 2003, n. 348<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></SPAN></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><B><SPAN style=\"FONT-SIZE: 13pt; FONT-FAMILY: TimesNewRoman,Bold\"><FONT face=\"Times New Roman\">Recepimento dell\'accordo sindacale e del provvedimento di concertazione integrativi<o:p></o:p></FONT></SPAN></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><B><SPAN style=\"FONT-SIZE: 13pt; FONT-FAMILY: TimesNewRoman,Bold\"><FONT face=\"Times New Roman\">per il personale non dirigente delle Forze di polizia ad ordinamento civile e militare.<o:p></o:p></FONT></SPAN></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><FONT face=\"Times New Roman\"><B><SPAN style=\"FONT-SIZE: 13pt; FONT-FAMILY: TimesNewRoman,Bold\">(</SPAN></B><B><I><SPAN style=\"FONT-SIZE: 13pt; FONT-FAMILY: TimesNewRoman,BoldItalic\">G.U. n. 298 del 24-12-2003</SPAN></I></B><B><SPAN style=\"FONT-SIZE: 13pt; FONT-FAMILY: TimesNewRoman,Bold\">)<o:p></o:p></SPAN></B></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: TimesNewRoman\"><FONT size=3><FONT face=\"Times New Roman\">(testo in vigore dal: 8-1-2004)<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: CourierNewPSMT\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>IL PRESIDENTE DELLA REPUBBLICA<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">Visto l\'articolo 87 della Costituzione;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">Visto il decreto legislativo 12 maggio 1995, n. 195, recante norme<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">sulle «Procedure per disciplinare i contenuti del rapporto di impiego del<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">personale di polizia e delle Forze armate»;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">Visti gli articoli 1, 2 e 7 del citato decreto legislativo n. 195 del<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">1995, che disciplinano le procedure negoziali e di concertazione, da<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">avviare, sviluppare e concludere con carattere di contestualità, per<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">l\'adozione di separati decreti del Presidente della Repubblica<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">concernenti, rispettivamente, il personale delle Forze di polizia ad<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">ordinamento civile e militare, nonché del personale delle Forze armate,<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">con esclusione dei rispettivi dirigenti civili e militari, del personale<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">di leva ed ausiliario di leva;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">Viste le disposizioni degli articoli 2 e 7 del predetto decreto<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">legislativo n. 195 del 1995 relative alle modalità di costituzione delle<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">delegazioni di parte pubblica, delle delegazioni sindacali e dei<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">rappresentanti del Consiglio centrale di rappresentanza che partecipano<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">alle richiamate procedure negoziali e di concertazione, rispettivamente<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: CourierNewPSMT\"><FONT face=\"Times New Roman\">per le Forze di polizia ad ordinamento civile (Polizia di Stato, Corpo<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNo