INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(153, '2004-02-01 00:00:00', 'Editoriale', 'GLI INTERVENTI SUL FORUM AL 31/1/2004\r\nDistinti per argomento dimostrano grande sensibilità verso i temi\r\nche quotidianamnte interessano il mondo militare.', '', '', '', 28, '', '', '', 1818, 1, 0, '2025-11-19 08:28:53', '0000-00-00'),
(154, '2004-02-02 00:00:00', 'Dal Parlamento', 'Pubblicato in G.U. nr 24 del 30/1/2004 il Decreto che attribuisce un assegno di valorizzazione dirigenziale per i funzionari del ruolo dei commissari e qualifiche o gradi corrispondenti della stessa Polizia di Stato, delle altre Forze di polizia e delle F', '', 'Pubblicato in G.U. nr 24 del 30/1/2004 il Decreto che attribuisce un assegno di valorizzazione dirigenziale per i funzionari del ruolo dei commissari e qualifiche o gradi corrispondenti della stessa Polizia di Stato, delle altre Forze di polizia e delle F', '<FONT face=\"times new roman, times, serif\">\r\n<TABLE class=MsoNormalTable style=\"WIDTH: 100%; mso-cellspacing: 2.2pt; mso-padding-alt: 0cm 0cm 0cm 0cm\" cellPadding=0 width=\"100%\" border=0>\r\n<TBODY>\r\n<TR style=\"mso-yfti-irow: 0; mso-yfti-lastrow: yes\">\r\n<TD style=\"BORDER-RIGHT: #ece9d8; PADDING-RIGHT: 0cm; BORDER-TOP: #ece9d8; PADDING-LEFT: 0cm; PADDING-BOTTOM: 0cm; BORDER-LEFT: #ece9d8; PADDING-TOP: 0cm; BORDER-BOTTOM: #ece9d8; BACKGROUND-COLOR: transparent\">\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>DECRETO 23 </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\">dicembre 2003<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></P></TD>\r\n<TD style=\"BORDER-RIGHT: #ece9d8; PADDING-RIGHT: 0cm; BORDER-TOP: #ece9d8; PADDING-LEFT: 0cm; PADDING-BOTTOM: 0cm; BORDER-LEFT: #ece9d8; PADDING-TOP: 0cm; BORDER-BOTTOM: #ece9d8; BACKGROUND-COLOR: transparent\">\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\">Determinazione dell\'assegno di valorizzazione dirigenziale per i funzionari del ruolo dei commissari e qualifiche o gradi corrispondenti della stessa Polizia di Stato, delle altre Forze di polizia e delle Forze armate, ai sensi dell\'art. 33, comma 2, della legge 27 dicembre 2002, n. 289. <SPAN style=\"COLOR: #058940\">(<I>GU n. 24 del 30-1-2004</I>) </SPAN><o:p></o:p></FONT></FONT></P></TD></TR></TBODY></TABLE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>IL MINISTRO PER LA FUNZIONE PUBBLICA<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>di concerto con<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">I MINISTRI DELL\'ECONOMIA E DELLE FINANZE, DELL\'INTERNO, DELLA DIFESA,<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;</SPAN>DELLA GIUSTIZIA E DELLE POLITICHE AGRICOLE E FORESTALI<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Visto l\'art. 33, comma 2, della legge 27 dicembre 2002, n. 289, che<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">ha<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>stanziato 35 milioni di euro per ciascuno degli anni 2003, 2004 e<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">2005,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>al fine di assicurare una graduale valorizzazione dirigenziale<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">dei<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>trattamenti<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>economici dei funzionari del ruolo dei commissari e<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">qualifiche<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>o<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>gradi<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>corrispondenti<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>della<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>stessa Polizia di Stato,<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">delle altre Forze di polizia e delle Forze armate;<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Visto<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>il<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>decreto<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>legislativo<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>30 maggio 2003, n. 193, recante il<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">sistema<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>dei<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>parametri<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>stipendiali<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>per<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>il personale non dirigente<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">delle<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Forze<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>di<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>polizia<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>e<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>delle Forze armate, a norma dell\'art. 7<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">della legge 29 marzo 2001, n. 86;<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Considerato<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>che<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>il<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>predetto art. 33, comma 2, della legge n. 289<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">del<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>2002,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>prevede<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>che la ripartizione delle risorse possa avvenire<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">anche attraverso l\'attribuzione di trattamenti perequativi al fine di<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">realizzare<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>una valorizzazione graduale dei trattamenti economici del<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">personale<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>direttivo<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>sopra<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>richiamato <SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>«fino<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>a<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>quando non saranno<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">approvate le norme per il riordinamento della dirigenza del personale<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">delle<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Forze<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>di<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>polizia<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>ad<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>ordinamento civile e agli ufficiali di<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">grado corrispondente delle Forze di polizia ad ordinamento militare e<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">delle<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Forze<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>armate,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>in<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>armonia<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>con i trattamenti economici della<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">dirigenza<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>pubblica<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>e<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>tenuto<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>conto<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>delle disposizioni del decreto<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">legislativo 30 marzo 2001, n. 165 ...»;<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Ritenuto<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>di<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>dover<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>attuare<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>la<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>valorizzazione<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>dirigenziale<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>dei<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">trattamenti<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>economici<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>del personale interessato con gradualita\', in<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">relazione:<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>a) alla<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>rilevanza<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>delle<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>funzioni<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>in<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>rapporto all\'ordinamento<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">gerarchico dei ruoli;<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>b) alla necessita\' di assicurare un rilievo economico adeguato in<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">rapporto<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>alle<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>risorse<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>disponibili,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>tenuto<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>anche<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>conto<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>degli<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">incrementi<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>conseguenti<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>all\'introduzione<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>del<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>sistema dei parametri<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">stipendiali<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>di<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>cui<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>al<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>decreto<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>legislativo n. 193 del 2003, sopra<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">richiamato;<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Ritenuto<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>pertanto necessario, in relazione alle risorse stanziate,<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">attuare<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>la prevista graduale valorizzazione dei predetti trattamenti<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">economici<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>con riguardo al personale che riveste la qualifica di vice<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">questore aggiunto o qualifica corrispondente della Polizia di Stato e<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">le<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>qualifiche<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>e gradi corrispondenti delle altre Forze di polizia e<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">delle<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Forze<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>armate,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>in<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>relazione<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>alle<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>funzioni<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>di piu\' elevato<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">livello,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>alla<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>maggiore<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>contiguita\'<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>dello<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>stesso<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>personale con i<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">dirigenti<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>delle<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>medesime<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Forze<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>di polizia e delle Forze armate ed<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">alla<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>prevista<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>sostituzione<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>degli<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>stessi dirigenti, con precedenza<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">rispetto al personale con qualifica di commissario capo e qualifica o<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">grado corrispondente;<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Decreta:<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Art. 1.<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>1.<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Fino<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>a<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>quando<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>non<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>saranno<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>approvate<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>le<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>norme<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>per<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>il<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">riordinamento<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>della dirigenza del personale delle Forze di polizia e<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">delle<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Forze<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>armate,<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>ai<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>vice<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>questori<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>aggiunti<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>e<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>qualifiche<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">corrispondenti<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>della<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Polizia<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>di<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Stato,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>al<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>personale che riveste<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">qualifiche corrispondenti delle altre Forze di polizia ad ordinamento<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">civile,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>nonche\'<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>ai<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>tenenti<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>colonnelli<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>e<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>maggiori delle Forze di<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">polizia<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>ad<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>ordinamento<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>militare e gradi corrispondenti delle Forze<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">armate<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>e\' attribuito, a decorrere dal 1° gennaio 2003, un assegno di<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">valorizzazione<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>dirigenziale<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>di<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>1.752<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>euro annui lordi per tredici<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">mensilita\',<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>utilizzando per ciascuno degli anni 2003, 2004 e 2005 lo<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">stanziamento<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>di<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>35<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>milioni<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>di<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>euro, di cui all\'art. 33, comma 2,<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">della legge 27 dicembre 2002, n. 289.<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>2.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>A<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>decorrere<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>dall\'anno<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>2006,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>con decreto del Ministro per la<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">funzione<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>pubblica, di concerto con il Ministro dell\'economia e delle<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">finanze,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>il<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Ministro dell\'interno e gli altri Ministri interessati,<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">l\'assegno di cui al comma 1 e\' rideterminato ogni triennio sulla base<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">dell\'entita\'<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>dei<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>destinatari, fermo restando lo stanziamento di cui<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">all\'art. 33, comma 2, della legge 27 dicembre 2002, n. 289.<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>3.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>L\'assegno<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>di<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>valorizzazione<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>dirigenziale<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>di cui al presente<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">articolo<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>e\' pensionabile ai sensi dell\'art. 13, comma 1, del decreto<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">legislativo<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>30 dicembre<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>1992, n. 503, e non produce effetti ai fini<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">della<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>determinazione<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>dell\'indennita\'<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>di<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>ausiliaria<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>e<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">dell\'attribuzione<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>di<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>qualsiasi<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>altro<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>beneficio<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>economico<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>per<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">promozione e scatti conferibili il giorno antecedente alla cessazione<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">dal servizio.<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Il<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>presente<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>decreto<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>sara\'<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>trasmesso alla Corte dei conti per la<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">registrazione<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>e pubblicato nella Gazzetta Ufficiale della Repubblica<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">italiana.<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>Roma, 23 dicembre 2003<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Il Ministro per la funzione pubblica<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Mazzella<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Il Ministro dell\'economia e delle finanze<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Tremonti<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Il Ministro dell\'interno<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</SPAN>Pisanu<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Il Ministro della difesa<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Martino<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Il Ministro della giustizia<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Castelli<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Il Ministro delle politiche agricole e forestali<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Alemanno<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">Registrato alla Corte dei conti il 23 gennaio 2004<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">Presidenza<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>del<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Consiglio<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>dei<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Ministri,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Ministeri<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>istituzionali,<o:p></o:p></SPAN></PRE><PRE style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">registro n. 1, foglio n. 134<o:p></o:p></SPAN></PRE>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT size=3>&nbsp;</FONT></o:p></P></FONT>', 0, '', '', '', 1554, 1, 0, '2026-01-14 04:17:12', '0000-00-00'),
(322, '2004-09-18 00:00:00', 'Dalle autorità giurisdizionali', 'Altra importante sentenza del TAR Lazio sul fenomeno mobbing.', '', 'Altra importante sentenza del TAR Lazio sul fenomeno mobbing.', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">Proseguendo nell\'attività di ricerca sull\'evoluzione della giurisprudenza sul tema del mobbing risulta importante,&nbsp;l\'allegata Sentenza 25 giugno 2004 della III sezione bis del&nbsp;T.A.R. Lazio, tratta integralmente da <A href=\"http://wpop8.libero.it/cgi-bin/vlink.cgi?Link=http%3A//www.giustizia%2Damministrativa.it/\" target=_blank><EM><B><SPAN style=\"COLOR: #003399; TEXT-DECORATION: none; text-underline: none\">www.giustizia-amministrativa.it</SPAN></B></EM></A><EM><B>,</B></EM> e commentata in <A href=\"http://wpop8.libero.it/cgi-bin/vlink.cgi?Link=http%3A//www.giurisprudenza.net/\" target=_blank><EM><B><SPAN style=\"COLOR: #003399; TEXT-DECORATION: none; text-underline: none\">www.giurisprudenza.net</SPAN></B></EM></A>, che&nbsp; sembra veramente una sorta di punto di svolta in tema di vessazioni nel pubblico impiego.<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">Secondo noi i principi di diritto ivi enucleati possano essere applicati anche alla<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>categoria dei militari, per tutte le motivazioni già<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>evidenziati in vari interventi sul sito e sulla rivista.<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">Gli aspetti rilevanti della sentenza,&nbsp;potrebbero essere così sintetizzati:<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">1.</SPAN></STRONG><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\"> Il danno da mobbing può essere dato in concorso fra responsabilità&nbsp;extracontrattuale e contrattuale.<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">Per tale motivo, può essere chiamato davanti il G.A. a risarcire il danno anche il singolo funzionario responsabile del procedimento; questa è già una prima importante novità, atteso che fino ad oggi nel processo amministrativo poteva essere chiamata in causa solo la P.A..<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">Infatti, alla fine della sentenza è scritto che al danno riconosciuto nel caso in esame. non possono esser condannati anche i singoli funzionari responsabili solo perché, in quella fattispecie, è mancata una rituale vocatio in ius degli stessi.<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">2.</SPAN></STRONG><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\"> Il mobbing è, quindi, un concetto giuridico e non solo patrimonio degli psicologi del lavoro (con buona pace dei vari Harald Ege); è evidente<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>la volontà del giudice di ancorare tale fenomeno a parametri prettamente giuridici.<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">3.</SPAN></STRONG><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\"> Il danno da mobbing non presuppone che <STRONG><U>tutti</U></STRONG> gli atti persecutori siano stati previamente dichiarati illegittimi dalla competente Autorità Giudiziaria (concetto del mobbing inteso quale &lt;&lt; legal framework&gt;&gt;, coniato dalla cd. scuola torinese - Cfr., \"\"Il danno non patrimoniale\"\", Monateri, Bona, Ipsoa maggio 2004 -, e recepito dalla giurisprudenza civile prima ed amministrativa poi.);<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">4.</SPAN></STRONG><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\"> Il danno non patrimoniale è attualmente configurato secondo la giurisprudenza di Cass. III Civ. 31 maggio 2003 nr.8827 - 8828 e da Corte Costituzionale 11 luglio 2003 nr.233 e 19 dicembre 2003 nr.359.<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">5.</SPAN></STRONG><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\"> Il danno esistenziale e morale derivante dalle vessazioni sul lavoro è <STRONG><U>IN RE IPSA</U></STRONG>; conseguenza importantissima sul piano probatorio è che, provata la vessazione, viene <STRONG><U>automaticamente provato</U></STRONG> il danno nella sua sussistenza ontologica concreta in capo al lavoratore.<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">6.</SPAN></STRONG><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\"> La quantificazione del danno non patrimoniale, secondo il nuovo corso giurisprudenziale, è determinata in via equitativa, ex artt.1226 e 2056 Codice Civile, in difetto di precise allegazioni attoree.<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">7.</SPAN></STRONG><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\"> Non deve essere necessariamente dimostrata l\'intenzione di danneggiare il lavoratore, in quanto, secondo il TAR Lazio, quello che rileva è l\'oggettività persecutoria delle condotte poste in essere.<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><B><SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana\">RINGRAZIAMO PER LA SEGNALAZIONE IL NOSTRO, SEMPRE AGGIORNATO, PROMETEO.</SPAN><o:p></o:p></B></P>', 90, '', '', '', 696, 1, 0, '2024-12-14 00:39:07', '0000-00-00'),
(157, '2004-02-02 00:00:00', 'Ordine del giorno', 'FORUM. Gli interventi sono subordinati all\'accettazione dello stile che da sempre ha distinto l\'operato della Associazione.', '', 'FORUM. Gli interventi sono subordinati all\'accettazione dello stile che da sempre ha distinto l\'operato della Associazione.', '<P>Ho avuto modo di sottolineare in varie occasioni la bontà dei contenuti dei vari interventi che hanno alimentato il dibattito sul FORUM relativamente ai vari argomenti introdotti, tutti interessanti sia per gli appartenenti al Corpo della Guardia di Finanza sia per i cittadini che tutti, in qualche modo, hanno a che fare con il fisco e hanno il diritto di pretendere che chi lo tutela sia motivato e preparato.</P>\r\n<P>Tutti hanno capito l\'importanza dello strumento che ci viene offerto, molti ne hanno intuito la delicatezza.</P>\r\n<P>Abbiamo cancellato alcuni messaggi&nbsp; di persone che al contrario non hanno&nbsp;voluto comprendere&nbsp;lo spirito del FORUM: nessun argomento è precluso, tutto si può dire, ma con toni ed espressioni&nbsp;che non siano offensive per nessuno, inteso sia come categoria che come singola persona.</P>\r\n<P>Se capiremo questo potremo continuare senza problemi, offrendo a chi lo reputi&nbsp; importante uno spaccato del nostro modo di essere militari e cittadini, se non lo capiremo daremo lo spunto a chi vede in questa possibilità di espressione un elemento negativo di poterci criticare e anche fermare.&nbsp;</P>', 0, '', '', '', 1596, 1, 0, '2026-01-10 02:18:23', '0000-00-00'),
(158, '2004-02-03 00:00:00', 'Editoriale', 'RIVISTA. Finalmente disponibile in pdf l\'intero numero della rivista \"FINANZIERI E CITTADINI\"', '', '', '', 32, '', '', '', 1958, 1, 0, '2026-01-14 10:50:38', '0000-00-00'),
(225, '2004-05-06 00:00:00', 'Ordine del giorno', 'SABATO 8 MAGGIO 2004, ORE 10, A BELLARIVA DI RIMINI,\r\nIMPORTANTE CONVEGNO NAZIONALE DELL’ASSOCIAZIONE \r\nSOLIDARIETA’ DIRITTO E PROGRESSO – ASSODIPRO\r\nSARA\' PRESENTE UNA DELEGAZIONE DI FICIESSE\r\nINTERVENITE NUMEROSI', '', 'SABATO 8 MAGGIO 2004, ORE 10, A BELLARIVA DI RIMINI,\r\nPRESSO IL CINEMA TEATRO BELLARIVA, IN VIALE REGINA MARGHERITA 41\r\nAVRA’ LUOGO IL CONVEGNO NAZIONALE\r\nDELL’ASSOCIAZIONE SOLIDARIETA’ DIRITTO E PROGRESSO – ASSODIPRO\r\n\r\n', '<P style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-FAMILY: \'Times New Roman\'\"><FONT size=3>SUL TEMA<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-FAMILY: \'Times New Roman\'\"><FONT size=3>“<B>DIFESA E SICUREZZA NELLA POLITICA NAZIONALE E COMUNITARIA<o:p></o:p></B></FONT></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><FONT size=3><B><SPAN style=\"FONT-FAMILY: \'Times New Roman\'\">IL MODELLO DI TUTELA PROFESSIONALE DEI MILITARI ITALIANI</SPAN></B><SPAN style=\"FONT-FAMILY: \'Times New Roman\'\">”<o:p></o:p></SPAN></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\"><FONT size=3>&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><FONT face=\"Times New Roman\" size=3>PARTECIPERANNO NUMEROSI PARLAMENTARI </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><FONT face=\"Times New Roman\" size=3>E UNA DELEGAZIONE DI FICIESSE </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><FONT face=\"Times New Roman\"><FONT size=3>&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><FONT face=\"Times New Roman\" size=3>TUTTI GLI INTERESSATI SONO INVITATI A PARTECIPARE</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\"><FONT size=3>&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyTextIndent3 style=\"MARGIN: auto 0cm; TEXT-ALIGN: center\" align=center><FONT size=3><B><SPAN style=\"COLOR: red; FONT-FAMILY: Arial\">Per informazioni e contatti chiamare i numeri:</SPAN></B><B><SPAN style=\"FONT-FAMILY: Arial\"> </SPAN></B></FONT></P>\r\n<P class=MsoBodyTextIndent3 style=\"MARGIN: auto 0cm; TEXT-ALIGN: center\" align=center><B><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>3409479069 - 06 70304885 - 0733 672644 - 045 6304078</FONT></SPAN></B></P>', 55, '', '', '', 4060, 1, 0, '2025-12-18 05:53:01', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(226, '2004-05-10 00:00:00', 'Lettere', 'Richiesta ai Segretari dei Partiti politici per chiedere chi vorrà essere portavoce in Europa delle problematiche dei militari.', '', 'Richiesta ai Segretari dei Partiti politici per chiedere chi vorrà essere portavoce in Europa delle problematiche dei militari.', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Questa breve introduzione ha lo scopo di accompagnare una lettera che, in <SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>accordo con tutti i componenti della Segreteria Nazionale, ho deciso di inviare ai Segretari dei partiti politici nell’imminenza delle votazioni per il rinnovo del Parlamento europeo.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>La nostra Associazione è, per Statuto, apolitica e apartitica: è questo un principio dal quale assolutamente non si può prescindere. E’ altrettanto chiaro, ormai, che gli obiettivi che perseguiamo, cito solo i più importanti, la libertà di espressione, la libertà di associazione, il riconoscimento di veri strumenti di tutela, un giusto rapporto tra cittadino e fisco, sono raggiungibili solo attraverso la politica. Il loro riconoscimento passa attraverso modifiche legislative la cui approvazione è spesso l’arrivo di percorsi sofferti e travagliati; basti pensare alla riforma della rappresentanza militare in Commissione Difesa ormai da due legislature.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Non sono più tra coloro che credono in mutamenti, in questi campi, che possano costituire il frutto di evoluzioni progressive interne. Dico “non più” con rammarico, poiché in passato ero invece, un convinto sostenitore della tesi opposta. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Le leggi, lo sappiamo tutti, le formula o le modifica il Parlamento, quello italiano sino a qualche tempo addietro, oggi, anche quello europeo. Il Parlamento è formato da Deputati e da Senatori che sono eletti dai cittadini.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Tra i cittadini<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>ci sono anche i militari. Anzi oggi molti militari hanno capito di essere prima cittadini e poi militari, hanno, cioè acquisito la consapevolezza del diritto-dovere di partecipare, con tutte le cautele previste dalle norme, alla “politica”, al “governo della città”, inteso quale contesto sociale, ai vari livelli, in cui può esprimere le proprie aspirazioni ed evidenziare i propri bisogni.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Tutti hanno compreso quindi che occorre essere rappresentati nelle sedi ove “si decide”. E questa rappresentanza non deve e non può più essere generica, deve essere diretta, deve, per così dire, “rispondere”.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Tutto questo per sostenere, con convinzione, di essere nella piena legittimità quando chiedo, nella veste di Segretario Generale dell’Associazione “FINANZIERI, CITTADINI e SOLIDARIETA’”, ai Segretari dei vari partiti politici di inserire nei loro programmi per le prossime elezioni europee, l’ impegno alla soluzione dei problemi che affliggono i “cittadini militari”, primo fra tutti quello della libertà di associazione; altrettanto giusto, mi sembra, chiederlo ai singoli candidati delle diverse liste che compongono gli schieramenti.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\">L’iniziativa si riassume nella lettera che ho inviato in data 5 maggio e che allego a questa breve premessa.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Il segretario generale</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</SPAN>Carlo GERMI</FONT></FONT></P>', 57, '', '', '', 2098, 1, 0, '2026-01-10 01:29:50', '0000-00-00'),
(227, '2004-05-10 00:00:00', 'Lettere', 'Risposta alla lettera del Segretario Generale  da parte dell\'On/le Carlo FATTUZZO, Parlamentare Europeo e Segretario del Partito Pensionati.', '', 'Risposta alla lettera del Segretario Generale  da parte dell\'On/le Carlo FATTUZZO, Parlamentare Europeo e Segretario del Partito Pensionati.', '<P>In allegato la prima risposta alla lettera inviata dal Segretario Generale ai Segretari dei Partiti Politici per chiedere chi abbia intenzione di portare avanti le problematiche dei militari al Parlamento Europeo.</P>', 58, '', '', '', 1765, 1, 0, '2026-01-11 04:29:59', '0000-00-00'),
(228, '2004-05-10 00:00:00', 'Lettere', 'Risposta alla lettera del Segretario Generale da parte del candidato al Parlamento Europeo\r\nAntonio PANZERI - Lista  Uniti nell\'Ulivo- Democratici di sinistra\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n', '', 'Risposta alla lettera del Segretario Generale da parte del candidato al Parlamento Europeo\r\nAntonio PANZERI - Lista Uniti nell\'Ulivo- Democratici di sinistra\r\n \r\n\r\n', 'In allegato la lettera in risposta all\'appello rivolto dal Segretario Generale ai Segretari dei Partiti e ai candidati per le elezioni Europee inviataci dal Antonio PANZERI, candidato per la circoscrizione Nord Ovest per l\'&nbsp; ULIVO- Democratici di Sinistra', 59, '', '', '', 1205, 1, 0, '2026-01-13 22:16:56', '0000-00-00'),
(229, '2004-05-10 00:00:00', 'Interrogazioni parlamentari', 'La risposta del Ministro della Difesa alla interrogazione parlamentare proposta dal senatore LONGHI alla Circolare del Ministro sulle Associazioni tra militari.', '', 'La risposta del Ministro della Difesa alla interrogazione parlamentare proposta dal senatore LONGHI alla Circolare del Ministro sulle Associazioni tra militari.', '<P>Riportiamo in allegato la risposta fornita dal Ministro della Difesa all\'interrogazione dei senatori LONGHI ed altri n. 4-05336 presentata per chiedere spiegazioni sul contenuto della Circolare emanata dal Ministro nel Luglio del 2003 avente per oggetto \" Direttiva in materia di Associazioni tra militari\".</P>\r\n<P>Per completezza riproponiamo l\'interrogazione a risposta immediata dell\'onorevole DEIANA, che contiene il testo della circolare, nonchè la risposta immediata in aula del Sottosegretario alla difesa&nbsp; On. BOSO, nonchè il testo dell\'interrogazione del Senatore LONGHI più 12.</P>\r\n<P>A breve proporremo un commento che abbiamo chiesto ad un legale alla risposta fornita il 16 Aprile 2004.</P>', 60, '', '', '', 1902, 1, 0, '2026-01-10 03:42:03', '0000-00-00'),
(230, '2004-05-18 00:00:00', 'Servizio di Ficiesse', 'INCONTRO TRA IL COCER e LO STATO MAGGIORE - Lo scorso 11 maggio 2004, il Consiglio Centrale ha partecipato ad un incontro informativo con il Capo di Stato Maggiore ed ufficiali del Comando Generale.\r\n\r\n', '', 'INCONTRO TRA IL COCER e LO STATO MAGGIORE - Lo scorso 11 maggio 2004, il Consiglio Centrale ha partecipato ad un incontro informativo con il Capo di Stato Maggiore ed ufficiali del Comando Generale.\r\n\r\n', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>A suo tempo abbiamo dato ampio risalto sul sito, ed è nostra intenzione riprendere l’argomento adeguatamente anche sul prossimo numero della rivista, alle risultanze dell’incontro avvenuto a Roma tra i rappresentanti degli organismi di rappresentanza. Oggi, in allegato, riportiamo il documento redatto dal COCER G. di F. a seguito di un recente incontro con lo Stato Maggiore, nel quale sono stati affrontati gli argomenti che<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>sono attualmente <SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>più sentiti dal personale.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"COLOR: black\"><FONT size=3><FONT face=\"Times New Roman\">La riunione, avvenuta l’11 maggio, ha avuto lo scopo di esporre ai delegati le decisioni assunte dal Comandante Generale su talune problematiche - risolvibili in via amministrativa - rappresentate dal COCER in sede di apposito “tavolo tecnico”<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>svoltosi il 17 marzo, nonché su altre questioni oggetto di delibere, ovvero emerse in occasione della riunione congiunta degli Organi di rappresentanza tenutasi a Roma il 30 e 31 marzo scorsi.<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>La lettura del<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>documento per<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>l’ampiezza degli argomenti toccati, per il loro approfondimento<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>e soprattutto per le risposte fornite ad alcune problematiche più vive, fanno intendere come qualcosa sia cambiato nell’atteggiamento del Comando Generale.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Noi riteniamo che questo cambiamento sia dovuto alla intelligenza del Comandante Generale che, sin dall’ inizio del suo mandato,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>ha dimostrato una particolare sensibilità nei confronti dei problemi che affliggono il personale del Corpo. Una sensibilità che finalmente sembra andare al di là degli enunciati e delle frasi fatte per entrare nel vivo dei problemi con delle soluzioni che possono essere condivise. </FONT></P><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: IT; mso-fareast-language: IT; mso-bidi-language: AR-SA\">Crediamo che questo sia molto positivo e costituisca un ottimo sistema di interazione tra la rappresentanza e l’ amministrazione. Il personale di ogni ordine e grado<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>è la più grande risorsa di cui dispone il Corpo e solo con una diversa apertura verso i&nbsp;suoi problemi <SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>ed una attenzione maggiore verso soluzioni condivise potrà cominciare il nuovo millennio della storia del Corpo. </SPAN>', 61, '', '', '', 1911, 1, 0, '2026-01-03 06:43:19', '0000-00-00'),
(354, '2004-10-25 00:00:00', 'Notizie Flash', 'Martedì 26 ottobre alle ore 16.00, in P.za Acquaverde, n. 5 c/o la stazione Principe Genova sarà inaugurata la sede dell\'associazione ricreativa lavoratori polizia -A.R.L.P. - .Associazione nata per il tempo libero e l\'impegno sociale di tutti i lavorator', '', 'Martedì 26 ottobre alle ore 16.00, in P.za Acquaverde, n. 5 c/o la stazione Principe Genova sarà inaugurata la sede dell\'associazione ricreativa lavoratori polizia -A.R.L.P. - .Associazione nata per il tempo libero e l\'impegno sociale di tutti i lavorator', '<SPAN style=\"FONT-SIZE: 7.5pt; COLOR: black; FONT-FAMILY: Verdana; mso-fareast-font-family: \'Times New Roman\'; mso-bidi-font-family: \'Times New Roman\'; mso-ansi-language: IT; mso-fareast-language: IT; mso-bidi-language: AR-SA\">Martedì 26 ottobre alle ore 16.00, in P.za Acquaverde, n. 5 c/o la stazione Principe Genova sarà inaugurata la sede dell\'associazione ricreativa lavoratori polizia -A.R.L.P. - .<BR>Associazione nata per il tempo libero e l\'impegno sociale di tutti i lavoratori del Comparto Sicurezza Genovese.<BR><BR>La comunicazione perchè chi può intervenga alla manifestazione.<BR style=\"mso-special-character: line-break\"><BR style=\"mso-special-character: line-break\"></SPAN>', 0, '', '', '', 1962, 1, 0, '2026-01-11 04:36:45', '0000-00-00'),
(162, '2004-02-11 00:00:00', 'Dalla rappresentanza militare', 'COMUNICATO STAMPA CONGIUNTO DEL Co.Ce.R. CARABINIERI E GUARDIA DI FINANZA\r\nSgomento per gli emendamenti presentati dagli Stati Maggiori al testo sulla riforma della Rapprersentanza Militare in Commissione Difesa.\r\n', '', 'COMUNICATO STAMPA CONGIUNTO DEL Co.Ce.R. CARABINIERI E GUARDIA DI FINANZA\r\nSgomento per gli emendamenti presentati dagli Stati Maggiori al testo sulla riforma della Rapprersentanza Militare in Commissione Difesa.\r\n', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Di seguito pubblichiamo il comunicato stampa diffuso in data odierna dai Co.Ce.R. Carabinieri <SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>e Guardia di Finanza dopo che gli Stati Maggiori hanno presentato emendamenti al testo varato dalla Commissione Difesa della Camera dei Deputati sulla riforma della rappresentanza militare.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Si tratta, in sostanza, di emendamenti che, non tenendo conto dei passi avanti ottenuti nelle sedute della Commissione in questi anni di iter, riportano il testo al punto di partenza.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Naturalmente l’Associazione condivide in pieno tutte le osservazioni avanzate ed aggiunge la richiesta, più volte proposta, di riconoscere il diritto di associazione tra militari.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Ai nostri rappresentanti diciamo, quindi, che possono contare su di noi.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Ai parlamentari della Commissione Difesa rivolgiamo l’accorato appello di non tener conto degli emendamenti proposti dal Governo e la richiesta di valutare attentamente quelle avanzate dal Co.Ce.R interforze.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Il comunicato congiunto dei Co.Ce.R. precede un nostro servizio che stavamo predisponendo, <SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>presto disponibile sul sito, <SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>che intendeva evidenziare, da una parte l’ultimo testo della Commissione, dall’altra gli emendamenti degli Stati Maggiori ed ancora quelli accolti e presentati dal Governo.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>A chi ci segue chiediamo di esprimere, attraverso il FORUM la solidarietà ai nostri rappresentanti, nonché le proposte che ritengono utili per migliorare il sistema della rappresentanza,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>tenendo presente che ogni innovazione è ancora possibile, dovendo il testo passare all’esame del Senato.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3><SPAN style=\"mso-tab-count: 6\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>COMUNICATO STAMPA<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>\"\"\"\"\"I Consigli di Rappresentanza dei Carabinieri e della Guardia di Finanza, ritengono che gli emendamenti presentati dalle Amministrazioni al nuovo testo unificato sulla riforma della rappresentanza militare, in discussione presso la Commissione difesa della Camera dei Deputati, siano evidente risultato di una differenza, ingiustificata, preconcetta ed ai limiti della legittimità, verso l\'Istituto.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Essi paiono volutamente ignorare gli indubbi vantaggi che nel tempo la rappresentanza ha portato al personale ed alle Amministrazioni stesse, che non hanno esitato a farvi ricorso, anche oltre le competenze previste, ogni qualvolta abbiano avuto bisogno di sostegno.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Esprimono pertanto il totale dissenso in merito, invitando la Commissione a non tener conto degli emendamenti proposti, tenendo invece presenti le svariate e ragionevoli proposte migliorative che il Co.Ce.R. interforze formulò sul testo unificato, a richiesta<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>della Commissione stessa.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Il testo che verrebbe a risultare dall\'accoglimento degli emendamenti in questione, lungi dal soddisfare le aspettative del personale, segnerebbe infatti una involuzione rispetto l\'attuale assetto normativo, porrebbe nel nulla le proposte dell\'organismo interforze, risultando, anzi, peggiorativo rispetto a quello di partenza.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>I Consigli sottoscrittori del presente documento, ribadiscono l\'esigenza di una riforma che riconosca:<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>- dignità di \"parte\" nella concertazione;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>- riconoscimento della personalità giuridica;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>- tutela dei delegati;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>- ratifica delle competenze di fatto acquisite nel tempo;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>- piena rieleggibilità dei delegati;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>- proporzionalità dei delegati in base alla consistenza di ciascuna categoria.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Utilizzeremo, in caso contrario, ogni forma di protesta consentita dall\'ordinamento, a tutela del personale e della funzionalità della rappresentanza.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Roma, 11 febbraio 2004<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>IL COCER CARABINIERI<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>IL COCER GUARDIA DI FINANZA\"\"\"\"<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none; mso-layout-grid-align: none\"><SPAN style=\"FONT-FAMILY: Arial\"><SPAN style=\"mso-spacerun: yes\"><FONT size=3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </FONT></SPAN><o:p></o:p></SPAN></P>', 0, '', '', '', 1106, 1, 0, '2026-01-14 08:17:20', '0000-00-00'),
(164, '2004-02-14 00:00:00', 'Ordine del giorno', 'CONVOCAZIONE DEL DIRETTIVO NAZIONALE DI FICIESSE PER SABATO 21 FEBBRAIO 2004 A BOLOGNA', '', 'CONVOCAZIONE DEL DIRETTIVO NAZIONALE DI FICIESSE PER SABATO 21 FEBBRAIO 2004 A BOLOGNA', '<FONT size=3><FONT face=\"Times New Roman\">\r\n<P class=MsoTitle style=\"MARGIN: 0cm 49.9pt 0pt 54pt\"><U></U>&nbsp;</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 49.9pt 0pt 54pt; TEXT-ALIGN: center\" align=center><U>AVVISO DI CONVOCAZIONE DEL DIRETTIVO NAZIONALE</U></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 49.9pt 0pt 54pt; TEXT-ALIGN: center\" align=center><U>DELL\'ASSOCIAZIONE FINANZIERI CITTADINI E SOLIDARIETA\' - FICIESSE</U></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 49.9pt 0pt 54pt; TEXT-ALIGN: center\" align=center>E’ convocato il Direttivo nazionale Ficiesse</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 49.9pt 0pt 54pt; TEXT-ALIGN: center\" align=center>per sabato 21 febbraio 2004, ore 11</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 49.9pt 0pt 54pt; TEXT-ALIGN: center\" align=center>a Bologna</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 49.9pt 0pt 54pt; TEXT-ALIGN: center\" align=center>&nbsp;<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 49.9pt 0pt 54pt; TEXT-ALIGN: center\" align=center><B><U>Ordine del giorno</U></B>:</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 49.9pt 0pt 72pt; TEXT-INDENT: -18pt; TEXT-ALIGN: center; mso-list: l0 level1 lfo1; tab-stops: list 72.0pt\" align=center>1)<SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Riforma della rappresentanza militare e iniziative da adottare</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 49.9pt 0pt 72pt; TEXT-INDENT: -18pt; TEXT-ALIGN: center; mso-list: l0 level1 lfo1; tab-stops: list 72.0pt\" align=center>2)<SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Esame del caso Parmalat e proposte per l’impiego della GDF a tutela del risparmio</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 49.9pt 0pt 72pt; TEXT-INDENT: -18pt; TEXT-ALIGN: center; mso-list: l0 level1 lfo1; tab-stops: list 72.0pt\" align=center>3)<SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Servizi ai soci e ai sostenitori non iscritti per l’anno 2004</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 49.9pt 0pt 72pt; TEXT-INDENT: -18pt; TEXT-ALIGN: center; mso-list: l0 level1 lfo1; tab-stops: list 72.0pt\" align=center>4)<SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Iniziative editoriali</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 49.9pt 0pt 54pt; TEXT-ALIGN: center\" align=center>&nbsp;<o:p></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 49.9pt 0pt 54pt; TEXT-ALIGN: center\" align=center>Il presidente del direttivo</P>\r\n<P class=MsoTitle style=\"MARGIN: 0cm 49.9pt 0pt 54pt\"></FONT></FONT>&nbsp;</P>', 0, '', '', '', 1920, 1, 0, '2026-01-09 19:36:38', '0000-00-00'),
(166, '2004-02-16 00:00:00', 'Documenti', 'RIFORMA DELLA RAPPRESENTANZA MILITARE.\r\nEcco gli emendamenti presentati dagli SS.MM. al Nuovo Testo sulla Riforma della Rappresentanza Militare', '', 'RIFORMA DELLA RAPPRESENTANZA MILITARE.\r\nEcco gli emendamenti presentati dagli SS.MM. al Nuovo Testo sulla Riforma della Rappresentanza Militare', '<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">Come anticipato, pubblichiamo gli emendamenti presentati dagli Stati Maggiori al Nuovo testo unificato C. 932 Molinari recante \" Nuove norme sulla rappresentanza militare\".<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">Abbiamo anche evidenziato nel Nuovo testo, sottolineate, le modifiche apportate rispetto al testo precedente, che in parte avevano accolto le richieste formulate dal COCER Interforze in un suo documento.<o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">Già da una lettura superficiale appare come gli emendamenti, sia pure con pochi interventi, stravolgano di fatto tutto lo schema predisposto: \"la presentazione di proposte, istanze e pareri su tematiche di interesse collettivo, anche relative ai singoli\" viene snaturata con l\'aggiunta \"ma comunque di rilevanza generale\", la parola \"Contrattazione\" che tante discussioni ha richiesto per la sua accettazione, viene sostituita dalla solita \"concertazione\".<o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">Nelle materie di competenza all\' \"articolazione dell\'orario di lavoro obbligatorio\", viene aggiunta la parola \"settimanale\", ai \"criteri generali relativi ai trasferimenti del personale\", viene aggiunta l\'allocuzione \"a domanda\", facendo così perdere ogni rilevanza alle motivate richieste formulate per poter intervenire, come giusto, in un settore così importante per la vita familiare dei militari.<o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">La maturità,sempre dimostrata da chi ci segue,farà facilmente comprendere come questi emendamenti svuotino di significato il testo della Commissione che pur soddisfaceva solo in minima parte le aspettative del personale. Il risultato è un sistema che non solo non fa fare passi avanti alla rappresentanza, ma neanche cristallizza le&nbsp;posizioni che, di fatto, sono state acquisite dal 1978 ad oggi.<o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">Con rammarico, oggi, dobbiamo dare ragione a quei vecchi delegati, che al nostro entusiasmo per una revisione della legge 382/1978, evidenziavano il loro timore di un probabile regresso, potendo, da qualche parte, cogliersi l\'occasione per un recupero di posizioni perse.<o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">Chiediamo a tutti di vigilare ed in particolare rivolgiamo un accorato appello ai deputati perchè non prendano in considerazione gli emendamenti proposti e perchè, al contrario, continuino nel solco intrapreso di avvicinamento del nostro sistema di rappresentanza a quello della maggior parte dei&nbsp; Paesi Europei.<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>', 35, '', '', '', 1585, 1, 0, '2026-01-14 08:41:05', '0000-00-00'),
(167, '2004-02-18 00:00:00', 'Dai media', 'REGISTRO DEI REVISORI RIAPERTO ALLE DOMANDE TARDIVE - SEMPRE AMMESSI GLI ISCRITTI AGLI ALBI NEL \'97 (da il Sole24Ore)', '', 'REGISTRO DEI REVISORI RIAPERTO ALLE DOMANDE TARDIVE - SEMPRE AMMESSI GLI ISCRITTI AGLI ALBI NEL \'97 (da il Sole24Ore)', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=left><FONT face=\"Times New Roman\" size=3>da Sole 24 Ore del 18 febbraio 2004, pagina 31</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=left><FONT size=3><FONT face=\"Times New Roman\">&nbsp;</FONT></FONT><FONT face=\"Times New Roman\" size=3>REGISTRO REVISORI APERTO ALLE DOMANDE TARDIVE</FONT></P>\r\n<H1 style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: center\" align=left><EM><FONT face=\"Times New Roman\" size=3>Sempre ammessi gli iscritti agli Albi nel ‘97</FONT></EM></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\" align=left><FONT face=\"Times New Roman\" size=3>Il Registro revisori resta aperto senza “termini decadenziali”, per tutti coloro che avevano i requisiti al 22 maggio 1997: lo ha chiarito il Consiglio di Stato (sezione terza, parere n. 1583/02 del 22 ottobre 2002), accogliendo il ricorso di un iscritto nell’Albo dei ragionieri che ha presentato domanda per iscriversi al Registro nel 1999. E la commissione, uniformandosi alle indicazioni dei giudici, ha accolto pochi giorni fa la domanda di iscrizione.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\" align=left><FONT face=\"Times New Roman\" size=3>L’aspirante revisore, infatti, ha sostenuto che con la legge 132 del ’97 è stato disposto l’esonero dall’esame per tutti coloro che al 22 maggio di quell’anno erano iscritti o avevano diritto a essere iscritti negli Albi di dottori commercialisti e ragionieri; ma non ha fissato termini entro i quali chi si trovava in quella condizione poteva richiedere l’accesso.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\" align=left><FONT face=\"Times New Roman\" size=3>Di fronte alò diniego opposto in un primo tempo dal Ministro della giustizia, il ragioniere (nell’Albo dal 1982) ha fatto ricorso straordinario al Capo dello Stato. E i giudici di Palazzo Spada gli hanno dato ragione. “Nessun termine decadenziale – spiega il parere, estensore Anselmo Di Napoli, presidente Walter Catallozzi – è comminato per far valere tale diritto all’iscrizione, né è prescritto che la relativa domanda debba essere presentata alla Commissione esaminatrice costituita presso la Corte di appello di cui all’art. 1 della legge n. 132/1997.</FONT></P>\r\n<P align=left><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: IT; mso-fareast-language: IT; mso-bidi-language: AR-SA\">Con il parere del Consiglio di Stato e la sentenza 35/2004 della Corte costituzionale (illustrata sul Sole-24 Ore del 27 gennaio scorso), il Registro revisori diviene di fatto accessibile per coloro che erano iscritti agli Albi o che erano abilitati all’iscizione al 22 maggio 1997, nonché – coma ha chiarito la Consulta – per chi stava sostenendo la sessione d’esame al momento dell’entrata in vigore della legge 132/1997. A quasi sette anni di distanza, i giudici correggono dunque la tormentata “sanatoria” della prima formazione del Registro, dando margini di intervento anche ad aspiranti tardivi. E il Consiglio nazionale dei ragionieri presenta sul suo sito internet (www.consrag.it, alla voce Ordinamento) il facsimile della domanda per l’ammissione.</SPAN></P>', 0, '', '', '', 1630, 1, 0, '2026-01-10 03:09:28', '0000-00-00'),
(168, '2004-02-18 00:00:00', 'Documenti', 'ECCO IL FACSIMILE DI DOMANDA PER L\'ISCRIZIONE ALL\'ALBO DEI REVISORI PUBBLICATO SUL SITO DEL CONSIGLIO NAZIONALE DEI RAGIONIERI', '', 'ALLEGHIAMO IL FACSIMILE DI DOMANDA PER L\'ISCRIZIONE ALL\'ALBO DEI REVISORI PUBBLICATO SUL SITO DEL CONSIGLIO NAZIONALE DEI RAGIONIERI\r\nALLA PAGINA http://www.consrag.it/ordinamento/default.htm', '', 37, '', '', '', 1544, 1, 0, '2026-01-14 07:40:21', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(169, '2004-02-18 00:00:00', 'Dal Parlamento', 'IL RESOCONTO DELLA SEDUTA, 15/1/2004, DELLA IV COMMISSIONE (DIFESA) DELLA CAMERA NELLA QUALE IL SOTTOSEGRETARIO ON/ LE  CICU SPIEGA LE RAGIONI DELLA POSIZIONE DEL GOVERNO.', '', 'IL RESOCONTO DELLA SEDUTA, 15/1/2004, DELLA IV COMMISSIONE (DIFESA) DELLA CAMERA NELLA QUALE IL SOTTOSEGRETARIO ON/ LE  CICU SPIEGA LE RAGIONI DELLA POSIZIONE DEL GOVERNO.', '<P>Dal resoconto della seduta del 15 gennaio scorso della Commissione Difesa della Camera si capisce chiaramente la posizione del Governo relativamente agli emendamenti che vi abbiamo presentato nel servizio di qualche giorno fà, predisposti dagli Stati Maggiori, ed accolti dal Governo.</P>\r\n<P>Ogni commento risulta superfluo, ognuno potrà e dovrà trarre le conclusioni circa la disponibilità ad accogliere le aspettative dei militari circa una vera forma di tutela che possa contribuire ad un rinnovamento degli ambienti di lavoro, da un lato, ed una sicura conseguente efficienza delle organizzazioni, dall\'altra.</P>\r\n<P>&nbsp;</P>\r\n<P>ECCO IL RESOCONTO:</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><B><FONT face=\"Times New Roman\" size=3>^^^^^^^^^^^^^^^^^^^^^^</FONT></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><B><FONT face=\"Times New Roman\" size=3>IV Commissione - Resoconto di giovedì 15 gennaio 2004</FONT></B></P>\r\n<H5 style=\"MARGIN: auto 0cm\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></H5>\r\n<H5 style=\"MARGIN: auto 0cm\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></H5>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><A name=13n1></A><B><FONT size=3><FONT face=\"Times New Roman\">SEDE REFERENTE <o:p></o:p></FONT></FONT></B></P>\r\n<P><I><FONT face=\"Times New Roman\" size=3>Giovedì 15 gennaio 2004. - Presidenza del presidente Luigi RAMPONI. - Interviene il sottosegretario di Stato per la difesa Salvatore CICU. </FONT></I></P>\r\n<P><A name=13n3></A><FONT size=3><FONT face=\"Times New Roman\"><B>Nuove norme sulla rappresentanza militare. <BR>Ulteriore nuovo testo unificato C. 932 Molinari, C. 1718 Ramponi, C. 1822 Lavagnini, C. 1958 Deiana, C. 2063 Ascierto e C. 2193 Minniti. <BR></B><I>(Seguito dell\'esame e rinvio). </I></FONT></FONT></P>\r\n<P><FONT face=\"Times New Roman\" size=3>La Commissione prosegue l\'esame rinviato nella seduta del 13 gennaio 2004. </FONT></P>\r\n<P><A name=INT13n2></A><FONT face=\"Times New Roman\" size=3>Luigi RAMPONI, <I>presidente</I>, invita il rappresentante del Governo a svolgere il proprio intervento in sede di replica. </FONT></P>\r\n<P><A name=INT13n3></A><FONT face=\"Times New Roman\" size=3>Il sottosegretario Salvatore CICU, pur condividendo 1\'impianto generale del provvedimento che, rispetto alla precedente versione, ha recepito in molti punti le istanze di modifica suggerite dal Governo ed ha definito in modo più dettagliato le materie di intervento e le modalità operative degli organi della rappresentanza, manifesta tuttavia alcune perplessità sulle quali invita a riflettere i componenti la Commissione. <BR>In particolare, si sofferma sulla questione del ruolo e del compito della rappresentanza in sede di confronto con il Governo. Ritiene infatti che il termine «contrattazione» usato nel testo unificato in esame non possa essere riferito alla rappresentanza militare se non con uno stravolgimento della natura giuridica del termine stesso. A tale riguardo ricorda che il sistema della contrattazione non può essere esteso alla rappresentanza militare poiché esso prevede un confronto tra una delegazione di parte pubblica e le rappresentanze sindacali al fine di pervenire alla stipula di un apposito accordo. <BR>In conformità alle disposizioni vigenti per il pubblico impiego in materia di accertamento della rappresentatività sindacale nell\'ambito della contrattazione collettiva - di cui all\'articolo 1, comma 1, del decreto legislativo 12 maggio 1995, n. 195 per quanto attiene alle Forze di polizia ad ordinamento civile - tale accordo, recepisce il consenso espresso delle organizzazioni sindacali, quantificabile attraverso la misura del dato associativo (numero degli iscritti) e del dato elettorale (voti ottenuti nelle elezioni). In altre parole, la condizione necessaria affinché venga sottoscritto il contratto è che le organizzazioni sindacali aderenti all\'ipotesi di accordo rappresentino nel loro complesso la maggioranza, in termini percentuali, del dato associativo e del dato elettorale dell\'intero comparto o dell\'area contrattuale di interesse. In tal modo, qualora non venga raggiunta l\'intesa dall\'unanimità delle organizzazioni sindacali rappresentative del comparto, si fa riferimento al dato associativo, cioè al numero di iscritti alle organizzazioni sindacali aderenti alla bozza di accordo, per verificare l\'esistenza di un adeguato consenso atto a legittimare l\'accordo raggiunto. In sostanza, anche se non firmano tutte le organizzazioni sindacali è sufficiente che 1\'accordo venga sottoscritto dalle organizzazioni sindacali che, in termini di iscritti, rappresentino complessivamente la maggioranza del comparto. <BR>Il termine contrattazione non può quindi essere utilizzato in riferimento alla rappresentanza militare in quanto quest\'ultima ha una valenza di organismo interno all\'organizzazione militare, non propriamente sindacale. <BR>Ritiene comunque che il relatore e la Commissione stiano facendo un buon lavoro per rafforzare il ruolo della rappresentanza militare, garantendo ad essa una maggiore partecipazione ed autonomia ed al riguardo manifesta il pieno appoggio ed apprezzamento del Governo. Ricorda che lo stesso ex Ministro della funzione pubblica, Franco Frattini, audito dalla Commissione sul provvedimento, aveva evidenziato tale opportunità. Occorre pertanto accrescere il ruolo del Cocer in sede di concertazione, garantendogli un\'attività più incisiva e partecipativa, soprattutto tenuto conto del ruolo sempre più importante svolto dalle Forze armate. <BR>Ci sono inoltre una serie di aspetti di minore rilievo, sui quali intende soffermarsi, che attengono, in particolare, al ruolo dei vertici militari (Stato maggiore difesa e Comandi generali) che, nell\'ambito dei procedimenti previsti dal decreto legislativo 12 maggio 1995, n. 195, verrebbero posti <I>a latere</I> - con funzioni non chiarite nel testo in esame - del processo decisionale intercorrente tra la rappresentanza militare e l\'autorità politica; per di più con compiti di mero supporto tecnico ai Cocer nelle «attività di contrattazione». Tale innovazione condizionerebbe fortemente il Governo e la stessa attività negoziale del comparto difesa, atteso che qualsiasi trattativa difficilmente potrebbe essere conclusa qualora il Cocer, con piena capacità contrattuale, non intendesse sottoscrivere l\'accordo. <BR>Un\'ulteriore questione riguarda le attribuzioni, agli organi della rappresentanza, di competenze di natura ispettiva in materia di vigilanza sulla corretta applicazione della normativa emanata a seguito della ipotizzata «contrattazione» e della legislazione concernente la sicurezza e la prevenzione degli infortuni sui luoghi di lavoro. <BR>In merito alle problematiche segnalate, il Governo si riserva di presentare gli opportuni emendamenti, anche al fine di migliorare il testo in esame, onde pervenire ad una riforma della rappresentanza che possa conciliare gli interessi istituzionali con quelli degli organismi rappresentativi del personale militare. </FONT></P>\r\n<P><A name=INT15n1></A><FONT face=\"Times New Roman\" size=3>Roberto LAVAGNINI (FI) ritiene che la Commissione stia svolgendo un lavoro molto approfondito sulla riforma della rappresentanza militare al fine di conferire al Cocer un ruolo più incisivo in sede di concertazione con il Governo. Ritiene inoltre che con tale riforma si aprirà una nuova fase per quanto attiene al ruolo della Funzione pubblica. Infatti, dopo l\'equiparazione delle Forze di polizia ad ordinamento militare e ad ordinamento civile, si è creato un comparto unitario «difesa e sicurezza». Proprio per tale aspetto, la Funzione pubblica verrà ad assumere un ruolo ancora più incisivo rispetto all\'attuale, in quanto si troverà a dover concertare con i vari dicasteri coinvolti - Interni - Difesa - Economia e Finanze - le questioni relative al personale di rispettiva competenza. È ovvio che permangono le differenziazioni relative ai compiti svolti dalle Forze di Polizia ad ordinamento civile, da un lato, e dalle Forze armate, dall\'altro. In conclusione, ritiene necessario considerare il nuovo ruolo che andrà a svolgere la Funzione pubblica inserendolo opportunamente nel testo unificato in esame e coordinandolo con le proposte di legge, attualmente all\'esame delle Commissioni I e IV, concernenti, rispettivamente, l\'istituzione del comparto autonomo per le Forze di polizia e le Forze armate (C. 3372 ed abb.) e la completa armonizzazione del trattamento giuridico ed economico del personale delle Forze armate e delle Forze di polizia (C. 3425 ed abb.). </FONT></P>\r\n<P><A name=INT15n2></A><FONT face=\"Times New Roman\" size=3>Giuseppe LUMIA (DS-U) pur osservando che il rappresentante del Governo si è soffermato su taluni aspetti di grande rilievo in tema di riforma della rappresentanza militare, ha tuttavia colto una certa preclusione nei confronti dell\'uso del termine «contrattazione». <BR>Una rappresentanza che non concerta e non contratta non può essere considerata una vera rappresentanza. Infatti, da un lato, si attenua il principio della responsabilità dei rappresentanti nei confronti dei rappresentati e, dall\'altro, vi è il rischio di una demotivazione da parte dello stesso personale. Ritiene quindi che l\'attribuzione al Cocer di un ruolo più incisivo, nell\'ottica di una maggiore democraticità, determinerebbe risultati migliori sia in termini di motivazione del personale, sia avendo riguardo all\'efficienza e alla qualità dei risultati. <BR>Occorre pertanto recepire sino in fondo tale aspetto, soprattutto tenuto conto del nuovo ruolo che la Funzione pubblica sarà chiamata a svolgere. Il ruolo del Cocer non potrà continuare ad essere ambiguo e poco incisivo, in quanto ciò potrebbe determinare un certo malessere tra il personale, che potrebbe sentirsi non adeguatamente tutelato. Invita pertanto il Governo ad una maggiore apertura in tal senso. Pur cogliendo il travaglio interno alla maggioranza ed al Governo volto a rendere più incisivo il ruolo della rappresentanza militare, ritiene necessaria una svolta intelligente e non avventuristica che, mantenendo la specificità propria del personale militare, aumenti la democraticità nell\'assunzione delle decisioni. Utilizzare il termine «concertazione» in riferimento alla rappresentanza, significa che quest\'ultima non può «contrattare» la politica di sicurezza e difesa, in riferimento ai temi di propria competenza. </FONT></P>\r\n<P><A name=INT15n3></A><FONT face=\"Times New Roman\" size=3>Luigi RAMPONI, <I>presidente</I>, ricorda al deputato Lumia che la «contrattazione» e la «concertazione» sono metodi per addivenire al raggiungimento di risultati, e le competenze al riguardo sono specificamente definite. </FONT></P>\r\n<P><A name=INT15n4></A><FONT face=\"Times New Roman\" size=3>Giuseppe LUMIA (DS-U) concorda con il presidente sul fatto che sia la concertazione, sia la contrattazione siano metodi per affrontare, rispettivamente, questioni generali, ovvero specifiche. Tuttavia, ritiene che entrambi i termini possano essere utilizzati nel testo unificato in esame. Preannuncia in tal senso la presentazione di specifiche proposte emendative da parte del suo gruppo. Infine, auspica una maggiore apertura del Governo in merito alla contrattazione decentrata, più facile da realizzare per il comparto sicurezza, e che rappresenta invece un obiettivo da raggiungere il prima possibile anche per il comparto difesa. </FONT></P>\r\n<P><A name=INT16n1></A><FONT face=\"Times New Roman\" size=3>Filippo ASCIERTO (AN) ribadisce il proprio apprezzamento per il lavoro svolto dal relatore, sottolineando in particolare i due aspetti maggiormente innovativi contenuti nel testo unificato in esame. Il primo riguarda la possibilità di tutela del singolo delegato; l\'altro attiene all\'uso del termine «contrattazione», in merito al quale ricorda le considerazioni svolte dall\'ex ministro della Funzione pubblica, Franco Frattini, nel corso dell\'audizione in Commissione. <BR>Ricorda che all\'epoca in cui si decise la concertazione quest\'ultima fu delineata su due livelli: da un lato, la Funzione pubblica, dall\'altro, gli Stati Maggiori. La questione è che ai vertici militari è stato attribuito un ruolo ibrido, in quanto, in una prima fase, devono concertare le singole questioni con i delegati del Cocer; successivamente, sono collocati dalla stessa parte della Funzione pubblica. Ritiene pertanto fondamentale chiarire tale aspetto. <BR>Evidenzia inoltre una diseguaglianza tra il ruolo svolto dai sindacati di Polizia rispetto a quello attribuito al Cocer. Mentre per i primi vige un sistema di contrattazione di secondo livello, i rappresentanti del Cocer non possono neanche discutere delle questioni concertate o da concertare, non essendo queste ultime contemplate dalla legge n. 382 del 1978. <BR>Esprime poi apprezzamento per la posizione manifestata dall\'opposizione, che ha escluso la sindacalizzazione delle Forze armate, pur nello sforzo di conferire una maggiore incisività all\'attività del Cocer. Ritiene quindi che il ruolo del Cocer debba essere reso più penetrante, pur preservando la specificità del personale militare. In conclusione, auspica la rapida approvazione della riforma della rappresentanza militare, fortemente attesa da tutto il personale. </FONT></P>\r\n<P><A name=INT16n2></A><FONT face=\"Times New Roman\" size=3>Luigi RAMPONI, <I>presidente</I>, pur ritenendo che gli aspetti della contrattazione e della concertazione costituiscano i punti più delicati della riforma, ricorda come l\'attività della rappresentanza abbia una valenza molto più ampia, comprendendo temi quali la tutela della qualità della vita, le condizioni di impiego, di salute, di lavoro e di riposo del personale. <BR>Ricorda poi che il testo approvato nella scorsa legislatura alla Camera non era assolutamente innovativo come quello in esame, per quanto concerne il ruolo del Cocer. Pertanto, ritiene che il lavoro fin qui svolto dalla Commissione vada sicuramente oltre rispetto a quanto fatto dall\'allora maggioranza nella XIII legislatura. Nonostante ciò, auspica che si giunga ad un testo condiviso da tutte le parti politiche su un tema di grande rilievo per il personale quale è quello della rappresentanza. <BR>Nessun altro chiedendo di intervenire, propone di fissare il termine per la presentazione degli emendamenti al testo unificato in esame a mercoledì 21 gennaio 2004, alle ore 16. <BR>Rinvia quindi il seguito dell\'esame ad altra seduta. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P>^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^</P>', 0, '', '', '', 2896, 1, 0, '2026-01-14 10:13:59', '0000-00-00'),
(170, '2004-02-18 00:00:00', 'Dal Parlamento', 'PER UN COMPLETO ESAME DA PARTE DI TUTTI, ECCO I RESOCONTI DELLE SEDUTE DELLA  IV COMMISSIONE DAL LUGLIO  2003 AD OGGI SULLA RIFORMA DELLA RAPPRESENTANZA.', '', 'PER UN COMPLETO ESAME DA PARTE DI TUTTI, ECCO I RESOCONTI DELLE SEDUTE DELLA  IV COMMISSIONE DAL LUGLIO  2003 AD OGGI SULLA RIFORMA DELLA RAPPRESENTANZA.', '<P>Pensando di fare cosa utile pubblichiamo, in allegato,&nbsp;i resoconti delle sedute della&nbsp; IV&nbsp;Commissione (DIFESA) della Camera dei Deputati, relativamente al tema trattato della Riforma della Rappresentanza.</P>\r\n<P>Le sedute sono quelle dal Luglio 2003 ad oggi che riteniamo più interessanti per capire come si sono sviluppati i lavori.</P>\r\n<P>E\' facile intuire dalla attenta lettura dei resoconti le posizioni dei vari parlamentari e dei rappresentanti del Governo su untema che tanto interessa il mondo militare.</P>\r\n<P>Ognuno tragga le conseguenze.</P>', 38, '', '', '', 2109, 1, 0, '2026-01-10 00:00:33', '0000-00-00'),
(173, '2004-02-22 00:00:00', 'Documenti', 'IMPORTANTE AUDIZIONE DEI VERTICI GDF IN PARLAMENTO SUL CASO PARMALAT. PUBBLICHIAMO IL RESOCONTO INTEGRALE COMPLETO DELLE DOMANDE E DELLE RISPOSTE', '', 'I GENERALI SPECIALE E DI PAOLO ASCOLTATI DALLE COMMISSIONI PARLAMENTARI SUL CASO PARMALAT. PUBBLICHIAMO IL DOCUMENTO INTEGRALE COMPLETO DELLE DOMANDE E DELLE RISPOSTE\r\n\r\n\r\n\r\n\r\n\r\n', '<SPAN id=lblArticolo>\r\n<P><SPAN style=\"FONT-SIZE: 8pt; COLOR: black; FONT-FAMILY: Verdana\">Il 10 febbraio scorso il Comandante generale della GDF, Speciale, e il Capo di stato maggiore Di Paolo sono stati ascoltati dalle Commissioni riunite di Camera e Senato competenti in materia di finanze e attività produttive sul caso Parmalat.<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 8pt; COLOR: black; FONT-FAMILY: Verdana\">In allegato, pubblichiamo il resoconto integrale, completo delle domande dei parlamentari e delle risposte degli alti ufficiali.<o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 8pt; COLOR: black; FONT-FAMILY: Verdana\">Si tratta di un documento importante che invitiamo i nostri lettori a leggere per intero.<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 8pt; COLOR: black; FONT-FAMILY: Verdana\">Nei prossimi giorni, i nostri commenti.</SPAN></P></SPAN>', 43, '', '', '', 1684, 1, 0, '2026-01-14 08:38:03', '0000-00-00'),
(174, '2004-02-24 00:00:00', 'Nostro Servizio', 'BUSTE PAGA PIU\' LEGGERE NEL MESE DI FEBBRAIO', '', 'BUSTE PAGHE PIU\' LEGGERE NEL MESE DI FEBBRAIO.\r\nMolti militari di ogni categoria  e grado nella busta paga di febbraio hanno trovato una sorpresa.', '<P>Nella busta paga di febbraio molti militari hanno trovato una amara sorpresa.</P>\r\n<P>Una decurtazione, per alcuni, sostanziosa, dovuta al conguaglio IRPEF per il 2003.</P>\r\n<P>In alcuni casi tali conguagli raggiungono addirittura i 600 euro, e comunque si riferiscono spesso a cifre considerevoli rispetto all\'intera busta paga, rendendo difficile la vita di una famiglia per un intero mese.</P>\r\n<P>Possibile che nessuno abbia pensato ad una rateizzazione del debito?</P>\r\n<P>Si risponderà che il debito fiscale non è rateizzabile, lo dicono varie circolari ministeriali. Eppoi ormai è troppo tardi, le trattenute sono state effettuate.</P>\r\n<P>Allora qualcuno deve spiegare, naturalmente se rispondono a verità le notizie riportate sul sito del sindacato della Polizia Penitenziaria O.S.A.P.P. e di quello della Polizia di Stato S.I.U.L.P., come è stata possibile per loro la rateizzazione.</P>\r\n<P>Non sono forse anche loro lavoratori come i nostri militari?, non hanno le stesse regole?</P>\r\n<P>Si legge infatti sul sito&nbsp; osapp.it:</P>\r\n<P>\"Dopo il duro intervento dell\'O.S.A.P.P. del 14 febbraio 2004 in merito alle somme ingenti richieste alle Colleghe e ai Colleghi quale recupero fiscale dell\'IRPEF relativa al 2003 e tale, in alcuni casi, da raggiungere somme di 1000 euro e più in una unica soluzione, l\'Amministrazione HA CONCORDATO sulla possibilità di procedere a rateizzazioni fino a 4 rate.</P>\r\n<P>Nello specifico, infatti, si potrà procedere a tale rimborso:</P>\r\n<P>1. in 2 rate se l\'importo eccede i 100 euro;</P>\r\n<P>2. in 3 rate se l\'importo eccede i 200 euro;</P>\r\n<P>3. in 4 rate se l\'importo eccede i 300 euro;</P>\r\n<P>........................</P>\r\n<P>............................\"</P>\r\n<P>Se così sarà confermato non ci sono parole.</P>\r\n<P>Sulla questione sembra si sia già pronunziato il COCER, non appena avremo notizie, le comunicheremo.</P>', 0, '', '', '', 1989, 1, 0, '2026-01-14 11:45:36', '0000-00-00'),
(176, '2004-02-25 00:00:00', 'Dai media', 'LA REPUBBLICA DEL 24 FEBBRAIO 2004 - PAG. 18 -\r\nDI PIETRO: “ALLE ELEZIONI CI AIUTERA’ UNA ASSOCIAZIONE DI CARABINIERI”\r\n', '', 'LA REPUBBLICA – martedì 24 febbraio 2004 - Politica interna – Il caso\r\nDI PIETRO: “ALLE ELEZIONI CI AIUTERA’ UNA ASSOCIAZIONE DI CARABINIERI”\r\n“Sostegno a Italia dei valori”. Il comando generale: non rappresentano l’Arma.', '<P>LA REPUBBLICA – martedì 24 febbraio 2004 - Politica interna – Il caso<BR>DI PIETRO: “ALLE ELEZIONI CI AIUTERA’ UNA ASSOCIAZIONE DI CARABINIERI”<BR>“Sostegno a Italia dei valori”. Il comando generale: non rappresentano l’Arma.</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>ROMA (Claudia Fusani). Di Piero, Occhetto, pezzi della società civile e anche quella fetta dell’Arma dei carabinieri che da anni, dal 1993, lotta “per un mondo militare più democratico e trasparente”. Sono gli uomini in divisa che in questi anni hanno denunciato i rischi delle munizioni all’uranio impoverito (l’ultimo morto, il 24esimo, è di pochi giorni fa). E quelli che hanno presentato l’esposto alla procura militare ipotizzando un’omissione nei sistemi di difesa e sicurezza per i nostri militari a Nassiriya.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>L’annuncio ufficiale l’ha dato ieri Antonio Di Pietro: “L’associazione dei Carabinieri che ha promosso il progetto di democrazia militare attraverso il “Giornale dei Carabinieri” sostiene la nostra lista”. Una cinquantina tra ufficiali e sottufficiali in servizio saranno candidati per le Europee, le amministrative e le prossime politiche. Più che sufficiente per provocare mugugni e distinguo da parte del comando generale dell’Arma: “Quelli non rappresentano i carabinieri”. Così stamani fra l’ex pm di Mani Pulite e l’uomo della svolta della Bolognina, sotto la dicitura lista Di Pietro-Occhetto-società civile e il simbolo dell’Italia di Valori-Nuovo Ulivo, ci saranno anche marescialli e ufficiali. “Abbiamo preso un giorno di ferie” spiegano. Per la campagna elettorale “usufruiremo dell’aspettativa di 30 giorni per motivi elettorali”.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>L’associazione dei carabinieri “ribelli” era nata nel ’93 reclamando il diritto, anche negli ambienti militari, all’associazionismo e al sindacato, “alla tutela – ricorda Ernesto Pallotta, 43 anni, maresciallo in servizio alla compagnia Montesacro a Roma – dei diritti individuali”. Si chiamavano Un arma e nel 1997, “nonostante censure e provvedimenti disciplinari”, sono arrivati fino a 3.700 iscritti. Poi, nel 1999, una sentenza della Corte costituzionale ribadisce il divieto al sindacato militare. Sono partiti i ricorsi alla Corte di giustizia europea<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>ma ancora oggi Un arma è “congelata” perché “illegale”. La sua eredità è stata raccolta da “Il giornale del carabiniere”, un mensile distribuito in tutte le caserma che conta 20mila abbonati. Pallotta, che non può più essere il segretario di Un arma, è il fondatore della rivista. Quella che comincia oggi è la prima campagna elettorale. Due gli obiettivi: “Aumentare la sfera dei diritti individuali all’interno delle forze armate ed evitare un processo di militarizzazione del paese”. E un motto: “Cittadini con le stellette”</FONT></P>\r\n<P></P>', 0, '', '', '', 8652, 1, 0, '2026-01-14 09:03:10', '0000-00-00'),
(177, '2004-02-28 00:00:00', 'Comunicato stampa', 'NUOVA GRAFICA DEL SITO\r\nDa oggi il sito si è rinnovato nella grafica.\r\nAbbiamo cercato di renderlo più fruibile.', '', 'NUOVA GRAFICA DEL SITO\r\nDa oggi il sito si è rinnovato nella grafica.\r\nAbbiamo cercato di renderlo più fruibile.', '<P>Da oggi è in linea la nuova grafica del sito. <BR></P>\r\n<P>Abbiamo cercato di renderlo più gradevole nel suo complesso ma soprattutto più fruibile nelle sue componenti.</P>\r\n<P>Le notizie dell\'ultima ora e quelle di primo piano sono ora distinte su due pagine consultabili attraverso un segnalibro sulla Home page.</P>\r\n<P>L\'accesso al FORUM pubblico è stato reso più agevole.</P>\r\n<P>Tutto per far sì che il sito sia sempre più il luogo di incontro e di scambio di idee per tutti coloro che ci seguono e per quanti abbiano qualcosa da dire sulle tematiche che affrontiamo quotidianamente.</P>\r\n<P>Saremo comunque lieti di ricevere suggerimenti, anche tecnici, da chiunque abbia delle idee per migliorare ancora le pagine del nostro sito.</P>\r\n<P>Naturalmente compatibilmente&nbsp;con le&nbsp;nostre modeste risorse economiche.</P>\r\n<P>&nbsp;</P>', 0, '', '', '', 14, 1, 0, '2010-10-07 09:03:39', '0000-00-00'),
(178, '2004-02-28 00:00:00', 'Comunicato stampa', 'SPEDIZIONE RIVISTA\r\nIl  perchè dei ritardi nella spedizione del numero 4 del 2003.', '', 'SPEDIZIONE RIVISTA\r\nIl  perchè dei ritardi nella spedizione del numero 4 del 2003.', '<P>Riceviamo molte richieste di notizie circa le ragioni del mancato invio dell\'ultimo numero della Rivista ai soci ed ai sostenitori , numero che peraltro abbiamo opportunamente messo in linea, in formato Pdf, sul nostro sito.</P>\r\n<P>Il problema è costituito dal fatto che non avendo spedito numeri per l\'intero 2003 sono scadute tutte le procedure attivate con le Poste per le agevolazioni tariffarie. Si è dovuto quindi riproporre tutto l\' iter che sembra, secondo quanto ci riferisce l\'editore, richiedere tempi lunghi.</P>\r\n<P>Non appena andrà in spedizione lo comunicheremo immediatamente.</P>', 0, '', '', '', 19, 1, 0, '2010-10-01 05:15:36', '0000-00-00'),
(239, '2004-06-10 00:00:00', 'Editoriale', 'IN LINEA IL NR. 8 DELLA RIVISTA \"FINANZIERI E CITTADINI\"\r\nUna nuova linea editoriale', '', 'IN LINEA IL NR. 8 DELLA RIVISTA \"FINANZIERI E CITTADINI\"\r\nUna nuova linea editoriale', '<P>Inauguriamo con il nr. 8 della Rivista dell\'Associazione un nuovo sistema di pubblicazione.</P>\r\n<P>In un primo momento la Rivista verrà messa online con la copertina in Homepage, cliccando su di essa apparirà il sommario dal quale si potrà accedere ai vari articoli che saranno in formato pdf. Quindi in un momento immediatamente successivo sarà in distribuzione la copia stampata.</P>\r\n<P>Il nuovo sistema risponde principalmente a due esigenze:</P>\r\n<P>° la prima di ordine tecnico, la pubblicazione online è molto più veloce ed è accessibile a tutti i visitatori del sito, soci o no che siano, inoltre ci consentirà, ci auguriamo, di tornare alla edizione bimestrale.</P>\r\n<P>° la seconda in considerazione del vantaggio economico derivante dal minor numero di copie stampate.</P>\r\n<P>I soci continueranno comunque a ricevere al proprio indirizzo la copia stampata.</P>\r\n<P>Saremo grati se, attraverso il forum, ci darete le vostre impressioni sull\'iniziativa.</P>', 0, '', '', '', 1532, 1, 0, '2026-01-09 10:55:16', '0000-00-00'),
(240, '2004-06-10 00:00:00', 'Dai media', 'IL CANDIDATO ALLE EUROPEE ANTONIO PANZERI\r\nRisponde ai lettori del  quotidiano “Il Giorno”. Una risposta in particolare ci interessa.\r\n\r\n', '', 'IL CANDIDATO ALLE EUROPEE ANTONIO PANZERI\r\nRisponde ai lettori del  quotidiano “Il Giorno”. Una risposta in particolare ci interessa.\r\n\r\n', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Il candidato alle elezioni europee nella lista dell’Ulivo, Antonio PANZERI, dal quale abbiamo ricevuto una lettera di consenso alla soluzione dei problemi dei militari che abbiamo pubblicato in altra parte del sito, nel rispondere, sul quotidiano il Giorno, del 4 giugno scorso, è tornato sull’argomento dei diritti ai militari. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>In allegato<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>la domanda e la risposta di PANZERI.</FONT></P>', 65, '', '', '', 3209, 1, 0, '2025-12-15 00:57:41', '0000-00-00'),
(241, '2004-06-10 00:00:00', 'Nostro Servizio', 'Contratto: questi i benefici economici\r\nTabella riepilogativa degli incrementi retributivi derivanti esclusivamente dal rinnovo contrattuale\r\n', '', 'Contratto: questi i benefici economici\r\nTabella riepilogativa degli incrementi retributivi derivanti esclusivamente dal rinnovo contrattuale\r\n', '<P class=OmniPage2 style=\"MARGIN: 0cm 2.25pt 0pt 0cm; TEXT-ALIGN: center; tab-stops: right 480.05pt\" align=center><SPAN style=\"FONT-SIZE: 11pt; FONT-FAMILY: Tahoma; mso-bidi-font-size: 10.0pt; mso-bidi-font-family: \'Times New Roman\'; mso-ansi-language: IT\"></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 9pt; FONT-FAMILY: Arial; mso-bidi-font-size: 12.0pt; mso-bidi-font-family: \'Times New Roman\'\">Pubblichiamo una tabella riepilogativa degli incrementi retributivi derivanti esclusivamente dal rinnovo contrattuale. Sono esclusi pertanto, gli ulteriori aumenti derivanti dalla parametrazione.</SPAN></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 9pt; FONT-FAMILY: Arial; mso-bidi-font-size: 12.0pt; mso-bidi-font-family: \'Times New Roman\'\">Speriamo di avere ulteriori e più precise notizie da parte dei nostri rappresentanti del COCER.</SPAN></B></P>', 67, '', '', '', 1229, 1, 0, '2026-01-14 04:03:23', '0000-00-00'),
(242, '2004-06-12 00:00:00', 'Ordine del giorno', 'CONVOCAZIONE DEL DIRETTIVO NAZIONALE DI FICIESSE\r\nBOLOGNA 19 GIUGNO ORE 11', '', 'CONVOCAZIONE DEL COMITATO DIRETTIVO NAZIONALE', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 18pt; TEXT-ALIGN: center\" align=center><FONT face=\"Times New Roman\" size=3>E’ CONVOCATO IL DIRETTIVO NAZIONALE</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 18pt; TEXT-ALIGN: center\" align=center><FONT face=\"Times New Roman\" size=3>DELL’ASSOCIAZIONE “FINANZIERI CITTADINI E SOLIDARIETA’ - FICIESSE”</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 18pt; TEXT-ALIGN: center\" align=center><FONT face=\"Times New Roman\" size=3>PER SABATO 19 GIUGNO 2004, ORE 11,00</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 18pt; TEXT-ALIGN: center\" align=center><FONT face=\"Times New Roman\" size=3>A BOLOGNA</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 18pt; TEXT-ALIGN: center\" align=center><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></P>\r\n<H1 style=\"MARGIN: 0cm 0cm 0pt 18pt\"><U><FONT face=\"Times New Roman\" size=3>ORDINE DEL GIORNO</FONT></U></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 18pt\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 54pt; TEXT-INDENT: 0cm; mso-list: l0 level1 lfo1; tab-stops: list 53.25pt\"><FONT face=\"Times New Roman\"><FONT size=3>1)</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><FONT size=3>Approvazione del bilancio consuntivo</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 54pt; TEXT-INDENT: 0cm; mso-list: l0 level1 lfo1; tab-stops: list 53.25pt\"><FONT face=\"Times New Roman\"><FONT size=3>2)</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><FONT size=3>Iniziative per l’eventuale richiesta di smilitarizzazione della GDF </FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 54pt; TEXT-INDENT: 0cm; mso-list: l0 level1 lfo1; tab-stops: list 53.25pt\"><FONT face=\"Times New Roman\"><FONT size=3>3)</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><FONT size=3>Fenomeno del mobbing nella GDF</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 54pt; TEXT-INDENT: 0cm; mso-list: l0 level1 lfo1; tab-stops: list 53.25pt\"><FONT face=\"Times New Roman\"><FONT size=3>4)</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><FONT size=3>Elezione di un nuovo componente della Segreteria nazionale</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 18pt\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 18pt; TEXT-ALIGN: center\" align=center><FONT face=\"Times New Roman\" size=3>Il presidente del Direttivo</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 18pt; TEXT-ALIGN: center\" align=center><FONT face=\"Times New Roman\" size=3>Giuseppe Fortuna</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></P>', 0, '', '', '', 1214, 1, 0, '2026-01-06 12:15:26', '0000-00-00'),
(247, '2004-06-18 00:00:00', 'Lettere', 'I COMPLIMENTI DEL GENERALE ANGIONI PER L’INTERVISTA DI “FINANZIERI E CITTADINI”', '', 'I COMPLIMENTI DEL GENERALE ANGIONI PER L’INTERVISTA DI “FINANZIERI E CITTADINI”', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=3>Abbiamo ricevuto dall’Onorevole Franco Angioni, componente della Commissione Difesa della Camera, la lettera che alleghiamo e nella quale l\'illustre parlamentare si complimenta per la fedeltà dell\'intervista resa alla rivista \"Finanzieri e Cittadini\" n. 7 del 2003. </FONT><FONT face=\"Times New Roman\" size=3>Ringraziamo di cuore per le parole di apprezzamento e invitiamo tutti i nostri lettori a rileggere l\'intervista in argomento che contiene numerosi spunti d\'interesse su questioni di particolare attualità. Tra queste, sottolineiamo in particolare </FONT><FONT face=\"Times New Roman\" size=3>il passaggio di pagina 22 nel quale Angioni spiega quali <SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: IT; mso-fareast-language: IT; mso-bidi-language: AR-SA\">siano i guasti che si verificano nelle organizzazioni che adottino, senza che ve ne sia effettiva necessità, il modello organizzativo militare.</SPAN></FONT></P>', 70, '', '', '', 6697, 1, 0, '2026-01-10 01:22:10', '0000-00-00'),
(353, '2004-10-24 00:00:00', 'Notizie Flash', 'DIFESA: CAMERA; DA LUNEDI\' IN AULA DL ISPETTORI FORZE POLIZIA - FORZE ARMATE: PALOMBO, MANDATO PIU\' CONGRUO A DELEGATI COCER - FINANZIARIA: RILIEVI SU RISORSE AGENZIA ENTRATE E GDF E SU STUDI DI SETTORE', '', 'DIFESA: CAMERA; DA LUNEDI\' IN AULA DL ISPETTORI FORZE POLIZIA - FORZE ARMATE: PALOMBO, MANDATO PIU\' CONGRUO A DELEGATI COCER - FINANZIARIA: RILIEVI SU RISORSE AGENZIA ENTRATE E GDF E SU STUDI DI SETTORE', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">DIFESA: CAMERA; DA LUNEDI\' IN AULA DL ISPETTORI FORZE POLIZIA<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SU RIALLINEAMENTO CARRIERE CON FORZE ARMATE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 21 OTT - Approda dalla prossima settimana nell\'Aula della Camera il decreto legge sulle misure urgenti per il personale dei ruoli degli Ispettori delle forze di polizia. La commissione Difesa di Montecitorio oggi ha votato il mandato al relatore, Pippo Fallica (FI), a riferire in Aula, dove da lunedi\' avra\' inizio la discussione generale sul testo, che e\' stato gia\' approvato dal Senato.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il decreto legge tende ad eliminare alcune sperequazioni sia nell\'ordinamento delle carriere sia nei trattamenti economici<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>nel personale non dirigente delle forze di polizia; sperequazioni accentuate da un recente dl che ha riallineato le carriere dei marescialli di Esercito, Marina ed Aeronautica con quelle degli ispettori dell\'Arma dei Carabinieri, senza intervenire su quelle di Polizia, <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>, Polizia penitenziaria e Corpo forestale dello Stato.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>In particolare, il provvedimento prevede l\'inquadramento anche in soprannumero degli ispettori capo e dei periti tecnici capo della Polizia di Stato, Polizia penitenziaria e Corpo forestale dello Stato nelle rispettive qualifiche di ispettore-sostituto ufficiale di pubblica sicurezza e di perito tecnico superiore; un avanzamento di grado pari a quello gia\' conseguito dalle Forze armate e\' previsto anche per i marescialli sostituto ufficiale di Pubblica sicurezza di Carabinieri e <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>. Si proroga, inoltre, fino al 2006 il mandato dei componenti in carica dei consigli di Rappresentanza militare delle forze armate e di polizia. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Le modifiche al Dl dal Senato, interamente recepite dalla commissione Difesa di Montecitorio, estendono poi a colonnelli e generali delle Forze armate una serie di benefici, tra cui quelli relativi ai trattamenti di missione e di trasferimento, all\'orario di lavoro (fissato in 36 ore settimanali), licenze ordinarie e straordinarie, aspettative, norme a tutela della maternita\' e della paternita\', diritto allo studio e buoni pasto. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">FORZE ARMATE: PALOMBO, MANDATO PIU\' CONGRUO A DELEGATI <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">COCER</SPAN></STRONG><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 20 OTT - \'\'I problemi giuridici dei militari, numerosi, complessi e strettamente interconnessi con quelli degli appartenenti ai corpi di polizia ad ordinamento civile, sono da sempre seguiti con competenza appassionata dai delegati dei Consigli centrali della Rappresentanza Militare. Considerati quelli in trattazione nella corrente legislatura e la situazione generale, resa anche piu\' delicata dalla chiusura a fine anno della leva obbligatoria, il Senato ha gia\' approvato la proroga del mandato dei delegati <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">COCER</SPAN></STRONG> da tre a quattro anni\'\'. Lo ha riferito ai giornalisti il senatore Mario Palombo, Vice presidente della Commisione Difesa del Senato e responsabile di An per le Forze Armate. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">\'\'Poiche\' sembra che presso la Camera dei Deputati, la maggioranza si stia orientando a denegare l\'allungamento del mandato - ha poi proseguito il senatore Palombo- mi sono rivolto ai Capigruppo della Casa delle Liberta\' ed anche al Presidente della Commissione Difesa, onorevole Ramponi, chiedendo il loro vivo interessamento per la conferma della norma approvata dai Senatori, la quale peraltro avvicina<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>i delegati del <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">COCER</SPAN></STRONG> ai colleghi dei sindacati di Polizia, che non avendo alcun limite temporale nel mandato offrono al Comparto Sicurezza una tutela giuridica priva di dannose soluzioni di continuita\' e quindi piu\' competitivamente efficace\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">FINANZIARIA: SI\' CON CONDIZIONI DA COMMISSIONE FINANZE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">RILIEVI SU RISORSE AGENZIA ENTRATE E GDF E SU STUDI DI SETTORE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 20 OTT - Si\' con qualche condizione dalla commissione Finanze al disegno di legge sulla finanziaria. Il relatore di maggioranza<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Maurizio Leo (An), dando parere positivo alla manovra, sottolinea la necessita che le norme relative al tetto di spesa che incidono anche sugli stanziamenti per l\'Agenzia delle entrate e per la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> \'\'non debbano comunque pregiudicare la piena ed efficace operativita\' di tali organismi\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>La relazione sottolinea inoltre, a proposito degli studi di settore che \'\'l\'introduzione di un meccanismo di revisione automatico, sulla base degli indici Istat, snaturerebbe il carattere degli studi di settore\'\' e chiede di \'\'escludere che gli accertamenti sulla base degli studi di settore possano essere effettuati anche nei confronti dei contribuenti in contabilita\' ordinaria, senza subordinare tale possibilita\' ad alcuna condizione\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">Sono anche altri i rilievi della commissione. Uno fra i piu\' significativi riguarda il patto di stabilita\' interno e il tetto di spesa per gli enti locali. Leo invita a rivedere, nel caso di applicazione del tetto del 4,8%, il meccanismo di applicazione della disposizione, \'\'evitando di far riferimento all\'andamento storico delle spese, stabilendo invece parametri oggettivi che tengano conto delle peculiarita\' delle singole realta\' locali\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Altre modifiche sono suggerite con lo scopo di ampliare la platea dei contribuenti interessati alla Pianificazione concordata fiscale, e di prorogare fino a fine 2006 le concessioni della riscossione, con la garanzia degli attuali livelli di investimento e di occupazione. Una parte dei rilievi, riguarda alcuni provvedimenti sull\'Iva che comporterebbero un appesantimento burocratico.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ancora, la commissione Finanze suggerisce di rivedere la maggiorazione fiscale prevista dalla manovra per il lotto perche\', \'\'rischia di determinare una riduzione del volume delle giocate, che potrebbe pregiudicare l\'obiettivo di maggior gettito atteso\'\'. Anche l\'incremento fiscale per le sigarette e\' stigmatizzato perche\' potrebbe indurre a minori acquisti \'legali\', favorendo il contrabbando.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La commissione, infine, per quel che riguarda l\'Irap, chiede di superare \'\'le attuali incertezze interpretative eliminando il notevole contenzioso gia\' insorto in materia, nel quale l\'Amministrazione finanziaria risulta prevalentemente soccombente\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2340, 1, 0, '2026-01-12 03:39:31', '0000-00-00'),
(350, '2004-10-21 00:00:00', 'Ordine del giorno', 'COMUNICAZIONE IMPORTANTE IL FORUM E\' DI NUOVO ATTIVO\r\n', '', 'COMUNICAZIONE IMPORTANTE IL FORUM E\' DI NUOVO ATTIVO', '<P><SPAN id=lblArticolo>Come avrete notato il sito e\' nuovamente attivo in tutte le sue strutture, forum compreso. \r\n<P>La ragione del fermo e\' da collegarsi alla circostanza che il Provider non aveva ricevuto il pagamento dello spazio database, per cui aveva interrotto tutti i possibili aggiornamenti.</P>\r\n<P>Dopo i dovuti chiarimenti tutto e\' tornato nella normalità.</P>\r\n<P>Saluti a tutti.</P></SPAN>\r\n<P></P>', 0, '', '', '', 2019, 1, 0, '2026-01-12 23:35:04', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(349, '2004-10-21 00:00:00', 'Documenti', 'APPROVATO IL NUOVO CONTRATTO PER IL BIENNIO 2004/2005 - PARTE ECONOMICA.\r\nAUMENTI IN LINEA CON L\'INFLAZIONE PROGRAMMATA.', '', 'APPROVATO IL NUOVO CONTRATTO PER IL BIENNIO 2004/2005 - PARTE ECONOMICA.\r\nAUMENTI IN LINEA CON L\'INFLAZIONE PROGRAMMATA.', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>E’ stato finalmente sottoscritto in data 13 0ttobre 2004, il provvedimento di concertazione per le Forze di polizia ad ordinamento militare e per le forze di polizia ad ordinamento civile – biennio economico 2004/2005.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>L’art. 9<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>prevede<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>l’incremento dello stipendio dal 1 gennaio 2004 con misure mensili che vanno dai 33,90 euro del IX livello ai 24, 28<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>del V.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Dal 1 gennaio 2005, data di entrata in vigore del sistema dei parametri, il valore del punto parametrale viene stabilito in euro 154,50<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>pertanto gli stipendi in relazione al nuovo sistema saranno rideterminati ed andranno a variare dai 23.175,00 per gli ufficiali superiori ( 150,00 punto parametro) ai 15643,13<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>per il finanziere ( 101,25 punto parametro).</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>L’art. 11 aumenta la indennità pensionabile in due rate, la prima dal 1 gennaio 2004, da 45, 30 per i gradi apicali e 28,00 per il finanziere, ulteriormente incrementati dal 1 gennaio 2005 di 15,90 e 10,00, divenendo a regime pari a<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>777,20 per i tenenti colonnelli e 442,20 per il finanziere.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>L’art. 13 ridetermina la misura della presenza festiva in euro 12 lordi.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>L’art. 14 indica in 5.906.000,00 l’ammontare del fondo per l’efficienza dei servizi istituzionali a disposizione del Corpo per il 2004<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>e in 9.615.000 per l’anno 2005. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Si tratta di aumenti molto limitati, tuttavia in linea con la inflazione programmata. Ancora una volta si rende necessario sganciare il comparto sicurezza dal comparto pubblico, in modo da avere a disposizione maggiori risorse<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>per un settore che effettivamente svolge servizi onerosi e particolari. </FONT></P>', 98, '', '', '', 1920, 1, 0, '2026-01-03 06:42:11', '0000-00-00'),
(351, '2004-10-21 00:00:00', 'Nostro Servizio', 'ANCORA SUL PROLUNGAMENTO DEL MANDATO', '', 'ANCORA SUL PROLUNGAMENTO DEL MANDATO', '', 99, '', '', '', 1892, 1, 0, '2026-01-10 09:31:31', '0000-00-00'),
(352, '2004-10-24 00:00:00', 'Notizie Flash', 'GUARDIA FINANZA: UNA DONNA AL COMANDO TENENZA IN ROMAGNA - TRASPORTI: LAZIO; ENTRO FINE 2004 BUS GRATIS PER MILITARI', '', 'GUARDIA FINANZA: UNA DONNA AL COMANDO TENENZA IN ROMAGNA - TRASPORTI: LAZIO; ENTRO FINE 2004 BUS GRATIS PER MILITARI', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">GUARDIA FINANZA: UNA DONNA AL COMANDO TENENZA IN ROMAGNA<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">GEN.ZANINI, CORPO ALL\'AVANGUARDIA NEI CRITERI DI IMPIEGO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - RAVENNA, 21 OTT - La <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> ha scelto per la prima volta un ufficiale donna per il comando di un reparto isolato. Si tratta del tenente Flavia Guarnaccia, 31 anni, nubile, laureata in giurisprudenza, originaria della provincia di Salerno, proveniente dall\' Accademia del Corpo di Bergamo, dove per tre anni e\' stata istruttrice delle giovani allieve ufficiali, che ha assunto il comando della Tenenza di Lugo (Ravenna) in sostituzione del tenente Massimo D\' Angelosante, trasferito al Nucleo provinciale di Polizia tributaria di Milano.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Alla cerimonia di insediamento, avvenuta oggi, era presente il generale Flavio Zanini, comandante regionale dell\'Emilia-Romagna delle Fiamme Gialle, che ha sottolineato l\' importanza dell\' evento, \'\'che pone il Corpo all\' avanguardia nei criteri d\'impiego delle ufficiali donne\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il tenente Flavia Guarnaccia dipendera\' dal Comando provinciale di Ravenna e avra\' alle dirette dipendenze 31 militari, impiegati in tutti i settori di servizio tipici delle funzioni di polizia economico-finanziaria attribuite al Corpo, con attenzione in particolare alle attivita\' di verifica fiscale, alle indagini di polizia giudiziaria, al contrasto alla commercializzazione di prodotti con marchi contraffatti, alla repressione dello spaccio di stupefacenti e di altri traffici illeciti. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">IL TEN GUARNACCIA,LA NORMALITA\' DI UNA DONNA AL COMANDO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">E\' IL PRIMO UFFICIALE AL FEMMINILE DI UN REPARTO DELLA GDF<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">(di Giorgia Bentivogli)<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - LUGO (RAVENNA), 21 OTT - E\' il primo ufficiale donna scelto dalla <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> per il comando di un reparto territoriale isolato, quello di Lugo, in provincia di Ravenna. L\'attenzione che questo primato di genere suscita, il tenente Flavia Guarnaccia la prende con filosofia: \'\'la curiosita\' e\' naturale, ma si inserisce nella nostra, normale, attivita\' quotidiana\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Non ama molto i titoli \'in rosa\', il tenente. Ma comprende \'l\'effetto visibilita\'\' che una donna in divisa suscita. Forse per questo deroga alla sua naturale riservatezza, e accetta di raccontare una scelta \'\'nata per caso, perche\' quando ti presenti ad un concorso e i posti sono venti, non ti spetti piu\' di tanto\'\'. Il concorso era quello del 2000, il primo in cui la Gdf accetto\' di avere qualcosa di femminile che andasse oltre al termine \'Fiamme Gialle\'. Il tenente Guarnaccia, che oggi ha 31 anni, arrivo\' all\'Accademia del Corpo a Bergamo il 27 novembre. Questa scelta, spiega, \'\'in tre mesi mi ha cambiato la vita\'\'. Guarnaccia supero\' tutti i testi scritti, psico-attitudinali e fisici. La Finanza aveva guadagnato un nuovo ufficiale, il foro di Nocera Inferiore aveva perso un avvocato. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Sono di Nocera, e li\' vive ancora la mia famiglia - racconta - mi sono laureata all\'universita\' di Salerno. Ho fatto pratica forense in uno studio legale e ottenuto l\'abilitazione per il Foro di Nocera Inferiore\'\'. Poi, Bergamo e il cambiamento di vita. \'\'Sono rimasta all\' Accademia come ufficiale istruttore\'\'. Fino a che non e\' stata scelta come comandante di un reparto. La nomina e\' stata ovviamente \'\'un grande momento di gioia, di soddisfazione e di orgoglio. L\'amministrazione mi ha dato fiducia\'\'. Poi il tenente si ferma, come a ponderare le sue parole: \'\'Ma tutto nella normalita\'\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>E spiega la normalita\' della sua presenza a Lugo. \'\'In un reparto territoriale isolato arriva sempre un giovane ufficiale\'\'. Come a dire, e\' un passo naturale di carriera. E il comandante con la gonna sembra susciti piu\' curiosita\' fuori che dentro la Tenenza. \'\'La sola differenza della \'prima\' donna al comando di un reparto, non solo per Gdf ma anche Esercito e Carabinieri, e\' la visibilita\'. Ma nella vita di tutti i giorni non c\'e\' tempo per queste differenze\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Non e\' speranza, ma esperienza. \'\'Ho lavorato con colleghi, inferiori e superiori uomini - racconta - faccio questo lavoro con un\' eta\' piu\' matura e dopo esperienze precedenti. Ho imparato una cosa: la mia normalita\' diventa la normalita\' degli altri e del mio lavoro\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Del suo nuovo incarico le piace il fatto che in una Tenenza si fanno, a 360 gradi, tutte le attivita\' della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di Finanza</SPAN></STRONG>. Dalla polizia economico-finanziaria a quella tributaria, dal contrasto al traffico di stupefacenti a quello della contraffazione dei marchi e della pirateria audiovisiva. E\' riottosa a dire quale sia il settore che preferisce, perche\' \'\'non possiamo scegliere, visto che ogni 2-3 anni veniamo trasferiti\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>I trasferimenti, uno dei lati piu\' duri del lavoro che ha scelto. Ma \'\'questo mestiere va preso come un pacchetto \'all-inclusive\' - scherza -. Come ogni scelta ha una contropartita. Certo, ci sono i trasferimenti frequenti, la disponibilita\' incondizionata di tempo, il fatto che non ci sono giornate programmabili. Ma la motivazione ti fa avere un impatto meno duro\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>E la vita personale? \'\'Quando ho fatto questa scelta non avevo legami, la mia decisione non coinvolgeva nessuno. Certo, nella vita pratica non sempre un compagno puo\' seguire i trasferimenti di una donna perche\' perderebbe il lavoro. Ma e\' anche un fatto culturale: difficilmente un uomo si mette in discussione per una donna\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Per un attimo nelle parole del tenente passa un\'ombra. Ma e\' solo un istante. \'\'Poi pero\' penso che ci poniamo questi problemi perche\' cerchiamo di pianificare tutto. Ma nei fatti la soluzione si trova sempre, magari il compagno e\' indulgente\'\'. Perche\' Flavia Guarnaccia una cosa l\'ha imparata in questi quattro anni: \'\'Ho la consapevolezza di fare un mestiere che fino a 4 anni fa era riservato agli uomini\'\'. Allora lei con \'\'calma, pacatezza e serenita\'\' percorre la sua via. \'\'Perche\' quando si vuole qualcosa veramente, una soluzione si trova sempre. L\'importante e\' vivere le proprie scelte con lucidita\', accettando i propri limiti, e con un pizzico di positivita\'\'\'. Forse per questo i 29 uomini, o meglio, le 29 persone che dipendono dal tenente Guarnaccia trovano la sua via \'\'normale\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">TRASPORTI: LAZIO; ENTRO FINE 2004 BUS GRATIS PER MILITARI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">PRIMA REGIONE AD ADOTTARE PROVVEDIMENTO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 23 OTT - Entro la fine di novembre oltre 220.000 appartenenti a forze armate e di polizia del Lazio viaggeranno gratuitamente sui mezzi di trasporto pubblici. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il Lazio e\' la prima regione italiana ad attuare un provvedimento del genere: purche\' in uniforme e per motivi di servizio, il trasporto pubblico sara\' gratis per appartenenti alle forze armate, carabinieri, agenti di polizia, finanzieri, agenti di polizia penitenziaria, corpo militare della Croce Rossa, guardie forestali, vigili urbani e polizia provinciale. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La proposta di legge licenziata all\'unanimita\' alla fine di settembre dalla Commissione Trasporti, Lavori Pubblici e Viabilita\' della Regione Lazio sta, infatti, per approdare in aula per il via libera definitivo. Prima che cio\' accada manca un passaggio tecnico in Commissione Bilancio e, quindi, la calendarizzazione da parte della conferenza dei capigruppo.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La prima seduta utile per l\'approvazione potrebbe gia\' essere quella del 27 ottobre. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Puntiamo a che la norma sia pienamente operativa prima della fine dell\'anno\'\', ha detto l\'assessore agli affari istituzionali della regione, Donato Robilotta. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Gli appartenenti alle Forze dell\'ordine purche\' in divisa - ha spiegato Robilotta - saranno esentati dal pagamento del biglietto. Sono soddisfatto anche perche\' nel provvedimento rientrano anche gli appartenenti ai corpi di polizia municipali e quelli della Polizia provinciale\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Abbiamo discusso questa norma anche attraverso le audizioni con i rappresentanti del <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG> nazionale che ci ha espresso la propria soddisfazione perche\' il Lazio e\' la prima regione italiana ad attuare un provvedimento del genere\'\', ha detto il presidente della Commissione trasporti della Pisana Stefano De Lillo(FI).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Secondo De Lillo le agevolazioni del pagamento del biglietto per le forze dell\'ordine, garantita da una legge nazionale e limitata alle FS, si era persa nel passaggio delle competenze sui trasporti dallo Stato alle Regioni.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt 45.8pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'La gratuita\' per il personale militare e della polizia<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>- ha detto De Lillo - avra\' due vantaggi per i cittadini. La diminuzione del traffico privato ed una maggiore sicurezza e tranquillita\' sui mezzi pubblici dove vedere personale in divisa sara\' un deterrente contro atti contro legge piccoli e grandi\'\'. <SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;</SPAN>(ANSA).</SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Courier New\">&nbsp;</FONT></o:p></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 5685, 1, 0, '2026-01-13 17:39:22', '0000-00-00'),
(235, '2004-06-02 00:00:00', 'Lettere', 'Risposta alla lettera del Segretario Generale da parte del candidato Vincenzo CRETELLA della Lista \" Società civile - OCCHETTO-DI PIETRO -Italia dei valori\"', '', 'Risposta alla lettera del Segretario Generale da parte del candidato Vincenzo CRETELLA della Lista \"  Società civile OCCHETTO-DI PIETRO - Italia dei valori\"', '<P>Abbiamo ricevuto e volentieri pubblichiamo in allegato, la lettera del candidato alle elezioni europee Vincenzo CRETELLA; chi segue le vicende di cui ci occupiamo certamente conosce l\'impegno che da sempre&nbsp; Vincenzo profonde per cercare di affermare i diritti dei militari.</P>\r\n<P>Lo ha fatto come finanziere, lo ha fatto come delegato del COCER, certamente potrà continuare a farlo con maggiori possibilità di successo qualora dovesse riuscire a raggiungere l\'obiettivo che si è prefissato: l\' elezione a Deputato al Parlamento Europeo.</P>\r\n<P>E\' candidato per il NORD- EST nella lista \"Società civile - OCCHETTO-DI PIETRO - Italia dei Valori.</P>', 64, '', '', '', 2516, 1, 0, '2026-01-04 22:39:36', '0000-00-00'),
(341, '2004-10-10 00:00:00', 'Notizie Flash', 'FISCO: MANZITTI, NESSUNA ESTENSIONE CONDONI AL 2003\r\n\r\n', '', 'FISCO: MANZITTI, NESSUNA ESTENSIONE CONDONI AL 2003\r\n\r\n', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">FISCO: MANZITTI, NESSUNA ESTENSIONE CONDONI AL 2003<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 6 OTT - \'\'Smentisco che il Dipartimento da me diretto abbia allo studio o in preparazione alcun intervento normativo riguardante sanatorie fiscali\'\'. Lo sottolinea il direttore del dipartimento politiche fiscali del ministero dell\'Economia e delle Finanze, Andra Manzitti, in una lettera inviata a un quotidiano finanziario, che oggi ha rilanciato l\'ipotesi di un\'estensione dei condoni all\'anno fiscale 2003. (ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 1724, 1, 0, '2026-01-09 20:55:07', '0000-00-00'),
(342, '2004-10-10 00:00:00', 'Notizie Flash', 'POLIZIA: RIORDINO CARRIERE; SINDACATI E COCER BOCCIANO LINEE', '', 'POLIZIA: RIORDINO CARRIERE; SINDACATI E COCER BOCCIANO LINEE', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">POLIZIA: RIORDINO CARRIERE; SINDACATI E COCER BOCCIANO LINEE<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 8 OTT - Sindacati di Polizia e Cocer contestano le linee guida sul riordino delle carriere presentate nelle scorse settimane e, \'\'nel respingere l\'ipotesi di un \'riordino\' certamente destinato a non risolvere le palesi problematiche esistenti, invitano il Governo ad un piu\' serrato confronto su un tema tanto delicato, che vada nella direzione di una non piu\' rinviabile<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>modernizzazione degli apparati delle Forze dell\' Ordine e di una definitiva soluzione dei problemi ordinamentali\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>In una nota organizzazioni sindacali e rappresentanze militari \'\'esprimono la loro ferma contrarieta\' nei confronti di un progetto di riordino che sia assolutamente parziale e non lasci intravedere alcuna riforma complessiva delle funzioni e delle<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>professionalita\' degli operatori delle Forze di Polizia; non elimini le tante ed ingiustificate disparita\' di trattamento a legislazione vigente; non proponga un modello di sviluppo ordinamentale in grado di assecondare i necessari processi di modernizzazione delle Forze di Polizia; non tenga in alcuna considerazione le complessive aspirazioni del personale, limitandosi a prevedere la creazione di un ruolo unico esecutivo e poco altro\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Per i sindacati e il Cocer \'\'e\' invece di tutta evidenza l\' importanza strategica di un progetto di riordino delle carriere che faccia perno: sulla specificita\' delle funzioni esercitate dal personale di ogni singola Forza di Polizia; sul riconoscimento delle professionalita\' maturate; sulla necessita\' di eliminare vecchie e nuove disparita di trattamento; su uno sviluppo complessivo e duraturo delle carriere che contribuisca a disegnare una organizzazione interna piu\' efficiente, in cui vi sia un rapporto chiaro e diretto tra gradi/ruoli e funzioni e, quindi, anche piu\' funzionale alle esigenze dei cittadini; su una piu\' adeguata qualificazione e professionalizzazione del personale\'\'. <SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;</SPAN>(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">Il documento e\' sottoscritto da varie sigle sindacali. Per la Polizia di Stato: Silp Cgil / Uilps / Fsp / Lisipo - Sodipo Rinnovamento sindacale per l\' Ugl Consap - Italia Sicura - Siap - Anfp - Coisp. Per la Polizia Penitenziaria: Cisl Fps - Cgil Fp - Uil Pa - Ugl - Sag. Per il Corpo Forestale dello Stato: Ugl - Cicsl Fp - Uil Pa – Sapecofs - Cgil Fp. Infine, il Cocer della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Non lo hanno firmato, invece, il Siulp e il Sap, due grandi sindacati che rappresentano oltre la meta\' del personale della Polizia di Stato. \'\'Noi chiediamo al ministro dell\' interno – ha spiegato Filippo Saltamartini, segretario del Sap – di utilizzare subito le risorse gia\' stanziate e mantenere l\' impegno che il governo ha assunto con Sap e Siulp il 9 agosto\'\'. <SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</SPAN>(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">FORZE POLIZIA: SOTT. D\' ALI\' SU RIALLINEAMENTO STIPENDI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - POTENZA, 8 OTT - \'\'A decorrere dal primo gennaio 2003 le indennita\' previste dal Dpr 164 del 2002 sono estese anche ai dirigenti civili e militari delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG> cui vanno estesi anche i diritti sindacali. Inoltre con decorrenzaprimo gennaio 2004, per gli stessi dirigenti l\' indennita\' pensionabile e\' incrementata del 4,91 per cento, fatti salvi trattamenti piu\' favorevoli in godimento\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Lo ha detto il Sottosegretario all\' Interno, Antonio D\' Ali\', rispondendo ad un\' interrogazione dell\' on. Giuseppe Molinari, capogruppo della Margherita in Commissione Difesa della Camera, precisando che le disposizioni sono contenute in un emendamento approvato dal Senato tre giorni fa, sul \'\'riallineamento normativo e perequativo economico dei trattamenti dei funzionari dirigenti e direttivi della Polizia di Stato\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Molinari, nella replica, ha espresso l\' auspicio di \'\'una rapida conversione in legge del decreto emendato\'\'. Il deputato lucano ha evidenziato che il provvedimento \'\'presenta, sotto l\' aspetto delle richieste economiche e contrattuali, sicuramente alcuni elementi positivi con il riconoscimento del lavoro svolto in questi mesi anche da parte della opposizione che piu\' volte attraverso ordini del girono, interrogazioni, interpellanze aveva sollevato all\' attenzione del governo i problemi della condizione giuridica ed economica dei funzionari e dirigenti della polizia\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 4285, 1, 0, '2026-01-13 09:26:22', '0000-00-00'),
(343, '2004-10-10 00:00:00', 'Dal Parlamento', 'APPROVATA AL SENATO LA PROROGA DEI COCER FINO A MAGGIO 2006. IL DISEGNO DI LEGGE ORA PASSA ALLA CAMERA', '', 'APPROVATA AL SENATO LA PROROGA DEI COCER FINO A MAGGIO 2006. IL DISEGNO DI LEGGE ORA PASSA ALLA CAMERA', '<P align=left>Il 05 ottobre il Senato ha approvato la conversione in legge del DL 238/2004 (quello degli Ispettori della PS); nell\'approvarlo sono state fatte delle aggiunte tra cui la proroga di un anno del mandato dei COCER -COIR e COBAR (vgs art. 5 quater).</P>\r\n<P>\"Disegno di legge N. 3105 </P>\r\n<P>Art. 5-quater. - (Proroga del mandato dei consigli della rappresentanza militare). – 1. Il mandato dei componenti in carica del consiglio centrale interforze della rappresentanza militare, nonché dei consigli centrali, intermedi e di base dell’Esercito, della Marina, dell’Aeronautica, dell’Arma dei carabinieri e del Corpo della guardia di finanza, eletti nelle categorie del personale in servizio permanente e volontario, è prorogato fino al 15 maggio 2006».\"</P>\r\n<P>L\'art. in questione è stato inserito da un emendamento del relatore, Sen. Boscetto di Forza Italia al quale sembra non si sia opposto esplicitamente nessun gruppo politico, come riportato nel resoconto stenografico:</P>\r\n<P>\"BOSCETTO, relatore. <BR>L\'emendamento 5.0.5 è volto a prorogare al 15 maggio 2006 la durata del mandato dei componenti in carica delle rappresentanze militari delle Forze armate e della Guardia di finanza, in attesa dell\'entrata in vigore della legge di riforma della rappresentanza militare (ndr: quindi è scontata la sua approvazione!), tenendo conto, fra l\'altro, dell\'ordine del giorno accolto dal Governo nella seduta dell\'Assemblea del Senato del 27 luglio 2004. </P>\r\n<P>Su tutti gli emendamenti presentati dalla Commissione, il parere del relatore è naturalmente favorevole.</P>\r\n<P>SAPORITO, sottosegretario di Stato alla Presidenza del Consiglio dei ministri. Il Governo concorda con il relatore e si dichiara favorevole agli emendamenti presentati dalla Commissione.</P>\r\n<P>Metto ai voti l\'emendamento 5.0.5, presentato dalla Commissione.</P>\r\n<P>È approvato.\"</P>\r\n<P>Nelle dichiarazioni di voto finale è stato affermato (trattasi di stralci):</P>\r\n<P>\"NIEDDU (DS-U). Infine, signor Presidente, votiamo a favore anche perché condividiamo, come d’altronde condividevamo da tempo, la necessità di disporre la proroga del mandato dei consigli della rappresentanza militare, che si collega sia all’esigenza di una ridefinizione dell’attuale legislazione in materia, sia al fatto che abbiamo accelerato la transizione verso Forze armate interamente professionali, con il superamento della leva obbligatoria.</P>\r\n<P>Per tali ragioni, riteniamo che questo disposto sia particolarmente urgente ed importante.</P>\r\n<P>STIFFONI (LP). Signor Presidente, intervengo solo per esprimere il voto favorevole della Lega Padana a questo provvedimento.</P>\r\n<P>RIPAMONTI (Verdi-U). Signor Presidente, intervengo per annunciare il voto di astensione del Gruppo dei Verdi.</P>\r\n<P>PETRINI (Mar-DL-U). Voteremo comunque a favore del provvedimento, giudicandolo positivo perché ottempera a una richiesta da tempo avanzata e a una promessa da tempo fatta. (Applausi dal Gruppo Mar-DL-U. Congratulazioni).\"</P>\r\n<P>Non vi sono state altre dichiarazioni.</P>\r\n<P>Dichiarazione di voto e votazione finale<BR>Votazione nominale a scrutinio simultaneo favorevoli 169, contrari 4, astenuti 4. (modificato rispetto al testo proposto)</P>\r\n<P>Ora tocca alla Camera l\'approvazione definitiva.<BR>Per l\'iter completo si puo consultare questo link:<BR><A href=\"http://www.senato.it/leg/14/BGT/Schede/Ddliter/21965.htm#TrAula\">http://www.senato.it/leg/14/BGT/Schede/Ddliter/21965.htm#TrAula</A></P>\r\n<P><EM>(Ringraziamo Berardo per aver fornito sul forum di Ficiesse il materiale qui pubblicato)</EM></P>', 0, '', '', '', 2052, 1, 0, '2026-01-09 22:24:33', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(344, '2004-10-14 00:00:00', 'Documenti', 'PUBBLICHIAMO NUOVAMENTE IL DOCUMENTO UNITARIO DEI DELEGATI DEI CONSIGLI DI RAPPRESENTANZA DELLA GDF A CONCLUSIONE DELL\'INCONTRO TENUTOSI A ROMA IL 30 E 31 MARZO 2004', '', 'PUBBLICHIAMO NUOVAMENTE IL DOCUMENTO UNITARIO DEI DELEGATI DEI CONSIGLI DI RAPPRESENTANZA DELLA GDF A CONCLUSIONE DELL\'INCONTRO TENUTOSI A ROMA IL 30 E 31 MARZO 2004', '<P class=MsoTitle style=\"MARGIN: 0cm -1cm 0pt 0cm; TEXT-INDENT: 0cm; TEXT-ALIGN: left\" align=left><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 10pt; FONT-FAMILY: Arial; mso-bidi-font-size: 18.0pt; mso-bidi-font-weight: bold; mso-bidi-font-family: \'Times New Roman\'\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p>&nbsp;</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p><SPAN style=\"mso-font-kerning: 0pt\"><FONT size=5>Incontro tra i Consigli di Rappresentanza<o:p></o:p></FONT></SPAN></P>\r\n<H1 style=\"MARGIN: 12pt 0cm 3pt; TEXT-ALIGN: center\" align=center><SPAN style=\"mso-font-kerning: 0pt\"><FONT size=5>della Guardia di Finanza.<o:p></o:p></FONT></SPAN></H1>\r\n<H1 style=\"MARGIN: 12pt 0cm 3pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 20pt; FONT-FAMILY: \'Arial Black\'; mso-bidi-font-size: 16.0pt\">PER UNA ROTTA CONDIVISA<o:p></o:p></SPAN></H1>\r\n<H1 style=\"MARGIN: 12pt 0cm 3pt; TEXT-ALIGN: center\" align=center><FONT size=5>Roma – Villa Spada 30 e 31 marzo 2004</FONT></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<H2 style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 16pt\"><U><FONT face=\"Times New Roman\">DOCUMENTO UNITARIO <o:p></o:p></FONT></U></SPAN></H2>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><B style=\"mso-bidi-font-weight: normal\"><U><SPAN style=\"FONT-SIZE: 14pt\"><FONT face=\"Times New Roman\">RAPPRESENTANZA MILITARE (LEGGE 382/78)<o:p></o:p></FONT></SPAN></U></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>I delegati dei Consigli di Rappresentanza della Guardia di Finanza delle varie categorie ritengono che gli emendamenti presentati al nuovo testo unificato sulla riforma della Rappresentanza Militare discusso presso la IV Commissione Difesa della Camera dei Deputati, siano evidente risultato di una diffidenza ingiustificata, preconcetta ed ai limiti della legittimità verso l’Istituto.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Essi paiono volutamente ignorare gli indubbi vantaggi che nel tempo la Rappresentanza ha portato al personale ed all’Amministrazione stessa, che non hanno esitato a farvi ricorso, anche oltre le competenze previste, ogni qualvolta abbiano avuto bisogno di sostegno.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Esprimono pertanto il <B>totale dissenso</B> in merito.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Il testo scaturito dall’accoglimento degli emendamenti in questione, lungi dal soddisfare le aspettative del personale, segna una netta involuzione rispetto all’attuale assetto normativo e vanifica le proposte del COCER Interforze risultando, anzi, peggiorativo rispetto a quello di partenza.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>I delegati oggi convenuti all’incontro, chiedono che si tenti la via di una mini riforma su 7 punti irrinunciabili:</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 48pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 48.0pt\"><SPAN style=\"FONT-FAMILY: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>·</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN></SPAN><FONT face=\"Times New Roman\" size=3>dignità di parte nella concertazione;</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 48pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 48.0pt\"><SPAN style=\"FONT-FAMILY: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>·</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN></SPAN><FONT face=\"Times New Roman\" size=3>il riconoscimento della personalità giuridica;</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 48pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 48.0pt\"><SPAN style=\"FONT-FAMILY: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>·</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN></SPAN><FONT face=\"Times New Roman\" size=3>la tutela dei delegati;</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 48pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 48.0pt\"><SPAN style=\"FONT-FAMILY: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>·</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN></SPAN><FONT face=\"Times New Roman\" size=3>la ratifica delle competenze di fatto acquisite nel tempo e la previsione di altre necessarie per la reale tutela ed il benessere del personale in senso lato;</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 48pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 48.0pt\"><SPAN style=\"FONT-FAMILY: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>·</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN></SPAN><FONT face=\"Times New Roman\" size=3>la separazione dei comparti difesa e sicurezza; </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 48pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 48.0pt\"><SPAN style=\"FONT-FAMILY: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>·</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN></SPAN><FONT face=\"Times New Roman\" size=3>la valorizzazione delle libere associazioni tra i militari;</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 48pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 48.0pt\"><SPAN style=\"FONT-FAMILY: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>·</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN></SPAN><FONT face=\"Times New Roman\" size=3>la piena rieleggibilità dei delegati.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 30pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>La fattibilità del progetto, andrà verificata entro il 30 settembre prossimo venturo, in apposita assise, da convocare.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>In caso negativo, l’assemblea ritiene che la strada allora da percorrere sia quella della smilitarizzazione e sindacalizzazione.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><B style=\"mso-bidi-font-weight: normal\"><U><SPAN style=\"FONT-SIZE: 14pt\"><FONT face=\"Times New Roman\">LE POLITICHE CONTRATTUALI <o:p></o:p></FONT></SPAN></U></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><U><o:p><SPAN style=\"TEXT-DECORATION: none\"><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></SPAN></o:p></U></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Le recenti esperienze in materia di rinnovo contrattuale hanno rafforzato la convinzione che il Comparto Sicurezza sia strumento ottimale per il conseguimento di condizioni lavorative e retributive più omogenee per gli operatori della sicurezza, siano essi a <I style=\"mso-bidi-font-style: normal\">status</I> civile o militare.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Occorre rafforzare il ruolo del Comparto Sicurezza, dall’altra è però essenziale riflettere sulla necessità di differenziare questo Comparto dal Comparto Difesa. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>E’ imprescindibile esercitare un’azione di rafforzamento, all’interno del Comparto Sicurezza, che porti alla realizzazione di strutture che siano per quanto possibile omogenee per tutti i lavoratori rappresentati.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<H1 style=\"MARGIN: 12pt 0cm 3pt; TEXT-ALIGN: justify\"><U><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-font-kerning: 0pt\">RISTRUTTURAZIONE DEGLI ASSETTI PREVIDENZIALI – PREVIDENZA COMPLEMENTARE <o:p></o:p></SPAN></U></H1>\r\n<H1 style=\"MARGIN: 12pt 0cm 3pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-font-kerning: 0pt\">La politica rivendicativa della rappresentanza militare è stata sempre caratterizzata dal perseguire obiettivi che tendessero ad esaltare, in termini civili e sociali, la dignità professionale e umana dell’appartenente al Corpo, cercando di inserirlo, a pieno titolo, in una dimensione avanzata nel mondo del lavoro, modernamente inteso e democraticamente concepito.<o:p></o:p></SPAN></H1>\r\n<H1 style=\"MARGIN: 12pt 0cm 3pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-font-kerning: 0pt\">E’ necessario decidere preventivamente le sorti del Fondo Assistenza Finanzieri (FAF) e quindi definire il passaggio dal Trattamento di Fine Servizio (TFS) al Trattamento di Fine Rapporto (TFR), anche per poter finalmente accedere all’agognato anticipo maturato per l’acquisto della prima casa o per grandi interventi, nonché avviare la previdenza complementare mediante la costituzione dei fondi di pensione.<o:p></o:p></SPAN></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<H1 style=\"MARGIN: 12pt 0cm 3pt; TEXT-ALIGN: justify\"><U><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-font-kerning: 0pt\">STIPENDI E SICUREZZA DEL LAVORATORE <o:p></o:p></SPAN></U></H1>\r\n<H1 style=\"MARGIN: 12pt 0cm 3pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-font-kerning: 0pt\">Si reputa prioritario, fra i propri obiettivi, il conseguimento di un trattamento retributivo che avvicini la condizione dell’appartenente al Corpo agli standard di retribuzione europei, valorizzando altresì il recupero del militare stesso a funzioni proprie del Corpo.<o:p></o:p></SPAN></H1>\r\n<H1 style=\"MARGIN: 12pt 0cm 3pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-font-kerning: 0pt\">E’ altrettanto prioritario l’obiettivo di garantire, per contratto, la tutela del lavoratore in relazione agli incidenti derivanti da attività di servizio.<o:p></o:p></SPAN></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>A tal proposito si stabilisce di destinare tutte le risorse al trattamento principale e all’indennità pensionabile auspicando impegni chiari del Governo nella prossima Finanziaria.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<H1 style=\"MARGIN: 12pt 0cm 3pt; TEXT-ALIGN: justify\"><U><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-font-kerning: 0pt\">RIORDINO DELLE CARRIERE<o:p></o:p></SPAN></U></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Si conviene sui seguenti punti:</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<UL style=\"MARGIN-TOP: 0cm\" type=disc>\r\n<LI class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-list: l1 level1 lfo2; tab-stops: list 36.0pt\"><FONT face=\"Times New Roman\" size=3>riforma che tenga conto del rapporto gradi/funzioni/ruoli;</FONT></LI></UL>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<UL style=\"MARGIN-TOP: 0cm\" type=disc>\r\n<LI class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-list: l1 level1 lfo2; tab-stops: list 36.0pt\"><FONT face=\"Times New Roman\" size=3>valorizzazione dei sovrintendenti, senza eccezioni, tenendo conto, nella riforma, del possesso delle qualifiche di Ufficiale di Polizia Giudiziaria e di Polizia Tributaria;</FONT></LI></UL>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<UL style=\"MARGIN-TOP: 0cm\" type=disc>\r\n<LI class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-list: l1 level1 lfo2; tab-stops: list 36.0pt\"><FONT face=\"Times New Roman\" size=3>esternalizzazione dei servizi non attinenti alle qualifiche professionali;</FONT></LI></UL>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<UL style=\"MARGIN-TOP: 0cm\" type=disc>\r\n<LI class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-list: l1 level1 lfo2; tab-stops: list 36.0pt\"><FONT face=\"Times New Roman\" size=3>valorizzazione delle peculiarità della Guardia di Finanza quale Polizia Economico-Finanziaria, in termini di:</FONT></LI></UL>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 18pt; TEXT-INDENT: 17.4pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>- indennità (a perequazione di quelle di Polizia non pienamente fruite);</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 17.4pt; TEXT-ALIGN: justify\"><SPAN style=\"mso-spacerun: yes\"><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 35.4pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>- formazione e qualificazione permanente; </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<UL style=\"MARGIN-TOP: 0cm\" type=disc>\r\n<LI class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-list: l1 level1 lfo2; tab-stops: list 36.0pt\"><FONT face=\"Times New Roman\" size=3>eliminazione delle sperequazioni ora esistenti rispetto all’Agenzia delle Entrate e all’Agenzia delle Dogane, in termini di indennità incentivanti;</FONT></LI></UL>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<UL style=\"MARGIN-TOP: 0cm\" type=disc>\r\n<LI class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-list: l1 level1 lfo2; tab-stops: list 36.0pt\"><FONT face=\"Times New Roman\" size=3>mobilizzazione dei ruoli attraverso un maggior ricorso ai “reclutamenti verticali”;</FONT></LI></UL>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<UL style=\"MARGIN-TOP: 0cm\" type=disc>\r\n<LI class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-list: l1 level1 lfo2; tab-stops: list 36.0pt\"><FONT face=\"Times New Roman\" size=3>pianificazione della mobilità in caso di “reclutamento verticale”, attesi gli effetti disincentivanti sui concorsi per l’accesso ai ruoli superiori, del regime attuale. </FONT></LI></UL>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: right\" align=right><B style=\"mso-bidi-font-weight: normal\"><FONT size=3><FONT face=\"Times New Roman\">I DELEGATI DEI CONSIGLI DI RAPPRESENTANZA <o:p></o:p></FONT></FONT></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><B style=\"mso-bidi-font-weight: normal\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>DELLA GUARDIA DI FINANZA</FONT></FONT></B></P>\r\n<P class=MsoTitle style=\"MARGIN: 0cm -1cm 0pt 0cm; TEXT-INDENT: 0cm; TEXT-ALIGN: left\" align=left></o:p></SPAN></P>', 0, '', '', '', 3452, 1, 0, '2026-01-11 06:04:05', '0000-00-00'),
(232, '2004-05-22 00:00:00', 'Editoriale', 'MOBBING: un utile contributo su un argomento di attualità.', '', 'MOBBING: un utile contributo su un argomento di attualità.', '<P>In allegato un contributo fornito da un nostro associato sul tema del&nbsp; mobbing.</P>\r\n<P>Si tratta di una panoramica sulla giurisprudenza, ormai notevole, che si è formata in materia, illustrata con&nbsp;una impostazione che può essere utile sia per chi, malauguratamente, dovesse trovarsi in una situazione tale da configurare il fenomeno del mobbing, sia per quegli operatori del diritto che dovessero impostare una difesa in materia nelle diverse sedi previste.</P>\r\n<P>Ringraziamo il nostro associato e lo invitiamo ad approfondire ulteriormente un argomento che interessa molti.</P>', 62, '', '', '', 2742, 1, 0, '2026-01-12 23:44:04', '0000-00-00'),
(321, '2004-09-18 00:00:00', 'Dalle autorità giurisdizionali', 'Sentenza del TAR Lazio sul diritto di Associazione- Circolare Ministro della difesa.', '', 'Sentenza del TAR Lazio sul diritto di Associazione- Circolare Ministro della difesa.', '<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><STRONG>LA SENTENZA E\' RIPORTATA DI SEGUITO</STRONG></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">Il TAR Lazio, Sezione I bis, il 15 luglio scorso ha emesso sentaenza in relazione al ricorso proposto dall\'Associazione AMID, che nel frattempo proprio in attesa della sentenza, aveva sospeso ogni attività.<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">Come ricorderete la Circolare, in sostanza, dettava regole stringenti per la costituzione di Associazioni cui appartenessero militari, anche assieme a cittadini civili, con un preventivo assenso da parte del ministero stesso. Le Associazioni già costituite dovevano essere comunque monitorate per stabilire se rispondessero ai criteri stabiliti con quella circolare.<o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">Ebbene la sentenza del giudice Amministrativo appare come un giudizio salomonico, nel senso che in definitiva \"<STRONG>Dichiara inamissibile, per carenza di interesse, il ricorso\".</STRONG><o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">Tale assunto potrebbe sembrare non portare luce sulle nostre vicende, così non è poichè comunque la sentenza, nelle motivazioni afferma un importante concetto; scrive infatti&nbsp; il giudice:<o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">\" Essa (Amid ), infatti, risulta già costituita; e quindi non necessitante dell\'assenso preventivo disciplinato dalla Circolare avversata, la quale non assume (come del tutto evidente) valenza retroattiva, sì da imporre, anche per le Associazioni già esistenti, un assenso che invece riguarda le formazioni associative la cui costituzione <STRONG>è successiva all \'adozione delle disposizioni da essa introdotte.\"</STRONG></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><STRONG>RIPORTO LA SENTENZA DI SEGUITO</STRONG></SPAN><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><SPAN style=\"FONT-SIZE: 12pt; mso-bidi-font-size: 10.0pt\"><?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /><v:shape id=_x0000_i1025 style=\"WIDTH: 48pt; HEIGHT: 54pt\" type=\"#_x0000_t75\">&lt;v:imagedata o:title=\"\" src=\"file:///C:DOCUME~1COL~1.GERIMPOST~1Tempmsohtml1 </v:shape></SPAN></SPAN></P>', 0, '', '', '', 437, 1, 0, '2018-04-11 13:15:07', '0000-00-00'),
(234, '2004-05-29 00:00:00', 'Lettere', 'Risposta dell\'On/le MINNITI alla lettera inviata dal Segretario Generale ai segretari dei partiti politici.', '', 'Risposta dell\'On/le MINNITI alla lettera inviata dal Segretario Generale ai segretari dei partiti politici.', '<P>Pubblichiamo, in allegato, la lettera indirizzata all\'Associazione da parte dell\'onorevole Minniti, per conto dell\'On/le Fassino, segretario dei Democratici di Sinistra.</P>\r\n<P>La lettera è la risposta all\'appello rivolto dal Segretario Generale dell\'Associazione, Carlo GERMI, a tutti i segretari dei partiti politici impegnati nella campagna elettorale per le prossime Europee.</P>\r\n<P>Rilevante è la presa di posizione, a questo punto impegnativa, relativamente al progetto di riforma della \"rappresentanza militare\", che sta per essere licenziato dalla Commissione Difesa della Camera.</P>\r\n<P>Se il contenuto,come sembra, rimmarrà quello attuale, afferma l\'On/le MINNITI, \"con ogni probabilità andremo in aula con un nostro testo ed un relatore di minoranza\".</P>\r\n<P>Ci sembra una affermazione importante&nbsp; che noi tutti auspichiamo possa essere condivisa da altre forze politiche di opposizione e, perchè no, anche della maggioranza.</P>', 63, '', '', '', 2069, 1, 0, '2026-01-10 06:55:34', '0000-00-00'),
(250, '2004-06-25 00:00:00', 'Nostro Servizio', 'Un altro autorevole riconoscimento da parte del Giudice naturale della Pubblica Amministrazione dei diritti fondamentali di cui sono pienamente titolari i cittadini con le stellette.', '', 'Un altro autorevole riconoscimento da parte del Giudice naturale della Pubblica Amministrazione dei diritti fondamentali di cui sono pienamente titolari i cittadini con le stellette.', '<P class=MsoBodyText2 style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Come era stato già anticipato nei precedenti articoli, in tema di tutela dei diritti della persona umana e segnatamente con riferimento ai diritti del lavoratore, si è registrato, negli ultimi anni e con crescita esponenziale, una vivissima attenzione da parte della giurisprudenza e della dottrina, intervenute a vario titolo e stimolate da una sensibilità sociale sempre più viva.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyText2 style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Numerosissime sono quindi le pronunzie e gli articoli di dottrina amministrativistica e giuslavoristica che si possono agevolmente consultare sui siti internet giuridici, ovvero sulle riviste, sui repertori e nei massimari.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Quello che fino ad oggi mancava, era invece una esplicita pronunzia del Giudice naturale della Pubblica Amministrazione militare, vale a dire il Giudice amministrativo, resa per di più proprio con specifico riferimento ad una controversia risarcitoria intentata con successo da un militare nei confronti dell’Ente pubblico di appartenenza per i danni subiti nell’espletamento del servizio.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Ed allora, eccola questa sentenza, riportata integralmente sul sito.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Ringraziamo&nbsp;&nbsp; PROMETEO per la sua collaborazione</FONT></P>', 71, '', '', '', 1844, 1, 0, '2026-01-10 00:39:13', '0000-00-00'),
(184, '2004-02-28 00:00:00', 'Nostro Servizio', 'LE DECISIONI DEL DIRETTIVO FICIESSE: \"SENZA ASSOCIAZIONISMO PROFESSIONALE LA GDF VA SUBITO SMILITARIZZATA\"', '', 'LE DECISIONI DEL DIRETTIVO FICIESSE: \"SENZA ASSOCIAZIONISMO PROFESSIONALE LA GDF VA SUBITO SMILITARIZZATA\"', '<p style=\"text-align:justify\">Sabato 21 febbraio si &egrave; tenuto il direttivo nazionale dell&rsquo;Associazione Finanzieri Cittadini e Solidariet&agrave;. Riportiamo di seguito le principali decisioni assunte .</p>\r\n\r\n<p style=\"text-align:justify\">RIFORMA DELLA RAPPRESENTANZA MILITARE. Suscitano grande preoccupazione gli emendamenti del Governo al secondo testo unificato presentato in Commissione Difesa della Camera che configurrebbero i nuovi organi della rappresentanza come mere &ldquo;appendici&rdquo; dei comandi, mentre sono stati bocciati tutti gli emendamenti volti a concedere ai militari il diritto di costituire e aderire ad associazioni professionali. In merito, &egrave; necessario procedere celermente ad azioni volte a sensibilizzare l&rsquo;opinione pubblica, le forze sociali e politiche e i media sull&rsquo;esigenza di non bloccare il processo di democratizzazione delle istituzioni militari in coerenza con quanto avviene nei paesi dell&rsquo;Unione europea. A tal fine il direttivo ha costituito un nucleo operativo di tre persone incaricato di prendere contatti con le altre associazioni di cittadini interessate al problema per preparare un protocollo d&rsquo;intesa e stabilire un programma di azione comune.</p>\r\n\r\n<p style=\"text-align:justify\">SMILITARIZZAZIONE DELLA GUARDIA DI FINANZA. Si &egrave; deciso che nella malaugurata ipotesi fosse mantenuto il divieto di associazionismo professionale che impedisce alla Guardia di Finanza di strutturarsi in modo efficiente, moderno e affidabile, Ficiesse intraprender&agrave; le iniziative necessarie affinch&eacute; si giunga alla smilitarizzazione del Corpo per evitare che un patrimonio immenso di professionalit&agrave; sia disperso a causa di una logica organizzativa e di una cultura operativa palesemente inadeguate.</p>\r\n\r\n<p style=\"text-align:justify\">ASSOCIAZIONISMO E POLITICA. Sono pervenute sollecitazioni da un&rsquo;altra associazione affinch&eacute; Ficiesse sostenga ufficialmente il partito il dell&rsquo;onorevole Antonio Di Pietro nelle prossime tornate elettorali. In merito, il direttivo ha riconosciuto unanimemente che l&rsquo;Italia dei Valori sta sostenendo con determinazione e coerenza le idee di innovazione e di democrazia del nostro sodalizio ed ha espresso gratitudine all&rsquo;onorevole Di Pietro che si &egrave; iscritto a Ficiesse. Nonostante ci&ograve;, &egrave; stato deciso che l&rsquo;associazione, come prevede lo statuto, debba mantenere un carattere di apartiticit&agrave; e apoliticit&agrave;, perch&eacute; il suo progetto &egrave; trasversale alle forze politiche e necessita del consenso pi&ugrave; ampio possibile. Ci&ograve; non toglie che gli iscritti a Ficiesse sono liberi di partecipare a titolo personale a ogni agone politico nei partiti in cui si riconoscono, senza impegnare con ci&ograve; il nome dell&rsquo;associazione.</p>\r\n\r\n<p style=\"text-align:justify\">PUBBLICIZZAZIONE DEI POLITICI CHE SOSTENGONO IL PIENO RICONOSCIMENTO DEL DIRITTO DI ASSOCIAZIONE PROFESSIONALE PER I MILITARI. L&rsquo;associazione ha deciso di dare ampia notizia sui suoi organi di informazione dei nomi dei politici che dichiarano di sostenere il riconoscimento del diritto di associazione professionale dei militari. Tale pubblicizzazione riguarder&agrave; anche i candidati alle diverse competizioni elettorali.</p>\r\n\r\n<p style=\"text-align:justify\">CASO PARMALAT. Ficiesse entrer&agrave; nel dibattito sul caso Parmalat per chiedere che la Guardia di finanza impegni risorse significative per la tutela dei risparmiatori e che non limiti le sue azioni, come avviene oggi, alle pur necessarie attivit&agrave; di carattere repressivo.</p>\r\n\r\n<p style=\"text-align:justify\">RIVISTA &ldquo;FINANZIERI E CITTADINI&rdquo;. Nel 2003 &egrave; stato possibile pubblicare un solo numero della rivista che peraltro non &egrave; ancora giunto ai soci e ai sostenitori non iscritti. La segreteria &egrave; stata invitata a porre in essere ogni iniziativa per migliorare la situazione nel 2004.</p>\r\n\r\n<p style=\"text-align:justify\">SITI INTERNET. L&rsquo;apertura del nuovo sito &ldquo;ficiesse.it&rdquo;, ispirato a una maggiore rapidit&agrave; e interattivit&agrave; con i lettori, &egrave; stata accolta favorevolmente come dimostra l&rsquo;elevato numero di contatti. E&rsquo; necessario, per&ograve;, procedere ad alcune correzioni. In primo luogo, c&rsquo;&egrave; esigenza di mantenere distinte le notizie e gli approfondimenti di pi&ugrave; ampio respiro dalle tutte le altre comunicazioni di interesse pi&ugrave; specifico e limitato. In secondo luogo, il forum &egrave; stato invaso negli ultimi giorni dallo spamming e si sono verificati diversi casi di &ldquo;usurpazioni&rdquo; di nickname. Si &egrave; dato mandato all&rsquo;esecutivo di: 1) valutare la possibilit&agrave; della riapertura del sito &ldquo;ficiesse.org&rdquo; da dedicare in via esclusiva alle notizie di portata pi&ugrave; generale e agli approfondimenti; 2) apportare al forum, se continueranno i negativi fenomeni osservati, modifiche atte ad evitare lo spamming, l&rsquo;appropriazione di nick altrui e, pi&ugrave; in generale, l&#39;uso indebito di uno spazio che deve servire principalmente per esprimere idee e chiedere e fornire informazioni.</p>', 0, '', '', '', 2296, 1, 0, '2026-01-10 00:31:25', '2014-06-29'),
(185, '2004-03-04 00:00:00', 'Notizie Flash', 'Convegno a Genova sul tema “ DIRITTO ALLA SICUREZZA E CULTURA DELLA LEGALITA’”.\r\nSi terrà  con inizio alle ore 15.30 presso il Salone della Provincia di Genova, Piazzale Mazzini  2\r\n\r\n\r\n', '', 'Convegno a Genova sul tema “ DIRITTO ALLA SICUREZZA E CULTURA DELLA LEGALITA’”.\r\nSi terrà  con inizio alle ore 15.30 presso il Salone della Provincia di Genova, Piazzale Mazzini  2\r\n\r\n\r\n', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Il Convegno in oggetto che si terrà a Genova venerdì &nbsp;5 marzo alle ore 15.30, presso il Salone della Provincia in Piazzale Mazzini n. 2&nbsp; affronta un tema,“DIRITTO ALLA SICUREZZA E CULTURA DELLA LEGALITA’”,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>di grande attualità&nbsp; sul quale la nostra Associazione deve poter esprimere una sua posizione. Il nostro punto di vista è indicato tra gli scopi dell’Associazione nello stesso Statuto: avvicinare la Guardia di Finanza al cittadino “ sviluppando costruttivi e trasparenti rapporti tra l’ Amministrazione Finanziaria ed i cittadini”.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Riteniamo che “ Diritto alla Sicurezza” debba essere inteso in senso atecnico quale ambito che attiene alla salvaguardia della sicurezza fisica e dei diritti delle persone contro ogni comportamento illecito o imprudente, quindi il suo conseguimento passa anche attraverso la salvaguardia di un corretto rapporto con il Fisco. In sostanza deve essere salvaguardato il “suo personale diritto” ad un fisco equo. Tutti devono pagare le tasse e le imposte in ragione della capacità contributiva perché queste siano distribuite con giustizia. Nostro precipuo compito è quindi, oltre a concorrere, quale Forza di Polizia, alla sicurezza fisica del cittadino in tutti i suoi ambiti, quello di salvaguardarlo dall’evasione, fenomeno che in definitiva si traduce nella necessità di una maggiore contribuzione da parte di chi non evade perché possano essere comunque garantiti i servizi fondamentali alla comunità.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>In questa ottica molto può essere fatto nel settore della prevenzione, settore nel quale molte sinergie possono essere sviluppate con gli Enti Locali e con le Polizie che varie Regioni si apprestano ad istituire.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Chi<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>è interessato all’argomento è invitato ad intervenire al Convegno di Genova.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>', 0, '', '', '', 4485, 1, 0, '2026-01-14 11:26:22', '0000-00-00'),
(186, '2004-03-06 00:00:00', 'Documenti', 'SI E\' SVOLTO IL CONVEGNO \"DIRITTO ALLA SICUREZZA e CULTURA DELLA LEGALITA\' \".\r\nIl testo dell\'intervento effettuato dal nostro Segretario Generale Col. Carlo GERMI.', '', 'SI E\' SVOLTO IL CONVEGNO \"DIRITTO ALLA SICUREZZA e CULTURA DELLA LEGALITA\' \".\r\nIl testo dell\'intervento effettuato dal nostro Segretario Generale Col. Carlo GERMI.', 'Ecco in allegato, formato Word, il testo dell\'intervento al Convegno di Genova del Seg. Generale Carlo GERMI. ', 45, '', '', '', 1744, 1, 0, '2026-01-14 09:04:10', '0000-00-00'),
(187, '2004-03-06 00:00:00', 'Ordine del giorno', 'COMUNICAZIONE AI COMPONENTI DEL DIRETTIVO.\r\nRecapiti', '', 'COMUNICAZIONE AI COMPONENTI DEL DIRETTIVO.\r\nRecapiti', 'Chiediamo cortesemente ai componenti del direttivo che evidentemente o non entrano, o non riescono ad entrare nell\'area riservata di comunicare via telefono o per E-mail a <A href=\"mailto:info@ficiesse.it\">info@ficiesse.it</A> l\'attuale recapito telefonico, e-mail ed indirizzo.', 0, '', '', '', 1657, 1, 0, '2026-01-09 10:55:14', '0000-00-00'),
(188, '2004-03-09 00:00:00', 'Dalla rappresentanza militare', 'POSIZIONE DEL COCER rispettoa alla Riforma della Rappresentanza.\r\nDelibera cristalizza posizione critica.', '', 'POSIZIONE DEL COCER rispettoa alla Riforma della Rappresentanza.\r\nDelibera cristalizza posizione critica.', '<P>In allegato&nbsp; la delibera adottata dal COCER della Guardia di Finanza&nbsp;nella quale viene presa una ferma posizione rispetto ai lavori della Commissione difesa, in particolare il COCER delibera:</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 45.2pt 0pt 54pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 54.0pt\"><SPAN style=\"FONT-FAMILY: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>·</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN></SPAN><FONT face=\"Times New Roman\" size=3>la propria assoluta contrarietà al progetto di riforma della legge 38\'2/78 come va delineandosi in Commissione Difesa in quanto informata ad emendamenti e posizioni preconcette ed involutive delle Amministrazioni Militari;</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 45.2pt 0pt 36pt; TEXT-ALIGN: justify\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 45.2pt 0pt 54pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 54.0pt\"><SPAN style=\"FONT-FAMILY: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>·</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN></SPAN><FONT face=\"Times New Roman\" size=3>di impegnare il Comandante Generale acché siano ripristinate le condizioni di equilibrio, trasparenza e dialogo nella gestione di una riforma di tale rilevanza, coerentemente all\'importanza ed alla funzionalità più volte riconosciute alla Rappresentanza Militare;</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 52.3pt 0pt 0cm; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 45.2pt 0pt 54pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 54.0pt\"><SPAN style=\"FONT-FAMILY: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>·</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN></SPAN><FONT face=\"Times New Roman\" size=3>di impegnare altresì il Comandante Generale acché il parere della Rappresentanza Militare sia acquisito ed adeguatamente considerato, in ogni ulteriore fase propositiva ed emendativa, relativa al progetto in questione, ovvero anche ad altri comunque rilevanti per la Rappresentanza Militare, ai sensi, peraltro, dell\'art. 9 della vigente legge 382/78.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 45.2pt 0pt 54pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 54.0pt\"><FONT face=\"Times New Roman\" size=3></FONT>&nbsp;</P>\r\n<P>Come abbiamo espresso in varie occasioni, sul punto siamo con i nostri rappresentanti e li sosteniamo con forza.</P>\r\n<P>A loro e ai Deputati della Commissione Difesa chiediamo, ancora una volta, di inserire nella riforma il diritto di&nbsp; libera associazione per i militari.</P>\r\n<P>Comunichiamo altresì che analoga delibera è stata adottata anche dal COCER INTERFORZE.</P>', 46, '', '', '', 1288, 1, 0, '2026-01-14 06:16:01', '0000-00-00'),
(189, '2004-03-10 00:00:00', 'Dal Parlamento', 'CASO LAURINO: RUSSO SPENA E GRANDI INSISTONO. PRESENTATA UNA SECONDA E PIU\' DETTAGLIATA INTERROGAZIONE.', '', 'CASO LAURINO: RUSSO SPENA E GRANDI INSISTONO. PRESENTATA UNA SECONDA E PIU\' DETTAGLIATA INTERROGAZIONE.', '<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">Il caso Laurino torna alla ribalta delle cronache parlamentari con un\'altra interrogazione,&nbsp;la seconda,&nbsp;presentata al Ministro dell’economia dagli onorevoli Giovanni RUSSO SPENA (Rifondazione Comunista) e Alfiero GRANDI (Democratici di Sinistra). Stavolta i fatti vengono descritti in modo molto dettagliato e sebbene, per una valutazione conclusiva, sia comunque necessario aspettare la risposta dell\'Amministrazione, non si può non notare la singolarità della lunga serie di provvedimenti negativi (trasferimenti, denuncie, abbassamenti di note caratteristiche) che hanno interessato l’ispettore e che potrebbero configurare una classica ipotesi di mobbing. </SPAN></P>', 47, '', '', '', 3155, 1, 0, '2026-01-10 01:25:36', '0000-00-00'),
(190, '2004-03-12 00:00:00', 'Libere manifestazioni di pensiero', '\"NON E’ AMMUTINAMENTO, SOLIDARIETA’ AI PILOTI RIMPATRIATI DALL’IRAQ\"\r\ndi Pasquale Fico (delegato Cocer Esercito)\r\n', '', '\"NON E’ AMMUTINAMENTO, SOLIDARIETA’ AI PILOTI RIMPATRIATI DALL’IRAQ\"\r\ndi Pasquale Fico (delegato Cocer Esercito)', '<P class=MsoBodyText style=\"MARGIN: auto 0cm\"><SPAN style=\"FONT-SIZE: 12pt\"><FONT face=\"Times New Roman\">Quattro militari con molti anni di servizio sulle spalle non si spaventano se vengono mandati in missione, per quanto pericolosa possa essere. Quattro professionisti, che conoscono e amano il loro lavoro, che sanno valutarne e accettarne i rischi perché si sono addestrati e formati per questo, non si ammutinano. Fanno sempre il proprio lavoro sapendo di poterlo portare a termine soltanto se regge un difficile equilibrio che è fatto di professionalità, lavoro di squadra e affidabilità dei mezzi .<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto\"><FONT size=3><FONT face=\"Times New Roman\">Se una sola di queste componenti viene a mancare non è solo le loro vite che mettono in pericolo, ma anche quelle degli uomini che dipendono da loro e il successo stesso di una missione.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto\"><FONT size=3><FONT face=\"Times New Roman\">Accusare dei soldati che doverosamente sollevano il problema della congruità dei mezzi a loro disposizione per portare a termine il compito che è stato loro assegnato, è ingiusto e pericoloso. Ingiusto perché farlo non è ammutinamento ma espressione della loro professionalità. Pericoloso perché è un messaggio che induce a tacere. In attesa di qualche disastro, di qualche altro lutto. In attesa di una nuova tragedia annunciata.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\">Chiederò al CO.CE.R. INTERFORZE di affrontare urgentemente la questione, che riguarda inevitabilmente tutti noi e non solo i quattro colleghi oggi sotto inchiesta.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\">Al Capitano, al Tenente e ai due Marescialli esprimo tutta la mia solidarietà.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 247.8pt; TEXT-INDENT: 35.4pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Novara, 6 marzo 2004</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Pasquale Fico</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Delegato Cocer Esercito</FONT></P>', 0, '', '', '', 4936, 1, 0, '2026-01-13 23:59:07', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(191, '2004-03-12 00:00:00', 'Libere manifestazioni di pensiero', '\"SOLIDARIETA\' AI PILOTI RIMPATRIATI\" di Lorenzo Lorusso (presidente Movimento Finanzieri Democratici)', '', '\"SOLIDARIETA\' AI PILOTI RIMPATRIATI\" di Lorenzo Lorusso (presidente Movimento Finanzieri Democratici)', '<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=3>Una vasta e consolidata giurisprudenza in materia ha stabilito che “gli ordini illegittimi non vanno eseguiti”.</FONT></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=3>Ho dei validi<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>motivi, pertanto, di ritenere che nella fattispecie riguardante gli elicotteristi rifiutatisi di partire alla volta di Nassirya ci si possa tranquillamente appellare a questo principio, al fine di fare crollare ogni accusa nei loro confronti. </FONT></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=3>A questi principi di carattere generale vanno aggiunte le logiche argomentazioni afferenti all’assoluta insicurezza dei veicoli e alla quasi certezza che questi vengano – in caso di attacco - facilmente abbattuti. </FONT></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=3>Inoltre, gli stessi elicotteristi hanno reiteratamente affermato di non essere in possesso di una adeguata preparazione tecnico professionale per far fronte ad una azione di guerra. </FONT></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=3>Molti ragazzi, infatti, si arruolano nell’Esercito non per andare in guerra ma perché, a torto o a ragione, in un contesto di forte disoccupazione, hanno ritenuto di doversi arruolare per ottenere un lavoro sicuro. </FONT></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=3>Ritengo che tutto ciò vada a ridimensionare notevolmente i contenuti della denuncia fatta dai comandi al tribunale militare. Mi sembra ovvio, d’altronde, che per i comandi militari queste argomentazioni potrebbero rappresentare un pericoloso precedente nel caso altri militari volessero emulare i quattro coraggiosi elicotteristi, pertanto sarà necessaria una grande mobilitazione delle forze politiche democratiche a favore di tutti coloro i quali hanno esercitato, o vorranno esercitare, il loro diritto alla salvaguardia della propria e dell’altrui incolumità. </FONT></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=3>Del resto l’ambiguità di queste missioni (impropriamente spacciate per missioni di pace) fa sì che qualora ci si volesse genericamente rifiutare di parteciparvi – per ragioni ideologiche o di coscienza – ci si ritroverebbe a dovere affrontare anche dei fastidiosi, logoranti, processi innanzi ai tribunali militari. </FONT></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=3>Per queste ragioni esprimo a nome di tutta l’associazione da me rappresentata l’incondizionata solidarietà ai quattro elicotteristi, che hanno sollevato il caso politico-militare.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Lorenzo Lorusso</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>presidente nazionale del Movimento dei Finanzieri Democratici</FONT></P>', 0, '', '', '', 1744, 1, 0, '2026-01-11 13:03:41', '0000-00-00'),
(192, '2004-03-12 00:00:00', 'Dai media', 'AVVENIMENTI: \"IN CORSA PER I DIRITTI. CIRCA SETTANTA TRA CARABINIERI, FINANZIERI E POLIZIOTTI NELLA LISTA DI PIETRO-OCCHETTO\" di Sebastiano Gulisano', '', 'AVVENIMENTI: \"IN CORSA PER I DIRITTI. CIRCA SETTANTA TRA CARABINIERI, FINANZIERI E POLIZIOTTI NELLA LISTA DI PIETRO-OCCHETTO\" di Sebastiano Gulisano', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p><FONT face=Times size=3>&nbsp;</FONT></o:p></SPAN><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT size=3><FONT face=Times>«Siamo l’unico paese occidentale che vieta i sindacati ai militari». </FONT></FONT></SPAN><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT size=3><FONT face=Times>Il maresciallo Pallotta: «Meglio diventare rappresentante che rappresentato».<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><o:p><FONT face=Times size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT size=3><FONT face=Times>di Sebastiano Gulisano<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><o:p><FONT face=Times size=3>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT face=Times size=3>Il ghiaccio l’ha rotto <I style=\"mso-bidi-font-style: normal\">Il Giornale dei Carabinieri</I>, il mensile fondato dal maresciallo Ernesto Pallotta, 43 anni, siciliano in servizio a Roma, che da anni si batte per la democratizzazione delle forze armate e di sicurezza. </FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT face=Times size=3>È stato proprio Pallotta, attraverso il mensile che dirige, prima a parlare di una generica «rappresentanza politica dei militari», poi ad aderire alle aperture della lista Di Pietro-Occhetto. </FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT face=Times size=3>Dopo è stata la volta di Progetto democrazia in divisa, un’associazione di finanzieri radicata principalmente nel Nordest. Mentre altre associazioni, sebbene non aderendo direttamente, lasciano liberi i propri iscritti di partecipare alla competizione elettorale e, come nel caso di Ficiesse (Finanzieri, cittadini e solidarietà), annunciano sul proprio sito internet (</FONT><A href=\"http://www.ficiesse.it/\"><SPAN style=\"COLOR: black; TEXT-DECORATION: none; text-underline: none\"><FONT face=Times size=3>www.ficiesse.it</FONT></SPAN></A><FONT face=Times size=3>) che segnaleranno sul sito e sul periodico <I style=\"mso-bidi-font-style: normal\">Finanzieri e cittadini</I> «i nomi dei politici che dichiarano di sostenere il riconoscimento del diritto di associazione professionale dei militari». </FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT face=Times size=3>Analogo atteggiamento è probabile che assuma anche Assodipro (Solidarietà, diritti, progresso), l’associazione del mondo militare con più aderenti, migliaia di «lavoratori con le stellette» concentrati tra esercito e aeronautica. E la cosa potrebbe assumere proporzioni persino clamorose, stando a quel che scrive Pallotta sul portale del <I style=\"mso-bidi-font-style: normal\">Giornale dei Carabinieri</I> (</FONT><A href=\"http://www.nsd.it)/\"><SPAN style=\"COLOR: black; TEXT-DECORATION: none; text-underline: none\"><FONT face=Times size=3>www.nsd.it):</FONT></SPAN></A><FONT size=3><FONT face=Times> «Cobar, coir e cocer militari attendono la fine dei lavori della commissione Difesa della Camera sull’iter parlamentare della riforma della rappresentanza militare per prendere una decisione». Cobar coir e cocer sono i tre organismi (locale, interregionale e nazionale) di rappresentanza di esercito, carabinieri, aeronautica e guardia di finanza. </FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT size=3><FONT face=Times>La legge di riforma della rappresentanza in discussione alla Camera «configura i nuovo organismi come mere “appendici” dei comandi - chiarisce il colonnello Giuseppe Fortuna, presidente di Ficiesse -, mentre sono stati bocciati tutti gli emendamenti volti a concedere ai militari il diritto di costituire e aderire ad associazioni professionali». <o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT size=3><FONT face=Times>«È una vita che chiediamo alle forze politiche più diritti per chi non ha voce» sottolinea Vincenzo Cretella, 47 anni, veneziano, fino a due anni fa brigadiere della guardia di finanza e un mandato da rappresentante cocer: «Poi mi sono dimesso, me lo hanno consigliato i medici, avevo subito un centinaio di procedimenti disciplinari e penali. Tra i militari - chiarisce Cretella - il mobbing si manifesta così, ed è un fenomeno di massa». Cretella è uno dei leader di Progetto democrazia in divisa, nonché tra i fondatori del Movimento finanzieri democratici che, però, ha deciso di non schierarsi con l’ex pm di Mani pulite. </FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT size=3><FONT face=Times>«Il nostro è un Movimento trasversale - chiarisce il colonnello Vincenzo Cerceo, triestino, iscritto a Rifondazione comunista - e tra noi ci stanno anche dei leghisti. Io non credo che il progetto di Di Pietro avrà grande successo, ma daremo indicazione di votare i nostri iscritti».<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT size=3><FONT face=Times>Antonio Di Pietro è raggiante e snocciola le cifre: «Sono 74 i militari - tra carabinieri, poliziotti e finanzieri - che hanno aderito al nostro progetto di aprire le liste per meglio difendere i loro hanno dei diritti che, invece, gli vengono negati. Sono persone che hanno dato nobiltà a un ruolo e che rischiano la vita per tutelare la collettività, invece vengono bistrattati, come se dovessero vergognarsi del proprio lavoro: è chi commette reati, non chi li scopre, che deve vergognarsi», sottolinea il leader dell’Italia dei valori.<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT size=3><FONT face=Times>«Abbiamo scelto Di Pietro perché è un moderato - spiega Ernesto Pallotta - perché così stiamo rompendo gli equilibri e togliendo voti alla destra. Persino il Movimento sportivo Fiamma sta cambiando nome - rivela il maresciallo - per entrare a far parte di questo progetto». Pallotta è un uomo di sinistra e non lo nasconde, così come non nasconde che «sono stato invitato da Diliberto al congresso dei Comunisti italiani, ci sono andato, e ho ripetuto le cose che dico da sempre. Ma se vuole coinvolge massicciamente i militari non si può scegliere un partito della sinistra radicale», sostiene. Le sue battaglie per la democratizzazione dell’Arma, nel ’93, sfociarono nella costituzione di Unarma, associazione di carabinieri a carattere sindacale che aveva circa 3.500 iscritti, ma che fu bocciata dal consiglio di Stato e dalla Corte costituzionale. L’ultimo ricorso lo ha fatto alla Corte di Strasburgo, che non si è ancora pronunciata.<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT size=3><FONT face=Times>«Siamo l’unico paese occidentale a non avere sindacati di lavoratori con le stellette - precisa il colonnello Cerceo -, ma la nuova Costituzione europea prevede che chi lavora ha diritto a iscriversi ai sindacati. Anche se, lo so, prima che la approvano…». Cretella, invece, sottolinea un’altra «unicità»: «Siamo l’unico paese europeo ad avere una polizia tributaria militare». La smilitarizzazione della guardia di finanza è da sempre tra gli obiettivi del Movimento finanzieri democratici. C’è chi mastica ancora amaro ripensando alle battaglie degli anni 70, quelle che portarono alla legge sulla rappresentanza del 1978 e alla smilitarizzazione della polizia. I finanzieri avevano le stesse aspettative dei poliziotti, ma quelle speranze furono frustrate: «Il Pci frenò - ricorda un maresciallo ormai in pensione - perché temeva di vanificare anche la smilitarizzazione della polizia. Pecchioli (che nel Pci si occupava dei problemi militari, <I style=\"mso-bidi-font-style: normal\">ndr</I>) ci chiese di pazientare, dopo sarebbe venuto il nostro turno. Invece…».<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT size=3><FONT face=Times>La richiesta di smilitarizzazione è anche la strada che si appresta ad imboccare Ficiesse: «Nella malaugurata ipotesi fosse mantenuto il divieto di associazionismo professionale - ha deciso il direttivo -, intraprenderemo le iniziative necessarie affinché si giunga alla smilitarizzazione del Corpo». Nella scorsa legislatura il centrodestra ha presentato un disegno di legge in tal senso - primo firmatario l’attuale ministro della Difesa, Antonio Martino. «Martino ha fatto marcia indietro», nota Cerceo. «Ha avuto un’involuzione - gli fa eco Cretella -, lui che era un liberal ora ha diramato una circolare in cui vieta alle associazioni di fare proselitismo e propone che se un militare vuole aderire a un’associazione, anche di civili, deve chiedere l’autorizzazione al ministro: non era mai accaduto, dopo la legge 382 del 1978». È questo clima che spinge Pallotta a preferire la possibilità di «essere rappresentante e non rappresentato», malgrado Cerceo ricordi il tentativo del colonnello Pappalardo, ex presidente del cocer carabinieri e deputato del Psdi, «che non ebbe grande successo». Pallotta, invece, ritiene che «la tutela dei diritti sia così importante da dovere tentare in ogni modo: attraverso le vie giurisdizionali, referendarie ed elettorali». Candidati, dunque. Alle europee e alle amministrative: «Per noi - chiarisce il maresciallo dell’Arma - è un test in vista delle politiche».<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT size=3><FONT face=Times>Resta la grande contraddizione, almeno in apparenza, di una lista che candida pacifisti acclarati (Giulietto Chiesa, ad esempio) e militari. «È una scommessa, una sfida - chiarisce Di Pietro -, chi vuole la pace può stringere la mano al carabiniere che vuole la pace. Il vero poliziotto non è quello che manganella, ma è vicino a chi sfila per i diritti». Nemmeno Pallotta ci trova contraddizione: «È un modo per aprirci alla società civile, per abbattere le barriere che ci separano, in un momento in cui rischiamo di diventare un paese militarizzato, come gli Usa: dopo la scelta del governo di intervenire in Iraq, siamo sottoposti al codice militare di guerra». Già, lo prevede la legge 6 del 2002. Non era successo nemmeno durante la guerra contro Milosevic. E dire che questa, a differenza di quella, è «una missione di pace».<o:p></o:p></FONT></FONT></SPAN></P>', 0, '', '', '', 1823, 1, 0, '2026-01-10 04:59:24', '0000-00-00'),
(288, '2004-08-10 00:00:00', 'Notizie Flash', 'CARABINIERI:LIBERATI, COCER NON AGISCE PER INTERESSE DI PARTE\r\nPRESIDENTE ORGANISMO RAPPRESENTANZA INTERVIENE SU POLEMICHE', '', 'CARABINIERI:LIBERATI, COCER NON AGISCE PER INTERESSE DI PARTE\r\nPRESIDENTE ORGANISMO RAPPRESENTANZA INTERVIENE SU POLEMICHE\r\n', '<PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 7 AGO - Il gen. Serafino Liberati, presidente<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">del <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Cocer</SPAN></STRONG> dell\' Arma, non era a conoscenza - perche\' all\' estero<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">e non contattabile telefonicamente - del comunicato con cui l\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">organismo di rappresentanza dei carabinieri ha stigmatizzato nei<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">giorni scorsi le modalita\' dell\' avvicendamento del gen. Elio<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Toscano al gen. Giorgio Piccirillo nell\' incarico di capo di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Stato Maggiore del Comando generale: \'\'Ma il <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Cocer</SPAN></STRONG> - afferma<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">oggi Liberati - ha agito nella sua piena competenza. Per questo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sono perfettamente d\' accordo con le sue finalita\', che non sono<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">certo quelle di difendere interessi di parte, ma di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">salvaguardare tutto il personale\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Quello che invece Liberati respinge \'\'fermamente\'\' e\' \'\'l\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">insinuazione avanzata sulla stampa da non meglio precisato<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">\'ambienti della Difesa\', che la presa di posizione del <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Cocer</SPAN></STRONG><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">carabinieri - afferma - sarebbe stata motivata da un presunto<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">mio interesse personale a mantenere il comando delle Unita\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">mobili e specializzate (cui e\' stato destinato il gen.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Piccirillo - ndr) assunto in sostituzione del gen. Vittorio<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Savino collocato in congedo\'\'. A sua tutela, su questo punto, il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">gen. Liberato non esclude di ricorre alle vie legali. (SEGUE).<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></PRE><PRE><B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">CARABINIERI:LIBERATI,</SPAN></B><STRONG><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">COCER</SPAN></STRONG><B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"> NON AGISCE PER INTERESSE DI PARTE(2)<o:p></o:p></SPAN></B></PRE><PRE><B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></B></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 7 AGO - Liberati ribadisce di \'\'non essere<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">stato assolutamente a conoscenza\'\' della nota del <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Cocer</SPAN></STRONG>, che<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">tuttavia \'\'ha agito nella sua piena competenza, avendo ottenuto<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">l\' adesione al comunicato della prescritta maggioranza dei suoi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">delegati\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Il <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Cocer</SPAN></STRONG> - afferma il suo presidente - non mirava a<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">difendere alcun interesse di parte, ma si e\' attivato, e per<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">questo<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>sono pienamente d\' accordo, unicamente perche\' teso alla<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">salvaguardia dei diritti di ogni appartenente all\' Arma, sia<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">esso generale o carabinieri ausiliario. Il motivo della presa di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">posizione del <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Cocer</SPAN></STRONG> non<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>risiede nella scelta operata dal<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">comandante generale, che nessuno puo\' sindacare, ma nel modo e<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">nei tempi in cui questa e\' stata resa esecutiva, che hanno<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">suscitato perplessita\' e malessere in tutta l\' Arma\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il presidente dell\' organismo di rappresentanza dei<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">carabinieri \'\'riafferma e rivendica quindi l\' assoluta onesta\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">mentale e morale del <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Cocer</SPAN></STRONG>, che nessuno puo\' mettere in dubbio o<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">intaccare\'\', ribadendone \'\'l\' indipendenza politica\'\'. \'\'La<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">funzione del <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Cocer</SPAN></STRONG> - afferma Liberati - e\' quella di tutelare<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">gli interessi di tutti i suoi rappresentati, qualunque grado<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">essi rivestano, con totale imparzialita\': se il provvedimento<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">avesse riguardato il comandante generale e l\' ultimo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">carabiniere, il <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Cocer</SPAN></STRONG> sarebbe intervenuto con la stessa<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">fermezza, in virtu\' di quei valori etici a protezione del<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">personale che sono alla base dell\' esistenza stessa della<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">rappresentanza\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il gen. Liberati torna quindi a replicare alla \'\'voce anonima<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">della \'Difesa\', che non poteva ignorare - sottolinea - l\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">impossibilita\' da parte mia a detenere in via definitiva il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">comando delle Unita\' mobili e specializzate perche\' non in<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">possesso del grado previsto e perche\' avevo assunto l\' incarico<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">temporaneamente, cosi\' come indicato nel messaggio del comando<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">generale. Invito, pero\', chiunque sia, a rappresentarmi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">personalmente questa pesante accusa - conclude il presidente del<o:p></o:p></SPAN></PRE><PRE><STRONG><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Cocer</SPAN></STRONG><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"> - in modo da poter agire a mia tutela nelle sedi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">opportune\'\'. (ANSA).<o:p></o:p></SPAN></PRE>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>', 0, '', '', '', 1454, 1, 0, '2026-01-14 09:15:18', '0000-00-00'),
(289, '2004-08-10 00:00:00', 'Notizie Flash', 'PUBBLICA AMMINISTRAZIONE: TAGLI A RISORSE, MA RIVOLUZIONE INFORMATICA VA AVANTI\r\nSTANCA, PASSI AVANTI NEL 2003, MA ANCORA TROPPE RESISTENZE\r\n', '', 'PUBBLICA AMMINISTRAZIONE: TAGLI A RISORSE, MA RIVOLUZIONE INFORMATICA VA AVANTI\r\nSTANCA, PASSI AVANTI NEL 2003, MA ANCORA TROPPE RESISTENZE\r\n', '<PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 8 AGO - La \'\'rivoluzione silenziosa\'\', quella<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">di una pubblica amministrazione sempre piu\' informatizzata e<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">moderna, \'\'fa passi avanti\'\'. Nonostante la riduzione delle<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">risorse (-8,9% nel 2003) e le troppe resistenze ancora presenti<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">nell\'apparato burocratico. Questo il quadro che emerge dalla<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Relazione 2003 sullo stato di informatizzazione<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dell\'amministrazione pubblica, che il ministro per<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">l\'Innovazione, Lucio Stanca, ha presentato in questi giorni al<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Parlamento.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Tra i risultati migliori, il boom nell\'uso della posta<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">elettronica per le comunicazioni interne a ministeri ed enti.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Ancora forti ritardi, invece, sul fronte dell\'alfabetizzazione<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dei dipendenti pubblici. Per questo, sottolinea il presidente<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">del Cnipa (Centro nazionale per l\'informativa nella Pa), Livio<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Zoffoli, \'\'bisogna guadare avanti ed impostare un programma<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">strutturale di rilancio. Il piano triennale appena varato -<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">aggiunge - va in questa direzione\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></PRE><PRE><B><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>- MENO RISORSE, CALA LA SPESA</SPAN></B><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">. E\' il capitolo piu\' dolente<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">della relazione. \'\'I risultati ottenuti lo scorso anno - spiega<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Stanca - sono ancora piu\' apprezzabili se si tiene conto della<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">riduzione delle risorse finanziarie disponibili nel 2003, dovuta<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">alla situazione dei conti pubblici che ha imposto il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">contenimento delle risorse messe a disposizione dei ministeri\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Fatto sta che nel 2003 la spesa per l\'acquisizione di beni e<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">servizi per l\'informatica si e\' ridotta di 175,4 milioni di euro<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">(-8,9% rispetto al 2002), sopratutto quella destinata agli<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">investimenti e alle attivita\' di sviluppo (-155 milioni nelle<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">amministrazioni centrali e -8,8 milioni di euro negli enti).<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>In particolare, l\'impegno di spesa delle amministrazioni<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">centrali e\' stato nel 2003 di 1.354,5 milioni di euro, l\'8% in<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">meno rispetto all\'anno precedente: un confronto negativo dovuto<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">soprattutto ai notevoli acquisti effettuati nel 2002 dal\'Arma<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dei Carabinieri e dalla <STRONG><SPAN style=\"COLOR: red\">Guardia</SPAN></STRONG> di <STRONG><SPAN style=\"COLOR: red\">Finanza</SPAN></STRONG> e non compensati nel<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">2003 dalla spesa del ministero dell\'Interno. Gli enti pubblici<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">non economici, invece, hanno speso nel 2003 445,3 milioni di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">euro, ben il 12% in meno rispetto al 2002; diminuzione su cui ha<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">inciso soprattutto il commissariamento e la mancata espansione<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dei principali enti previdenziali.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>- BOOM FIRME DIGITALI ED E-MAIL. Quattro obiettivi sui dieci<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">di legislatura sono stati ormai raggiunti. In particolare e\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">stato superato il milione di firme digitali e raggiunto il 40%<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">di posta interna tramite e-mail. Su quest\'ultimo fronte - si<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">legge nella relazione - nel 2003 si registrano 144 milioni di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">messaggi, con una crescita del 76% rispetto al 2002. E i<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dipendenti informatizzabili con una casella di posta elettronica<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sono passati dal 48% del 2002 al 61% del 2003. Raggiunto anche<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">un altro obiettivo: quello dei 20 milioni di impegni e di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">mandati elettronici di pagamento (olre 24 milioni nel 2003).<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>- ANCORA TROPPI \'ANALFABETI\'. Moltissimo da fare c\'e\' invece<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sul fonte dell\'alfabetizzazione dei dipendenti pubblici. Il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">numero di quelli che possiedono la cosidetta \'patente<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">informatica europea\' \'\'e\' molto limitato\'\': circa 6.300, pari<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">all\'1,2% dei dipendenti informatizzabili. Attualmente il 91% di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">questi utilizza gia\' una postazione di lavoro computerizzata, e<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">oltre 50.000 hanno gia\' effettuato negli ultimi anni corsi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">analoghi a quelli necessari per conseguire la patente. Ancora<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">scarso anche l\'utilizzo della formazione tramite e-learning e<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">l\'accesso on line all\'iter delle pratiche.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>- TROPPE RESISTENZE. Del resto - si legge nella relazione -<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">lo sforzo per un\'amministrazione pubblica piu\' efficiente e<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">moderna si scontra ancora \'\'con una cultura poco incline ai<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">cambiamenti e alle novita\' , che tende a frenare tutte quelle<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">iniziative che abbiano forti implicazioni organizzative\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Queste resistenze, spiega Stanca, \'\'sono causate<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">fondamentalmente da un ritardo culturale, ma talvolta anche da<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">posizioni di difesa de proprio ruolo, del proprio potere a<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">livello brocratico-amministrativo\'\'.(ANSA).<o:p></o:p></SPAN></PRE>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>', 0, '', '', '', 1181, 1, 0, '2025-12-20 18:24:15', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(196, '2004-03-19 00:00:00', 'Dai media', 'DAL SITO DELL’ASSOCIAZIONE AMID (WWW.AMID.ORG):\r\n\"L\'IDEOLOGIA DELLA GUERRA PERMANENTE\"\r\nUn articolo del professor Claudio De Fiores sul ddl per la revisione della legge penale militare di guerra\r\n', '', 'DAL SITO DELL’ASSOCIAZIONE AMID (WWW.AMID.ORG):\r\n\"L\'IDEOLOGIA DELLA GUERRA PERMANENTE\"\r\nUn articolo del professor Claudio De Fiores sul ddl per la revisione della legge penale militare di guerra\r\n', '<P style=\"MARGIN-BOTTOM: 6pt; TEXT-ALIGN: center\" align=center><SPAN class=titolo><SPAN style=\"FONT-SIZE: 13.5pt\"><FONT color=#000000><FONT face=\"Times New Roman\">DAL SITO DELL’ASSOCIAZIONE AMID (<A href=\"http://www.AMID.ORG\">WWW.AMID.ORG</A>)<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></SPAN></SPAN></P>\r\n<P style=\"MARGIN-BOTTOM: 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\"><FONT size=3><SPAN style=\"mso-bidi-font-weight: bold\">Pubblichiamo il seguente articolo dal sito dell\'Associazione per i Militari Democratici - Amid.</SPAN></FONT></FONT></P>\r\n<P style=\"MARGIN-BOTTOM: 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\"><FONT size=3><SPAN style=\"mso-bidi-font-weight: bold\">\"L\'IDEOLOGIA DELLA GUERRA PERMANENTE. A PRETENDERLO È LA COSTRUZIONE DEL NUOVO ORDINE MONDIALE<B>.\"</B></SPAN><SPAN style=\"FONT-SIZE: 13.5pt\"><o:p></o:p></SPAN></FONT></FONT></P>\r\n<P style=\"MARGIN-BOTTOM: 6pt\"><FONT size=3><SPAN class=testonewshome1><SPAN style=\"FONT-FAMILY: \'Times New Roman\'; mso-ansi-font-size: 12.0pt; mso-bidi-font-size: 12.0pt\">Il Governo ha presentato in Parlamento un inquietante d.d.l. con il quale vorrebbe annullare le limitazioni imposte alla giurisdizione militare dal terzo comma dell\'art. 103 della Costituzione previste in tempo di pace. Lo scopo è dilatare la sfera giurisdizionale di competenza dei Tribunali militari.</SPAN></SPAN><SPAN class=titolo><o:p></o:p></SPAN></FONT></P>\r\n<P style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN class=titolo><SPAN style=\"FONT-SIZE: 13.5pt\"><FONT color=#000000><FONT face=\"Times New Roman\">GUERRA COME PACE: <o:p></o:p></FONT></FONT></SPAN></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN class=titolo><SPAN style=\"FONT-SIZE: 13.5pt\"><FONT color=#000000><FONT face=\"Times New Roman\">NEL NUOVO CODICE MILITARE CADE LA DIFFERENZA<o:p></o:p></FONT></FONT></SPAN></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN class=sommario><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">Una nuova legge delega Il governo vuole «adeguare» l\'ordinamento giudiziario militare. <o:p></o:p></FONT></FONT></I></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN class=sommario><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">Per legittimare lo stato di guerra continua. Una definizione ambigua La nuova norma parla di «tempo di guerra», una condizione che non sarà più legata a una dichiarazione formale<o:p></o:p></FONT></FONT></I></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\">di <SPAN class=firma>CLAUDIO DE FIORES*</SPAN></FONT></FONT></P>\r\n<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN class=testo><FONT size=3><FONT face=\"Times New Roman\">Un disegno di legge si aggira in queste settimane nelle stanze del parlamento italiano. A proporlo è il governo e nella sua intestazione reca «Delega per la revisione delle leggi penali militari di pace e di guerra, nonché per l\'adeguamento dell\'ordinamento giudiziario militare». Ma nelle sue disposizioni c\'è qualcosa di più. Qualcosa che non esiterei a definire inquietante. Mi riferisco alla temeraria operazione di «assedio» dei principi costituzionali da esso perseguita. E in modo particolare all\'abusivo utilizzo che è stato fatto delle disposizioni contenute all\'art. 103 della Costituzione, maldestramente utilizzate dal disegno di legge per dilatare la sfera giurisdizionale di competenza dei Tribunali militari. A questo proposito va, preliminarmente, precisato che l\'articolo 103, terzo comma, della Costituzione pone limiti alla giurisdizione militare, ma solo per il tempo di pace, mentre attribuisce alla piena disponibilità della legge ordinaria la determinazione della giurisdizione per il tempo di guerra. Il progetto di legge delega, nel tentativo di limitare drasticamente l\'ambito di azione della giurisdizione ordinaria, si incunea all\'interno di tale delicato discrimine. Ma, nel far ciò è \"costretto\" a sottoporre la nozione costituzionale «tempo di guerra» ad una torsione interpretativa talmente profonda da assorbire finanche la nozione di «tempo di pace». La dicotomia costituzionale «tempo di pace-tempo di guerra» viene così progressivamente distillata dal disegno di legge delega, fino alla sua pressoché integrale dissoluzione.<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN class=testo><FONT size=3><FONT face=\"Times New Roman\">Al suo posto verrebbe, di converso, profilandosi una zona grigia dai contorni normativi flebili e indistinti. È quanto si desume, altresì, dall\'opzione normativa di <I>sganciare </I>definitivamente le disposizioni contenute nel codice militare dai vincoli costituzionali posti dagli artt. 78 e 87 Cost., al fine di rendere <I>finalmente</I> possibile «l\'applicazione della legge penale militare di guerra, anche indipendentemente dalla dichiarazione dello stato di guerra» (lett. <I>l)</I> dell\'art. 4 del Disegno di legge). Un istituto, quest\'ultimo, che dopo esser stato ripetutamente circuito in questi anni (dalla guerra del Golfo all\'Iraq) si vuole ora espungere, in tutte le sue implicazioni, anche dalle leggi militari di guerra.<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN class=testo><FONT size=3><FONT face=\"Times New Roman\">È questa la posta in gioco della partita che oggi si è aperta sul terreno costituzionale. E a nulla valgono i contorsionismi interpretativi contenuti nella relazione introduttiva intenti a distinguere la nozione di «tempo di guerra» da quella di «stato di guerra». Le due definizioni sono strettamente connesse. Direi, anzi, complementari visto che il <I>tempo di guerra</I> non può che decorrere, sul piano giuridico, dalla dichiarazione dello <I>stato di guerra</I>. La decomposizione, sul terreno giuridico, di questa discriminante <I>temporale </I>non può che risolversi in un insidioso e progressivo assorbimento dello stato di pace nello stato di guerra: a ribadirlo è l\'ideologia delle guerra permanente. A pretenderlo è la costruzione del nuovo ordine mondiale. A ricordarcelo, nel nostro disastrato paese, adesso è anche la relazione a questo disegno di legge, che ci esorta a comprendere, nell\'epoca della globalizzazione, che la dimensione bellica è parte integrante del tempo normale della nostra vita, così come del «tempo normale di vita dell\'ordinamento giuridico». Il profilarsi di una minaccia bellica non prefigurerebbe più, in altre parole, secondo gli ispiratori della riforma, uno stato di emergenza, visto che nel nuovo ordine mondiale la guerra è oramai «in grado di coesistere con una normale situazione ordinamentale». L\'obiettivo perseguito dalla destra con la revisione delle leggi militari è, a questo punto, evidente: <I>normare </I>l\'emergenza bellica per <I>normalizzare </I>la guerra.<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN class=testo><FONT size=3><FONT face=\"Times New Roman\">A fronte di un disegno politico così insidioso bisogna allora tentare di invertire la rotta e puntare a recuperare un\'altra idea di <I>codice militare, </I>incardinato sui principi costituzionali,<I> </I>sulla centralità del parlamento e che, soprattutto, sia in grado di fare i conti con quel ripudio della guerra che è parte integrante della Costituzione repubblicana e oggi anche della coscienza politica di tanta parte dell\'opinione pubblica (italiana e internazionale). La difesa della legalità democratica e della Costituzione non può prescindere da tale impegno.<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN class=testo><I><FONT face=\"Times New Roman\" size=3>*Professore associato di Diritto costituzionale, Università Napoli 2</FONT></I></SPAN></P>', 0, '', '', '', 4557, 1, 0, '2026-01-14 10:26:09', '0000-00-00'),
(197, '2004-03-19 00:00:00', 'Dai media', 'AVVENIMENTI N. 11, 18 MARZO 2004: \"STATO DI PACE, TEMPO DI GUERRA\"\r\ndi Sebastiano Gulisano', '', 'AVVENIMENTI N. 11, 18 MARZO 2004: \"STATO DI PACE, TEMPO DI GUERRA\"\r\ndi Sebastiano Gulisano', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\"><FONT size=3>AVVENIMENTI, N. 11, 18 MARZO 2004<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\"><FONT size=3>“STATO DI PACE, TEMPO DI GUERRA”<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\"><FONT size=3>E i Tribunali militari potranno processare anche i civili.<o:p></o:p></FONT></SPAN></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\"><FONT size=3>Parlamentari giuristi e militari parlano del testo del governo che vuole svuotare la Costituzione<o:p></o:p></FONT></SPAN></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\"><FONT size=3>di Sebastiano Gulisano<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">«Le chiamano missioni di pace, ma sono missioni di guerra», sostiene Alberto Tuzzi, segretario di Assodipro, l’associazione di militari che raccoglie diverse migliaia di iscritti tra esercito e aeronautica. «È stata l’amministrazione - sostiene Tuzzi, riferendosi agli stati maggiori - a chiedere al ministro della Difesa l’applicazione del codice penale militare di guerra nei confronti dei nostri contingenti in Afghanistan e Iraq». Ed è proprio il codice penale militare di guerra che verrebbe applicato ai quattro elicotteristi rimpatriati dall’Iraq e indagati per ammutinamento, nell’ipotesi in cui dovessero essere processati. Se lo stesso reato lo commettessero dei loro colleghi in missione in Somalia o in Albania o nell’ex Jugoslavia, invece, sarebbero processati in base al codice penale militare di pace. La differenza? «Quello di guerra comporta pene più severe», chiarisce Massimo Villone, capogruppo Ds in commissione Affari costituzionali del Senato. Proprio Villone è intervenuto a Palazzo Madama, durante la conversione in legge del decreto che proroga le missioni militari italiane all’estero, per porre la pregiudiziale di costituzionalità sul provvedimento che, appunto, «diversifica le missioni applicando due codici diversi e, così facendo, viola l’articolo 11 della Costituzione».<o:p></o:p></SPAN></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">Il ritorno del codice penale militare di guerra è figlio dell’11 settembre, era rimasto in naftalina dalla fine del secondo conflitto mondiale. Lo ha rispolverato, con qualche modifica, il governo Berlusconi per la missione <I style=\"mso-bidi-font-style: normal\">Enduring freedom</I>, con un decreto del dicembre 2001. «Il governo, attraverso il continuo ricorso ai decreti legge, ha spogliato il parlamento delle proprie prerogative», accusa Tuzzi. Il mese successivo, comunque, il testo è stato convertito in legge, la numero 6 del 2002. E non si applica solo «ai corpi di spedizione all’estero per operazioni militari armate», ma «anche al personale militare di comando e controllo e di supporto che resta nel territorio nazionale o che si trova nel territorio di altri paesi» (articolo 2).<o:p></o:p></SPAN></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">Per avere detto queste cose alla stampa, il maresciallo capo Ernesto Pallotta, fondatore del <I style=\"mso-bidi-font-style: normal\">Giornale dei carabinieri</I> e membro del Cobar Lazio, si è beccato quattro giorni di consegna di rigore. «Il vero effetto di quella legge - dicono al <I style=\"mso-bidi-font-style: normal\">Giornale dei carabinieri</I> - è stato quello di ammonire i militari contro una spinta innovatrice che porta il nome di democratizzazione delle Forze armate».<o:p></o:p></SPAN></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'\">E non è certo finita qui.<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\"><FONT size=3>Le commissioni Difesa e Giustizia del Senato hanno cominciato l’esame del disegno di legge 2493, <I style=\"mso-bidi-font-style: normal\">Delega al governo per la revisione delle leggi penali e militari di pace e di guerra, nonché per l’adeguamento dell’ordinamento giudiziario militare</I>, che porta la firma dei ministri di Difesa e Giustizia, Antonio Martino e Roberto Castelli. Villone non si sbilancia sul ddl («Non ho avuto modo di studiarlo a fondo»), ma sottolinea che invece di diversificare l’utilizzo dei due codici penali militari per le missioni all’estero, «sarebbe il caso di pensare a un nuovo corpo di regole per le missioni “a rischio”».<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\"><FONT size=3>Per Claudio De Fiores, invece, quello del governo è un testo «inquietante». De Fiores è professore associato di diritto costituzionale all’Università Napoli 2. In un intervento pubblicato sul sito internet dell’Amid, Associazione per i militari democratici (</FONT><A href=\"http://www.amid.it)/\"><SPAN style=\"COLOR: windowtext; TEXT-DECORATION: none; text-underline: none\"><FONT size=3>www.amid.it)</FONT></SPAN></A><FONT size=3>, descrive il ddl come una «temeraria operazione di “assedio” dei principi costituzionali» che dilata «la sfera giurisdizionale di competenza dei Tribunali militari», forzando il dettato dell’articolo 103 della Costituzione, che, precisa De Fiores, «pone limiti alla giurisdizione militare, ma solo in tempo di pace, mentre attribuisce alla piena disponibilità della legge ordinaria la determinazione della giurisdizione per il tempo di guerra». Il testo governativo, invece, tenta di limitare il campo d’azione del giudice ordinario e, per fare ciò, «è costretto a sottoporre la nozione costituzionale “tempo di guerra” ad una torsione interpretativa talmente profonda da assorbire finanche la nozione di “tempo di pace”». L’obiettivo? «Rendere finalmente possibile “l’applicazione della legge penale militare di guerra, anche indipendentemente dallo stato di guerra” (lettera l dell’articolo 4 del disegno di legge)». Insomma: «Normare l’emergenza bellica per normalizzare la guerra», nella logica «dell’ideologia della guerra permanente».<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\"><FONT size=3>È d’accordo Elettra Deiana, deputata di Rifondazione comunista della commissione Difesa della Camera: «Si incrementano le competenze dei Tribunali militari, che attualmente hanno un’attività ridotta, e si militarizzano i reati comuni dei militari sottraendoli alla giurisdizione ordinaria. Si vuole istituire una zona franca? Si vuole corporatizzare?». Secondo il magistrato Domenico Gallo, esponente del Coordinamento nazionale dei Giuristi democratici (</FONT><A href=\"http://www.giuristidemocratici.it)/\"><SPAN style=\"COLOR: windowtext; TEXT-DECORATION: none; text-underline: none\"><FONT size=3>www.giuristidemocratici.it)</FONT></SPAN></A><FONT size=3>, «il governo ha l’esigenza di salvare e rilanciare una giurisdizione militare in crisi, che con l’abolizione della leva lo sarà sempre più perché va in crisi la sua ragione d’essere. Si tenta di rilanciare i Tribunali militari proprio mentre altrove vengono aboliti, come in Francia, in Germania, in Belgio e, mi pare, in Spagna e Portogallo». La «militarizzazione dei reati» comporta l’ampliamento delle competenze dei Tribunali militari ai reati comuni commessi dai cittadini con le stellette, anche fuori delle caserme. Vale a dire che, se durante un controllo in un’azienda un finanziere chiede una mazzetta a un imprenditore, a giudicarlo non è più il giudice ordinario ma quello militare. In Italia i giudici militari sono solo 103 ripartiti in 9 giurisdizioni giudiziarie. Dunque, chiarisce Gallo, «non vuol dire più rigore, ma più impunità, poiché nei casi di processi complicati e lunghi, a causa delle incompatibilità previste dalla legislazione, portare a conclusione un procedimento sarebbe assai difficile».<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\"><FONT size=3>Elettra Deiana mette l’accento anche su un altro aspetto, non certo secondario, della legge delega: «Si decostituzionalizza la materia militare e si abbassa sostanzialmente la differenza fra pace e guerra fino a confonderle. La Costituzione e il diritto bellico moderno fissano meccanismi chiari e consentono di individuare chiaramente i percorsi per arrivare allo stato di guerra, nel testo del governo questi principi svaporano, è il governo a decidere». Come? «La legge delega prevede l’instaurazione del tempo di guerra con decreto del governo», chiarisce Gallo. Mentre la parlamentare del Prc, infine, sottolinea la «mutazione semantica che sta subendo il concetto di guerra, all’interno di una logica di mutazione genetica di tutti i meccanismi di difesa e di politica interna». Un esempio di questa mutazione è certamente la cosiddetta «guerra al terrorismo». Un altro esempio concreto lo fa Domenico Gallo, il quale rileva che l’articolo 4 del disegno di legge, tra l’altro, «introduce la legge militare di guerra senza ostacoli procedurali, per le missioni all’estero e sul territorio nazionale o in parte di esso. Vale a dire che i Tribunali militari non giudicano solo i militari ma anche i civili. Un esempio? I fatti del G8 di Genova, con una legge così, avrebbero potuto essere di competenza del Tribunale militare». <o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><SPAN style=\"FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\"><o:p><FONT size=3>&nbsp;</FONT></o:p></SPAN></P>', 0, '', '', '', 1427, 1, 0, '2026-01-14 11:22:39', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(290, '2004-08-10 00:00:00', 'Notizie Flash', 'POLIZIA:PROTESTA FERRAGOSTO,NO A PRIVILEGI FORZE ARMATE\r\nIN MIGLIAIA A PALAZZO CHIGI, \'GOVERNO CI PRENDE IN GIRO\'\r\nA MANIFESTAZIONE ANCHE MOGLI COCER CC E GDF –', '', 'POLIZIA:PROTESTA FERRAGOSTO,NO A PRIVILEGI FORZE ARMATE\r\nIN MIGLIAIA A PALAZZO CHIGI, \'GOVERNO CI PRENDE IN GIRO\'\r\nA MANIFESTAZIONE ANCHE MOGLI COCER CC E GDF –', '<PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 9 AGO - Domenica prossima, mentre il ministro<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dell\' Interno, Giuseppe Pisanu, sara\' impegnato nella<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">tradizionale visita di Ferragosto alle forze dell\' ordine, i<o:p></o:p></SPAN></PRE><PRE><STRONG><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">poliziotti</SPAN></STRONG><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"> aderenti ai sindacati Siulp, Sap, Sapaf, Sappe e Fsp<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">si sono dati appuntamento al centro di Roma, dove sfileranno fin<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sotto Palazzo Chigi per protestare contro il Governo. Sotto<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">accusa la politica contrattuale \'\'strabica\'\' dell\' esecutivo,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">che \'\'ha dimostrato un forte sbilanciamento a favore delle forze<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">armate e in danno delle forze di polizia\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>A ROMA IN MIGLIAIA - \'\'Saremo in migliaia, ci sono arrivate<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">tantissime richieste di adesione, stiamo organizzando pullman<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">speciali\'\', hanno annunciato oggi i rappresentanti sindacali in<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">una conferenza stampa. \'\'Abbiamo scelto il 15 agosto - spiega il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">segretario del Siulp, Oronzo Cosi - perche\' e\' la giornata in<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">cui tradizionalmente il ministro Pisanu ringrazia le forze dell\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ordine per il loro lavoro di tutto l\' anno. Ebbene - sottolinea<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">- noi non accettiamo i ringraziamenti, vogliamo che siamo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">mantenuti gli impegni\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></PRE><PRE><B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'GOVERNO CI HA PRESO IN GIRO\'</SPAN></B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"> - Nelle trattative per il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">contratto di lavoro, ricorda Cosi, \'\'il Governo ha proposto a<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">tutti aumenti minimi di stipendio, appellandosi alla difficile<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">situazione economica del Paese. Poi, dopo avere ottenuto a<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">cavallo delle ultime elezioni la firma di una preintesa, ha<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dimostrato di avere ulteriori risorse, approvando un<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">provvedimento sul riallineamento delle carriere dei marescialli<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">delle forze armate, che comportera\' promozioni e vantaggi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">economici solo per i 55.000 marescialli di esercito, marina ed<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">aeronautica: ci siamo sentiti quindi traditi e presi in giro<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">rispetto agli impegni assunti\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></PRE><PRE><B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>STOP A STRAPOTERE COMMISSIONI DIFESA </SPAN></B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">- Filippo Saltamartini<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">(Sap) critica \'\'le commissioni Difesa di Camera e Senato,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">composte da militari ed ex militari, che tendono a svolgere un<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ruolo simile a quello dei sindacati nelle contrattazioni<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">collettive. Diventano cosi\' il piu\' grande sindacato degli<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">uomini in divisa e sviluppano un\' attivita\' contrapposta a<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">quella dei sindacati di polizia. Nei Paesi democratici i<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">lavoratori sono rappresentati dai sindacati, non dai generali o<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dai Cocer\'\'. Succede cosi\', prosegue Saltamartini, \'\'che si<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">assegnano 900 milioni di euro alle missioni internazionali di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">pace e i <STRONG>poliziotti</STRONG> sono costretti ad elemosinare i fondi per la<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sicurezza interna, per le automobili, la benzina, le gomme. Di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">fronte ad una minaccia concreta del terrorismo internazionale,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">la risposta finanziaria e\' assolutamente inadeguata\'\'. I<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sindacati auspicano quindi la costituzione di una commissione<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Interni alla Camera ed al Senato, \'\'per bilanciare lo strapotere<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">della lobby dei generali delle commissioni Difesa\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></PRE><PRE><B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>A MANIFESTAZIONE ANCHE MOGLI COCER CC E GDF </SPAN></B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">– <o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">\'\'Non si tratta - rileva poi Marco Moroni segretario del Sapaf - di una<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">guerra tra il comparto sicurezza e quello Difesa. Vogliamo solo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">avere lo stesso trattamento degli altri\'\'. La dimostrazione e\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">che \'\'alla manifestazione di Ferragosto ci saranno anche le<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">mogli dei rappresentanti dei Cocer dei carabinieri e della<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">guardia di finanza, che appoggiano le nostre rivendicazioni\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>NO AD ANNO MILITARE OBBLIGATORIO - E nel \'cahier de<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">doleances\' delle forze di polizia c\' e\' anche quanto stabilito<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">con la sospensione della leva e cioe\' l\' obbligo per chi vuole<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">entrare in polizia di prestare un anno di servizio militare.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">\'\'In pratica - osservano i sindacati - si dice agli italiani: se<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">volete entrare in polizia, dove fare il soldato per un anno. E\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">una cosa assurda ed anticostituzionale, il segnale di una<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">militarizzazione crescente della nostra societa\'\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></PRE><PRE><B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>PEZZOTTA, SOLIDARIETA\' A FORZE POLIZIA </SPAN></B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">- Il segretario della<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Cisl, Savino Pezzotta, esprime la \'\'massima solidarieta\' alle<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">donne ed agli uomini delle forze di polizia, chiamati in questo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">difficile momento a garantire la nostra sicurezza contro le<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">aggressioni del terrorismo e della criminalita\'\'\'. \'\'Forte e\' la<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">preoccupazione - afferma il segretario della Cisl - per il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">rischio di uno squilibrio istituzionale causato da un\' azione<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sbilanciata del Governo nel mondo dei lavoratori in divisa: e\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">necessario recuperare al piu\' presto imparzialita\', equidistanza<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">e serenita\'\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"> <B><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;</SPAN>ASCIERTO (AN), PROTESTA PRETESTUOSA </B>- Il responsabile del<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dipartimento sicurezza di Alleanza nazionale, Filippo Ascierto,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">definisce \'\'pretestuosa\'\' la protesta di Ferragosto e rassicura:<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">\'\'in Finanziaria ci saranno i soldi per il riordino delle<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">carriere delle forze di polizia\'\'. Il Siulp replica parlando di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">\'\'ulteriore, gratuita provocazione\'\'. Intanto, anche l\' Uilps<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">critica la mobilitazione. \'\'Manifestare per un diritto - spiega<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">il segretario Michelangelo Astarita - e\' una cosa sacrosanta, ma<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">manifestare dopo aver firmato un\' intesa con il Governo sul<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">rinnovo contrattuale e\' una cosa francamente poco<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">comprensibile\'\'. (ANSA).<o:p></o:p></SPAN></PRE>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P><PRE><B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">POLIZIA: SINDACATI, IN MIGLIAIA A FERRAGOSTO CONTRO GOVERNO - \'LOBBY DEI GENERALI IN PARLAMENTO FAVORISCE SOLO MILITARI\'<o:p></o:p></SPAN></B></PRE><PRE><B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></B></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 9 AGO - Migliaia di <STRONG>poliziotti</STRONG> aderenti a<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Siulp, Sap, Sappe, Sapaf e Fsp, sfileranno sotto Palazzo Chigi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">il giorno di Ferragosto per protestare contro il Governo.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Abbiamo scelto il 15 agosto - spiega il segretario del<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Siulp, Oronzo Cosi - perche\' e\' la giornata in cui<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">tradizionalmente il ministro dell\' Interno, Giuseppe Pisanu,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ringrazia le forze dell\' ordine per il loro lavoro di tutto l\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">anno. Ebbene - sottolinea - noi non accettiamo i ringraziamenti,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">vogliamo che siamo mantenuti gli impegni\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Nel mirino dei sindacati, che rappresentano circa 100.000<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">uomini in divisa delle forze civili, in particolare il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">provvedimento sul riallineamento delle carriere dei marescialli<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">delle forze armate che, spiegano, \'\'comportera\' promozioni, e<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">benefici economici a vantaggio dei soli marescialli di esercito,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">aeronautica e marina\'\'. Sotto accusa la \'\'lobby dei generali\'\',<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">che nelle commissioni Difesa di Camera e Senato \'\'riesce a<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">favorire i militari a scapito delle forze civili\'\'. (SEGUE).<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></PRE><PRE><B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">POLIZIA: SINDACATI, IN MIGLIAIA A FERRAGOSTO CONTRO GOVERNO </SPAN></B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">(2)<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 9 AGO - \'\'Prima delle elezioni europee -<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ricorda Cosi - e\' stata raggiunta una preintesa contrattuale cui<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sarebbe dovuto seguire un decreto legge che avrebbe dovuto<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">accogliere le rivendicazioni di forze armate e polizia. E\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">invece accaduto che il Consiglio dei ministri ha consentito al<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ministro della Difesa, Antonio Martino, di presentare un<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">emendamento a favore dei 55.000 marescialli delle forze armate.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Ci siamo sentiti quindi traditi e presi in giro rispetto agli<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">impegni assunti. Noi non abbiamo voluto ricattare il Governo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">come invece hanno fatto le forze armate e questo e\' stato il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ringraziamento\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Filippo Saltamartini (Sap) critica \'\'le commissioni Difesa di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Camera e Senato, composte da militari ed ex militari, che<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">tendono a svolgere un ruolo simile a quello dei sindacati nelle<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">contrattazioni collettive. Diventano cosi\' il piu\' grande<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sindacato e degli uomini in divisa e sviluppano un\' attivita\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">contrapposta a quella dei sindacati di polizia Nei Paesi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">democratici i lavoratori sono rappresentati dai sindacati, non<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dai generali o dai Cocer\'\'. Succede cosi\', prosegue<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Saltamartini, \'\'che si assegnano 900 milioni di euro alle<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">missioni internazionali di pace e i <STRONG>poliziotti</STRONG> sono costretti ad<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">elemosinare i fondi per la sicurezza interna, per le automobili,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">la benzina, le gomme. Di fronte ad una minaccia concreta del<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">terrorismo internazionale, la risposta finanziaria e\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">assolutamente inadeguata\'\'. Alla manifestazione di Ferragosto,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">comunque, aggiunge, \'\'ci saranno anche le mogli dei<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">rappresentanti dei Cocer dei carabinieri e della guardia di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">finanza, che appoggiano le nostre rivendicazioni\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Non si tratta - rileva poi Marco Moroni segretario del<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Sapaf - di una guerra tra il comparto sicurezza e quello Difesa.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Vogliamo solo avere lo stesso trattamento degli altri\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>I sindacati contestano anche quanto stabilito con la<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sospensione della leva e cioe\' l\' obbligo per chi vuole entrare<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">in polizia, di prestare un anno di servizio militare. \'\'In<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">pratica - osservano - si dice agli italiani: se volete entrare<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">in polizia, dove fare il soldato per un anno. E\' una cosa<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">assurda ed anticostituzionale, il segnale di una<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">militarizzazione crescente della nostra societa\'\'\'. (ANSA).<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></PRE><PRE><B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">POLIZIA: <STRONG>GIARDULLO</STRONG></SPAN></B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"> (<B>SILP), BENE ALLARGAMENTO FRONTE PROTESTA<o:p></o:p></B></SPAN></PRE><PRE><B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></B></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 6 AGO - \'\'Registriamo con grande soddisfazione<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">che i sindacati di polizia che hanno firmato a giugno la<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">preintesa contrattuale proposta dal Governo, oggi decidano di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">modificare le loro posizioni e di rafforzare il fronte della<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">protesta contro le scelte dell\' Esecutivo in materia di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sicurezza\'\'. Lo afferma Claudio <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Giardullo</SPAN></STRONG>, segretario del<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Silp-Cgil, commentando la mobilitazione decisa da diversi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sindacati di polizia per il giorno di Ferragosto.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Noi - sottolinea <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Giardullo</SPAN></STRONG> - non abbiamo siglato quella<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">preintesa perche\' costituisce la peggiore ipotesi di contratto<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">degli ultimi dieci anni e perche\' gli impegni sulle carriere<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">erano, e si sono dimostrati alla prova dei fatti, inaffidabili.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Al Governo, in sede di esame del Dpef, abbiamo gia\' notificato<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">la nostra mobilitazione, se la bozza di legge Finanziaria non<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">segnera\' una netta inversione di tendenza rispetto all\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">insensata politica di riduzione delle risorse per la sicurezza,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">e la miriade di impegni disattesi dal Governo nel confronti<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">degli operatori di polizia\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Siamo pronti - conclude - a proseguire una battaglia<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">importante per la sicurezza del paese e la tutela dei lavoratori<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">di polizia. Una battaglia che non dovra\', ovviamente, esaurirsi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">in una mattina di mezza estate\'\'. (ANSA).<o:p></o:p></SPAN></PRE>', 0, '', '', '', 4976, 1, 0, '2025-12-28 15:35:12', '0000-00-00'),
(201, '2004-03-24 00:00:00', 'Dai media', 'INTERVISTA DI QUADERNI RADICALI A GIUSEPPE FORTUNA E ANTONIO ROBERTI SUL CASO CIRIO E PARMALAT E SUL RUOLO DELLA GUARDIA DI FINANZA', '', 'INTERVISTA DI QUADERNI RADICALI A GIUSEPPE FORTUNA E ANTONIO ROBERTI SUL CASO CIRIO E PARMALAT E SUL RUOLO DELLA GUARDIA DI FINANZA', '<P>Riportiamo di seguito il testo dell\'intervista pubblicato oggi sul sito di Quaderni Radicali e Nuova Agenzia Radicale alla pagina internet <FONT face=\"Times New Roman\" size=3><A href=\"http://www.quaderniradicali.it/agenzia/index.php?op=read&amp;nid=706\">http://www.quaderniradicali.it/agenzia/index.php?op=read&amp;nid=706</A></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3></FONT>&nbsp;</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>QUADERNI RADICALI - NUOVA AGENZIA RADICALE</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3></FONT>&nbsp;</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>News del 24-03-2004</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3></FONT>&nbsp;</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>\"CASI CIRIO E PARMALAT. INTERVISTA ALL’ASSOCIAZIONE FICIESSE (FINANZIERI CITTADINI E SOLIDARIETÀ)\"</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>di Maurizio Navarra</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>I recenti accadimenti intorno ai casi che hanno visto coinvolti consistenti ed importanti settori della nostra industria, sembra abbiano posto sotto accusa tutto il nostro apparato di vigilanza economico–finanziaria. Posto che in Italia è presente ed è operativo il Corpo della Guardia di Finanza, prestigioso e collaudato organo di Polizia Tributaria Investigativa, è necessario un approfondimento di informazione anche per poter comprendere e far comprendere quali siano state le “maglie larghe della rete” attraverso le quali possano essere stati sottratti all’accertamento dello Stato fatti macroscopici come i crak Cirio e Parmalat, di valenza destabilizzante per il nostro Sistema Paese. <BR>L’intervista è stata rilasciata dall’ “Associazione Finanzieri Cittadini e Solidarietà – Ficiesse” a «Quaderni Radicali» da Giuseppe Fortuna ed Antonio Roberti, rispettivamente Presidente e Segretario Nazionale del Direttivo Ficiesse. <BR><BR><BR><B>Che cosa fa, nella realtà, la Guardia di Finanza? </B><BR><BR>R. Fa quasi esclusivamente repressione. Basta leggere i rapporti annuali. Vi sono esposti soltanto moduli operativi di tipo repressivo: verifiche, controlli, indagini. Come repressivi sono i risultati: tributi evasi, beni sequestrati, persone denunciate. La prevenzione non c’è, anche se è prevista dalla legge d’ordinamento del 1959 come il primo compito della GdiF, che è chiamata, appunto, a “prevenire ricercare e denunciare le evasioni e le violazioni finanziarie”. <BR><BR><B>Ma in Italia il sommerso bisogna pur combatterlo.</B> <BR><BR>R. Non c’è dubbio. E bisogna farlo con grande determinazione. Ma dobbiamo limitarci a rincorrere i buoi usciti dai recinti o pensare anche a quelli che stanno dentro cercando di farceli rimanere? Noi siamo convinti che la strategia giusta deve saper calibrare gli strumenti della repressione con quelli della prevenzione. Invece, si chiede ai comandi sempre e soltanto di colpire. Un finanziere fa carriera se porta a casa più verbali dell’anno precedente, non se si adopera per far crescere il gettito. Guardiamo il caso degli evasori totali scoperti: il loro numero cresce ininterrottamente da otto anni. Vuol dire che i nostri amici finanzieri sono stati bravi, ma vuol dire anche che non gli si fa fare nulla per prevenire il fenomeno. <BR><BR></FONT><FONT size=3><FONT face=\"Times New Roman\"><B>In questo quadro, quali erano le possibilità di intervento sul caso Parmalat? <BR></B><BR>R. Con le attuali competenze del Corpo non c’era alcuna possibilità di intervento. Lo ha detto chiaramente il comandante generale Speciale nell’audizione del 10 febbraio scorso davanti alle commissioni parlamentari. I controlli della polizia tributaria sono finalizzati a scovare l’evasione fiscale e non a scoprire truffe ai danni dei risparmiatori. Si tratta effettivamente di atti fraudolenti che – come ha detto Speciale – “esulano dai normali sentieri investigativi”. <BR><BR></FONT></FONT><FONT size=3><FONT face=\"Times New Roman\"><B>E dov’è allora che non siete d’accordo? <BR></B><BR>R. Il punto non è su quello che ha fatto o non ha fatto il Corpo, ma su quello che dovrebbe fare nell’immediato futuro. Il punto è la disponibilità della Guardia di Finanza ad assumere un ruolo da protagonista a tutela dei mercati finanziari e dei risparmiatori. E qui la risposta dei vertici militari è stata negativa e deludente. <BR><BR></FONT></FONT><FONT size=3><FONT face=\"Times New Roman\"><B>È grave? <BR></B><BR>R. I treni quando passano si devono prendere, specialmente se sono uno stimolo di crescita per l’organizzazione. Negli anni Settanta, quando partì la riforma tributaria, successe qualcosa di simile. Fu chiesto alla Guardia di finanza di fare le verifiche esterne alle imprese quando fino ad allora, in quel settore, i finanzieri erano stati impiegati soltanto per redigere verbalini di una paginetta per l’ige, l’imposta generale sulle entrate. Si trattava di mandare fuori da un giorno all’altro sottufficiali e finanzieri, molti dei quali con la scuola dell’obbligo, a controllare che fossero stati pagati tributi nuovi e complessi che nessuno conosceva bene come l’imposta sul valore aggiunto. Anche allora i generali cercarono di resistere, ma la svolta fu ordinata ugualmente e il risultato è stato che la Guardia di Finanza è cresciuta enormemente. <BR><BR></FONT></FONT><FONT size=3><FONT face=\"Times New Roman\"><B>Anche in quest’ultimo caso c’è stata sfiducia nella possibilità del Corpo da parte dei vertici? <BR></B><BR>R. Probabilmente un diverso tipo di sfiducia. Oggi la Finanza le professionalità di elevato profilo le sa creare, ma non riesce a trattenerle. Il primo problema dei vertici è quello che gli anglosassoni chiamano “retention”, cioè la capacità di mantenere i migliori al proprio interno e di non lasciarseli catturare sul mercato del lavoro dalla concorrenza esterna. E ogni anno è una vera e propria emorragia. Figuriamoci se si acquisissero ulteriori competenze nei confronti dei primari gruppi economici del paese. <BR><BR></FONT></FONT><FONT size=3><FONT face=\"Times New Roman\"><B>E quali sono gli altri problemi? <BR></B><BR>R. Essenzialmente la paura della corruzione, il terrore che possano ripetersi gli scandali che purtroppo flagellano periodicamente l’istituzione distruggendone la credibilità e l’immagine. Nessuno nel Corpo sa davvero cosa fare. <BR><BR></FONT></FONT><FONT size=3><FONT face=\"Times New Roman\"><B>Ma la Guardia di Finanza ce l’ha davvero la capacità tecnico-professionale per compiere interventi presso realtà aziendali di dimensione internazionale? <BR></B><BR>R. In questo momento, la Guardia di Finanza è l’istituzione che meglio di ogni altra è in grado di scoprire se un bilancio sia basato o no su documenti falsi perché coniuga la professionalità della polizia giudiziaria con quella della polizia tributaria. I migliori verificatori del Corpo riconoscono a vista i documenti falsi e non si spaventano davanti a masse anche imponenti di documenti contabili. In più, la Finanza ha un’estensione territoriale che gli consente controlli incrociati praticamente in tempo reale in tutto il territorio nazionale, sa attivare gli organi collaterali fiscali e di polizia degli altri paesi e possiede un grosso apparato di intelligence con canali rodati sia in Italia che all’estero. <BR><BR></FONT></FONT><FONT size=3><FONT face=\"Times New Roman\"><B>Si può far qualcosa per rimediare? <BR></B><BR>R. Purtroppo, temiamo che il treno sia passato e la Guardia di Finanza non ci è salita sopra. Certo, si darà supporto alle authority e si continuerà a lavorare alle dipendenze della magistratura. Ma conquistare spazi di iniziativa in un settore così importante per l’economia come quello della tutela dei risparmiatori sarebbe stato un altro salto di qualità epocale per l’istituzione. <BR><BR></FONT></FONT><FONT size=3><FONT face=\"Times New Roman\"><B>Dà maggiori garanzie al nostro Sistema/Paese nel settore fiscale un Corpo militarmente organizzato oppure sarebbe migliore garanzia un’organizzazione civile? <BR></B><BR>R. Come ogni organizzazione, anche quella militare ha punti di forza e punti di debolezza. Il punto di forza è essenzialmente uno, ma molto importante: la capacità di reagire con immediatezza alle sollecitazioni del potere politico. Se ad esempio il ministro dell’Economia decide oggi di avviare una campagna per il controllo dei prezzi, vedremo dopo pochissimi giorni finanzieri in divisa nei mercati e nei negozi di tutta Italia. I punti di debolezza sono rappresentati dalla rigidità della struttura e delle carriere, dalla eccessiva e dannosa frammentazione del personale in troppe inutili categorie e dalla inadeguatezza culturale a dominare a costi accettabili scenari operativi troppo complessi, come certamente sono quelli propri di una polizia economica e finanziaria. <BR><BR><B>Spiegatevi meglio.</B> <BR><BR>R. Il militare nasce per prepararsi alla guerra, ha bisogno di un nemico da sopraffare fisicamente e vive in una dimensione di sfida continua. Se non gli si fa vedere un nemico si siede, si svuota, vivacchia ed il rischio di comportamenti opportunistici o devianti è più elevato che nelle organizzazioni civili. Inoltre, quando si enfatizza troppo il valore dell’obbedienza, valore indispensabile se non ci vuol sfaldare davanti al nemico, si paga inevitabilmente un prezzo in termini di dialettica e di confronto interno e quindi anche di capacità di modernizzazione e di innovazione. Alla lunga, i professionisti migliori se ne vanno e l’organizzazione entra inevitabilmente in crisi. <BR style=\"mso-special-character: line-break\"><BR style=\"mso-special-character: line-break\"></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT size=3><FONT face=\"Times New Roman\"><B>Questo vale anche per i carabinieri? <BR></B><BR>R. I carabinieri sono agevolati perché operano in uno scenario meno ampio e più stabile di quello della Guardia di finanza. Basti pensare al precedente codice di procedura penale, che è rimasto in vigore per quasi sessant’anni, dal 1930 al 1988. <BR><BR><BR>L’intervista ha termine ed offre un quadro complessivo non incoraggiante. La Guardia di Finanza è quindi un’organizzazione altamente qualificata e specializzata, ben disposta a livello territoriale, dalle possibilità operative non comuni. Un’organizzazione che ha però l’attitudine a perdere i suoi quadri più preparati, ed è relegata a svolgere una funzione di mero controllo. Una funzione certamente necessaria, nessuno può mettere in dubbio il concetto, ma che non può ritenersi esclusiva a meno che si passi il principio (erroneo) che una repressione forte e capillare abbia di per sé sola il potere di agire da fattore di prevenzione. <BR>L’Arma dei Carabinieri opera in un settore diverso ed ha problematiche diverse. Se ci si limita a vedere l’Arma soltanto come Polizia Giudiziaria, i compiti appaiono più semplici e lineari. Una riflessione di superficie, che non tiene conto di tutte le specializzazioni che vedono i Carabinieri protagonisti in disparati settori organizzativi, di prevenzione e di controllo. Ma l’Arma è di fatto protagonista di una rivoluzione copernicana dal momento in cui è divenuta la quarta Forza Armata dello Stato. La prima Forza Armata dello Stato a poter contare su forze professionali, addestrate e specializzate in settori di impiego operativo non facili. <BR>Modernizzare le due strutture militari è una sfida reale del nostro Paese. Dotare queste strutture di un sistema democratico interno, capace di poter realmente fornire una linea di confronto autentico alla linea gerarchica, potrebbe essere il nodo gordiano da sciogliere per vincere questa sfida.<BR><BR>Maurizio Navarra</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT size=3><FONT face=\"Times New Roman\"></FONT></FONT>&nbsp;</P>', 0, '', '', '', 2814, 1, 0, '2026-01-11 12:36:08', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(204, '2004-04-02 00:00:00', 'Servizio di Ficiesse', 'RIUNITI A ROMA GLI “STATI GENERALI” DELLA RAPPRESENTANZA.Il documento finale in cui si parla di smilitarizzazione. Il comunicato stampa. Le prime impressioni.', '', 'RIUNITI A ROMA GLI “STATI GENERALI” DELLA RAPPRESENTANZA.\r\nIl documento finale in cui si parla di smilitarizzazione. Il comunicato stampa. Le prime impressioni.\r\n\r\n\r\n', '<p style=\"text-align:justify\">A Roma, nella sede di Villa Spada, il 30 ed il 31&nbsp; marzo, si sono tenuti, gli &ldquo;stati generali&rdquo; della Rappresentanza della Guardia di Finanza.</p>\r\n\r\n<p style=\"text-align:justify\">I delegati del COCER, quelli di tutti i COIR ed un delegato per categoria di ogni COBAR d&rsquo;Italia ( circa 130 delegati ), si sono dati appuntamento per fare il punto sulle tante problematiche che interessano il personale del Corpo e, soprattutto, per cercare di approvare una linea condivisa che potesse costituire un punto di riferimento per le azioni da intraprendere sino alla conclusione del mandato.</p>\r\n\r\n<p style=\"text-align:justify\">Dopo il benvenuto del Comandante Generale, che ha sottolineato la sua vicinanza al personale tutto e in particolare agli Organismi di Rappresentanza,&nbsp; sono iniziati i lavori con gli interventi di numerosi delegati, interventi ai quali daremo ampio spazio non appena ci perverranno.</p>\r\n\r\n<p style=\"text-align:justify\">Erano presenti in qualit&agrave; di graditi ospiti anche rappresentanti del COCER Carabinieri, nonch&eacute; numerosi sindacalisti delle forze di polizia ad ordinamento civile.</p>\r\n\r\n<p style=\"text-align:justify\">Le due giornate si sono concluse con un documento unitario, molto importante e significativo che alleghiamo , sul quale saremo lieti si potesse aprire&nbsp; una ampia discussione sul FORUM.</p>\r\n\r\n<p style=\"text-align:justify\">Al termine dei lavori &egrave; stato siglato il comunicato stampa allegato i cui contenuti non sembrano riflettere completamente il documento conclusivo.</p>\r\n\r\n<p style=\"text-align:justify\">In questa prima comunicazione a noi piace sottolineare come i delegati, rappresentanti di tutto il personale del Corpo, siano giunti a quella stessa conclusione che l&rsquo; Associazione, nel suo ultimo direttivo di Bologna, aveva indicato: se non saranno raggiunti i 7 obiettivi del documento l&#39; &quot;assemblea ritiene che la strada allora da percorrere sia quella della smilitarizzazione e sindacalizzazione.</p>\r\n\r\n<p style=\"text-align:justify\"><!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /-->&nbsp;Infine un&nbsp; saluto ed un augurio di pronta guarigione a Dario DALMUT, delegato del Cobar Liguria e del COIR di Milano,&nbsp; che nella serata del 31 in un malaugurato incidente ha subito la frattura del femore ed &egrave; stato ricoverato in ospedale.</p>\r\n\r\n<p style=\"text-align:justify\">Nell&rsquo;occasione&nbsp; un vivo ringraziamento al Presidente del COCER, Gen. POLETTI, ai delegati del COCER, alla Segreteria e a chi nel Comando generale ha acconsentito a che Dario&nbsp; fosse accompagnato ad Imperia con una Ambulanza del Corpo.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>', 49, '', '', '', 5709, 1, 0, '2026-01-06 07:08:28', '2014-06-29'),
(274, '2004-07-30 00:00:00', 'Libere manifestazioni di pensiero', 'INTERVENTI NEL FORUM DI FICIESSE DI MAURIZIO DORI, PRESIDENTE DELLA CATEGORIA ISPETTORI DEL COCER-GDF, IN TEMA DI RIALLINEAMENTO DELLE CARRIERE, UNIVERSITA’ E PROLUNGAMENTO DEL MANDATO PER GLI ATTUALI COCER', '', 'INTERVENTI NEL FORUM DI FICIESSE DI MAURIZIO DORI, PRESIDENTE DELLA CATEGORIA ISPETTORI DEL COCER-GDF, IN TEMA DI RIALLINEAMENTO DELLE CARRIERE, UNIVERSITA’ E PROLUNGAMENTO DEL MANDATO PER GLI ATTUALI COCER', '<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"></SPAN>&nbsp;</P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">PUBBLICHIAMO GLI INTERVENTI NEL FORUM DI MAURIZIO DORI, DELEGATO DEL COCER-GDF, CHE RINGRAZIAMO A NOME DI TUTTI I LETTORI<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P style=\"MARGIN-BOTTOM: 6pt; TEXT-ALIGN: justify\"><B><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"><o:p></o:p></SPAN></B></P>\r\n<P style=\"MARGIN-BOTTOM: 6pt; TEXT-ALIGN: justify\"><B><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\"></SPAN></B>&nbsp;</P>\r\n<P style=\"MARGIN-BOTTOM: 6pt; TEXT-ALIGN: justify\"><B><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">PRIMO INTERVENTO<o:p></o:p></SPAN></B></P>\r\n<P style=\"MARGIN-BOTTOM: 6pt; TEXT-ALIGN: justify\"><B><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">&nbsp;<o:p></o:p></SPAN></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Cari colleghi ed amici porto a conoscenza dei frequentatori di questo forum la posizione assunta dal Co.Ce.R. con delibera nr. 01/94/9° del 26 maggio 2004.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>La sezione, ancora una volta, ribadiva la netta contrarietà al riallineamento che il Parlamento si accingeva a votare. Si faceva presente nella delibera che 5.400 nostri colleghi sarebbero stati \"scavalcati\" attraverso, tra l\'altro, un reinquadramento ope legis al grado apicale.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Nella circostanza, così come si era già verificato in precedenza, si inviava al Ministro della Funzione Pubblica, Avv. Luigi MAZZELLA, una missiva dove si esprimevano le riserve della Sezione per i gravi fatti innanzi richiamati. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Sapete tutti che il riallineamento è oggi legge dello stato e che nulla è valso per fermare l\'iniziativa parlamentare. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Il Comparto Difesa non ha rivali ottiene ciò che vuole e quando vuole. Le missioni internazionali hanno preminenza sulla sicurezza interna. I caduti all\'estero su quelli interni. Avete osservato, sulle nostre televisioni, i funerali dell\'ultimo Caduto in Patria? Tra le autorità i vertici dell\'Arma nessuna autorità di Governo inquadrata dalle telecamere. Al rientro dall\'estero delle ultime salme ad accogliere le spoglie i vertici del Parlamento, del Governo e le più alte autorità militari. Cari colleghi, cari amici penso che sarebbe bello poter transitare nel Comparto Difesa. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\"><U>Università</U>.</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Vedo che l\'argomento è molto sentito ed è oggetto di animata discussione. Mi chiedo che cosa aspetta il Comando Generale del Corpo ad attuare quanto si verifica nel Comparto Difesa. Dovete sapere che già da alcuni anni i sottufficiali dell\'Esercito dopo la frequenza di un corso di tre anni gli viene riconosciuta una laurea di 1° livello. Tale beneficio oggi viene esteso alle altre Armi del Comparto Difesa. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Come vedete la nostra alta professionalità è sbeffeggiata da amici che non devono aggiornarsi professionalmente né tanto meno affrontare i liberi professionisti pronti a massacrarci nelle udienze penali.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Un abbraccio</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Cordialmente Maurizio Dori</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P style=\"MARGIN-BOTTOM: 6pt; TEXT-ALIGN: justify\"><B><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">SECONDO INTERVENTO<o:p></o:p></SPAN></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><U><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></U></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\"><U>Università</U>.</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Il Ministro dell\'Istruzione, del\'Università e della Ricerca, Letizia Moratti, ha avviato formalmente le procedure per la revisione del Regolamento nr. 509 del 1999. Tale regolamento aveva definito la nuova architettura degli ordinamenti didattici universitari, anche a seguito degli impegni europei assunti con la sottoscrizione della Dichiarazione di Bologna.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Il testo delle modifiche al Regolamento è stato trasmesso, per l\'acquisizione dei pareri, al Cun, alla Crui, al Cnsu e al Cnvsu. L’iter prevede successivamente il parere del Consiglio di Stato e delle Commissioni VII di Camera e Senato. Dopo la registrazione della Corte dei Conti il Regolamento sarà pubblicato sulla G.U..</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Il sistema cosiddetto dei 3 + 2 viene sostanzialmente confermato. Vengono però introdotti alcuni correttivi per garantire una maggiore flessibilità e porre rimedio ad alcune disfunzioni rilevate fin dalla prima attuazione della riforma.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>\"Il nostro progetto di revisone del Regolamento\", ha spiegato, il ministro Moratti, \"corrisponde alle esigenze di una formazione universitaria di maggior qualità, che crei nel contempo più opportunità di sblocchi professionali\".</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Il ministro Moratti ha assicurato che le modifiche potranno essere recepite dagli atenei negli ordinamenti didattici con tutta la gradualità necessaria, per evitare disagi sia agli atenei stessi sia agli studenti.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Queste, tra l\'altro, le principali innovazioni previste dalla bozza di revisione del Regolamento 509/1999:</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt 53.25pt; TEXT-INDENT: -35.25pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 53.25pt\"><FONT face=\"Times New Roman\"><FONT size=3>-</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><FONT size=3>la laurea di secondo livello, precedentemente denominata specialistica, viene ridenominata \" laurea magistralis\". Ad essa corrispondono 120 crediti;</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt 53.25pt; TEXT-INDENT: -35.25pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 53.25pt\"><FONT face=\"Times New Roman\"><FONT size=3>-</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><FONT size=3>vengono mantenuti i dottorati di ricerca e i master di primo e secondo livello;</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt 53.25pt; TEXT-INDENT: -35.25pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 53.25pt\"><FONT face=\"Times New Roman\"><FONT size=3>-</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><FONT size=3>anche coloro che sono in possesso dei diplomi universitari di durata triennnale conseguiti a seguiti della legge 341/1990 potranno accedere direttamente ai corsi di \"laurea magistralis\" senza dover preventivamente acquisire la laurea;</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt 53.25pt; TEXT-INDENT: -35.25pt; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 53.25pt\"><FONT face=\"Times New Roman\"><FONT size=3>-</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><FONT size=3>viene infine confermato che i laureati di primo livello spetta il titolo di \"dottore\" e ai possessori del dottorato di ricerca il titolo di \"dottore di ricerca\". Ai possessori della \"laurea magistralis\" spetterà il titolo di \"dottore magistrale\".</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>A presto </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Maurizio Dori</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><B><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">TERZO INTERVENTO</SPAN></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>La Camera dei Deputati in data 26 ha respinto l\'emendamento 8 - terdecies relativo al prolungamento di un ulteriore anno di mandato.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Hanno votato contro il prolungamento 404 onorevoli su 415 votanti.Voci assicurano un intervento ulteriore presso il Senato della Repubblica. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>I Colleghi della sezione COCER Carabinieri sono<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>inviperiti. Attendevano con ansia questo prolungamento.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Spero di essere stato abbastanza chiaro. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Cordialmente </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Maurizio Dori - delegato COCER</FONT></P>', 0, '', '', '', 6035, 1, 0, '2026-01-10 16:26:01', '0000-00-00'),
(346, '2004-10-17 00:00:00', 'Notizie Flash', 'GUARDIA FINANZA: TRASFERITI 600 UFFICIALI, INTERROGAZIONE A SINISCALCO - MAFIA: EX VILLA DI RIINA SARA\' SEDE GUARDIA DI FINANZA - GDF: VISITA CRIMINOLOGI A CENTRO ADDESTRAMENTO ORVIETO', '', 'GUARDIA FINANZA: TRASFERITI 600 UFFICIALI, INTERROGAZIONE A SINISCALCO - MAFIA: EX VILLA DI RIINA SARA\' SEDE GUARDIA DI FINANZA - GDF: VISITA CRIMINOLOGI A CENTRO ADDESTRAMENTO ORVIETO', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">GUARDIA FINANZA: LEGNINI (DS), TRASFERITI 600 UFFICIALI<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SU 2.500 IN SERVIZIO, INTERROGAZIONE A SINISCALCO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - CHIETI, 15 OTT - Il senatore diessino Giovanni Legnini ha presentato un\'interrogazione al ministro dell\'Economia, Domenico Siniscalco, per conoscere le motivazioni del trasferimento d\'ufficio, per esigenze di servizio, di 600 dei 2.500 ufficiali in servizio presso la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Secondo il parlamentare teatino si tratta di \'\'una massiccia operazione di redistribuzione di incarichi di comando, le cui motivazioni non e\' dato conoscere\'\'. Nell\'interrogazione si chiede anche se l\'entita\' complessiva della spesa sia o meno \'\'compatibile con l\'inderogabile esigenza di risanamento della spesa pubblica\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Agli ufficiali trasferiti competono 12 mila euro di indennita\' di trasferimento, 1.500 euro di indennita\' forfettaria di prima sistemazione e circa 3.500 euro per le spese di trasloco il tutto per una spesa complessiva di oltre 10 milioni di euro.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'I trasferimenti, oltre a determinare seri pregiudizi personali e familiari per gli ufficiali interessati – secondo Legnini - sono potenzialmente idonei, stante il loro enorme numero e la suddetta motivazione a determinare decisioni arbitrarie o peggio punitive\'\'. (ANSA). <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">MAFIA: EX VILLA DI RIINA SARA\' SEDE <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">GUARDIA</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">DI</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">FINANZA</SPAN></STRONG><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - PALERMO, 14 OTT - La villa di Corleone appartenuta al boss mafioso Toto\' Riina, diventera\' sede della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di Finanza</SPAN></STRONG>. Attualmente la struttura ospita l\'istituto agrario, che sara\' trasferito nei locali che ospitano il liceo scientifico, il classico e lo psicopedagogico, che a loro volta saranno collocati in un nuovo plesso di 2.500 metri quadrati. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>Il trasferimento degli studenti e la consegna dei locali alle Fiamme gialle dovrebbero avvenire entro i primi mesi del nuovo anno.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Con l\'apertura del nuovo istituto – afferma il sindaco di Corleone, Nicolo\' Nicolosi - raggiungeremo due importanti obiettivi: da un lato gli studenti corleonesi potranno studiare in ambienti piu\' gradevoli e dall\'altro proseguira\' il nostro cammino di legalita\' con la consegna della villa Riina alla <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanza</SPAN></STRONG>\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">GDF: VISITA CRIMINOLOGI A CENTRO ADDESTRAMENTO ORVIETO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">DA DOMANI A CONVEGNO MONDIALE PRESSO ATENEO SACRO CUORE ROMA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ORVIETO (TERNI), 12 OTT - Oltre 70 delegati della Societa\' internazionale di criminologia, provenienti da numerosi paesi del mondo, ed in particolare dagli Stati Uniti, hanno visitato stamani il centro addestramento di specializzazione della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> ad Orvieto.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Una struttura, quella della Gdf, individuata nel panorama professionale italiano quale \'\'polo di eccellenza\'\' delle forze di polizia nazionali nella formazione del personale destinato ad incarichi di particolari taglio operativo.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La visita degli esponenti della societa\' di criminologia - ricorda una nota del Comune di Orvieto - e\' inserita nel programma del convegno annuale dell\' associazione, che quest\' anno si svolge in Italia, all\' Universita\' Cattolica del Sacro Cuore di Roma, a partire da domani, con psicologi, psichiatri e medici forensi che operano come consulenti delle polizie di tutto il mondo.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Stamani la delegazione, guidata dal presidente della societa\', Gary Aumiller, e\' stata accolta nella caserma \'\'Monte Grappa\'\' di Orvieto dal comandante della struttura, il colonnello Sandro Itro. La visita si e\' articolata in esposizioni teoriche e dimostrazioni pratiche dell\' attivita\' dell\' istituto.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>(ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 3094, 1, 0, '2026-01-09 21:21:50', '0000-00-00'),
(266, '2004-07-26 00:00:00', 'Libere manifestazioni di pensiero', 'ANCHE LA LAUREA TRIENNALE DA’ IL TITOLO DI DOTTORE.\r\n(di Raffaele Dalessandro)', '', 'Libere manifestazioni di pensiero \r\nANCHE LA LAUREA TRIENNALE DA’ IL TITOLO DI DOTTORE.\r\ndi Raffaele Dalessandro\r\n', '<P>Le università, ai sensi del decreto nr. 509 del 3 novembre 1999, rilasciano i seguenti titoli di primo e secondo livello: laurea (L) e laurea specialistica (Ls).</P>\r\n<P>Da più parti ci si chiede se al laureato di primo livello (laurea), il cui corso di studi dura tre anni e matura 180 crediti formativi spetta il titolo di dottore, considerato che il decreto nulla dice in merito e che non è mai stato abrogato l’articolo 48 del regio decreto nr. 1269 del 4 giugno 1938.</P>\r\n<P>Ed invero il comma 3 dell’articolo 48 del regio decreto 4 giugno 1938 – XVI – nr. 1269 (in “Gazzetta Ufficiale” 24 agosto, nr. 192) recita “a coloro che hanno conseguito una laurea, e a essi soltanto, compete la qualifica accademica di dottore”. <BR>&nbsp;<BR>Il comma citato non precisa quindi la durata in anni per conseguire la “laurea”. E’ pertanto da intendere a parere dello scrivente e di autorevoli esperti del settore che anche quella triennale, purchè laurea, attribuisce al possessore la qualifica di dottore.</P>\r\n<P>Credo che sia questa la giusta interpretazione da dare al quadro normativo attuale. Trovo ingiusto e scorretto il tentativo da parte di chicchessia di deprezzare e sminuire i notevoli sforzi economici ed i sacrifici personali e familiari dei numerosi colleghi ispettori, sovrintendenti, appuntati e finanzieri che a vario titolo hanno inteso ed intenderanno&nbsp; rimettersi in discussione per conseguire un ulteriore titolo di studio, contribuendo così all’elevazione culturale del Corpo.&nbsp;&nbsp; </P>\r\n<P>RAFFAELE DALESSANDRO<BR>Delegato Co.Ce.R. Guardia di Finanza – IX Mandato</P>\r\n<P>&nbsp;</P>', 0, '', '', '', 6683, 1, 0, '2026-01-14 01:51:47', '0000-00-00'),
(345, '2004-10-15 00:00:00', 'Dai media', 'ELICOTTERISTI, CHIESTA L\'ARCHIVIAZIONE', '', 'ELICOTTERISTI, CHIESTA L\'ARCHIVIAZIONE', 'Come avevamo a suo tempo auspicato il procuratore miltare di Roma, dottor Antonino INTELISANO, con la sagacia, l\'imparzialità e la competenza che sempre lo channo caraterizzato, ha chiesto l\'archiviazione nel procedimento a carico dei quattro piloti dell\'Aviazione dell\'Esercito che, nel Novembre del 2003, in IRAQ, si rifiutarono di eseguire una missione. Si è trattato, come hanno dimostrato le perizie non si trattò di codardia o di imperizia ma di buone ragioni alla base della loro decisione. Apprendiamo questa notizia da alcuni articoli stampa apparsi su quotidiani a tiratura nazionale nei giorni scorsi. Ai quattro piloti rinnoviamo la nostra solidarietà.', 97, '', '', '', 2109, 1, 0, '2026-01-12 16:31:34', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(347, '2004-10-17 00:00:00', 'Notizie Flash', 'SICUREZZA: OK A CONTRATTO, 111 EURO IN PIU\' IN BUSTA PAGA MA CGIL-CISL-UIL NON FIRMANO, OFFENDE DIGNITA\' LAVORATORI', '', 'SICUREZZA: OK A CONTRATTO, 111 EURO IN PIU\' IN BUSTA PAGA MA CGIL-CISL-UIL NON FIRMANO, OFFENDE DIGNITA\' LAVORATORI', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: OK A CONTRATTO, 111 EURO IN PIU\' IN BUSTA PAGA<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">MA CGIL-CISL-UIL NON FIRMANO, OFFENDE DIGNITA\' LAVORATORI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - Si chiude la vertenza sul rinnovo del contratto dei corpi di polizia e delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">armate</SPAN></STRONG>: poliziotti, agenti della polizia penitenziaria e del corpo forestale, carabinieri e militari, troveranno 111 euro lordi in piu\' al mese nella busta paga. Probabilmente gia\' a partire dal prossimo mese. L\'accordo, valido per il biennio 2004/05 e che riguarda circa 450 mila dipendenti, e\' stato firmato oggi al ministero della Funzione Pubblica dal governo e dalla maggioranza dei sindacati dei corpi di polizia e delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">armate</SPAN></STRONG>, ma non dai sindacati confederali che l\'hanno definito \'\'offensivo per la dignita\' dei lavoratori\'\' ed hanno gia\' annunciato battaglia per il rinnovo del contratto di tutto il pubblico impiego. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Per il governo, oltre al ministro della Funzione Pubblica Luigi Mazzella, erano presenti il vicepremier Fini e i sottosegretari Mantovano, Cicu, Santelli e Vegas. L\'accordo, ha detto Fini, \'\'conferma, compatibilmente con le disponibilita\' finanziarie, che il governo e\' particolarmente sensibile al tema della sicurezza\'\'. Stesse parole da Mazzella: \'\'L\'intesa dimostra che mettersi attorno ad un tavolo per discutere i problemi paga\'\'. Soddisfazione anche da parte di Cicu. \'\'Siamo riusciti - ha commentato - a dare ai militari italiani un ulteriore segnale di attenzione e riconoscimento per il loro impegno in <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Italia</SPAN></STRONG> e all\'estero\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Soddisfatti anche la maggioranza dei sindacati di polizia, anche se quasi tutti definiscono l\'accordo a mala pena sufficiente e invitano il governo a mantenere gli impegni presi e ribaditi oggi da Fini. \'\'E\' un fatto importante - ha detto il segretario del Sap, Filippo Saltamartini - ora si passi al riordino delle carriere, si razionalizzi il comparto sicurezza e si istituisca la Commissione interni\'\'. Per l\'Usp, invece, il contratto e\' \'\'molto insoddisfacente\'\' ma e\' stato firmato \'\'per lanciare un ultimo avviso al governo affinche\' mantenga le promesse fatte\'\', mentre il Sappe lo definisce \'\'soddisfacente\'\' e il Siap \'\'sottotono\'\' rispetto alle aspettative e \'\'sufficiente soltanto alla luce del quadro economico generale ed internazionale\'\'. Quanto all\'Ugl, l\'intesa \'\'permette il recupero del potere d\'acquisto degli stipendi dei lavoratori\'\' e per il Consap si tratta del \'\'miglior contratto possibile\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Diametralmente opposto il giudizio di Cgil, Cisl e Uil. \'\'E\' il peggior accordo degli ultimi dieci anni - ha sostenuto il segretario del Silp, Claudio Giardullo - un contratto profondamente offensivo che si c iude con 400 milioni in meno rispetto al biennio appena scaduto e che penalizza ancora una volta le fasce piu\' basse\'\'. L\'accordo \'\'offende la dignita\' degli operatori\'\', ha aggiunto la Uil chiedendosi \'\'dove sono gli aumenti promessi dal governo in campagna elettorale quando tutti gridavano allo scandalo sugli stipendi delle forze dell\'ordine\'\'. Piu\' \'soft\' la posizione della Cisl che si limita a definire \'\'fumoso\'\' l\'impegno assunto dal governo. I sindacati confederali ora annunciano battaglia sul rinnovo del contratto del pubblico impiego, ribadendo la richiesta di aumento dell\'8 per cento.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Richiesta che il governo ha gia\' detto che non soddisfera\'. \'\'Quello di oggi e\' un buon viatico per aprire il confronto sul problema del rinnovo del contratto del pubblico impiego – ha avvertito Fini - ma la richiesta dell\'8 per cento e\' immotivata\'\'. \'\'Sia per le condizioni di bilancio - ha aggiunto Mazzella - sia perche\' oggettivamente la riteniamo eccessiva\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: CICU, CONTRATTO DIMOSTRA ATTENZIONE VERSO MILITARI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - Il rinnovo del contratto firmato oggi dimostra l\'attenzione e il riconoscimento verso i militari italiani. Cosi\' il sottosegretario alla Difesa Salvatore Cicu ha commentato l\'accordo per il comparto sicurezza.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Grazie alla decisa volonta\' del ministro Martino siamo riusciti a dare ai militari italiani un ulteriore segnale di attenzione e riconoscimento per quanto stanno facendo in difesa del nostro paese e della stabilita\' internazionale\'\' ha detto il sottosegretario. E pur nelle \'\'note difficolta\' finanziarie – ha proseguito - abbiamo reperito le risorse necessarie, mettendo in evidenza ancora una volta la linea politica del ministero della Difesa, che considera la risorsa umana la componente fondamentale nel processo di riforma delle forze armate\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>In questo quadro, ha concluso, \'\'e\' stata assicurata ai <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer </SPAN></STRONG>la disponibilita\' e l\'impegno del ministero ad aprire da subito il confronto sulla ridefinizione delle indennita\' accessorie\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">COCER</SPAN></STRONG> ESERCITO, SODDISFAZIONE PER IMPEGNI GOVERNO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - Il <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG> dell\'Esercito ha espresso \'\'soddisfazione\'\' per la firma del rinnovo del contratto delle forze armate e dei corpi di polizia e per \'\'l\'accoglimento da parte del governo di alcuni impegni\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>In particolar modo, prosegue il <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG>, gli impegni \'\'a tutela del lavoro e delle retribuzioni\'\' e quelli di \'\'rivalutazione dello straordinario\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: CIARDULLO (SILP), PEGGIOR ACCORDO ULTIMI 10 ANNI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - Il rinnovo del contratto del comparto sicurezza relativo al biennio 2004/05 raggiunto oggi con la sigla dell\'accordo tra parti sociali e governo e\' il \'\'peggior contratto degli ultimi dieci anni\'\'. Lo afferma Claudio Giardullo, segretario del Silp-Cgil che non ha firmato l\'intesa.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Si tratta di un contratto, sostiene Giardullo, \'\'che non difende gli stipendi dei poliziotti dall\'inflazione, perche\' prevede per gli agenti un incremento di 50 euro netti al mese, ben al di sotto dell\'erosione gia\' subita, che fara\' pagare l\'ora di straordinario di meno dell\'ora di servizio ordinario e che congela tutte le indennita\', tranne il turno festivo\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Per la prima volta, inoltre, il governo \'\'chiude una trattativa esattamente con le stesse risorse con le quali l\'aveva aperta\'\'. \'\'Consideriamo grave questa scelta, che la dice lunga (mentre e\' ancora aperta la discussione sulle risorse da destinare ai contratti nella finanziaria) – conclude Giardullo - sulla reale attenzione dell\'esecutivo verso le funzioni svolte dalle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">polizia</SPAN></STRONG> e verso l\'impegno dimostrato dagli operatori anche in questa difficile stagione di minacce alla sicurezza dei cittadini\'\'.(ANSA). <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: CONSAP, FIRMATO MIGLIOR CONTRATTO POSSIBILE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - Il \'\'miglior contratto possibile\'\', che consolida una politica contrattuale \'\'volta allo sganciamento del comparto sicurezza rispetto al pubblico impiego, mediante risorse aggiuntive\'\'. La Confederazione sindacale autonoma di polizia (Consap) definisce \'\'positiva\'\' l\'intesa siglata oggi dalla maggioranza dei sindacati di polizia e dai <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG> delle forze armate per il biennio 2004-2004. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Questo rinnovo - e\' detto in una nota del sindacato - prelude ad un ulteriore riconoscimento delle funzioni e delle professionalita\' delle forze di polizia, da realizzarsi attraverso un disegno di legge di riordinamento delle carriere di tutto il personale\'\'.(ANSA). <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: LUCIDI (DS), GOVERNO PENALIZZA <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">FORZE</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">DI</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">POLIZIA</SPAN></STRONG><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">DA BERLUSCONI DELUSIONI E IL PEGGIOR CONTRATTO ULTIMI ANNI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - Marcella Lucidi, dei Ds, sostiene che le <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">polizia</SPAN></STRONG> sono sempre \'\'piu\' deluse\'\' dal governo Berlusconi, \'\'che consegna oggi alle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">polizia</SPAN></STRONG> il peggiore contratto degli ultimi anni e una legge finanziaria che penalizza il loro lavoro\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Le risorse contrattuali, infatti, sono inferiori rispetto ai contratti precedenti, non recuperano l\'inflazione e penalizzano fortemente il lavoro straordinario\'\', afferma la parlamentare, mentre \'\'nella legge finanziaria, oltre al taglio delle risorse economiche per le dotazioni e i mezzi, non c\'e\' alcuna previsione che mantenga le promesse finora fatte: non ci sono i soldi per il riordino delle carriere, per la previdenza integrativa, per i trattamenti accessori, per l\'edilizia abitativa, per il turn over del personale\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'E\' davvero umiliante e inammissibile - conclude Marcella Lucidi - che, a fronte delle necessita\' che la sicurezza impone, il governo tenti di usare ancora la strategia del bastone e della carota verso le <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">polizia</SPAN></STRONG>, che ormai non gli danno piu\' credito\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">POLIZIA: RIORDINO CARRIERE, SAP SOLLECITA GOVERNO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - TRIESTE, 14 OTT - Il Sap, sindacato autonomo di polizia, rinnova al ministro dell\'Interno Pisanu la richiesta di di presentazione alla Camera della delega legislativa sul riordino delle carriere nell\'ambito della polizia. L\'invito e\' stato rilanciato oggi a Trieste dal segretario nazionale del Sap, Filippo Saltamartini, intervenuto all\'assemblea dei quadri regionali del Friuli Venezia Giulia. La delega sul riordino delle carriere, rientra nell\'ambito della riconversione del decreto legge sul riassetto delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG>, approvato lo scorso 10 settembre dal Consiglio dei Ministri. \'\'Il riordino delle carriere - ha spiegato Saltamartini - e\' parte di un preciso accordo dello scorso 9 agosto, tra i rappresentanti sindacali delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG>, il sottosegretario alla Presidenza del Consiglio Gianni Letta, il Ministro dell\'Interno Giuseppe Pisanu e il sottosegretario alla Funzione Pubblica Learco Saporito\'\'. \'\'Il decreto legge sul riassetto delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG> - ha aggiunto - fa parte di un preciso impegno del Governo nella lotta al terrorismo, attraverso uno stanziamento di 200 milioni di euro inseriti nella Finanziaria\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Saltamartini ha infine illustrato ai delegati del Sap intervenuti all\'assemblea i termini dell\'accordo sul rinnovo contrattuale per il biennio 2004-2005, siglato due giorni fa alla presenza del vicepresidente del Consiglio Gianfranco Fini. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: ANCHE CISL NON FIRMA, CONTRATTO FUMOSO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - Anche la Cisl, come la Cgil e la Uil, non ha siglato l\'intesa per rinnovo del contratto delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze armate</SPAN></STRONG> e dei corpi di polizia relativo al biennio 2004-2005. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Avevamo gia\' manifestato la nostra contrarieta\' al momento della firma della preintesa, lo scorso giugno - ha spiegato il coordinatore della Cisl Fps Penitenziario Marco Mammucari – e ne\' allora ne\' oggi potevamo accontentarci di un impegno cosi\' fumoso da parte del governo\'\'. La questione, sostiene la Cisl, \'\'e\' semplice e chiara: si tratta di garantire il recupero dellþinflazione, e secondo noi occorre l\'8% di aumento, non il 3,6% che ci hanno offerto. Ne va della difesa dei diritti dei lavoratori e del potere d\'acquisto delle loro retribuzioni\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La Cisl ha annunciato che \'\'intende partecipare alle nuove iniziative di lotta che si stanno preparando e che l\'azione sindacale continuera\' con fermezza\'\' con Cgil e Uil a sostegno di tutto il pubblico impiego.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: ASCIERTO (AN), BENE CONTRATTO FORZE ORDINE E ARMATE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - \'\'La sottoscrizione del contratto alla Funzione Pubblica da parte delle forze dell\'ordine e delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">armate</SPAN></STRONG> e\' un fatto significativo ed importante\'\'. Lo dichiara Filippo Ascierto, responsabile del Dipartimento Sicurezza di An.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'E\' il secondo contratto biennale - aggiunge Ascierto – che il governo Berlusconi sottoscrive con militari e poliziotti che, insieme alla riparametrazione che andra\' a regime dal 2005, rappresenta tre volte gli stanziamenti effettuati dai precedenti governi di centrosinistra.\'\'<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Grazie all\'impegno di An, oltre ai miglioramenti economici, nelle prossime settimane sara\' possibile - conclude il deputato di An - un riordino delle carriere ampiamente atteso dai due comparti\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: CONTRATTO; CGIL NON FIRMA, ACCORDO OFFENSIVO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - La Cgil non ha firmato il contratto del comparto sicurezza, ritenendolo \'\'profondamente offensivo nei confronti dei 448 mila operatori di questo delicatissimo settore\'\'. Lo ha detto Fabrizio Rossetti, della Cgil Funzione Pubblica, secondo il quale \'\'la trattativa del rinnovo del contratto 2004-2005 si chiude con la sottoscrizione del peggiore degli accordi possibili\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Questo contratto - spiega Rossetti - si chiude con 400 milioni di euro in meno di quello con il quale si e\' chiuso il biennio appena scaduto (1.300 milioni di euro, contro gli 890 milioni di euro attuali). L\' accordo non prevede alcun recupero del potere di acquisto di salari per gli anni 2002-2003 e indica impossibili inflazioni programmate che gia\' oggi si discostano dall\' andamento reale in maniera evidente\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ma secondo la Cgil l\' intesa \'\'penalizza ancora una volta i livelli professionali piu\' bassi aumentando una forbice retributiva fra gli alti gradi e gli agenti: 66 euro lordi per un commissario, 45 euro lordi per un agente. Infine, questo contratto sara\' ricordato come l\' intesa che ha sancito che il lavoro straordinario, svolto in situazioni di impiego operativo dei poliziotti, e\' retribuito meno di un ora di lavoro ordinario. L\' accordo si chiude con un aumento del 4,7 per cento, molto lontano dall\' 8 per cento chiesto da Cgil, Cisl e Uil per i lavoratori pubblici\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: FINI, GOVERNO PARTICOLARMENTE ATTENTO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - L\'accordo siglato oggi e\' la dimostrazione che il governo e\' attento al tema della sicurezza. Cosi\' il vicepremier Gianfranco Fini ha commentato la sigla del rinnovo contrattuale del comparto sicurezza relativo al biennio 2004-2005.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Il contratto - ha spiegato - garantisce a regime un aumento lordo di 111 euro e che quindi conferma, compatibilmente con le disponibilita\' finanziarie, che il governo e\' particolarmente sensibile al tema della sicurezza\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: SAP, FIRMA ACCORDO FATTO IMPORTANTE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - La firma dell\'accordo per il rinnovo del contratto del comparto sicurezza e\' un \'\'fatto importante\'\'. Cosi\' il segretario del Sap, Filippo Saltamartini, ha commentato l\' intesa raggiunta oggi a Palazzo Guidoni.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Ora - ha aggiunto - il governo deve mantenere fede al riordino delle carriere, mediante l\'utilizzazione delle risorse stanziate nella Finanziaria dello scorso anno\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il segretario del Sap, infine, ha poi ribadito \'\'l\'esigenza di razionalizzare il comparto sicurezza e di istituire la commissione interni in parlamento per dare maggiore vigore ai provvedimenti di politica della sicurezza\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: CONTRATTO; UIL PENITENZIARIA, AGENTI PENALIZZATI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - \'\'La polizia penitenziaria viene penalizzata ancora una volta sul trattamento economico accessorio. Lo dichiara Massimo Tesei, segretario generale della Uil di categoria, in riferimento all\'accordo biennale 2004-2005 per le forze di polizia e armate, firmato oggi. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Non viene sanato - spiega Tesei - il gap negativo determinato dal primo biennio per cui ogni poliziotto penitenziario continuera\' a percepire mensilmente circa 50 euro in meno rispetto ai colleghi delle altre forze di polizia\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La Uil penitenziari, sottlinea il segretario, non ha sottoscritto l\'accordo proprio perche\' non consentirebbe \'\'alcun recupero del differenziale inflattivo\'\' per i passati due anni. \'\'Anzi - conclude Tesei - con questo accordo viene svilito il significato del lavoro straordinario che sara\' remunerato in misura inferiore alla retribuzione oraria ordinaria\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: SAPPE, SODDISFAZIONE PER FIRMA CONTRATTO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - \'\'Soddisfazione\'\' e\' stata espressa dal Sindacato autonomo di polizia penitenziaria (Sappe) per la firma dell\'accordo sul rinnovo del contratto del comparto sicurezza per il biennio 2004-2005. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Il governo - e\' detto in una nota - ha recepito la nostra specifica richiesta e si e\' impegnato ufficialmente a costituire un tavolo tecnico per esaminare le problematiche emerse in sede di contrattazione sui fondi per l\'efficienza dei servizi istituzionali\'\'. Si arriva dunque alla concretizzazione, aggiunge il Sappe, di una trattativa che molti volevano gia\' chiusa in estate. E questo \'\'soltanto per l\'impegno del Sappe e della consulta sicurezza, che ha permesso di evitare un\'ennesima ingiustizia obbligando il governo a prendere gli impegni adeguati\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: SIAP, CONTRATTO SOTTOTONO SOLTANTO SUFFICIENTE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - Il rinnovo del contratto firmato oggi e\' un accordo \'\'sottotono\'\' rispetto alle aspettative, \'\'sufficiente soltanto alla luce del quadro economico generale ed internazionale\'\'. Cosi\' il segretario del sindacato italiano appartenenti polizia (Siap) Giuseppe Tiani commenta l\'intesa per il biennio 2004-2005.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ora speriamo, aggiunge il Siap, che \'\'l\'impegno assunto dal vicepremier Fini a nome del governo, rispetto ad una maggiore attenzione per gli aumenti stipendiali e sulle voci accessorie, abbia un effettivo riscontro, cosi\' come promesso, entro la fine della legislatura\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: UGL, SI VA NELLA GIUSTA DIREZIONE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - \'\'Un contratto che va nella giusta direzione e che permette il recupero del potere d\'acquisto degli stipendi dei lavoratori del comparto della sicurezza\'\'. Commenta cosi\' il segretario di Rinnovamento sindacale per l\'Ugl, Paolo Varesi l\'accordo per il rinnovo del contratto del comparto sicurezza siglato stamattina. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>\'\'E\' inoltre significativo - continua Varesi- che il governo si sia formalmente impegnato per garantire sicurezza ed igiene sui luoghi di lavoro\'\' e che \'\'abbia permesso al sindacato di partecipare concretamente all\'individuazione delle priorita\' economiche\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: USP, DOPO CONTRATTO GOVERNO MANTENGA PROMESSE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 OTT - L\' Unione sindacale di polizia (Usp) non lo giudica soddisfacente, ma ha sottoscritto il contratto del comparto sicurezza. A spiegarne il perche\' e\' il presidente nazionale Usp, Giampaolo Tronci: \'\'Noi dell\'unione sindacale di polizia nonostante si tratti di un contratto molto insoddisfacente abbiamo sottoscritto l\' ipotesi di governo: da una parte per non perdere le prerogative sindacali, e dall\'altra per lanciare un ultimo, un ultimissimo avviso all\'esecutivo di Berlusconi di disponibilita\' e nel contempo di invito a mantenere le promesse fatte finora e soprattutto a non fare nell\'immediato futuro promesse che non si possono poi mantenere\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2859, 1, 0, '2026-01-11 06:46:57', '0000-00-00'),
(264, '2004-07-25 00:00:00', 'Ordine del giorno', 'L’ASSOCIAZIONE \"FINANZIERI CITTADINI E SOLIDARIETA’ - FICIESSE\"\r\nRIVOLGE UN SALUTO COMMOSSO ALL’APPUNTATO DEI CARABINIERI \r\nALESSANDRO GIORGIONI\r\nUCCISO DA MANO VILE NELL’ESPLETAMENTO DEL SUO DOVERE\r\n', '', '', '', 0, '', '', '', 4204, 1, 0, '2025-12-31 14:27:14', '0000-00-00'),
(259, '2004-07-10 00:00:00', 'Comunicato stampa', 'CONVENZIONE CON L’ UNIVERSITA’ DI TORINO', '', 'CONVENZIONE CON L’ UNIVERSITA’ DI TORINO', '<P>Un&nbsp; visitatore del sito ci ha inviato la seguente mail:</P>\r\n<P>&nbsp;</P>\r\n<P>&nbsp;</P>\r\n<P>“PUBBLICATE TANTE COSE... E POI NON RIPORTATE, DETTAGLIANDOLA, LA<BR>CONVENZIONE CON L\'UNIVERSITA\' DI TORINO. MI SEMBRA UN PO\' RIDICOLO E<BR>SOPRATTUTTO DIMOSTRATE CHE PER LE COSE IMPORTANTI TANTI SALUTI.”</P>\r\n<P>&nbsp;</P>\r\n<P>Gli abbiamo risposto:</P>\r\n<P>“devi sapere che non siamo in molti a poterci occupare dell\'aggiornamento del<BR>sito. anzi colgo l\'occasione per chiederti, se ne hai la voglia ed il tempo, di partecipare attivamente alla vita dell\'associazione.<BR>In particolare, attualmente proprio a Torino non abbiamo nessuno. Non<BR>possiamo quindi pubblicare una convenzione che materialmente non<BR>abbiamo. Ti chiedo quindi, gentilmente di farcela avere, magari con un<BR>tuo commento, e noi la pubblicheremo ben volentieri.”</P>\r\n<P>&nbsp;</P>\r\n<P>A stretto giro di posta ci è stata inviata la Convenzione che in allegato pubblichiamo.</P>\r\n<P>La&nbsp; stessa è stata siglata il 2 luglio 2004 ed entrerà in vigore con l’avvio del prossimo anno Accademico.</P>\r\n<P>&nbsp;</P>\r\n<P>Assieme alla&nbsp; CONVENZIONE, ci sono state inviate&nbsp; una serie di tabelle riguardanti i crediti riconosciuti alle diverse categorie del personale che pubblicheremo, dopo averle esaminate, in un servizio a parte dopo averle esaminate</P>\r\n<P>&nbsp;</P>\r\n<P>In questa sede ci preme sottolineare come con la collaborazione di chiunque è in grado di contribuire sia possibile rendere un servizio di informazione ed a volte anche qualcosa in più a tutti coloro che individualmente non riescono ad arrivare a risolvere problematiche per varie ragioni.</P>\r\n<P>&nbsp;</P>\r\n<P>Invito quindi tutti a partecipare attivamente alla vita dell’ Associazione&nbsp; al fine di&nbsp; garantire al massimo la circolazione dell’ informazione all’ interno dell’ Organizzazione e soprattutto contribuire a migliorare il rapporto tra cittadino ed Istituzione&nbsp; per consolidare quella solidarietà che deve essere alla base della fiducia che deve intercorrere e che è uno degli scopi della nostra Associazione.<BR></P>', 78, '', '', '', 174, 1, 0, '2010-10-01 05:25:30', '0000-00-00'),
(260, '2004-07-15 00:00:00', 'Editoriale', 'UNIVERSITA\' DI BOLOGNA- ESAME DI LAUREA', '', 'UNIVERSITA\' DI BOLOGNA- ESAME DI LAUREA', '<P>UN&nbsp; COLLEGA, il 12 luglio,&nbsp;CI SCRIVE:</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>le scrivo al fine di mettere a conoscenza i visitatori del sito in merito a quanto accade presso l\'Università di Bologna.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>&gt; Ieri è iniziata la sessione di Laurea di luglio, al momento della proclamazione il Preside di Facoltà ha spiegato che non potevamo più fregiarci del titolo di dottore in base a quanto stabilito dal Senato Accademico ai primi di luglio.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>&gt; In merito a questo ci tengo a fare due considerazioni:</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3></FONT>&nbsp;</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>&gt; - come mai chi si è presentato alla sessione di giugno è stato proclamato dottore in Economia e Commercio;</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>&gt; - come si possono cambiare le regole in corso d\'anno, prima ci chiedono una fracco di soldi, ci fanno sostenere gli esami e il giorno della laurea cambiano le carte in tavola.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>&gt; </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>&gt; Scusi lo sfogo, ma la presente è solo per mettere a conoscenza (se le vorrà farne menzione nel sito) i colleghi che magari devono discutere la<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>tesi o che devono iscriversi all\'Università di Bologna della situazione.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3></FONT>&nbsp;</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>ABBIAMO INTESO DOCUMENTARCI SULLA QUESTIONE ED IN EFFETTI:</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>siamo riusciti a parlare con un docente di economia e con alcuni ragazzi che si sono appena laureati con la laurea breve.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Effettivamente le cose sono come rappresentate. Una delibera del Senato Accademico ha stabilito che a seguito della laurea breve non spetta il titolo di dottore ma ancor di più ha abbassato i punti in più che vengono concessi sulla media dei voti di esami per il voto finale di laurea.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>In particolare sembra che se ci si laurea in corso ( tre anni) rimangono i 5 punti, se si va anche al primo semestre successivo, al contrario di prima che si avevano 3 punti, attualmente non spetta più aumento.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>La decisione è criticabile per la sperequazione che crea rispetto a coloro che si sono laureati nei periodi precedenti, tuttavia bisogna prenderne atto poichè è stata assunta legittimamente. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3></FONT>&nbsp;</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Invitiamo&nbsp; tutti coloro che hanno notizie sull\'argomento, anche da altre università, a fornire chiarimenti sul FORUM del sito o per e-mail.&nbsp;</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></P>\r\n<P></P>', 0, '', '', '', 2320, 1, 0, '2026-01-10 03:18:07', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(261, '2004-07-19 00:00:00', 'Notizie Flash', 'UNIVERSITA\' DI  BOLOGNA', '', 'UNIVERSITA\' DI  BOLOGNA', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36pt; TEXT-INDENT: -18pt; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt\"><SPAN style=\"FONT-FAMILY: Wingdings\"><SPAN style=\"FONT: 7pt \'Times New Roman\'\"><FONT size=2>Riportiamo perchè possa servire al dibattito in corso&nbsp; la risposta&nbsp; ad alcuni quesiti riportati sul sito <A href=\"http://www.unibo.it/Portale/FAQ/didattica.htm\">www.unibo.it/Portale/FAQ/didattica.htm</A>&nbsp;. La pagina risponde ai molteplici quesiti che si pongono gli studenti in relazione ai crediti, ai corsi triennali&nbsp;ed a quelli di specializzazioni.</FONT></SPAN></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36pt; TEXT-INDENT: -18pt; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt\"><SPAN style=\"FONT-FAMILY: Wingdings\"><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;</SPAN></SPAN><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></P>\r\n<TABLE style=\"WIDTH: 286.45pt; mso-padding-alt: 0cm 0cm 0cm 0cm; mso-cellspacing: 0cm\" cellSpacing=0 cellPadding=0 width=382 border=0>\r\n<TBODY>\r\n<TR>\r\n<TD style=\"BORDER-RIGHT: #d4d0c8; PADDING-RIGHT: 0cm; BORDER-TOP: #d4d0c8; PADDING-LEFT: 0cm; PADDING-BOTTOM: 0cm; BORDER-LEFT: #d4d0c8; PADDING-TOP: 0cm; BORDER-BOTTOM: #d4d0c8; BACKGROUND-COLOR: transparent\" vAlign=top>\r\n<TABLE style=\"WIDTH: 100%; mso-padding-alt: 0cm 0cm 0cm 0cm; mso-cellspacing: 0cm\" cellSpacing=0 cellPadding=0 width=\"100%\" border=0>\r\n<TBODY>\r\n<TR>\r\n<TD style=\"BORDER-RIGHT: #d4d0c8; PADDING-RIGHT: 0cm; BORDER-TOP: #d4d0c8; PADDING-LEFT: 0cm; PADDING-BOTTOM: 0cm; BORDER-LEFT: #d4d0c8; WIDTH: 100%; PADDING-TOP: 0cm; BORDER-BOTTOM: #d4d0c8; BACKGROUND-COLOR: transparent\" vAlign=top width=\"100%\">\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT size=3><FONT face=\"Times New Roman\"><STRONG>Con le lauree triennali e le lauree specialistiche esiste ancora il titolo di Dottore?</STRONG><BR>Il Decreto Ministeriale 509/99 non prevede il conseguimento del titolo di dottore al termine dei corsi di laurea triennali e dei corsi di laurea specialistica. L\'unico caso in cui si cita esplicitamente il conseguimento del titolo al termine di un ciclo di studi universitari è quello del corso di dottorato di ricerca.Attualmente lo studente che termina un corso di studi triennale è dichiarato laureato mentre lo studente che termina un corso di studi specialistico è dichiarato laureato specialista.<BR><BR><STRONG>Che valore avrà la laurea conseguita secondo il vecchio ordinamento?</STRONG><BR>Se consegui o conseguirai un titolo di laurea secondo il vecchio ordinamento conservi tutti i diritti previsti dalla normativa vigente: le lauree conseguite secondo il vecchio ordinamento mantengono, infatti, inalterato il loro valore. </FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<SPAN style=\"FONT-FAMILY: \'Arial Unicode MS\'\"><o:p></o:p></SPAN></FONT></FONT></P></TD></TR>\r\n<TR>\r\n<TD style=\"BORDER-RIGHT: #d4d0c8; PADDING-RIGHT: 0cm; BORDER-TOP: #d4d0c8; PADDING-LEFT: 0cm; PADDING-BOTTOM: 0cm; BORDER-LEFT: #d4d0c8; WIDTH: 100%; PADDING-TOP: 0cm; BORDER-BOTTOM: #d4d0c8; BACKGROUND-COLOR: transparent\" vAlign=top width=\"100%\">\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<SPAN style=\"FONT-FAMILY: \'Arial Unicode MS\'\"><o:p></o:p></SPAN></FONT></FONT></P></TD></TR></TBODY></TABLE>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><BR style=\"mso-special-character: line-break\" clear=all><SPAN style=\"FONT-FAMILY: \'Arial Unicode MS\'\"><o:p></o:p></SPAN></P></TD>\r\n<TD style=\"BORDER-RIGHT: #d4d0c8; PADDING-RIGHT: 0cm; BORDER-TOP: #d4d0c8; PADDING-LEFT: 0cm; PADDING-BOTTOM: 0cm; BORDER-LEFT: #d4d0c8; PADDING-TOP: 0cm; BORDER-BOTTOM: #d4d0c8; BACKGROUND-COLOR: transparent\">\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><?xml:namespace prefix = v ns = \"urn:schemas-microsoft-com:vml\" /><v:shapetype id=_x0000_t75 coordsize=\"21600,21600\" o:spt=\"75\" o:preferrelative=\"t\" path=\"m@4@5l@4@11@9@11@9@5xe\" filled=\"f\" stroked=\"f\"><v:stroke joinstyle=\"miter\"></v:stroke><v:formulas><v:f eqn=\"if lineDrawn pixelLineWidth 0\"></v:f><v:f eqn=\"sum @0 1 0\"></v:f><v:f eqn=\"sum 0 0 @1\"></v:f><v:f eqn=\"prod @2 1 2\"></v:f><v:f eqn=\"prod @3 21600 pixelWidth\"></v:f><v:f eqn=\"prod @3 21600 pixelHeight\"></v:f><v:f eqn=\"sum @0 0 1\"></v:f><v:f eqn=\"prod @6 1 2\"></v:f><v:f eqn=\"prod @7 21600 pixelWidth\"></v:f><v:f eqn=\"sum @8 21600 0\"></v:f><v:f eqn=\"prod @7 21600 pixelHeight\"></v:f><v:f eqn=\"sum @10 21600 0\"></v:f></v:formulas><v:path o:extrusionok=\"f\" gradientshapeok=\"t\" o:connecttype=\"rect\"></v:path><o:lock v:ext=\"edit\" aspectratio=\"t\"></o:lock></v:shapetype><v:shape id=_x0000_i1025 style=\"WIDTH: 11.25pt; HEIGHT: 0.75pt\" type=\"#_x0000_t75\" alt=\"\"><v:imagedata src=\"file:///C:/DOCUME~1/ADMINI~1/IMPOST~1/Temp/msoclip1/01/clip_image001.png\" o:href=\"http://www.unibo.it/CMSUniboWeb/Resources/Common/images/spacer.gif\"></v:imagedata></v:shape><SPAN style=\"FONT-FAMILY: \'Arial Unicode MS\'\"><o:p></o:p></SPAN></P></TD></TR></TBODY></TABLE>', 0, '', '', '', 3256, 1, 0, '2026-01-09 17:05:49', '0000-00-00'),
(340, '2004-10-07 00:00:00', 'Notizie Flash', 'SICUREZZA: POLIZIA INTEGRATA,A NAPOLI VIA A CORSI FORMAZIONE\r\nDIVERSE FORZE A LEZIONE PER CONTROLLO TERRITORIO', '', 'SICUREZZA: POLIZIA INTEGRATA,A NAPOLI VIA A CORSI FORMAZIONE\r\nDIVERSE FORZE A LEZIONE PER CONTROLLO TERRITORIO', '<PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - NAPOLI, 5 OTT - Quel coordinamento tra diverse forze<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dell\'ordine che non sempre si realizza pienamente, diventa </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">concreto nel Mezzogiorno attraverso il progetto formativo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">\'Polizia integrata\': oggi a Napoli, alla presenza del prefetto </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Renato Profili che ha presentato l\'iniziativa, e\' partita<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">l\'edizione pilota del progetto che coinvolge 2500 tra </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">appartenenti a Polizia di Stato, carabinieri, <STRONG><SPAN style=\"COLOR: red\">guardia</SPAN></STRONG> di <STRONG><SPAN style=\"COLOR: red\">finanza</SPAN></STRONG><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">e polizie locali (municipale e provinciale) del Mezzogiorno.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Tutti insieme, nelle singole regioni (quelle dell\'obiettivo 1 </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">come Campania, Puglia, Calabria, Basilicata, Sicilia, Sardegna),<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">seduti nella stessa classe, a lezione di sicurezza per </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">condividere la stessa formazione e acquisire quegli elementi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">necessari ad affrontare con competenza le nuove emergenze in </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">materia di controllo e gestione del territorio. Il progetto e\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">realizzato con i fondi del programma operativo nazionale </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">\'Sicurezza per lo sviluppo del Mezzogiorno d\'Italia\' nell\'ambito<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">della misura II.3 \'Risorse umane per la diffusione della </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">legalita\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Non solo dunque, lotta alla criminalita\' ma anche tutela </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ambientale, sicurezza stradale, tutela dei beni archeologici,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">guerra all\'abusivismo edilizio. Insomma, lotta per la sicurezza </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">a 360 gradi. Alle lezioni partecipano appartenenti ai rispettivi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Corpi con particolari peculiarita\' dal punto di vista della </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">conoscenza delle lingue e dell\'informatica. Le lezioni servono<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">per apprendere nuovi aspetti ma anche per raccontarsi </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">esperienze. Il bagaglio di conoscenze ulteriori consente, una<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">volta finito il corso, di andare sul territorio con piu\' armi a </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">disposizione per la lotta in difesa della legalita\' ma anche da<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">trasmettere ai propri colleghi. Il progetto, secondo quanto e\' </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">stato spiegato oggi, e\' rivolto ad integrare ed elevare la<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">qualita\' della presenza degli operatori di polizia nel </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">territorio \'\'nell\'ottica di una cultura della cooperazione e<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">della condivisione delle conoscenze e delle prassi operative\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Secondo quanto e\' stato sottolineato \'\'tale obiettivo si </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">consegue da un lato attraverso l\'aumento della consapevolezza<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dei singoli operatori del proprio ruolo istituzionale e </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dall\'altro, atraverso l\'ottimizzazione dei comportamenti per<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">migliorare i rapporti con i cittadini e i colleghi di lavoro, </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ancorche\' appartenenti ad istituzioni diverse\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La presentazione si e\' aperta con la visione del video </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">messaggio registrato dal vice capo della polizia preposto<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">all\'attivita\' di coordinamento e pianificazione delle forze di </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">polizia, Giuseppe Procaccini, per illustrare l\'azione formativa<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">e le sue finalita\'. Il corso pilota (a Napoli e in provincia </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sono interessati in totale 250 appartenenti ai diversi Corpi) e\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">iniziato al termine della cerimonia inaugurale organizzata </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dall\'ufficio per la promozione dell\'innovazione della Direzione<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">centrale per gli istituti di istruzione della Polizia di Stato, </SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">diretto dal vice prefetto Emilia Zarrelli.(ANSA).</SPAN></PRE>', 0, '', '', '', 1356, 1, 0, '2026-01-03 08:23:01', '0000-00-00'),
(256, '2004-07-08 00:00:00', 'Servizio di Ficiesse', 'PROTOCOLLO D’INTESA TRA IL CORPO, ASSOCIAZIONE DEI COMUNI  e UNIONE  PROVINCE D’ ITALIA  PER ALLOGGI  PERSONALE', '', 'PROTOCOLLO D’INTESA TRA IL CORPO, ASSOCIAZIONE DEI COMUNI  e UNIONE  PROVINCE D’ ITALIA  PER ALLOGGI  PERSONALE', '<FONT face=\"Times New Roman\" size=2>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 10pt; mso-bidi-font-size: 12.0pt\"><FONT size=3>In allegato pubblichiamo il protocollo d’intesa che è intervenuto tra la Guardia di Finanza, l’Unione delle Province d’Italia e l’Associazione Nazionale dei Comuni Italiani per la ricerca e la resa in disponibilità di alloggi di proprietà comunale e provinciale in favore degli appartenenti al Corpo della Guardia di Finanza.<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; mso-bidi-font-size: 12.0pt\"><FONT size=3>&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; mso-bidi-font-size: 12.0pt\"><FONT size=3>La Convenzione ci sembra di interesse notevole e non è stata, a nostro avviso, pubblicizzata abbastanza.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; mso-bidi-font-size: 12.0pt\"><FONT size=3>&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; mso-bidi-font-size: 12.0pt\"><FONT size=3>Lo facciamo oggi in concomitanza dell’ imminente trasferimento di un numero considerevole di militari delle diverse categorie, in particolare giovani ufficiali, che potrebbero trovare nella Convenzione un valido aiuto ai problemi che certamente dovranno affrontare nella ricerca di alloggi, soprattutto nelle grandi aree urbane.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; mso-bidi-font-size: 12.0pt\"><FONT size=3>&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 10pt; mso-bidi-font-size: 12.0pt\"><FONT size=3>Ci piace sottolineare che il Protocollo d’intesa, certamente preparato nel tempo, è stato comunque<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>siglato, per il Corpo, dall’attuale Comandante Generale, Gen.C.A. Roberto Speciale, che dimostra così, ancora una volta, di essere sensibile ai problemi che il personale deve affrontare. <o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 0pt\"></FONT>&nbsp;</P>', 79, '', '', '', 7131, 1, 0, '2025-12-30 08:56:46', '0000-00-00'),
(319, '2004-09-13 00:00:00', 'Notizie Flash', 'POLIZIA: RIORDINO CARRIERE, SINDACATI CHIEDONO LEGGE DELEGA - SAPORITO, LEGGE DELEGA STRADA MIGLIORE', '', 'POLIZIA: RIORDINO CARRIERE, SINDACATI CHIEDONO LEGGE DELEGA - SAPORITO, LEGGE DELEGA STRADA MIGLIORE', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><FONT face=\"Times New Roman\"><STRONG><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 14pt; COLOR: black; mso-bidi-font-weight: bold\">POLIZIA</SPAN></STRONG><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\">: RIORDINO CARRIERE, <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">SINDACATI</SPAN></STRONG> CHIEDONO LEGGE DELEGA<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></FONT></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">PRIMO INCONTRO CON IL VICE CAPO GIUSEPPE PROCACCINI<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 8 SET - Una legge delega capace di dar voce alle esigenze di tutto il personale e impiegare le risorse gia\' stanziate nella legge finanziaria 2004. E\' la richiesta fatta dai <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">sindacati</SPAN></STRONG> di categoria Sap, Sappe e Sapaf nel corso del primo incontro tecnico sul riordino delle carriere con il vicecapo della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">polizia</SPAN></STRONG>, prefetto Giuseppe Procaccini. <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Procaccini - e\' detto in una nota del Sappe - ha ricordato che dopo la mobilitazione di ferragosto della categoria \'\'il Governo ha voluto accelerare i tempi hanno per giungere alla soluzione dei problemi sollevati dai <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">sindacati</SPAN></STRONG>\'\'. I responsabili del Sindacato Autonomo <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG> (SAP), del Sindacato Autonomo <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG> Penitenziaria (SAPPe), e del Sindacato Autonomo <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia </SPAN></STRONG>Ambientale Forestale (SAPAF) hanno criticato il fatto che migliaia di agenti siano stati collocali tra i \'\'militari di truppa\'\' (5ølivello retributivo); il trattamento riservato agli ispettori i cui omologhi del pubblico impiego sono stati inquadrati nel ruolo direttivo e, l\'urgenza di realizzare la \'dirigenzializzazione\' di funzionari e dirigenti.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'I <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">sindacati</SPAN></STRONG> autonomi delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG> Civili - conclude la nota - hanno chiesto l\' accelerazione delle procedure concertative e un incontro per la precisazione delle rispettive posizioni, al fine di consentire al Governo di assumere l\'azione legislativa\'. La riunione e\' stata aggiornata al 18 settembre. <SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;</SPAN>(ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><FONT face=\"Times New Roman\"><STRONG><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 14pt; COLOR: black; mso-bidi-font-weight: bold\">POLIZIA</SPAN></STRONG><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\">: CARRIERE; SAPORITO, LEGGE DELEGA STRADA MIGLIORE<o:p></o:p></SPAN></FONT></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 8 SET - Quella di definire \'\'in tempi brevi\'\' una \'\'legge delega di avvio di riordino delle carriere\'\', e\' la strada migliore\'\'. Lo afferma il sottosegretario alla Funzione Pubblica Learco Saporito dopo l\'incontro tecnico sul riordino delle carriere tra i <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">sindacati</SPAN></STRONG> di <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">polizia</SPAN></STRONG> e il prefetto Giuseppe Procaccini.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Mi sembra la strada migliore<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>- spiega - poiche\' e\' necessario utilizzare normativamente le risorse previste dalla legge finanziaria per il riordino delle carriere del personale di <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG>\'\'. Noi siamo pronti, conclude Saporito, \'\'a definire le linee della legge delega appena il Comitato costituito presso il Viminale, avra\' definito nei prossimi giorni i suoi approfondimenti\'\'. (ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2213, 1, 0, '2026-01-14 07:22:08', '0000-00-00'),
(320, '2004-09-13 00:00:00', 'Dal Parlamento', 'Pubblicato il Decreto Legge 10 settembre 2004 n. 238 (misure urgenti per il personale appartenente ai ruoli degli ispettori delle Forze di polizia).', '', 'Pubblicato il Decreto Legge 10 settembre 2004 n. 238 (misure urgenti per il personale appartenente ai ruoli degli ispettori delle Forze di polizia).', '<P><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: IT; mso-fareast-language: IT; mso-bidi-language: AR-SA\">Il Decreto Legge 10 settembre 2004 n. 238 (misure urgenti per il personale appartenente ai ruoli degli ispettori delle Forze di polizia) è stato pubblicato sulla G.U. n. 213 del 10 settembre 2004. </SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: IT; mso-fareast-language: IT; mso-bidi-language: AR-SA\">Come già indicato da alcuni sul Forum il provvedimento crea nuovi disallineamenti tra forze di polizia ad ordinamento civile e militare.</SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: IT; mso-fareast-language: IT; mso-bidi-language: AR-SA\">Una prima lettura indica il personale cui esso si applica quale appartenente a ruoli ad esaurimento.</SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: IT; mso-fareast-language: IT; mso-bidi-language: AR-SA\">Sarebbe interessante che gli addetti ai lavori, spiegassero, sul Forum, a tutti, i contenuti del D.di L., concretamente i disallineamenti che esso crea, nonchè i rimedi che nelle sedi opportune chi di competenza&nbsp;( COCER ed AMM/NE) dovrebbero richiedere per riallineare la situazione</SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: IT; mso-fareast-language: IT; mso-bidi-language: AR-SA\"></SPAN>&nbsp;</P>', 0, '', '', '', 1868, 1, 0, '2026-01-14 10:40:49', '0000-00-00'),
(317, '2004-09-13 00:00:00', 'Notizie Flash', 'FISCO:LEO(AN); PER CONTROLLO CONTI CORRENTI SERVE AUTORIZZAZIONE GDF - TESORO; CONTROLLO CONTI CORRENTI E\' FANTASIA ESTIVA - ENTRO FINE ANNO RIVISTI 86-87 STUDI SETTORE', '', 'FISCO:LEO(AN); PER CONTROLLO CONTI CORRENTI SERVE AUTORIZZAZIONE GDF - TESORO; CONTROLLO CONTI CORRENTI E\' FANTASIA ESTIVA - ENTRO FINE ANNO RIVISTI 86-87 STUDI SETTORE', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">FISCO:LEO(AN);REDDITOMETRO PUO\' ESSERE UTILE CONTRO EVASIONE<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">PER CONTROLLO CONTI CORRENTI SERVE AUTORIZZAZIONE ENTRATE-GDF<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 7 set - Il controllo dei conti correnti puo\' gia\' essere effettuato, secondo la normativa vigente, se esiste un fondato sospetto di evasione. Ma occorre una doppia autorizzazione: quella del direttore regionale delle Entrate e del comandante di zona della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>. <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Lo ricorda Maurizio Leo, responsabile fiscale di Alleanza Nazionale e vicepresidente della Commissione Finanze della Camera, commentando le indiscrezioni di stampa secondo le quali, in vista della prossima Finanziaria e per dare una \'stretta\' alla lotta all\'evasione fiscale, si starebbe studiando la possibilita\' di controlli sui conti correnti oppure quella di rispolverare il redditometro.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Bisogna muoversi con le procedure oggi previste - afferma - che stabiliscono che se c\'e\' fondato sospetto di evasione si puo\' fare un controllo, ma non senza le prescritte autorizzazioni che sono - appunto - quella del direttore regionale delle Entrate e del comandante di zona della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>. Andrebbe quindi rivista tutta la normativa\'\'. Leo spiega che la \'ratio\' della legge sta nella necessita\' di far valutare a piu\' enti la richiesta di procedere al controllo in modo da non lasciare ad un solo ufficio la valutazione della posizione del contribuente che eventualmente sarebbe sottoposto ad accertamenti.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Per quanto riguarda un\'eventuale \'rispolverata\' al redditometro Leo ritiene che questo strumento possa essere utile nella lotta all\'evasione. Ma mette in guardia: \'\'mi ricordo il \'modello lunare\' difficilissimo da compilare per i contribuenti. Si puo\' rispolverare il redditometro, ma non ci devono essere aggravi per i contribuenti. Lo strumento consente di andare a vedere quale e\' il tenore di vita, tipo se uno possiede barche, macchine, cavalli. Poi pero\' fu tolto dal modello 740 anche perche\' l\'amministrazione poteva avere le stesse informazioni attraverso le banche dati\'\'.(ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">FISCO: TESORO; CONTROLLO CONTI CORRENTI E\' FANTASIA ESTIVA<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 7 set - \'\'Fantasia estiva\'\': cosi\' fonti ufficiali del ministero dell\' Economia commentano la notizia, riportata oggi dalla stampa, secondo cui sarebbe allo studio la possibilita\' di un accesso online \'libero\' da parte dell\'amministrazione fiscale sui conti correnti bancari.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Oltre ai conti correnti bancari la stampa cita anche la possibilita\' di \'rispolverare\' il redditometro contro l\'evasione fiscale. (ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">FISCO: IN ARRIVO RITOCCO TASSE PER 2-3 MILIONI DI AUTONOMI<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">BRUNELLO (SO.SE),ENTRO FINE ANNO RIVISTI 86-87 STUDI SETTORE<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 8 SET - In arrivo entro fine anno un ritocco delle tasse per 2-3 milioni tra commercianti, artigiani, professionisti. \'\'Entro la fine dell\'anno verranno rivisti 86-87 studi di settore\'\', riferisce<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Giampiero Brunello, amministratore delegato di So.Se, societa\' per gli Studi di Settore. \'\'E\' probabile, anzi e\' certo, che ci sia un recupero dell\'evasione\'\', afferma Brunello il quale precisa che si tratta della \'\'normale attivita\' di revisione degli studi di settore perche\' questo strumento fiscale sia sempre piu\' rappresentativo della realta\' \'\'. Non e\' detto che in tutti i casi venga aumentato il prelievo: \'\'Noi dobbiamo far emergere dove c\'e\' sommerso e invece restituire a quei settori – sottolinea Brunello - dove c\'e\' crisi\'\'.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">L\'amministratore delegato di So.Se ritiene che al momento \'\'non e\' assolutamente possibile ipotizzare\'\' quanto gettito arrivera\' dalla manutenzione di questi studi, anche perche\' - spiega - \'\'non e\' detto che tutti i contribuenti si adegueranno, lo studio di settore non e\' una minimum tax\'\'. In questi giorni gli esperti del So.Se stanno incontrando tutte le categorie interessate.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ma la revisione del prelievo per gli autonomi potrebbe estendersi oltre questa novantina di studi di settore la cui revisione in un certo senso era gia\' programmata. In questi giorni infatti il So.Se sta raccogliendo e archiviando tutti i dati dei controlli svolti nei mesi scorsi dalla <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di Finanza</SPAN></STRONG> per fronteggiare il caro-prezzi. \'\'Se emergera\' qualcosa di particolare ne terremo conto e interverremo secondo la norma di legge\'\', affermo Brunello riferendosi all\'ultima Finanziaria che ha stabilito una revisione degli studi di settore in quei comparti dove si siano verificati aumenti sostanziali dei prezzi. Un lavoro, questo, che dovrebbe giungere ai primi risultati gia\' nel prossimo mese di ottobre.(ANSA). <o:p></o:p></FONT></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2029, 1, 0, '2026-01-14 00:11:07', '0000-00-00'),
(318, '2004-09-13 00:00:00', 'Notizie Flash', 'SOLO NEL PRIMO SEMESTRE 2004 GDF SCOPRE 6,3 MLD IN MENO AL FISCO - 11/9: ACCADEMIA GUARDIA FINANZA DOMANI A GROUND ZERO - IMMIGRAZIONE: COORDINAMENTO POLIZIE ITALIA-SLOVENIA-AUSTRIA', '', 'SOLO NEL PRIMO SEMESTRE 2004 GDF SCOPRE 6,3 MLD IN MENO AL FISCO - 11/9: ACCADEMIA GUARDIA FINANZA DOMANI A GROUND ZERO - IMMIGRAZIONE: COORDINAMENTO POLIZIE ITALIA-SLOVENIA-AUSTRIA', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">FINANZIARIA: LOTTA A EVASIONE, VALE 4 VOLTE MANOVRA <?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">SOLO NEL PRIMO SEMESTRE 2004 GDF SCOPRE 6,3 MLD IN MENO AL FISCO<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 12 SET - Doppi lavori uno dei quali in nero, soggetti del tutto sconosciuti al Fisco, evasione contributiva, affitti in nero sia al mare, sia in citta\', vendita abusiva: la mappa dell\'evasione, su cui il Governo punta per recuperare risorse attraverso la Finanziaria, e\' estremamente ampia ed abbraccia tutte le categorie sociali e, soprattutto, sottrae alle risorse pubbliche una cifra che e\' pari circa a 4 volte la manovra 2005.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Basti pensare che, secondo le ultime proiezioni della Commissione Ue, il 17% del pil italiano sfugge alla tassazione, determinando cosi\' un \'\'vuoto teorico nelle entrate fiscali superiore agli 85 miliardi di euro all\'anno\'\'. Non a caso il Dpef approvato a luglio prevedeva, tra le altre voci, \'\'misure per un piu\' efficace contrasto all\'evasione ed al sommerso, unitamente al perseguimento dell\'azione di semplificazione degli adempimenti tributari\'\'. Ovvero si punta ad abbassare la pressione fiscale e alla semplificazione che, insieme ad una piu\' stringente azione di contrasto, potrebbe far emergere, almeno in parte, questa mole enorme di risorse.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Nel frattempo pero\' l\'azione di contrasto gia\' impostata, e sulla quale il Governo dovrebbe ulteriormente puntare, prosegue a pieno ritmo: l\'imponibile che sfugge all\'Iva, ha di recente scoperto la Direzione Centrale Accertamento dell\'Agenzia delle Entrate, si aggira intorno ai 3 miliardi di euro e i settori piu\' colpiti dall\'evasione fiscale, dalle auto ai telefonini, dai computer alla carne, sarebbero circa una ventina. Proprio su questi soggetti (circa 500) l\'amministrazione fiscale sta operando attraverso blitz simultanei nelle varie regioni, coordinati da un Gruppo AntiFrode nazionale costituito ad hoc. <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Oppure: nel primo semestre del 2004 la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanza</SPAN></STRONG> ha compiuto 35.000 interventi ispettivi e scoperto un\'evasione fiscale per 6,3 miliardi di euro. La cifra risulta dai 5,1 miliardi di euro di base imponibile occultata, e dagli 1,2 miliardi di euro evasi in materia di Iva. In tema di \'sommerso d\'azienda\', sono stati scoperti 4.200 evasori totali, vale a dire il 16% in piu\' rispetto a quelli individuati nello stesso periodo del 2003.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ma nonostante queste attivita\' di contrasto il \'nero\' e\' sempre piu\' \'corposo\': secondo l\'ultimo rapporto Censis, ad esempio grazie solo ad un recupero \'\'minimo\'\' delle risorse sottratte al Fisco, l\'erario potrebbe incassare circa 6 miliardi di euro all\'anno.(ANSA). <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">11/9: ACCADEMIA GUARDIA FINANZA DOMANI A GROUND ZERO<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 10 SET - Gli allievi ufficiali dell\' Accademia della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> parteciperanno alla cerimonia commemorativa prevista domani a New York presso il sito Ground Zero\', in occasione del terzo anniversario dell\' attacco terroristico al World Trade Center.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\' invito e\' stato rivolto da varie autorita\' americane (Governatore dello Stato di New York, Governatore dello Stato di New Jersey e sindaco New York City) ai cadetti in visita di istruzione negli Usa dal 9 al 19 settembre, a conclusione del secondo anno accademico.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Durante la cerimonia, a fianco della Bandiera italiana, 54 allievi ufficiali del 102/o Corso ruolo normale \'Sile II\' e del 1/o Corso ruolo aeronavale \'Sirio\' saranno schierati in uniforme storica su due plotoni, insieme ai reparti delle forze armate statunitensi, della Polizia metropolitana di New York, dei Vigili del Fuoco di New York e degli agenti della Port Authority di New York e New Jersey.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Nel corso della manifestazione saranno osservati quattro distinti \'\'momenti di silenzio\'\', in concomitanza con gli orari dell\' impatto del primo aereo sulla Torre Nord; del secondo aereo sulla Torre Sud; del crollo della Torre Sud; del crollo della Torre Nord. <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Capo della delegazione italiana sara\' l\' ispettore per gli Istituti d\' istruzione della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>, generale Italo Pappa che, insieme al comandante dell\' Accademia, generale Domenico Minervini ed agli altri istruttori accompagnatori, assistera\' all\' evento dal palco riservato alle autorita\'. (ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">IMMIGRAZIONE: COORDINAMENTO POLIZIE ITALIA-SLOVENIA-AUSTRIA<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">LA FIRMA MARTEDI\' PROSSIMO IN AUSTRIA<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - UDINE, 9 SET - I ministeri dell\' Interno di Italia, Slovenia e Austria hanno definito un accordo per la creazione di un centro di cooperazione nel settore della polizia di frontiera che sara\' firmato martedi\' prossimo, 14 settembre, a Torlmaglern, in Austria.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\' intesa prevede l\' attivazione di una struttura nella quale opereranno poliziotti italiani, sloveni e austriaci che potranno usufruire di una sala operativa nella quale affluiranno le notizie riguardanti l\' immigrazione, sia quella clandestina, sia quella regolare. <SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;</SPAN>La sala consentira\' di compiere accertamenti e approfondimenti su un fenomeno che da anni coinvolge in particolare il Friuli-Venezia Giulia. La parte italiana sara\' rappresentata a turno da Polizia di stato, Carabinieri e <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>. (ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 1788, 1, 0, '2026-01-14 06:23:55', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(291, '2004-08-10 00:00:00', 'Dai media', 'LA REPUBBLICA, MARTEDI’ 10 AGOSTO 2004, PAG. 18\r\n“POLIZIOTTI IN PIAZZA. PEZZOTTA: STO CON LORO”\r\nConfermata dai sindacati la protesta di Ferragosto. Cc e Gdf: anche le famiglie in corteo', '', 'LA REPUBBLICA, MARTEDI’ 10 AGOSTO 2004, PAG. 18\r\n“POLIZIOTTI IN PIAZZA. PEZZOTTA: STO CON LORO”\r\nConfermata dai sindacati la protesta di Ferragosto. Cc e Gdf: anche le famiglie in corteo\r\n', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>ROMA – “Massima solidarietà agli uomini e alle donne delle forze di polizia” e “e forte preoccupazione per il rischio di uno squilibrio istituzionale causato da un’azione sbilanciata del governo nel mondo dei lavoratori in divisa”: il segretario della Cisl Savino Pezzotta ha garantito l’adesione alla protesta delle forze di polizia fissata per Ferragosto. Domenica prossima, infatti, mentre il ministro dell’interno, Giuseppe Pisanu, sarà impegnato nella tradizionale visita alle forze dell’ordine, migliaia di poliziotti aderenti ai sindacati Siulp, Sap, Sapaf e Fsp sfileranno, insieme alle famiglie, fin sotto Palazzo Chigi per protestare contro “le mancate promesse del governo”. Con loro anche le mogli dei rappresentanti dei Cocer dei carabinieri e della Finanza, che appoggiano le rivendicazioni, ma che per regolamento non possono sfilare.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>In ballo c’è una lunga trafla per il riallineamento delle carriere e per l’equità dei trattamenti contrattuali. “Dopo 7 anni scendiamo in piazza solo perché costretti da una politica strabica – spiega il segretario del Siulp, Oronzo Cosi – il governo ha proposto a tutti aumenti minimi di stipendio, appellandosi alla difficile situazione economica. Poi, dopo la firma di una pre intesa, ha dimostrato di avere ulteriori risorse , approvando un provvedimento sul riallineamento delle carriere dei marescialli delle forze armate, che comporterà promozioni e vantaggi economici solo per i 55 mila marescialli di esercito, marina e aeronautica”. I sindacati auspicano la costituzione di una commissione alla Camera e al Senato, “per bilanciare lo strapotere della lobby dei generali delle commissioni Difesa”.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN lang=EN-GB style=\"mso-ansi-language: EN-GB\"><FONT size=3><FONT face=\"Times New Roman\">(a.m.l.)<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></SPAN></P>', 0, '', '', '', 6637, 1, 0, '2025-12-26 05:35:51', '0000-00-00'),
(292, '2004-08-10 00:00:00', 'Dai media', 'IL MESSAGGERO, Martedì 10 Agosto 2004, Pagina 8\r\n“POLIZIA, PROTESTE E SIT-IN A FERRAGOSTO”\r\nMigliaia di agenti in piazza a Roma: «Il governo si disinteressa della sicurezza interna»\r\ndi Carlo Mercuri', '', 'IL MESSAGGERO, Martedì 10 Agosto 2004, Pagina 8\r\n“POLIZIA, PROTESTE E SIT-IN A FERRAGOSTO”\r\nMigliaia di agenti in piazza a Roma: «Il governo si disinteressa della sicurezza interna»\r\ndi Carlo Mercuri', '<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">ROMA - E’ il “compromesso storico” della sicurezza. Due sigle sindacali, Siulp e Sap, che da sole raccolgono la stragrande maggioranza dei poliziotti e che tradizionalmente danno voce a diverse tendenze di pensiero, si trovano stavolta insieme su un unico fronte a protestare contro un nemico comune: la disattenzione del governo nei loro confronti. Né sono i soli: accanto a loro ci saranno altre sigle sindacali, ci saranno gli agenti penitenziari del Sappe e i forestali del Sapaf, ci saranno perfino mogli e figli di carabinieri e Guardie di Finanza, in rappresentanza dei loro mariti e padri che non possono uscire allo scoperto. <?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">Sfileranno in migliaia il giorno di Ferragosto. «Abbiamo scelto il 15 agosto - spiega Oronzo Cosi, leader del Siulp - perché è la giornata in cui tradizionalmente il ministro dell’Interno, Giuseppe Pisanu, ringrazia le Forze dell’Ordine per il lavoro di tutto l’anno. Ebbene - dice - noi quei ringraziamenti non li accettiamo, vogliamo che siano mantenuti gli impegni». <o:p></o:p></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">Quali sono gli impegni che il Governo non avrebbe mantenuto? «Prima delle elezioni europee - spiega Cosi - fu raggiunta una pre-intesa contrattuale cui sarebbe dovuto seguire un decreto legge che avrebbe dovuto accogliere le rivendicazioni della Polizia. E’ invece accaduto che il Consiglio dei ministri ha consentito al ministro della Difesa, Antonio Martino, di presentare un emendamento a favore dei 55 mila marescialli delle Forze armate. Ci siamo sentiti quindi traditi e presi in giro rispetto agli impegni assunti».<o:p></o:p></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">Ma l’episodio a cui si riferisce Cosi è solo la goccia che ha fatto traboccare il vaso. In realtà, a sentire i sindacati di Polizia, ciò che dà loro più fastidio è la «militarizzazione strisciante della società», come dice Felice Romano, del Siulp. E’ il fatto che, come affermano da più parti, da qualche tempo a questa parte in Italia si privilegia il “militare” (missioni all’estero, equipaggiamenti, finanche sviluppi di carriera dei professionisti con le stellette) e si trascura il “civile”, che nel caso della Polizia di Stato vuol dire sicurezza delle nostre città e delle nostre strade. «Vanno bene i missili “patriot”, le navi e i parà - chiosa Filippo Saltamartini, segretario del Sap - ma prima dovrebbe venire la salvaguardia dei diritti di sicurezza. Succede invece che si assegnano 900 milioni di euro alle missioni internazionali di pace e i poliziotti sono costretti ad elemosinare i fondi per le automobili, la benzina, le gomme. Noi andiamo all’estero ma poi scopriamo che i terroristi che hanno fatto la strage di Madrid avevano casa in Italia. No, la risposta del Governo alla sicurezza in Italia non ci pare congrua».<o:p></o:p></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">Ma c’è dell’altro. Quello per cui la “Santa Alleanza” delle Forze di Polizia a ordinamento civile a un certo momento si è sentita accerchiata: lo strapotere, a detta dei sindacati dei poliziotti, delle Commissioni Difesa di Camera e Senato. Dice Saltamartini: «Queste commissioni sono composte in parte da militari ed ex militari, i quali tendono a svolgere un ruolo simile a quello dei sindacati nelle contrattazioni collettive. Diventano così il più grande sindacato degli uomini in divisa e sviluppano un’attività contrapposta a quella dei sindacati di Polizia». «Una lobby trasversale di generali - rincara la dose Cosi - che riesce a favorire i militari a scapito delle forze civili». <BR>Il riallineamento delle carriere, come si diceva, ha poi colmato la misura. Sotto questa misteriosa definizione si cela un recente provvedimento che ha riequilibrato lo sviluppo di carriera dei sottufficiali delle Forze armate. Carriera che venne “squilibrata” nel 1995 a favore dei carabinieri, perché si riconosceva loro un percorso professionale un po’ più complicato e impegnativo di quello dei sottufficiali delle Forze armate. Ora è stato tutto riportato in pristino. Con il risultato che carabinieri e sottufficiali delle Forze armate si sono “riallineati” e insieme hanno scavalcato i pari grado della Polizia di Stato, che non hanno potuto beneficiare del provvedimento.<o:p></o:p></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt\">Ecco quindi che cosa sarà la marcia di Ferragosto: la protesta dei “non allineati”, che chiedono lo stesso trattamento degli altri.</SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt; mso-fareast-font-family: \'Times New Roman\'\"><o:p></o:p></SPAN></P>\r\n<P style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><I><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 12.0pt; mso-fareast-font-family: \'Times New Roman\'\">(Carlo Mercuri)<o:p></o:p></SPAN></I></P>', 0, '', '', '', 3916, 1, 0, '2025-12-20 14:49:52', '0000-00-00'),
(293, '2004-08-10 00:00:00', 'Dai media', 'IL MESSAGGERO, Martedì 10 Agosto 2004, Pagina 8\r\nLA MAGGIORANZA: «MANOVRE STRUMENTALI, IL RIORDINO SI FARÀ»\r\nIl responsabile per la sicurezza di An, Ascierto: questo Governo ha stanziato, per le Forze di Polizia, il triplo rispetto a quello di Centrosinis', '', 'IL MESSAGGERO, Martedì 10 Agosto 2004, Pagina 8\r\nLA MAGGIORANZA: «MANOVRE STRUMENTALI, IL RIORDINO SI FARÀ»\r\nIl responsabile per la sicurezza di An, Ascierto: questo Governo ha stanziato, per le Forze di Polizia, il triplo rispetto a quello di Centrosinis', '<P>&nbsp;</P>\r\n<P class=MsoBodyText2 style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=4>ROMA - «Sono le scorie del tremontismo. Basta avere un po’ di pazienza e supereremo anche questo problema». Al ministero dell’Interno non c’è sorpresa per l’annunciata protesta di Ferragosto delle forze di polizia contro il governo. D’altra parte, raccontano, il ministro dell’Interno Beppe Pisanu aveva più volte sollecitato i colleghi di governo ad attuare in blocco gli impegni presi con le forze armate e le forze di polizia.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><FONT size=4>Nulla da fare però contro le articolate argomentazioni ricche di numeri dell’allora ministro dell’Economia. E così subito dopo le elezioni Europee si è data attuazione alla riforma che ha di fatto riallineato le carriere dei militari a quelle delle forze di polizia comportando un esborso per l’erario di 83 milioni di euro. Ora c’è da rispettare l’impegno che lo stesso presidente del Consiglio ha preso di recente con il ministro Pisanu, con il capo della Polizia e, soprattutto, con il vicepresidente del Consiglio Gianfranco Fini, di procedere alla realizzazione della seconda parte dell’accordo che il governo prese prima delle elezioni Europee, il cui costo dovrebbe aggirarsi sui 77 milioni di euro.<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><FONT size=4>Basterà quindi attendere qualche settimana per verificare se nella Finanziaria resterà lo stanziamento promesso e, soprattutto, che passi la legge di riordino delle carriere che dovrebbe porre rimedio agli squilibri denunciati anche ieri dai sindacati di settore. L’accusa lanciata dal sindacato sulla lobby pro-forze armate che si anniderebbe nella commissione Difesa viene respinta da uno dei componenti: «E’ assurdo parlare di lobby - spiega l’azzurro Gregorio Fontana - abbiamo fatto il riallineamento delle carriere e ora procederemo al riordino sanando eventuali squilibri».<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><FONT size=4>Più secca la risposta di un altro componente della commissione Difesa. «A settembre partiremo con il riordino delle carriere delle forze dell’ordine», assicura il deputato di An Filippo Ascierto che da buon maresciallo dei carabinieri in aspettativa non può non avere sensibilità per le questioni sollevate da Sap e Siulp. «Toglieremo le disparità di trattamento che sono emerse dopo il provvedimento di riallineamento dei marescialli delle forze armate. Vorrei sottolineare - assicura Ascierto - che è la prima volta che un governo stanzia i soldi prima che ci sia la legge. Sono disponibili 77 milioni per quest’anno e 160 per il prossimo. Su questo punto An è fermissima e l’impegno di Fini è netto. Noi faremo un riordino complessivo e non a macchia di leopardo come vorrebbe il Siulp. L’impegno nostro c’è e mi sembra strumentale sollevare questa protesta proprio ora, visto che questo governo ha stanziato il triplo per le forze di polizia. Inoltre vorrei che i sindacati guardino all’interno delle proprie amministrazioni per chiedersi perchè il riordino non è ancora partito».<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><FONT size=4>Malgrado le rassicurazioni, resta altissimo il grado di sospettosità dei sindacati delle forze dell’ordine. Il Siulp «non si fida più delle promesse che spostano alla prossima Finanziaria la soluzione di problemi che già da tempo andavano risolti. Quello che non possiamo accettare è la falsissima affermazione che questo Governo abbia stanziato, per gli operatori delle Forze di Polizia ”tre volte di più rispetto a quelli precedenti per stipendi ed altro”».<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><FONT size=4>La polemica resta quindi rovente anche perchè il fronte è compatto e a fianco del Siulp c’è il Sap e, sigla vicina alle forze della maggioranza, e ha l\'appoggio dei familiari dei carabinieri e dei finanzieri. A settembre, tra le maglie di un bilancio statale non particolarmente brillante, si dovranno quindi trovare le risorse per le forze di polizia.<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><I><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><FONT size=4>(Ma.Con.)<o:p></o:p></FONT></FONT></SPAN></I></P>\r\n<P><FONT size=4></FONT>&nbsp;</P>', 0, '', '', '', 3307, 1, 0, '2025-11-22 00:20:46', '0000-00-00'),
(294, '2004-08-11 00:00:00', 'Documenti', 'DAL FORUM DI FICIESSE – PROVVEDIMENTO DEL GARANTE DELLA PRIVACY – E’ ILLECITA L’UTILIZZAZIONE DI ELENCHI CON DICITURE NON INDISPENSABILI CHE RIVELINO LO STATO DI SALUTE DEI DIPENDENTI', '', 'DAL FORUM DI FICIESSE – PROVVEDIMENTO DEL GARANTE DELLA PRIVACY – E’ ILLECITA L’UTILIZZAZIONE DI ELENCHI CON DICITURE NON INDISPENSABILI CHE RIVELINO LO STATO DI SALUTE DEI DIPENDENTI', '<H1 style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black\"><FONT face=\"Times New Roman\" size=3>Con due post pubblicati ieri e oggi sul forum di Ficiesse (</FONT><A href=\"http://www.ficiesse.it/\"><FONT face=\"Times New Roman\" color=#800080 size=3>www.ficiesse.it</FONT></A><FONT face=\"Times New Roman\"><FONT size=3>) è stata data notizia del provvedimento del Garante della privacy che riportiamo di seguito, pubblicato sul sito internet della medesima Autorità.</FONT></FONT></SPAN></STRONG></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\"><FONT size=3>&nbsp;<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></P>\r\n<P style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-FAMILY: \'Times New Roman\'; mso-fareast-font-family: \'Times New Roman\'\"><FONT size=3>&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<H1 style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><STRONG><I><U><SPAN style=\"COLOR: black\"><FONT size=3><FONT face=\"Times New Roman\">DAL SITO DEL GARANTE DELLA PRIVACY<o:p></o:p></FONT></FONT></SPAN></U></I></STRONG></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><FONT size=3><FONT face=\"Times New Roman\"><STRONG><I><U><SPAN style=\"COLOR: black\">NEWSLETTER N. 223 DEL 26 LUGLIO – 1° AGOSTO 2004</SPAN></U></I></STRONG><B><I><U><o:p></o:p></U></I></B></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><B><I><U><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></U></I></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<H1 style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><SPAN style=\"COLOR: black\"><FONT size=3><FONT face=\"Times New Roman\">Militari in malattia e accessi alle caserme<o:p></o:p></FONT></FONT></SPAN></STRONG></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><I><FONT size=3><FONT face=\"Times New Roman\">Informazioni sulla salute non indispensabili. Il Garante interrompe il trattamento dei dati.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Con un provvedimento adottato nei confronti del Comando regionale di una forza di polizia il Garante ha vietato un illecito trattamento di informazioni sulla salute di alcuni militari. Al Comando è stato imposto di interrompere ogni operazione e di limitarsi alla sola conservazione dei dati.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Lo ha stabilito il Garante accogliendo i ricorsi di alcuni sottufficiali che lamentavano una violazione della privacy ogni volta che, in licenza per malattia o in aspettativa, recandosi in caserma trovavano all\'ingresso un elenco con i nomi di tutti i militari corredati dalle varie cause dell\'assenza. In particolare, accanto ai nominativi dei militari temporaneamente assenti dal servizio che l\'amministrazione comunica al responsabile che disciplina gli ingressi in caserma, compariva la dicitura \"in convalescenza\" o \"in aspettativa\". Queste informazioni, anche senza l\'indicazione di particolari patologie, sono in grado di rivelare lo stato di salute. Per lo scopo perseguito è sufficiente un elenco con i nominativi privo di altre indicazioni.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Dopo una prima istanza rivolta all\'amministrazione nella quale si contestava la presenza di tali diciture che a parere dei sottufficiali costituiscono un illecito trattamento di dati sulla salute, insoddisfatti della risposta ricevuta, hanno presentato separati ricorsi al Garante. I militari non contestavano le misure di sicurezza, ma si opponevano al tipo di procedura adottata dall\'amministrazione ritenuta lesiva della privacy chiedendone l\'interruzione.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Il Garante ha dato loro ragione e ha, dunque, disposto l\'interruzione del trattamento illegittimo dei dati.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Contrariamente a quanto sostenuto dal Comando regionale, infatti, l\'indicazione del dato relativo all\'assenza per \"convalescenza\" dà luogo ad un trattamento di dati sensibili dal momento che questa informazione è come detto in grado di rivelare lo stato di salute del dipendente e non risulta, peraltro, indispensabile. Con l\'introduzione del nuovo Codice i soggetti pubblici per svolgere attività istituzionali devono&nbsp;poi rispettare il principi di necessità: devono, cioè, impiegare solo dati indispensabili, riducendo al minimo l\'utilizzazione di dati personali ed identificativi quando le finalità perseguite possono essere altrimenti raggiunte, come nel caso in esame. Per disciplinare l\'ingresso dei militari che si assentano dal servizio è sufficiente indicare solo i loro nomi, senza menzionare espressamente la ragione di tale assenza attinente allo stato di salute. Ed anche se l\'amministrazione non ha ancora adottato un regolamento in cui specificare i tipi di dati trattati, le operazioni eseguibili, le finalità perseguite, essa deve comunque attenersi a tale principio, altrimenti il trattamento è illecito.</FONT></P>', 0, '', '', '', 5463, 1, 0, '2026-01-09 21:23:13', '0000-00-00'),
(295, '2004-08-11 00:00:00', 'Notizie Flash', 'LIBONI: IL CARABINIERE CHE LO HA UCCISO, “HO PREGATO PER LUI” - INTERVISTA A \'GENTE\', HO SPARATO SOLO PERCHE\' E\' STATO NECESSARIO', '', 'LIBONI: IL CARABINIERE CHE LO HA UCCISO, “HO PREGATO PER LUI” - INTERVISTA A \'GENTE\', HO SPARATO SOLO PERCHE\' E\' STATO NECESSARIO', '<PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;<PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 10 AGO - \'\'L\' ho ucciso e poi ho pregato per<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">lui\'\'. A parlare e\' Alessandro Palmas, 29 anni, il carabiniere<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">che ha fermato a fuga di Luciano Liboni, in un\' intervista al<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">settimanale \'Gente\', da domani in edicola.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Il bandito - racconta Palmas - puntava la pistola contro l\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ostaggio, era fuori di se\'. Urlava: \'Le faccio un buco in testa,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">vi ammazzo tutti\'. Non avevo scelta, ho sparato e l\' ho ucciso.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Alla fine pero\' mi e\' rimasto un profondo dispiacere per quel<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">disgraziato. Da cristiano non avrei mai voluto arrivare a un<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">simile epilogo. Subito dopo sono andato in Chiesa e ho recitato<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">una preghiera anche per lui\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Ho fatto tutto il possibile - aggiunge il carabiniere - per<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">evitare che finisse in tragedia, appoggiando addirittura la<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">pistola a terra. Ho sparato solo perche\' e\' stato necessario.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Si\', ho la coscienza a posto\'\'. (ANSA).<o:p></o:p></SPAN></PRE></SPAN></SPAN></PRE>', 0, '', '', '', 5093, 1, 0, '2026-01-13 22:14:41', '0000-00-00'),
(296, '2004-08-11 00:00:00', 'Notizie Flash', 'POLIZIA: GOVERNO CONVOCA SINDACATI PER DOMANI\r\nDOPO ANNUNCIO MOBILITAZIONE FERRAGOSTO\r\nMINNITI (DS), GIUSTA LA PROTESTA DI FERRAGOSTO\r\nGOVERNO NON RISPETTA GLI IMPEGNI, SOSTEGNO A SINDACATI', '', 'POLIZIA: GOVERNO CONVOCA SINDACATI PER DOMANI\r\nDOPO ANNUNCIO MOBILITAZIONE FERRAGOSTO', '<PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 10 AGO - Convocazione urgente per domani<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">mattina alle 11 dei sindacati di polizia da parte del Governo.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Lo fanno sapere i segretari di Sap, Filippo Saltamartini e<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Siulp, Oronzo Cosi, che insieme ad altre sigle hanno annunciato<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">la mobilitazione di Ferragosto contro il Governo.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Siamo soddisfatti per la convocazione - spiegano<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Saltamartini e Cosi - si tratta di un risultato molto<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">importante: domani vedremo cosa ci proporranno\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Al tavolo, per conto del Governo, dovrebbe esserci il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ministro dell\' Interno, Giuseppe Pisanu, il sottosegretario alla<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Funzione pubblica, Learco Saporito e rappresentanti del<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ministero del Tesoro.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ieri, in una conferenza stampa, i sindacati avevano promesso<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">una mobilitazione di migliaia di persone per domenica prossima<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">fin sotto Palazzo Chigi, per protestare contro la politica del<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Governo che \'\'ha dimostrato un forte sbilanciamento a favore<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">delle forze armate e in danno delle forze di polizia\'\'. (ANSA).<o:p></o:p></SPAN></PRE>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;</P><PRE><B><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">POLIZIA: MINNITI (DS), GIUSTA LA PROTESTA DI FERRAGOSTO<o:p></o:p></SPAN></B></PRE><PRE><B><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">GOVERNO NON RISPETTA GLI IMPEGNI, SOSTEGNO A SINDACATI<o:p></o:p></SPAN></B></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 10 AGO - \'\'La manifestazione di protesta che<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">stanno organizzando per ferragosto i sindacati di polizia e\' una<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">iniziativa giusta, ma segnala anche un momento di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">preoccupazione\'\'. Lo afferma Marco Minniti, responsabile Ds per<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">i problemi dello Stato.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Il comparto sicurezza - continua Minniti - e\' stato<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">fortemente trascurato, ed e\' attraversato da un evidente<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">malessere. Mancano i soldi, manca una politica. Il Governo non<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">rispetta neppure gli impegni presi solennemente\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Da tempo abbiamo lanciato l\' allarme - conclude l\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">esponente dei Ds - e su tutte le questioni oggi rivendicate dai<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sindacati e dalle rappresentanze del comparto, ai quali va il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">nostro sostegno, abbiamo sviluppato una puntuale e coerente<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">battaglia parlamentare che siamo decisi a continuare\'\'. (ANSA).<o:p></o:p></SPAN></PRE><o:p></o:p></FONT></FONT>', 0, '', '', '', 1121, 1, 0, '2026-01-13 22:14:42', '0000-00-00'),
(297, '2004-08-11 00:00:00', 'Notizie Flash', 'FORZE ARMATE: MILITARI ASSENTI? NON SI PUO\' SCRIVERE CAUSA\r\nGARANTE PRIVACY, INFORMAZIONI SU SALUTE NON INDISPENSABILI', '', 'FORZE ARMATE: MILITARI ASSENTI? NON SI PUO\' SCRIVERE CAUSA\r\nGARANTE PRIVACY, INFORMAZIONI SU SALUTE NON INDISPENSABILI', '<PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;</SPAN><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 10 AGO - Militari assenti dal servizio? Non si<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">puo\' indicare la causa scrivendola accanto al nome all\' ingresso<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">delle caserme.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Lo ha stabilito il Garante della privacy, che ha<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">accolto i ricorsi di alcuni sottufficiali che lamentavano una<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">violazione della privacy ogni volta che, in licenza per malattia<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">o in aspettativa, tornando nella caserma di un comando regionale<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">trovavano all\' ingresso un elenco con i nomi di tutti i militari<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">corredati dalle varie cause dell\' assenza.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>In particolare, si spiega nella newsletter del Garante,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">accanto ai nominativi dei militari temporaneamente assenti dal<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">servizio che l\' amministrazione comunica al responsabile che<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">disciplina gli ingressi in caserma, compariva la dicitura \'\'in<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">convalescenza\'\' o \'\'in aspettativa\'\'. Queste informazioni, anche<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">senza l\' indicazione di particolari patologie, secondo il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Garante, \'\'sono in grado di rivelare lo stato di salute. Per lo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">scopo perseguito e\' sufficiente un elenco con i nominativi privo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">di altre indicazioni\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Dopo una prima istanza rivolta al Comando, nella quale si<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">contestava la presenza delle diciture che, a parere dei<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sottufficiali, costituiscono un illecito trattamento di dati<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sulla salute, insoddisfatti della risposta ricevuta, hanno<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">presentato separati ricorsi al Garante. I militari non<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">contestavano le misure di sicurezza, ma si opponevano al tipo di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">procedura adottata dall\' amministrazione, ritenuta lesiva della<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">privacy, chiedendone l\' interruzione. Il Garante ha dato loro<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ragione e ha, dunque, disposto l\' interruzione del trattamento<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">illegittimo dei dati.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Contrariamente a quanto sostenuto dal Comando regionale,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">infatti, rileva l\' Autorita\', l\' indicazione del dato relativo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">all\' assenza per \'\'convalescenza\'\' da\' luogo ad un trattamento<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">di dati sensibili, dal momento che questa informazione e\' in<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">grado di rivelare lo stato di salute del dipendente e non<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">risulta, peraltro, indispensabile.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Con l\' introduzione del nuovo Codice, prosegue il Garante, i<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">soggetti pubblici devono rispettare il principi di necessita\':<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">devono, cioe\', impiegare solo dati indispensabili, riducendo al<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">minimo l\' utilizzazione di dati personali ed identificativi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">quando le finalita\' perseguite possono essere altrimenti<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">raggiunte, come nel caso in esame. Per disciplinare l\' ingresso<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dei militari che si assentano dal servizio, dunque, e\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sufficiente indicare solo i loro nomi, senza menzionare<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">espressamente la ragione dell\' assenza riguardante lo stato di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">salute. Ed anche se l\' amministrazione non ha ancora adottato un<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">regolamento in cui specificare i tipi di dati trattati, le<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">operazioni eseguibili, le finalita\' perseguite, essa deve<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">comunque attenersi al principio, altrimenti il trattamento e\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 12pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">illecito. (ANSA).<o:p></o:p></SPAN></PRE>', 0, '', '', '', 1244, 1, 0, '2026-01-01 16:23:15', '0000-00-00'),
(298, '2004-08-12 00:00:00', 'Dai media', 'GAZZETTA DEL SUD, GIOVEDI’ 12 AGOSTO 2004\r\n“RICHIESTE ACCOLTE, LA POLIZIA REVOCA IL CORTEO”\r\n', '', 'GAZZETTA DEL SUD, GIOVEDI’ 12 AGOSTO 2004\r\n“RICHIESTE ACCOLTE, LA POLIZIA REVOCA IL CORTEO”\r\n', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><I><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Pisanu: «dimostrato un alto senso di responsabilità»<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></I></STRONG></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><I><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 14pt; mso-bidi-font-size: 13.5pt\"><FONT face=\"Times New Roman\">Trovato ieri mattina un accordo fra governo e sindacati: la manifestazione di Ferragosto è stata annullata<o:p></o:p></FONT></SPAN></I></STRONG></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"mso-bidi-font-size: 13.5pt\"><FONT size=3><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>ROMA – Il ministro dell\'Interno, Giuseppe Pisanu, potrà svolgere serenamente la tradizionale visita alle questure di Ferragosto. Senza che la contemporanea protesta di un migliaio di poliziotti sotto Palazzo Chigi gli guasti la giornata.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Nella riunione di ieri mattina, convocata con urgenza lunedì sera, è stato trovato infatti un accordo tra il Governo ed i sindacati che avevano organizzato la mobilitazione (Siulp, Sap, Sappe, Sapaf). In sostanza, secondo quanto riferiscono gli stessi sindacati, «sono state accolte tutte le nostre richieste, quindi revochiamo la manifestazione». Pisanu parla di «buon accordo che risponde positivamente a legittime richieste degli operatori della polizia e ad esigenze più vaste del sistema di sicurezza nazionale». Ed un impulso alla soluzione del contrasto è arrivato anche dal presidente del Consiglio, Silvio Berlusconi, che ha invitato le parti al dialogo. Un invito formulato attraverso il sottosegretario del Consiglio, Gianni Letta ed il ministro Pisanu. Del resto, in un momento così delicato sul fronte delle minacce terroristiche, una protesta agostana di parte delle forze dell\'ordine non avrebbe raccolto soltanto consensi. Scade proprio a Ferragosto, infatti, l\'ultimatum all\'Italia lanciato dalle Brigate Abu Hafs al Masri e le misure di vigilanza sono ovunque al massimo. In relazione a ciò, Pisanu ha spiegato che «ancora una volta i sindacati di polizia hanno dimostrato un alto senso di responsabilità facendosi carico anche della situazione generale del Paese». </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Quattro i punti cardine dell\'intesa raggiunta. Il Governo, accogliendo le richieste in merito dei sindacati, si è impegnato a varare con urgenza un decreto legge che dia agli ex sottufficiali gli stessi benefici dati recentemente a 55.000 marescialli delle forze armate; alla ridefinizione del comparto contrattuale che preveda un doppio binario per il comparto Sicurezza e quella Difesa, in modo da riconoscerne le specificità; a lavorare all\'istituzione di una commissione Interni alla Camera ed al Senato; all\'apertura, entro breve, di un tavolo al Viminale per la definizione di un riordino dei ruoli e delle carriere delle forze di polizia che attinga anche a fondi reperibili nella prossima Finanziaria. Tra le quattro sigle che avevano organizzato la protesta di Ferragosto l\'esultanza è unanime.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\" size=3>Per il segretario del Siulp, Oronzo Cosi, «ha trionfato il buon senso ed è stata resa giustizia agli operatori di polizia: sono state accolte, per essere state ritenute giuste, tutte le nostre rivendicazioni». Soddisfatto anche il segretario del Sap, Filippo Saltamartini, che parla di «svolta nell\'interesse del Paese e della sicurezza. Si avvia finalmente una nuova fase nelle relazioni sindacali, con la forza e la determinazione che la Consulta Sicurezza è stata capace di produrre». Il Sappe sottolinea che «come il sottosegretario Letta al termine della riunione ha affermato che “l\'incontro di oggi è stata la riprova che sulle forze dell\'ordine si può fare sempre affidamento perché sono forze sane e responsabili”, così noi possiamo certamente affermare che finalmente c\'è una prova reale e tangibile dell\'attenzione del Governo verso le forze dell\'ordine». Concetto analogo è stato espresso dalla Federazione Consap-Italia Sicura. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><I><FONT size=3><FONT face=\"Times New Roman\">(Giovanni Panetta)<o:p></o:p></FONT></FONT></I></P>', 0, '', '', '', 7106, 1, 0, '2026-01-01 16:23:15', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(299, '2004-08-12 00:00:00', 'Dai media', 'L’ARENA DI VERONA, GIOVEDÌ 12 AGOSTO 2004\r\n“REVOCATO IL CORTEO DEI POLIZIOTTI”', '', 'L’ARENA DI VERONA, GIOVEDÌ 12 AGOSTO 2004\r\n“REVOCATO IL CORTEO DEI POLIZIOTTI”', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\"><FONT size=4><B><I><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; mso-bidi-font-size: 12.0pt\">Interviene il presidente del Consiglio, scongiurata la protesta del 15 agosto</SPAN></I></B><B><I><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; mso-bidi-font-size: 10.0pt\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></I></B></FONT></FONT></P>\r\n<H1 style=\"MARGIN: 0cm 0cm 0pt\"><EM><FONT face=\"Times New Roman\" size=4>Il Viminale accoglie le richieste dei sindacati. In arrivo un decreto</FONT></EM></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><FONT size=4>&nbsp;<o:p></o:p></FONT></FONT></SPAN></B></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=4>Roma. I poliziotti italiani non scenderanno in piazza a Roma il 15 agosto per sostenere le loro rivendicazioni salariali. La manifestazione nazionale (un vero e proprio sciopero in pieno allarme terrorismo) è stata evitata dal governo che ieri in un incontro al Viminale con i dirigenti dei sindacati delle forze di polizia, presenti il ministro dell’Interno Giuseppe Pisanu ed i sottosegretari Gianni Letta e Learco Saporito, ha promesso che le loro richieste saranno accolte e verrà varato un decreto legge. È stato il presidente del Consiglio Silvio Berlusconi, si è poi appreso, ad intervenire presso il ministro dell’Interno ed il sottosegretario Letta per sollecitare il massimo impegno per risolvere positivamente la vertenza in atto con gli agenti di polizia. I sindacati hanno così accolto l’invito al dialogo rivolto loro dal presidente del consiglio.</FONT></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=4>A determinare lo sbocco positivo della vertenza è stata senz’altro da parte delle organizzazioni sindacali della polizia la consapevolezza dell’inopportunità di una manifestazione di protesta mentre in tutta Italia è scattata la massima vigilanza per il timore di attentati da parte degli estremisti islamici legati ad al-Qaida, tornata anche ieri a lanciare l’ultimatum. Il ministro Pisanu ha elogiato il senso di responsabilità dimostrato dai sindacati di polizia ed ha definito un buon accordo quello raggiunto.</FONT></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=4>La mobilitazione generale degli agenti di polizia è stata così scongiurata con la soddisfazione di tutti i sindacati di categoria. Il governo ha in pratica accolto le richieste dei sindacati su quattro punti importanti della vertenza: il riequilibrio delle sperequazioni (rispetto ai marescialli di carriera militare) a danno degli ispettori di polizia; il ministro dell’Interno si è impegnato ad aprire un tavolo tecnico per avviare a soluzione due problemi; il riordino dei ruoli del personale di polizia e la contrattualizzazione della dirigenza; il governo ha promesso anche di promuovere la ricostituzione della Commissione Interni della Camera dei Deputati; infine, si è impegnato ad avviare la separazione contrattuale dei comparti Difesa e Sicurezza. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><FONT size=4>L’incontro al Viminale si è concluso con la soddisfazione di tutti. Per il segretario del Siulp, Oronzo Cosi, il governo ha capito quelle che erano le esigenze della categoria ed ha promesso di riequilibrare le sperequazioni denunciate. È stata una svolta nell’interesse del Paese e della sicurezza, ha commentato il segretario del Sap Filippo Saltamartini, perché da parte del governo ha prevalso «la parte più sensibile alle istanze istituzionali».<o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\" size=4>Per Claudio Giardullo (Silp-Cgil) è importante l’impegno assunto dal governo ma restano ancora da sciogliere dei nodi fondamentali su cui, ha spiegato, non sono state fornite assicurazioni essendo assente il rappresentante del ministero dell’Economia. Il governo, comunque, ha reso noto il Sappe, si è impegnato a trovare una soluzione a tutte le questioni poste.</FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><I><FONT size=3><FONT face=\"Times New Roman\">(e.s.) <o:p></o:p></FONT></FONT></I></P>', 0, '', '', '', 7618, 1, 0, '2026-01-13 22:14:42', '0000-00-00'),
(300, '2004-08-13 00:00:00', 'Dai media', 'IL GIORNALE DI VICENZA, Venerdì 13 Agosto 2004\r\nDEFICIT, SINISCALCO CERCA NUOVE VIE\r\nIl ministro dell’Economia pensa di rifarsi all’esperienza inglese per contenere le spese.\r\nMentre Tremonti fa sapere «di non aver mai truccato i conti»\r\n(Paolo Tavella)\r\n', '', 'IL GIORNALE DI VICENZA, Venerdì 13 Agosto 2004\r\nDEFICIT, SINISCALCO CERCA NUOVE VIE\r\nIl ministro dell’Economia pensa di rifarsi all’esperienza inglese per contenere le spese.\r\nMentre Tremonti fa sapere «di non aver mai truccato i conti»\r\n(Paolo Tavella)\r\n', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\"><B><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">Roma. </SPAN></B><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\">Giulio Tremonti fa sapere che non ha mai truccato conti in vita sua. Domenico Siniscalco studia un nuovo approccio per contenere la spesa pubblica, in vista della Finanziaria. Attraverso interventi indiretti su quotidiani e settimanali, il ministro in carica dell’Economia e il suo predecessore mettono a confronto a distanza due filosofie diverse in materia di economia. Tremonti elude ogni intervista, ma fa trapelare la sua amarezza, ancora non sopita a un mese e mezzo dalla sua brusca defenestrazione dalla potrona di via XX settembre, per le accuse di aver tenuta nascosta la reale entità del disavanzo. Realtà diventata pubblica con la necessità, a carico del suo successore, di trovare 24 miliardi per tenere a galla i conti pubblici.<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Siniscalco non vuole fare polemiche, del resto a via XX settembre per i tre anni della gestione Tremonti anche lui ricopriva il ruolo non secondario di direttore generale. Ma propone nuove strade rispetto alla celebre «finanza creativa» attribuita al suo illustre e discusso predecessore. Batte in particolare la strada inglese, messa a punto negli anni da Gordon Brown e Tony Blair. Si tratta in sostanza di prendere la spesa dell’anno precedente e fissare in accordo con i ministri interessati precisi paletti di crescita. I singoli ministri saranno chiamati a scegliere e motivare gli interventi da finanziare entro precisi tetti di spesa. In inglese questo metodo si chiama ’spending rewiw’ e di fatto capovolge l’antica usanza italiana di partire dagli aumenti tendenziali di spesa e procedere poi ai tagli necessari. Una consuetudine abbandonata, circa dieci anni fa, solo da Carlo Azeglio Ciampi all’epoca presidente del Consiglio.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Non si tratta come si vede di una sconfessione diretta del metodo Tremonti, ma di certo ne rappresenta una chiara evoluzione. Tremonti per quel che lo riguarda già guarda avanti, a un futuro che lo vedrà impegnato a scrivere (riprenderà la sua attività di commentatore del Corriere della Sera) e ad interessarsi agli aspetti istituzionali più che a quelli economici. Tra le sue mosse ci dovrebbe essere anche il trasloco dalla Commissione Finanze di Montecitorio, dove era assegnato prima dell’incarico di ministro, a quella Affari Costituzionali. Ma prima di lanciarsi nella sua nuova vita Tremonti un’ultima occhiata indietro la getta. «Non ho mai truccato conti. Del resto il tempo è galantuomo e certe accuse si scioglieranno al sole quando si vedrà che nel 2004 il famoso criterio del 3% sarà pienamente rispettato».<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">(<I>Paolo Tavella)<o:p></o:p></I></FONT></SPAN></P>', 0, '', '', '', 6713, 1, 0, '2026-01-13 22:14:43', '0000-00-00'),
(301, '2004-08-13 00:00:00', 'Dai media', 'LA PADANIA - 13 AGOSTO 2004\r\n“A SETTEMBRE IL FEDERALISMO FISCALE”\r\nVitaletti: 50% dell’Iva alle Regioni, ai Comuni le tasse sull’auto', '', 'LA PADANIA - 13 AGOSTO 2004\r\n“A SETTEMBRE IL FEDERALISMO FISCALE”\r\nVitaletti: 50% dell’Iva alle Regioni, ai Comuni le tasse sull’auto', '<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=5>Alle Regioni le imposte sui servizi, a partire da quelle su sigarette e giochi, e più del 50% dell’Iva, a Province e Comuni quelle sull\'auto e sugli immobili, a partire dal bollo e dalla benzina, con l’Ici trasformata e ridotta e il trasferimento dell’Irpef sulle seconde case e sugli affitti. Sono queste alcune delle ipotesi a cui sta lavorando l’Alta commissione sul federalismo fiscale guidata dall’economista Giuseppe Vitaletti che saranno presentate a settembre.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 6.5pt\"><FONT face=\"Times New Roman\">Il federalismo fiscale -premette Vitaletti- è l\'unica possibilità di responsabilizzare la spesa degli enti locali. Il fatto che molti enti spendono male le loro risorse, come emerge anche da inchieste giornalistiche, è anche il risultato della mancata realizzazione di un effettivo federalismo fiscale. Nel nostro Paese va ancora avanti un sistema basato su due criteri sbagliati: trasferimenti che hanno la sola giustificazione di spese storiche e un processo di attribuzione di entrate proprie senza nessuna correlazione con le funzioni di spesa svolte e del tutto sbagliato. Per esempio alle regioni è stata data l’Irap pagata dalle aziende per finanziare la sanità che è un servizio ai cittadini. Le stesse regioni incassano il bollo auto, ma si tratta dell’ente che meno ha a che fare con gli autoveicoli. Mentre la gran parte dei servizi comunali finiscono con l’essere pagati con una imposta patrimoniale sugli immobili».<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 6.5pt\"><FONT face=\"Times New Roman\">Una situazione, dunque, ad avviso di Vitaletti, che «ignora i principi fondamentali della finanza locale» che invece sono stati richiamati fortemente nel documento sui meccanismi strutturali del federalismo fiscale varato da comuni, province e regioni nel 2003 e che sono alla base dei lavori dell’Alta commissione.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 6.5pt\"><FONT face=\"Times New Roman\">E cioè il principio di correlazione tra entrate e spese degli enti. Si tratta -sottolinea il presidente dell’Alta commissione sul federalismo fiscale- di ribaltare l\'attuale meccanismo basato sull\'anticorrelazione e sui trasferimenti secondo le piogge del passato. Quindi le diverse ipotesi che presenteremo entro il 30 settembre su come basare le entrate proprie e le compartecipazioni fiscali partono da un lato dal principio di correlazione tra spesa e entrate e dall’altro da una perequazione responsabilizzante basata su un livello di spesa standard ed efficiente e non più storica».<BR>Insomma, rimarca Vitaletti, «la perequazione deve dare agli enti più poveri i soldi necessari ad amministrare con efficienza. Chi non sarà in grado di amministrare con efficienza dovrà ricorre ad addizionali e aggravi di prelievo locali». Secondo l’economista, comunque, la legge 56/2000 per la perequazione alle regioni «che ha tentato di superare i meccanismi del passato fallendo completamente lo scopo potrà essere trasformata secondo i principi di correlazione ed efficienza».<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 6.5pt\"><FONT face=\"Times New Roman\">Ma come potrebbe tradursi in pratica il principio di correlazione nell’individuazione delle risorse da attribuire ai vari livelli di governo locale? Vediamo alcune delle ipotesi a cui sta lavorando l’Alta commissione per regioni, province e comuni.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 6.5pt\"><FONT face=\"Times New Roman\">REGIONI: nella ipotesi estrema di applicazione del principio di correlazione, alle regioni potrebbe andare oltre il 50% dell’Iva calcolata a livello locale sulla base delle vendite al consumo registrate fiscalmente. E non sulla base di indici statistici e della popolazione residente come avviene ora. Accanto all’Iva potrebbe andare alle regioni l’imposta sui servizi a partire dai prelievi sui cosiddetti vizi: e cioè alcol, tabacco e giochi. Inoltre alle regioni andranno le imposte di bollo, imposta su spettacoli e concessioni e le imposte sulle assicurazioni diverse da quelle sull\'auto, L’addizionale Irpef inoltre potrebbe essere trasformata in una sovrimposta sull\'Irpef statale mantenendo cosi la sua progressività anche a livello locale. Per contro le regioni perderebbero il 50% dell’Irap che torna allo Stato e le imposte su bollo auto e la compartecipazione sulla benzina che andrebbero a province e comuni.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 6.5pt\"><FONT face=\"Times New Roman\">PROVINCE E COMUNI: ai due enti andrebbe tutto il prelievo relativo agli autoveicoli e agli immobili. Le modalità di ripartizione tra i due enti sono ancora da definire comunque alle province potrebbe andare il bollo auto e la compartecipazione dell’imposta sulla benzina attualmente delle regioni. Inoltre manterrebbero l’imposta di trascrizione sugli autoveicoli e quella sulle assicurazioni auto.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 6.5pt\"><FONT face=\"Times New Roman\">Ai comuni invece oltre all’Ici, che verrebbe però ridimensionata perdendo la caratteristica di imposta patrimoniale per essere commisurata al reddito e trasformata in imposta legata ai servizi a favore degli immobili, potrebbe essere assegnato il residuo prelievo Irpef sui redditi degli immobili che colpisce gli affitti, le seconde case, ecc. Il prelievo comunque sarebbe trasformato in una imposta sostitutiva. L’addizionale Irpef anche per i comuni verrebbe trasformata in una sovrimposta.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 6.5pt\"><FONT face=\"Times New Roman\">PEREQUAZIONE: il meccanismo di perequazione che sostituirà gli attuali trasferimenti sarà completamente rivoluzionato. Il punto di partenza non sarà più la spesa storica dell’ente, ma un criterio di “spesa standard ed efficiente” legato ai servizi da fornire. Si stabilirà cioè i servizi essenziali da garantire, il costo necessario in presenza di una amministrazione efficiente e quindi il livello di trasferimenti perequativi necessari dopo aver verificato le risorse proprie dell’ente sulla base dei tributi gestiti direttamente.</FONT></SPAN></P>', 0, '', '', '', 6038, 1, 0, '2026-01-13 22:14:43', '0000-00-00'),
(302, '2004-08-13 00:00:00', 'Dai media', 'IL MANIFESTO - 13 AGOSTO 2004 - PAGINA 7\r\n“SCERIFFI A ROMA. CONTRO DUE TURISTE”\r\nDue giovani statunitensi malmenate nella notte dalla polizia e arrestate. Il «pubblico»: «Hanno fatto bene»\r\n(Cinzia Gubbini)', '', 'IL MANIFESTO - 13 AGOSTO 2004 - PAGINA 7\r\n“SCERIFFI A ROMA. CONTRO DUE TURISTE”\r\nDue giovani statunitensi malmenate nella notte dalla polizia e arrestate. Il «pubblico»: «Hanno fatto bene»\r\n(Cinzia Gubbini)', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\"><SPAN class=luogo1><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">ROMA - </SPAN></SPAN><SPAN class=testo1><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">Ore 23,30 di mercoledì. Uscita dal </SPAN></SPAN><SPAN class=testo1><I><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">manifesto</SPAN></I></SPAN><SPAN class=testo1><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">, salgo sul motorino per raggiungere degli amici. Su via Cavour, davanti al bar indiano </SPAN></SPAN><SPAN class=testo1><I><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">Sitar</SPAN></I></SPAN><SPAN class=testo1><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">, noto un assembramento di persone, un taxi con le portiere aperte e un\'auto della polizia. Ma soprattutto due poliziotti che strattonano una ragazza. Mi fermo. Vicino alla portiera c\'è un\'altra ragazza a terra. Il confronto tra gli agenti e la prima ragazza è violento: loro mollano ceffoni, lei si difende con calci e gomitate. L\'unica cosa chiara è che le due sono amiche e che sono ubriache o molto agitate. La prima ragazza insulta gli agenti, sembra americana o inglese: «</SPAN></SPAN><SPAN class=testo1><I><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">Shit, bastard, fuck you</SPAN></I></SPAN><SPAN class=testo1><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">». Interviene l\'altro poliziotto per metterle le manette, lei urla, si ribella. Si rivolge all\'amica: «</SPAN></SPAN><SPAN class=testo1><I><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">We have the shoppers</SPAN></I></SPAN><SPAN class=testo1><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">». In effetti a terra ci sono delle buste, ne riconosco una della </SPAN></SPAN><SPAN class=testo1><I><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">Diesel</SPAN></I></SPAN><SPAN class=testo1><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">, le ragazze hanno fatto shopping. E\' evidente che si tratta di due turiste. Continuo a chiedere informazioni al tassista: «Fanno bene, fanno bene, hanno menato alla polizia». Penso che devono aver fatto qualcosa di grave. Ma non posso credere ai miei occhi quando vedo arrivare, una, due, tre, quattro macchine della polizia. Gli agenti sono in tutto una decina. Scendono dalle gazzelle e danno «una mano» ai loro colleghi. Un uomo grande e grosso si infila dei guanti di pelle. «Ma si può sapere che è successo?», chiedo a un poliziotto che ha già infilato la prima ragazza ammanettata nella macchina della polizia e sta infilando di peso l\'altra. Il tassista finalmente mi dice qualcosa: «Mi hanno chiesto di portarle fino a qua, poi si sono rifiutate di pagare la corsa. Erano ubriache. Io ho chiamato la polizia, quando sono arrivati gli agenti quella alta ha mollato due ceffoni al poliziotto». Non posso credere che questa sia tutta la storia. Chiedo informazioni anche alla polizia: «Scusi, sono una giornalista, mi dice cosa è successo?». «Ah, sei una giornalista? Bene, facci vedere il tesserino così ti identifichiamo subito». E ancora: «Perché non ci vieni tu a parlare con queste due ubriacone, eh? Non lo capisci che questi stranieri al paese loro devono stare con due gambe in una scarpa e quando vengono qua ne fanno di tutti i colori?». Un altro: «Non sei un\'italiana, anzi, non sei neanche una persona». Ancora: «Ma come ti permetti di intrometterti durante un\'operazione? Ci stai facendo fare una brutta figura». Obietto che ci pensano benissimo da soli. Si scatenano quando scoprono che sono del </SPAN></SPAN><SPAN class=testo1><I><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">manifesto</SPAN></I></SPAN><SPAN class=testo1><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">. «Vi abbiamo fatto la scorta per sei mesi sotto al giornale, bella». Grazie, rispondo. «Sì, vi abbiamo fatto la scorta perché siete dei cagasotto». Rispondo che è perché ci mettono le bombe i fascisti. «Ma lo sai quante persone con precedenti penali ho identificato mentre venivano su da voi?». «Che cosa sta insinuando?», chiedo. «Che tanto siete tutti uguali».<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\"><SPAN class=testo1><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">Allibita, cerco di riportare la «discussione» al punto di partenza. Che cosa è successo? «Fai una cosa, scrivi sulla tua testata che i poliziotti hanno menato due stranieri», risponde uno sprezzante ridandomi il tesserino. Insisto. Si girano in tre o quattro: «Vuoi più informazioni? Te le diamo, te le diamo, ma solo al commissariato. Vieni con noi». Nel frattempo vengo fermata da un ragazzo: «Sono di sinistra, ma tu hai sbagliato». Chiedo perché. «Ma te pare normale che due ragazze danno un ceffone a un poliziotto?». Si avvicina un altro signore, con i capelli bianchi: «Ho visto tutta la scena, e sono di sinistra: ma così come fa lei non va bene. Siamo in un clima di emergenza, lo vogliamo capire o no?». Da un balcone una voce mi apostrofa: «</SPAN></SPAN><SPAN class=testo1><I><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">Ah stronza!</SPAN></I></SPAN><SPAN class=testo1><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\"> ».<o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\"><SPAN class=testo1><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">Voglio raggiungere il commissariato, ma tutti lo sconsigliano: «peggioreresti solo le cose». Non è mia intenzione. Magari, penso, ne saprò di più domani. Ieri mattina sulle agenzie di stampa, sotto il capitolo «criminalità», la notizia:</SPAN></SPAN><SPAN class=testo1><I><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\"> </SPAN></I></SPAN><SPAN class=testo1><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\">«Una ragazza americana di 20 anni è stata arrestata ieri sera per resistenza a pubblico ufficiale e lesioni dagli agenti di una volante».<o:p></o:p></SPAN></SPAN></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt; TEXT-ALIGN: justify\"><SPAN class=firma1><I><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 10.0pt; mso-ascii-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'\"><FONT face=\"Times New Roman\">(Cinzia Gubbini)</FONT></SPAN></I></SPAN></P>', 0, '', '', '', 3918, 1, 0, '2026-01-13 22:14:44', '0000-00-00'),
(303, '2004-08-13 00:00:00', 'Notizie Flash', 'DOPING: NUOVA STRUTTURA INVESTIGATIVA IN DIREZIONE ANTIMAFIA. LO HA ANNUNCIATO A FESTAMBIENTE IL PROCURATORE VIGNA', '', 'DOPING: NUOVA STRUTTURA INVESTIGATIVA IN DIREZIONE ANTIMAFIA. LO HA ANNUNCIATO A FESTAMBIENTE IL PROCURATORE VIGNA', '<PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - RISPESCIA (GROSSETO), 12 AGO - Nella Direzione<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">nazionale antimafia e\' stata costituita una nuova struttura che<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">si occupera\' di doping. Lo ha annunciato ieri sera a<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Festambiente il procuratore nazionale antimafia Pier Luigi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Vigna.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Da un po\' di tempo - ha spiegato Vigna - era forte l\' idea<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">che le mani della criminalita\' organizzata fossero dietro anche<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">allo smercio delle sostanze dopanti. Per questo nella Dna si e\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">aggiunta una nuova struttura di analisi, di coordinamento e di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">impulso alle indagini. Si chiama \'Doping e criminalita<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">organizzata\'. Abbiamo fatto delle riunioni con i magistrati che<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">in Italia seguono queste indagini - ha aggiunto il procuratore -<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">e si e\' visto come spesso lo smercio, il traffico delle sostanze<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dopanti, che provengono soprattutto dalla Grecia e dalla Spagna,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">e\' collegato a quello delle sostanze stupefacenti\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>A questo punto, ha aggiunto Vigna, \'\'e\' stata fatta una<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ricognizione presso i carabinieri del Nas, la <STRONG><SPAN style=\"COLOR: red\">guardia</SPAN></STRONG> di <STRONG><SPAN style=\"COLOR: red\">finanza</SPAN></STRONG><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">e la polizia di stato di tutti i procedimenti esistenti in<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Italia per traffico di sostanze dopanti. Quello che impressiona<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">e\' non solo l\' uso di queste sostanze da parte dei<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">professionisti ma da parte dell\' ambiente amatoriale. Io sono<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sempre piu\' convinto - ha aggiunto - che colui che abita nell\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">attico, quando fa le gare la domenica con i condomini, non puo\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">perdere rispetto a chi sta al pianterreno, e quindi si dopa\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Vigna ha rivelato che c\' e\' \'\'un camorrista che ha<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">collaborato e ha parlato di questo duplice traffico al quale si<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dedicava. Il traffico avviene tramite internet. Pensate che un<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">signore, chiamiamolo cosi\', che stava nel bolognese e che<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">smistava attraverso internet gli acquisti e le vendite, aveva<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">quattromila clienti in America nella zona di Miami, oltre a<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">tutti gli altri che aveva in Europa\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Dopo l\' intervento di Vigna, il presidente di Libera, don<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Luigi Ciotti, ha preannunciato la presentazione di un dossier<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">nazionale su doping e criminalita\' organizzata in Italia.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">(ANSA).<o:p></o:p></SPAN></PRE>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>', 0, '', '', '', 1847, 1, 0, '2026-01-13 22:14:44', '0000-00-00'),
(304, '2004-08-13 00:00:00', 'Notizie Flash', 'COSSIGA, CHIARIMENTO CON VIMINALE O ARMA VERSO EMARGINAZIONE. BASTA CONFLITTI ISTITUZIONALI DIFESA-INTERNO PER BENE PAESE', '', 'COSSIGA, CHIARIMENTO CON VIMINALE O ARMA VERSO EMARGINAZIONE. BASTA CONFLITTI ISTITUZIONALI DIFESA-INTERNO PER BENE PAESE', '<PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 12 AGO - Basta conflitti istituzionali tra<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ministeri della Difesa e dell\'Interno sull\'Arma dei Carabinieri.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Lo chiede \'\'per il bene del Paese\'\' Francesco Cossiga, in un<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">articolo pubblicato su \'Panorama\' in edicola domani. L\'ex capo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dello Stato interviene dopo \'\'il conflitto esploso\'\' tra i<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">responsabili di Palazzo Baracchini e del Viminale sulla nomina<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">del nuovo capo di stato maggiore dell\'Arma.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Non sembra conforme a principi di correttezza e criteri di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">opportunita\'\'\', scrive Cossiga, che la nomina dell\'alto<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ufficiale sia stata fatta \'\'senza previo assenso e addirittura<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">senza previa informazione del ministro dell\'Interno\'\', da cui<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">l\'Arma dipende per i compiti di tutela dell\'ordine pubblico, che<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">costituiscono ormai \'\'il 95% dell\'attivita\' dell\'Arma stessa\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ma il profilo piu\' delicato, secondo l\'ex presidente della<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Repubblica, e\' quello \'\'ordinativo e istituzionale, su cui -<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">afferma - pesano leggi confuse e contraddittorie e la permanente<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">aspirazione del ministero della Difesa a ingerirsi sempre di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">piu\' nell\'attivita\' interna, atteggiandosi appunto a \'ministero<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dell\'Interno parallelo\'\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Occorre dunque, secondo Cossiga, \'\'una drastica riforma delle<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dipendenze e dell\'inquadramento ordinativo\'\'. Altrimenti, si<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">costituira\' una sorta di \'\'corpo separato\'\' e il risultato sara\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">\'\'una anacronistica rimilitarizzazione di una parte essenziale<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">del comparto di sicurezza interna, sottraendolo di fatto al<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">controllo effettivo delle autorita\' politiche responsabili\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Insomma, i tempi sono maturi per una riorganizzazione che<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">consenta l\'utilizzo dei Carabinieri da parte \'\'del ministero<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">competente in materia di tutela civile dell\'ordine e della<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sicurezza pubblica\'\'. Per il resto, osserva Cossiga, \'\'a ognuno<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">il suo mestiere: i militari facciano i militari e i poliziotti<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">facciano i poliziotti. E i carabinieri sanno fare in modo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">eccellente i poliziotti\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Se questo non avverra\' - scrive il senatore a vita - a<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">correre rischi sara\' anzitutto il Paese, ma piu\' che il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ministero dell\'Interno, sara\' soprattutto l\'Arma dei Carabinieri<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">che, per miopia del ministro della Difesa e di alcuni suoi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ufficiali, verra\' inevitabilmente, progressivamente emarginata,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">trovandosi il ministero stesso e le autorita\' di pubblica<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sicurezza nell\'assoluta necessita\' di rafforzare, specie<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">nell\'antiterrorismo e nella collaborazione internazionale, le<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">forze di polizia di cui possono piu\' facilmente disporre\'\'. In<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">particolare, Cossiga, cita la <STRONG><SPAN style=\"COLOR: red\">Guardia</SPAN></STRONG> di <STRONG><SPAN style=\"COLOR: red\">Finanza</SPAN></STRONG>, \'\'che ha una<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">lunga tradizione di disponibilita\' nei confronti del ministero<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dell\'Interno\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Riuscira\' l\'attuale governo ad affrontare e risolvere il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">problema \'\'in senso moderno, europeo e democratico, evitando<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">conflitti e sovrapposizioni dannose?\'\'. \'\'Pur sperandolo e </SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"></SPAN><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ritenendolo necessario - conclude Cossiga - non lo credo, almeno<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">a giudicare dalla incapacita\' finora dimostrata di realizzare<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">l\'ormai urgente e indispensabile riforma dei servizi di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">informazione e sicurezza\'\'. (ANSA).<o:p></o:p></SPAN></PRE>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>', 0, '', '', '', 1537, 1, 0, '2025-12-30 06:23:45', '0000-00-00'),
(305, '2004-08-17 00:00:00', 'Notizie Flash', 'SICUREZZA: PISANU, 6.000 POLIZIOTTI E CC QUARTIERE NEL 2005\r\nAD OGGI, 15 MILIONI DI CONTATTI CON LA GENTE', '', 'SICUREZZA: PISANU, 6.000 POLIZIOTTI E CC QUARTIERE NEL 2005\r\nAD OGGI, 15 MILIONI DI CONTATTI CON LA GENTE', '<PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 16 AGO - I poliziotti e carabinieri di<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">quartiere saranno 6.000 a fine legislatura. Lo ha detto il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ministro dell\' Interno, Giuseppe Pisanu, nel corso dela<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">conferenza stampa di ieri al Viminale.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ad oggi, si legge nel Rapporto sullo stato della sicurezza in<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Italia, ci sono stati oltre 15 milioni di contatti tra questi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">operatori in divisa e commercianti, imprenditori, istituzioni<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">locali e gente comune. Attualmente sono 1.900 i poliziotti e<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">carabinieri di quartiere che coprono 433 zone. Nel quadro di un<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">piano di progressivo rafforzamento del servizio, destinato ad<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">andare a regime entro il 2006, altre 300 unita\' verranno immesse<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">a breve. (ANSA).<o:p></o:p></SPAN></PRE>', 0, '', '', '', 1110, 1, 0, '2026-01-14 10:24:32', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(308, '2004-08-16 00:00:00', 'Notizie Flash', 'INAUGURATA NUOVA CENTRALE OPERATIVA CAPITANERIE DI PORTO', '', 'INAUGURATA NUOVA CENTRALE OPERATIVA CAPITANERIE DI PORTO', '<PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 15 AGO - Un grande occhio tecnologico per<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">osservare tutto cio\' che avviene nei 550 mila chilometri<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">quadrati di mare di competenza italiana. E\' la nuova centrale<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">operativa unificata delle Capitanerie di Porto e Guardia<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Costiera, inaugurata oggi dal viceministro delle Infrastrutture<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">e Trasporti Mario Tassone e dal comandante delle Capitanerie<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ammiraglio Eugenio Sicurezza.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La centrale si trova all\'interno del ministero delle<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Infrastrutture all\'Eur a Roma ed e\' collegata 24 ore su 24 con<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">le 13 direzioni marittime italiane, a loro volta in contatto con<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">le 50 sedi delle Capitanerie di Porto sparse sul territorio.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">\'\'La centrale - ha detto l\'ammiraglio Sicurezza - nasce con lo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">scopo piu\' nobile: salvare la vita in mare e osservare cio\' che<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">nei mari italiani avviene in ogni momento\'\'. Un compito<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">particolarmente gravoso quando si ha a che fare con gli<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">immigrati clandestini che in questo periodo dell\'anno si<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">riversano a migliaia sulle coste italiane. \'\'Quando li vediamo -<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ha ribadito l\'ammiraglio Sicurezza - abbiamo l\'obbligo morale di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">andare a prenderli e salvarli\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Oltre a cio\', pero\', il grande occhio telematico della<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">centrale e\' in grado di controllare in tempo reale i movimenti<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">di tutti i pescherecci battenti bandiera italiana nelle nostre<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">acque e di monitorare costantemente la sicurezza ambientale e il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">traffico commerciale grazie a sofisticati software e a sistemi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">satellitari. Dalla centrale, inoltre, e\' anche possibile capire<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">se una nave oltre le 1600 tonnellate e\' stata dirottata da<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">eventuali terroristi: un allarme acustico entra in funzione nel<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">caso in cui si verificasse un problema su una nave. Al<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">comandante dell\'imbarcazione, o all\'ufficiale in comando, basta<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">premere un bottone e segnalare cosi\' l\'allarme alla centrale:<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sugli schermi apparira\' in tempo reale posizione e rotta della<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">nave per poter intervenire in tempi brevissimi.(ANSA).<o:p></o:p></SPAN></PRE>', 0, '', '', '', 1583, 1, 0, '2026-01-14 06:01:15', '0000-00-00'),
(307, '2004-08-17 00:00:00', 'Dai media', 'IL MATTINO, Martedì 17 Agosto 2004\r\n“GAETA, RISCHIANO DI ANNEGARE: SALVATI DA FINANZIERE”', '', 'IL MATTINO, Martedì 17 Agosto 2004\r\n“GAETA, RISCHIANO DI ANNEGARE: SALVATI DA FINANZIERE”', '<P style=\"TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Padre e due figli, di 13 e 9 anni, di Aversa, sono stati salvati da un sommozzatore di 25 anni della guardia di finanza. L’episodio si è registrato nello specchio d’acqua antistante la spiaggia dell’Ariana, a Gaeta. «Appena l’ho raggiunto - dice Roberto Serio, finanziere in servizio a Pescara - l’uomo mi ha detto urlando di salvare i suoi figli che erano a circa centocinquanta metri dalla riva. Ho sistemato il salvagente e ho raggiunto il figlio tredicenne. Quando sono riuscito ad afferrarlo anche lui disperato chiedeva di salvare l’altro fratello. L’ho riportato a riva e mi sono diretto verso l’altro. Ero stanchissimo, ma vedevo il bambino che rischiava di finire contro gli scogli. Lo avevo quasi raggiunto, quando è finito sott’acqua». Il finanziere si è immerso ed è riuscito a riportarlo a galla. «Chiedeva aiuto. C’erano delle onde tremende e ho cercato di sfruttare la corrente per tornare verso riva e con grande difficoltà ci sono riuscito».<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P style=\"TEXT-ALIGN: justify\"><I><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Arial Unicode MS\">(i.ci.)<o:p></o:p></FONT></SPAN></I></P>', 0, '', '', '', 1664, 1, 0, '2026-01-14 08:29:20', '0000-00-00'),
(309, '2004-08-19 00:00:00', 'Notizie Flash', 'ILLECITI ELBA: CHIESTO RINVIO A GIUDIZIO PER 10 INDAGATI\r\nTRA GLI IMPUTATI DUE PREFETTI, UN GIUDICE E IMPRENDITORI', '', 'ILLECITI ELBA: CHIESTO RINVIO A GIUDIZIO PER 10 INDAGATI\r\nTRA GLI IMPUTATI DUE PREFETTI, UN GIUDICE E IMPRENDITORI\r\n', '<PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - GENOVA, 18 AGO - Con la richiesta di 10 rinvii a<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">giudizio si e\' chiusa l\' inchiesta condotta dalla Procura di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Genova sui presunti illeciti all\' isola d\' Elba che ha coinvolto<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">tra gli altri Vincenzo Gallitto, prefetto di Livorno, il suo ex<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">vice Giuseppe Pesce, ora prefetto di Isernia, e l\' ex capo dei<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Gip di Livorno, Germano Lamberti.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Le accuse sono a vario titolo quelle di corruzione,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">corruzione in atti giudiziari, concorso in peculato e abuso d\'<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ufficio.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La richiesta di giudizio, contenuta in 82 pagine, e\' firmata<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dai magistrati genovesi, il procuratore aggiunto Mario Morisani<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">e il pm Paola Calleri. Tra i dieci imputati figurano inoltre gli<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">imprenditori edili di Pistoia, Franco Giusti e Fiorello Filippi,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">il sindaco di Marciana, Luigi Logi, e il dirigente dell\' ufficio<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">tecnico del comune di Marciana, Gabriele Mazzarri.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Al centro dell\' inchiesta, condotta dalla <STRONG><SPAN style=\"COLOR: red\">guardia</SPAN></STRONG> di <STRONG><SPAN style=\"COLOR: red\">finanza</SPAN></STRONG><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">di Livorno, avviata nella citta\' toscana e poi passata a Genova<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">per competenza essendo coinvolto un magistrato toscano, due<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">futuri complessi edilizi di Procchio, nel comune di Marciana, e<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">di Cavo, nel comune di Rio Marina, in cui erano interessati gli<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">imprenditori pistoiesi e l\' ingegnere Uberto Coppetelli, a sua<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">volta indagato, morto alcuni mesi fa.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ad accusare il magistrato ci sarebbero alcune intercettazioni<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">telefoniche. In una il gip, secondo l\'accusa, avrebbe suggerito<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">all\' ingegnere quali atti presentare per ovviare ad una<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">richiesta di sequestro dell\' area in costruzione a Procchio<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">presentata allo stesso Lamberti dal pm di Livorno Antonio<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Giaconi e poi rigettata dal giudice arrestato. Inoltre il gip si<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sarebbe attivato personalmente per verificare se a Livorno ci<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">fosse un\' inchiesta in corso su di loro.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Le intercettazioni riportate nella misura cautelare si<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">riferiscono anche ad una conversazione tra i due prefetti: in<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">una telefonata Gallitto chiede a Pesce se \'\'era tutto a posto\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">La risposta del prefetto di Isernia e\': \'\'No per niente, quello<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">(il Giusti), non capisce niente ed e\' pure pericoloso\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Le indagini hanno coinvolto anche i due prefetti Vincenzo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Gallitto e Giuseppe Pesce, il primo accusato, insieme a Luca<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Lamberti, figlio del giudice, di concorso in peculato. Secondo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">l\' accusa Gallitto mise a disposizione del giovane due auto di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">servizio della prefettura (con autista) per accompagnarlo da<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Livorno a Piombino e, una volta sbarcato all\' Elba, in giro per<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">l\' isola. Pesce invece e\' accusato, tra l\' altro, di avere<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ottenuto a prezzi di favore dagli imprenditori indagati alcuni<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">appartamenti nel complesso residenziale \'\'Costa dei Barbari\'\' a<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Cavo.(ANSA).<o:p></o:p></SPAN></PRE>', 0, '', '', '', 1730, 1, 0, '2025-12-17 08:48:07', '0000-00-00'),
(310, '2004-08-19 00:00:00', 'Notizie Flash', 'ALBANIA: COLPITO ELICOTTERO ITALIANO,PARLA UFFICIALE A BORDO', '', 'ALBANIA: COLPITO ELICOTTERO ITALIANO,PARLA UFFICIALE A BORDO', '<PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - TIRANA, 18 AGO - \'\'Abbiamo udito prima una raffica<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ma era distante e non credevamo fosse diretta contro di noi, poi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">una serie di altre raffiche e a quel punto abbiamo sentito i<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">colpi che centravano l\'elicottero\'\': il vice questore Sandro De<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Angelis, vice comandante dell\'ufficio di collegamento italiano<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">in Albania, racconta all\'Ansa gli attimi drammatici vissuti oggi<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">pomeriggio nei cieli dell\'Albania meridionale. Trafficanti di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">droga hanno aperto il fuoco contro un elicottero della polizia<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">di Stato italiana che stava effettuando una ricognizione aerea<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">alla ricerca di coltivazioni di marjiuana sul villaggio di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Lazarat, vicino alla citta\' di Argirocastro, a ridosso del<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">confine con la Grecia.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>A bordo oltre a De Angelis c\'erano tre piloti, il vice<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">questore Antonio D\'Amario capo pattuglia, un maresciallo della<o:p></o:p></SPAN></PRE><PRE><STRONG><SPAN style=\"FONT-SIZE: 14pt; COLOR: red; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">guardia</SPAN></STRONG><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"> di <STRONG><SPAN style=\"COLOR: red\">finanza</SPAN></STRONG> e un\'ufficiale donna della sezione antidroga<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">della polizia albanese.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'L\'elicottero e\' stato centrato da tre proiettili - dice De<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Angelis - uno ha colpito la plancia, uno il serbatoio principale<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">e un terzo il motore posteriore\'\'. Dopo essere stato centrato<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">l\'elicottero, che in quel momento volava ad<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>un\'altezza di circa<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">400 metri, ha immediatamente abbandonato la zona effettuando un<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">atterraggio precauzionale alla periferia di Argirocastro: il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">tempo di verificare che non ci fossero danni rilevanti, e poi il<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">rientro con l\'intero equipaggio incolume fino all\'aeroporto di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Tirana da dove la missione era partita.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Siamo partiti da Tirana alle 16:30 (ora locale e italiana)<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">e dopo circa un\'ora abbiamo raggiunto Lazarat - racconta ancora<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">il vice questore De Angelis - il nostro obiettivo era quello di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">verificare la presenza di coltivazioni di marijuana che ci erano<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">stato state segnalate\'\'.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>I mezzi aerei della polizia italiana, in base ad un accordo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">di cooperazione con le autorita\' albanesi, in questo periodo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">effettuano periodiche ricognizioni aeree per localizzare e<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">distruggere le vaste estensioni di droga leggera che sono<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">visibili solo dall\'alto. L\'elicottero colpito era giunto in<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Albania questa mattina, proveniente dal Reparto volo della<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">polizia di Stato di Napoli.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Il primo sorvolo ha confermato i nostri sospetti - dice De<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Angelis - sotto i nostri occhi abbiamo visto un\'enorme distesa<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">di marijuana piantata fra le case del villaggio\'\'. E\' in questo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">momento che e\' stata sparata una prima raffica andata a vuoto:<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">\'\'In quel momento volavamo ad una quota di 700 metri - racconta<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">il vice questore - abbiamo deciso di effettuare un secondo<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sorvolo a 400 metri per completare le nostre riprese aeree,<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">quando siamo stati colpiti\'\'. Secondo De Angelis l\'elicottero<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sarebbe stato centrato da una raffica di kalashnikov, ma ora i<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">periti del ministero dell\'interno albanese stanno effettuando le<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">verifiche sui fori ben visibili sulla carlinga del velivolo.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Testimoni avevano raccontato nei giorni scorsi che a Lazarat<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">(zona fuori dal controllo della polizia locale) quasi tutta la<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">popolazione e\' armata, e molti dispongono sui tetti persino di<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">mitragliatrici pesanti.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Dell\'incidente e\' stata immediatamente informata anche<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">l\'ambasciata d\'Italia a Tirana che si e\' mantenuta in stretto<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">contatto con l\'ufficio di collegamento della polizia.<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Sconfitto ormai da due anni il fenomeno dell\'emigrazione<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">clandestina, le autorita\' albanesi e italiane sono impegnate da<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">mesi nella lotta al traffico di droga che costituisce la nuova<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sfida nella cooperazione bilaterale. L\'Albania, oltre che zona<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">di transito di eroina e cocaina dirette verso l\'Italia, e\' anche<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">una zona di produzione di droga leggera, poi esportata sul<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">mercato italiano e in Grecia. Nelle scorse settimane per la<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">prima volta sono state individuate nel sud dell\'Albania persino<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">piccole piantagioni di papavero da oppio, pianta da cui si<o:p></o:p></SPAN></PRE><PRE><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">estrae la materia prima per la produzione di eroina.(ANSA).<o:p></o:p></SPAN></PRE>', 0, '', '', '', 2278, 1, 0, '2026-01-14 01:12:07', '0000-00-00'),
(311, '2004-09-01 00:00:00', 'Dai media', 'Sentenza della Corte di Cassazione a Sezioni Unite  sul  fenomeno del Mobbing.', '', 'Sentenza della Corte di Cassazione a Sezioni Unite  sul  fenomeno del Mobbing.', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Da<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>“il GIORNALE<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>nuovo DEI MILITARI”, con piacere, apprendiamo e volentieri riportiamo una notizia che interesserà molti: finalmente la Corte di Cassazione a Sezioni Unite ha stabilito che la cognizione del danno da mobbing,<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>in un rapporto di lavoro pubblico, spetta al Giudice Amministrativo, trattandosi di una violazione contrattuale e non extracontrattuale che, diversamente, avrebbe comportato la competenza del Giudice ordinario nella veste di Giudice del lavoro.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>In allegato l’articolo de “il GIORNALE nuovo DEI MILITARI”<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>per la cui pubblicazione ringraziamo l’amica Antonella Manotti. </FONT></P>', 84, '', '', '', 2111, 1, 0, '2026-01-14 07:48:10', '0000-00-00'),
(312, '2004-09-01 00:00:00', 'Servizio di Ficiesse', 'PARERE CONTRARIO del COCER  GdiF. al progetto di riforma della rappresentanza licenziato dalla commissione difesa della camera\r\n\r\n\r\n', '', 'PARERE CONTRARIO del COCER  GdiF. al progetto di riforma della rappresentanza licenziato dalla commissione difesa della camera\r\n\r\n\r\n', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Pubblichiamo, in allegato, la delibera del Cocer Guardia di Finanza relativa al parere fornito al progetto di Disegno di Legge<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>sulla Riforma della Rappresentanza Militare, licenziato dalla Commissione Difesa della Camera.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Nel condividere in pieno le argomentazione dei rappresentanti del Cocer che esprimono un motivato parere contrario, li invitiamo ad assumere le opportune iniziative, anche in sintonia con i rappresentanti delle altre Forze di Polizia, al fine di far recepire in pieno le aspettative del personale del Corpo della Guardia di Finanza che sono, in definitiva, il riconoscimento di strumenti di<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>piena tutela e vera rappresentanza sindacale.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>', 85, '', '', '', 2804, 1, 0, '2025-12-30 06:18:53', '0000-00-00'),
(313, '2004-09-01 00:00:00', 'Servizio di Ficiesse', 'FERMA PRESA DI POSIZIONE del COCER GdiF. sul “ Fondo unico per l’Efficienza dei Servizi Istituzionali – anno 2003 - \r\n\r\n', '', 'FERMA PRESA DI POSIZIONE del COCER GdiF. sul “ Fondo unico per l’Efficienza dei Servizi Istituzionali – anno 2003 - \r\n\r\n', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Pubblichiamo in allegato la delibera del Cocer Guardia di Finanza relativa al parere fornito sulla distribuzione del Fondo per l’ Efficienza dei servizi Istituzionali – anno 2003 -.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Sottolineiamo la richiesta dei rappresentanti, che condividiamo, di destinare le providenze di cui all’art. 8 anche al personale Peisaf trasferito a domanda nelle regioni non ambite e, ancor più, siamo d’accordo con la ferma presa di posizione circa il sistema di trasferimento degli ufficiali e la affermazione: “ <B style=\"mso-bidi-font-weight: normal\">Respinge qualsiasi tentativo, pur indiretto, di creare un collegamento tra la permanenza alla sede e l’inefficienza se non l’infedeltà”.</B> </FONT></P>', 86, '', '', '', 9228, 1, 0, '2026-01-14 10:54:52', '0000-00-00'),
(314, '2004-09-06 00:00:00', 'Notizie Flash', 'AFFONDA VEDETTA DELLA FINANZA, SALVI TRE UOMINI EQUIPAGGIO - GDF: COMANDANTE GENERALE IN VISITA A CASERME DELLA CAPITALE', '', 'AFFONDA VEDETTA DELLA FINANZA, SALVI TRE UOMINI EQUIPAGGIO - GDF: COMANDANTE GENERALE IN VISITA A CASERME DELLA CAPITALE', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">AFFONDA VEDETTA DELLA FINANZA, SALVI TRE UOMINI EQUIPAGGIO<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">APPUNTATO RICOVERATO IN OSSERVAZIONE; E\' ACCADUTO IERI SERA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - GENOVA, 21 AGO - Una vedetta della Finanza in navigazione lungo il canale di calma dell\'aeroporto di Genova, per cause ancora in via di accertamento, e\' affondata ieri sera. Salvi i tre uomini dell\'equipaggio. Uno di loro, un appuntato scelto, si trova attualmente ricoverato in osservazione all\'ospedale Villa Scassi di Sampierdarena. Il militare ha riportato un forte stato di choc e ipotermia.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\'incidente e\' accaduto ieri sera intorno alle 23 nel canale davanti alla pista dell\'aeroporto, all\'incirca all\'altezza della torre di controllo. Al momento viene categoricamente escluso il sabotaggio; l\'ipotesi piu\' probabile e\' che durante la navigazione la vedetta, a idrogetto, abbia urtato un corpo sommerso che ha causato una falla nella chiglia.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Secondo una prima ricostruzione dei fatti, quando i componenti dell\'equipaggio, un maresciallo capo (comandante dell\'unita\' navale), un maresciallo ordinario (direttore di macchina) ed un appuntato scelto, si sono accorti che nel vano motore stava entrando acqua, hanno immediatamente attivato la pompa ad esaurimento, nel tentativo di limitare l\'allagamento. In quel momento, pero\', la vedetta, in navigazione verso ponente, si e\' trovata il mare di poppa e l\'operazione di svuotamento dell\'acqua e\' stata resa vana dalle onde che investivano il mezzo.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Quando ormai l\'acqua aveva invaso completamente la vedetta, i tre finanzieri a bordo si sono lanciati in mare. L\'operazione e\' risultata piuttosto complicata per l\'appuntato, rimasto bloccato sottocoperta. Il militare e\' pero\' riuscito ad uscire da un finestrino.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>I tre naufraghi, che avevano in precedenza lanciato l\'allarme attraverso i telefoni cellulari (la radio di bordo era infatti inutilizzabile a causa dell\'acqua), sono stati soccorsi da altri mezzi della Finanza, della Guardia Costiera e dei Vigili del fuoco. Solo per l\'appuntato i medici hanno poi disposto il ricovero in ospedale in via precauzionale. Le sue condizioni, comunque, sono buone. Gli altri due militari, invece, gia\' stamani si sono presentati al lavoro.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La vedetta, che fino a questa mattina e\' rimasta semisommersa lungo il canale, e\' ora affondata e sono in corso ricerche per poter avviare le operazioni di recupero. Soltanto dopo una analisi dello scafo si potranno scoprire le cause dell\'incidente.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">La motovedetta, una classe 55 in vetroresina di 12 metri con due motori idrogetto e scafo planante, potrebbe aver subito un cedimento strutturale a causa delle vibrazioni dovute all\' impegno del motore contro un mare molto mosso oppure aver urtato un corpo semigalleggiante sceso a mare con il dilavamento. Lo ha confermato il comandante del reparto operativo aeronavale regionale della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Lo scafo potrebbe comunque non trovarsi nel punto dell\' affondamento a causa delle forti correnti subacquee che potrebbero averlo trascinato verso ponente.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il comandante ha confermato che il relitto verra\' cercato con tutti i mezzi disponibili, Rov incluso, che rilevino masse anche non metalliche nell\' acqua. Nel punto dove e\' affondata la vedetta, il<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>canale di calma e\' profondo oltre 20 metri: nel caso si trovasse insabbiato sul fondo, per il recupero sara\' necessario ricorrere a palombari e ai palloni galleggianti.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Migliorano, intanto, le condizioni dell\' appuntato ricoverato in ospedale per ipotermia e stato di choc. Il militare, che era rimasto imprigionato sottocoperta ed e\' uscito spaccando un oblo\', rimarra\' qualche giorno in osservazione.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">GDF: COMANDANTE GENERALE IN VISITA A CASERME DELLA CAPITALE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">PER RENDERSI CONTO DELL\'ADEGUATEZZA DELLE STRUTTURE LOGISTICHE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 24 AGO - Il comandante generale della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>, generale Roberto Speciale, questa mattina ha visitato diverse caserme del Corpo nella capitale. Con queste visite, fa sapere una nota del comando generale delle Fiamme Gialle, \'\'il generale Speciale ha voluto rendersi conto di persona dell\'adeguatezza delle strutture logistiche alle esigenze operative dei Reparti della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> della capitale\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>In particolare, il comandante generale delle Fiamme Gialle ha visitato la caserma di via Nomentana 591, sede del comando regionale della Gdf del Lazio, dove e\' stato accolto dal comandante regionale, generale Virgilio Elio Ciccio\', e dal comandante provinciale Roma, colonnello Giuseppe Zafarana; le caserme di via Raffaele Costi, sedi distaccate del Comando nucleo regionale polizia tributaria Lazio, del Comando nucleo provinciale polizia tributaria di Roma e del Comando unita\' speciali, dove e\' stato accolto dal comandante di quest\'ultimo Reparto, generale Giuseppe Quaranta. (ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2657, 1, 0, '2026-01-11 00:33:27', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(315, '2004-09-06 00:00:00', 'Notizie Flash', 'PREZZI: ALEMANNO, A SETTEMBRE GDF CONTRO SPECULATORI - PREZZI: CONTRIBUENTI.IT; GDF NON HA STRUMENTI CONTRO RINCARI - FISCO: GDF, IN PRIMO SEMESTRE SCOPERTA EVASIONE PER 6,3 MLD', '', 'PREZZI: ALEMANNO, A SETTEMBRE GDF CONTRO SPECULATORI - PREZZI: CONTRIBUENTI.IT; GDF NON HA STRUMENTI CONTRO RINCARI - FISCO: GDF, IN PRIMO SEMESTRE SCOPERTA EVASIONE PER 6,3 MLD', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">PREZZI: ALEMANNO, A SETTEMBRE GDF CONTRO SPECULATORI<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">FORBICE TROPPO AMPIA TRA PREZZI PAGATI A AGRICOLTORI E A CONSUMO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 26 AGO - A settembre interverra\' la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di finanza</SPAN></STRONG> contro le speculazioni agroalimentari. Lo ha detto il ministro delle Politiche Agricole Gianni Alemanno, intervenendo in diretta a Radio Anch\'io. \'\'Forzeremo la situazione – ha spiegato il ministro di An durante la trasmissione di RadioRai - per utilizzare le prerogative della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanza</SPAN></STRONG> per fare in modo che qualche speculatore salti fuori\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il ministro ha ammesso che nel nostro paese ci sono troppi passaggi nella distribuzione delle derrate agricole e questo crea una \'\'forbice troppo ampia tra i prezzi pagati agli agricoltori e quelli registrati al consumo\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Eliminare questi passaggi con decreto legge non e\' tuttavia possibile in un\'economia libera\'\', ha aggiunto Alemanno, che ha quindi indicato come \'\'valida e percorribile la realizzazione di grandi centri agroalimentari all\'ingrosso che funzioneranno come calmieri dei prezzi su tutto il mercato\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Sul modello francese, il ministro ha poi annunciato l\'intenzione di concordare con produttori e distributori alcuni \'\'pacchetti alimentari\'\' i cui prezzi saranno contenuti e<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">controllati. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">PREZZI: CONTRIBUENTI.IT; GDF NON HA STRUMENTI CONTRO RINCARI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">PIU\' POTERI ALLE FIAMME GIALLE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 30 AGO - \'\'Le Fiamme gialle non possono controllare il carovita\'\': lo afferma, in una nota, Vittorio Carlomagno, presidente di \'Contribuenti.it\', l\'associazione dei contribuenti italiani.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Non sono in grado di contrastare tale fenomeno per una ragione molto semplice - spiega - non hanno nessun potere di intervento nel processo di formazione dei prezzi\'\'. Ed e\' questo il dato di fatto che spinge Contribuenti.it a richiedere \'\'maggiori poteri alla <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> per combattere i rincari\'\'. L\'associazione si dice inoltre \'\'pronta a collaborare immediatamente con le Fiamme Gialle nel rispetto dei principi stabiliti dallo Statuto del Contribuente. Quello che il corpo puo\' fare - afferma Vittorio Carlomagno - si limita soltanto al controllo della corretta esposizione dei cartellini che esprimono il costo dei prodotti venduti al dettaglio. Tanto che l\'azione di monitoraggio sui prezzi, iniziata a fine 2003, ha visto effettuare fino al 31 luglio scorso 20.592 controlli riscontrando circa 2.000 violazioni esclusivamente per violazione della pubblicita\' nell\'esposizione dei prezzi e per mancato rispetto delle regole concorrenziali\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Attualmente le Fiamme gialle possono agire solamente in modo indiretto, rilevando i prezzi e trasmettendoli all\'Agenzia delle entrate per ricalcolare gli studi di settore\'\', sottolinea l\'associazione, spiegando che di essere \'\'a favore di una radicale revisione degli studi di settore\'\', e chiedendo \'\'una modifica sia degli studi che riguardano i venditori all\'ingrosso, sia di quelli relativi ai venditori al dettaglio\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">FISCO: GDF, IN PRIMO SEMESTRE SCOPERTA EVASIONE PER 6,3 MLD<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 31 AGO - Nel primo semestre del 2004 la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanza</SPAN></STRONG> ha compiuto 35.000 interventi ispettivi e scoperto un\'evasione fiscale per 6,3 miliardi di euro. Lo rende noto la stessa <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanza</SPAN></STRONG>, spiegando che questa e\' la somma risultante dai 5,1 miliardi di euro di base imponibile occultata, e dagli 1,2 miliardi di euro evasi in materia di Iva.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">In tema di \'sommerso d\'azienda\', aggiunge la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>, sono stati scoperti 4.200 evasori totali, vale a dire il 16% in piu\' rispetto a quelli individuati nello stesso periodo del 2003; nel campo del \'sommerso di lavoro\', sono stati individuati 14.700 lavoratori \'irregolari\', di cui piu\' di 9.600 totalmente in nero.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Il quadro dell\'attivita\' di contrasto all\'evasione e all\'elusione fiscale della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> - si legge nella nota - rivela che, nel primo semestre 2004, sono stati portati a conclusione 35.000 interventi ispettivi, tra verifiche generali, parziali e specifiche, a fronte dei 41.800 pianificati a inizio anno. Comparando il dato con quello del medesimo periodo del 2003, in cui erano stati compiuti 32.000 interventi, si registra un aumento della frequenza ispettiva in campo fiscale pari all\'11%\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La maggior parte degli interventi ispettivi - spiega la GdF - e\' terminata con la constatazione di violazioni sostanziali alla normativa tributaria: infatti, le verifiche generali e quelle parziali effettuate hanno dato luogo alla formulazione di rilievi, rispettivamente, nel 92% e nel 79% dei casi. (ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 1535, 1, 0, '2026-01-09 21:20:24', '0000-00-00'),
(316, '2004-09-06 00:00:00', 'Notizie Flash', 'SICUREZZA: FORZE DELL\'ORDINE GRATIS SUI TRENI IN PIEMONTE - POLIZIA: SAPORITO, DECRETO SU ISPETTORI FACILITA CONTRATTO - SICUREZZA: SAPPE, GOVERNO RISPETTA IMPEGNO CON SINDACATI', '', 'SICUREZZA: FORZE DELL\'ORDINE GRATIS SUI TRENI IN PIEMONTE - POLIZIA: SAPORITO, DECRETO SU ISPETTORI FACILITA CONTRATTO - SICUREZZA: SAPPE, GOVERNO RISPETTA IMPEGNO CON SINDACATI', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: FORZE DELL\'ORDINE GRATIS SUI TRENI IN PIEMONTE<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">IN CAMBIO DISPONIBILI A GARANTIRE CONTROLLI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - TORINO, 3 SET - Il personale delle forze dell\'ordine viaggera\' gratis sui treni regionali piemontesi per garantire maggiore sicurezza ai passeggeri. L\'accordo, che durera\' un anno, e\' stato firmato oggi da Trenitalia, la societa\' dei trasporti torinesi Gtt e la Regione Piemonte. Intese analoghe sono state siglate anche in Lazio, Liguria e Lombardia. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Per viaggiare gratis poliziotti, carabinieri, agenti della polizia penitenziaria, della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanza</SPAN></STRONG> e del Corpo forestale dovranno presentarsi al capotreno prima di salire a bordo. In cambio saranno disponibili a intervenire, su richiesta del personale di Trenitalia o su propria iniziativa, in caso di problemi per la sicurezza e la legalita\' sui convogli. E l\'impatto dell\'iniziativa non dovrebbe essere di poco conto, perche\' i rappresentanti delle forze dell\' ordine in Piemonte sono circa 15 mila, dei quali 1.700 usano quotidianamente il treno.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Per Trenitalia e Gtt far viaggiare gratis le forze dell\'ordine significa incassare rispettivamente 700 mila euro e 40 mila euro in meno all\' anno, ma meta\' del mancato introito verra\' coperto dalla Regione Piemonte che ha stanziato infatti 370.000 euro. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">POLIZIA: SAPORITO, DECRETO SU ISPETTORI FACILITA CONTRATTO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 3 SET -<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>\'\'L\'approvazione da parte del Consiglio dei ministri di oggi del decreto legge concernente disposizioni urgenti per il personale appartenente ai ruoli degli ispettori di polizia, decreto concordato nell\'incontro al Viminale con i sindacati di polizia, potrebbe far superare ogni residua difficolta\' per la trasformazione in contratto della preintesa<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>gia\' sottoscritta il 12 giugno scorso dai <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG> e dai rappresentanti sindacali delle Forze Armate di Polizia\'\'.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Lo ha detto il sottosegretario alla Funzione Pubblica Learco Saporito a margine di un convegno sul pubblico impiego. <SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;</SPAN>(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: SAPPE, GOVERNO RISPETTA IMPEGNO CON <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">SINDACATI</SPAN></STRONG><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">IERI LA CONVOCAZIONE DEL GOVERNO, IL 7 PRIMA RIUNIONE TECNICA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 1 SET - \'Prendiamo atto con piacere che il Governo rispetta la parola data ai <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Sindacati</SPAN></STRONG> di <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG> lo scorso 11 agosto al Viminale, nell\'incontro teso a scongiurare la manifestazione di piazza a Roma per Ferragosto\'\' Lo afferma la segreteria generale del Sindacato Autonomo <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia </SPAN></STRONG>Penitenziaria (Sappe) riferendosi alla convocazione ufficiale del Governo, arrivata ieri mattina, 31 agosto, \'\'in ordine alla necessita\' di istituire un tavolo tecnico al quale affidare il compito di approfondire le problematiche ordinamentali e di carriera delle forze di <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG>\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>La prima riunione tecnica - dice una nota del Sappe - e\' fissata per il 7 settembre alle 10 presso l\'Ufficio Coordinamento e Pianificazione Forze di <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG>. \'\'Quello appena concluso - dice il sindacato - e\' stato un mese denso di novita\' e di intenso lavoro da parte del SAPPE, dei <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Sindacati</SPAN></STRONG> aderenti alla Consulta Sicurezza SAP e SAPAF e del SIULP, organizzazioni che da sole rappresentano oltre la meta\' di tutti gli appartenenti alle Forze di <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG>, che non hanno accettato il diktat del decreto legge per il riallineamento delle carriere dei militari ed hanno contestato lo sbilanciamento del Governo verso le pretese corporative delle Forze Armate\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Nel ricordare che non e\' stato necessario manifestare a Ferragosto \'\'poiche il Governo ha accolto tutte le nostre richieste, quelle del SAPPE, del SAP, del SAPAF, del SIULP: quelle, insomma, dei grandi <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Sindacati</SPAN></STRONG> di <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG>, che non vanno mai in ferie, che non esitano a scendere in piazza per la tutela economica, sociale e professionale dei poliziotti italiani\'\', il sindacato autonomo sottolinea che il Governo si e\' assunto l\' impegno per: \'\'istituire una Commissione Interni in Parlamento che affronti adeguatamente le problematiche legate alle Polizie del Paese; ridefinire il Comparto contrattuale, prevedendo un doppio binario per Comparto Sicurezza e Comparto Difesa; emanare una Legge delega sul riordino dei ruoli e delle carriere delle Forze di <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG> istituendo e istituzione immediata di un tavolo tecnico al Viminale per l\'utilizzo delle risorse gia\' stanziate e che individui, nella prossima legge Finanziaria 2005, gli ulteriori fondi necessari; emanare un Decreto Legge del Governo per \'sanare\' alcuni profili della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Polizia</SPAN></STRONG> di Stato e Penitenziaria rimasti ingiustamente esclusi dal recente provvedimento di riallineamento delle carriere delle Forze Armate\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 6388, 1, 0, '2026-01-09 09:22:17', '0000-00-00'),
(323, '2004-09-20 00:00:00', 'Notizie Flash', 'DOPING: GDF SCOPRE TRAFFICO INTERNAZIONALE VIA INTERNET - MATERIALE DA SEXY SHOP SEQUESTRATO IN DOGANA A COMO', '', 'DOPING: GDF SCOPRE TRAFFICO INTERNAZIONALE VIA INTERNET - MATERIALE DA SEXY SHOP SEQUESTRATO IN DOGANA A COMO', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">DOPING: GDF SCOPRE TRAFFICO INTERNAZIONALE VIA INTERNET<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">A FIUMICINO SEQUESTRATE 50MILA DOSI ANABOLIZZANTI E ORMONI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - FIUMICINO (ROMA), 14 SET - Piu\' di 50 mila dosi di steroidi anabolizzanti ed ormoni ad effetto dopante sono stati sequestrati e 15 persone sono state denunciate dalla <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di Finanza</SPAN></STRONG> di Fiumicino nel corso di una vasta operazione condotta in alcune regioni italiane, tra cui Emilia-Romagna, Marche, Piemonte, Abruzzo e Sardegna.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>I militari della GdF di Fiumicino, diretti dal comandante provinciale Giuseppe Zafarana, hanno incominciato le indagini all\'inizio dell\'anno, in collaborazione con colleghi delle regioni coinvolte dai presunti traffici di anabolizzanti, ed hanno intercettato e sequestrato le fiale di sostanze dopanti, che il piu\' delle volte erano occultate in anonime spedizioni postali, oppure nascoste all\' interno di videocassette provenienti da Pakistan, Argentina, Polonia e Thailandia, scoprendo un ramificato traffico internazionale, attraverso la rete internet, di sostanze proibite del tipo testosterone, nandrolone, ormone della crescita, insulina ed efedrina.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Secondo le analisi avviate successivamente, i finanzieri hanno appurato che si tratta di potentissimi medicinali che producono un aumento della cosiddetta massa magra e dell\'ossigenazione sanguigna, ma che possono provocare effetti collaterali devastanti se assunti in un rischioso mix ormonale, con altri farmaci impiegati per il trattamento di malattie gravi come il cancro ed il nanismo.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>A tutte le persone coinvolte - tra cui insospettabili professionisti, consulenti aziendali, imprenditori e persino giovani studenti universitari, orbitanti negli ambienti delle palestre di body building - sono stati contestati i reati che vanno dal commercio di farmaci ad effetto dopante, alla ricettazione, all\'introduzione in Italia di medicinali esteri senza la necessaria prescrizione o autorizzazione del Ministero della Salute.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">MATERIALE DA SEXY SHOP SEQUESTRATO IN DOGANA A COMO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - COMO, 14 SET - E\' illegale introdurre in Italia oggetti osceni, anche se bolle e documenti sono in regola: con questa motivazione la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> di Ponte-Chiasso e i funzionari di dogana hanno sequestrato trecento riproduzioni in lattice di organi sessuali maschili alla frontiera italo-svizzera di Brogeda.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il sequestro, per \'introduzione nello Stato di materiali osceni\', risale allo scorso fine settimana ma e\' stato reso noto soltanto oggi.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il materiale da sexy shop, giudicato osceno e quindi non idoneo alla commercializzazione in Italia, faceva parte del carico di un Tir proveniente da Amsterdam destinato in parte a Bergamo e in parte a Pordenone.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La documentazione doganale di accompagnamento della merce e\' risultata in regola dal punto di vista fiscale, ma questo non e\' bastato a superare la frontiera. Il provvedimento di sequestro e\' ora sul tavolo del magistrato di turno che dovra\' decidere se dar seguito all\'azione penale.(ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2093, 1, 0, '2026-01-14 11:43:08', '0000-00-00'),
(324, '2004-09-20 00:00:00', 'Notizie Flash', 'CONTRAFFAZIONI: GDF, 41 MLN PEZZI SEQUESTRATI NEL 2004 - SANITA\': A GDF INCARICO CONTROLLO SPESA USL VALLE D\' AOSTA', '', 'CONTRAFFAZIONI: GDF, 41 MLN PEZZI SEQUESTRATI NEL 2004 - SANITA\': A GDF INCARICO CONTROLLO SPESA USL VALLE D\' AOSTA', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">CONTRAFFAZIONE: GDF, NEL 2004 6.443 DENUNCE E 255 ARRESTI<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">OLTRE 75 MLN BENI SEQUESTRATI, DOMANI PRESENTAZIONE RAPPORTO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 15 SET - Nei primi 8 mesi dell\' anno la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> ha denunciato 6.443 persone per contraffazione (di cui 255 arrestate) ed ha sequestrato oltre 75 milioni di beni contraffatti. I dati completi in materia di contrasto a questo fenomeno verranno presentati domani presso il Comando generale della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>, alla presenza del comandante, generale Roberto Speciale.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Nell\' ambito della conferenza, rilevano le Fiamme Gialle, \'\'saranno diffusi dati e analisi riguardanti la contraffazione, in esito all\' impulso all\' azione dato dal ministro dell\'Economia e delle Finanze, Domenico Siniscalco, tra cui i risultati delle 20.000 operazioni di servizio che la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di Finanza</SPAN></STRONG> ha portato a termine tra il 2003 ed il 2004\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">CONTRAFFAZIONI: GDF, 41 MLN PEZZI SEQUESTRATI NEL 2004<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 16 SET - Oltre 41 milioni di pezzi sequestrati, in netto aumento rispetto ai 34 milioni dell\'intero 2003, quasi 10 mila denuncie e 255 arresti. Sono i numeri della lotta della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> alla contraffazione nei primi otto mesi dell\'anno. Una vera e propria industria, quella del falso, che un giro di affari stimato tra i 4 e i 6 miliardi di euro all\'anno in Italia. Solo il valore dei beni sequestrati in 12 mesi da tutte le forze dell\'ordine supera il miliardo e mezzo di euro.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Ci troviamo di fronte - ha spiegato il comandante delle Fiamme Gialle, Generale Roberto Speciale - ad un fenomeno illecito complesso dalle dimensioni molto rilevanti. La contraffazione e\' in grado oggi di riprodurre illecitamente qualsiasi bene e agisce provocando sensibili alterazioni delle dinamiche di mercato, causando danni rilevanti per produttori, commercianti, consumatori e lavoratori\'\'. La <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>, ha sottolineato, \'\'in ottemperanza al rinnovato impulso dato dal ministro dell\'Economia, Domenico Siniscalco, mette in campo tutta la sua competenza tecnico professionale per una lotta senza quartiere alla contraffazione e alla pirateria e ad ogni forma di abusivismo e usurpazione nel settore dei marchi e dei brevetti\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Dal gennaio 2003 ad oggi sono dunque oltre 75 milioni i pezzi sequestrati dai finanzieri. I giocattoli (52%) e\' il settore che ha fatto registrare il maggior numero di beni sequestrati; segue poi la moda (21%), i beni di consumo (16%) e l\'elettronica (11%). Per quanto riguarda i beni di consumo (oltre 12 milioni di pezzi sequestrati) la Campania, con oltre il 60% e\' la regione con il maggior numero di prodotti sequestrati, seguita dall\'Emilia Romagna, dalla Sicilia e dalla Puglia. Per la moda (16 milioni di sequestri) il Lazio e\' la regione in testa ai sequestri, seguita da Campania, Lombardia e Liguria. Nel settore dell\'elettronica (oltre 8 milioni di pezzi sequestrati) Campania, Lazio e Emilia Romagna sono le regioni piu\' colpite. Infine, per quanto riguarda i giocattoli (oltre 39 milioni di pezzi sequestrati), Veneto, Campania ed Emilia Romagna sono le regioni con il maggior numero di prodotti sequestrati. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">A livello mondiale, stime indicano nel 5-7% del commercio totale il valore delle merci contraffatte, per una cifra compresa tra i 200 ed i 3000 miliardi di euro l\' anno.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Negli ultimi due anni la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanza</SPAN></STRONG> ha fatto circa 20.000 interventi in questo settore. Per quanto riguarda le persone verbalizzate dai finanzieri, si rileva una prevalenza di senegalesi, marocchini, cinesi e cittadini provenienti dal Bangladesh.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La contraffazione, ha proseguito il generale Speciale, \'\'oltre ai danni per le imprese, genera un pericolo anche per il consumatore finale, connesso alla sicurezza dei prodotti, specie in alcuni settori, come quello dei giocattoli. E\' infatti facile immaginare quali siano i rischi per la salute dei bambini che entrano in possesso di giocattoli contraffatti\'\'. Inoltre, ha aggiunto, \'\'la maggior parte degli introiti finisce nelle mani dei sodalizi criminali, organizzatori del grande traffico di beni contraffatti. E questi capitali non rimangono a lungo inattivi, ma vengono riciclati attraverso operazioni finanziarie nell\' economia legale, oppure reinvestiti in attivita\' delittuose (traffico di droga, armi, ecc.)\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Lo sviluppo del fenomeno criminale, ha poi sottolineato, \'\'e\' stato facilitato dalla diffusione di internet, che ha aperto nuovi canali di distribuzione per i prodotti falsi, specie quelli che possono essere facilmente veicolati sulla rete (ad esempio i file musicali ed i video)\'\'. Anche per questo, ha aggiunto, \'\'dal comando generale abbiamo appena proceduto ad una rivisitazione della nostra componente specialistica. In particolare, abbiamo previsto la costituzione, nell\' ambito del Nucleo speciale tutela mercati, del Gruppo marchi brevetti e proprieta\' intellettuale, cui e\' assegnato il compito di svolgere analisi operative sul fenomeno; il Gruppo anticrimine tecnologico (Gat) sara\' poi elevato a Nucleo speciale anticrimine tecnologico, reparto rafforzato nei suoi organici, cui e\' affidata l\' azione di contrasto agli illeciti economico-finanziari commessi tramite internet\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SANITA\': A GDF INCARICO CONTROLLO SPESA USL VALLE D\' AOSTA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">CONVENZIONE TRA USL E GDF APPROVATA DALLA GIUNTA REGIONALE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - AOSTA, 15 SET - Sara\' la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> ad individuare nella gestione dell\' Usl della Valle d\' Aosta \'\'gli elementi indicativi di inefficienze, diseconomie ovvero di situazioni potenzialmente irregolari, tali da rendere necessari riscontri\'\'. Lo prevede una convenzione tra la Regione, l\' Usl ed il Comando della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>. L\' atto, che e\' stato approvato dalla Giunta regionale, non prevede oneri a carico del bilancio della Regione.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'La <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>, quale corpo di Polizia economico-finanziaria alle dipendenze del Ministero dell\'Economia e delle Finanze - si legge nella delibera presentata da Antonio Fosson, assessore alla Sanita\' - in collaborazione con l\' Usl puo\' fornire un importante contributo attraverso un\'efficace azione sia sul piano preventivo che repressivo a salvaguardia dell\' attivita\' di gestione ed erogazione delle risorse pubbliche\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il Comando regionale della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>, che ha proposto la propria collaborazione in materia di controllo della spesa sanitaria, in considerazione delle competenze territoriali assegnate, valutera\' l\' opportunita\' di attivare tempestivamente altri Reparti del Corpo per l\' esecuzione dei necessari riscontri. Nel caso individui fatti penalmente rilevanti \'\'s\'impegna, previo nulla osta dell\' Autorita\' Giudiziaria, a comunicare all\' USL le generalita\' delle persone fisiche e giuridiche coinvolte, ai fini dell\' avvio delle procedure di recupero delle somme indebitamente percepite\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>protocollo d\' intesa ha durata annuale dalla data della sottoscrizione e si considera tacitamente rinnovato di anno in anno, salvo disdetta di una delle parti. (ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 1928, 1, 0, '2026-01-14 00:31:18', '0000-00-00'),
(325, '2004-09-20 00:00:00', 'Notizie Flash', 'UE: DIFESA; NASCE IL CORPO DEI CARABINIERI EUROPEI', '', 'UE: DIFESA; NASCE IL CORPO DEI CARABINIERI EUROPEI', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">UE: DIFESA; NASCE IL CORPO DEI CARABINIERI EUROPEI<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">UNA GENDARMERIA PER LE MISSIONI DI PACE, DOMANI LA FIRMA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">(dell\'inviato Rodolfo Calo\')<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - NOORDWIJK (OLANDA), 16 SET - Con una firma che verra\' apposta a margine di una riunione informale dei ministri della difesa dell\'Ue in Olanda, nasce domani un nuovo corpo che vede l\'<STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Italia</SPAN></STRONG> in prima fila: la \'\'<STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Gendarmeria</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">europea</SPAN></STRONG>\'\', una forza di polizia militare creata prendendo a modello i Carabinieri e i \'gendarmes\' francesi. <SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;</SPAN>Scopo della nuova forza - il cui Stato maggiore, secondo quanto si e\' appreso, avra\' sede a Vicenza -<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>e\' quello di intervenire nell\'ambito di future missioni di pace dell\'Ue per svolgere compiti di polizia che i semplici militari, senza specifica preparazione, non sono in grado di assolvere. Si pensa ai Balcani, ma la presidenza di turno, affidata all\'Olanda, parla apertamente di situazioni di crisi \'\'comparabili\'\' a quella irachena (per ora comunque si esclude un impiego della Gendarmeria in Iraq).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Gli uomini saranno forniti dagli unici cinque Paesi dell\'Ue che hanno un qualche forma di polizia militarizzata: oltre a <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Italia</SPAN></STRONG> e Francia, anche Spagna, Olanda e Portogallo. E proprio i ministri della difesa di questi Paesi, per l\'<STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Italia</SPAN></STRONG> il ministro Antonio Martino, firmeranno a \'\'dichiarazione di intenti\'\' che dara\' vita alla Gendarmeria Ue, forte di 800-900 uomini pronti con un preavviso di un mese con una riserva di altri 2.300 militari che portano il potenziale oltre quota 3.000.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il Corpo, ha affermato il ministro della difesa olandese, Henk Kamp, sara\' pronto per l\'anno prossimo ma gia\' entro dicembre dovrebbe essere creato lo stato maggiore nel capoluogo veneto: non si trattera\' di una caserma ma di un quartier generale da cui sara\' guidata la mobilitazione delle forze di stanza nei rispettivi Paesi. Già in marzo, Martino aveva preannunciato che la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Gendarmeria</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">europea</SPAN></STRONG> sarebbe stata guidata dai Carabinieri con quartier generale in <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Italia</SPAN></STRONG>.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La firma viene apposta a margine di un incontro informale dei ministri della difesa convocato a Noordwijk, una ventosa localita\' di mare nei pressi dell\'Aja in cui si discutera\' nel concreto su altri aspetti cruciali della politica di difesa europea: tra l\'altro, come utilizzare al meglio e in maniera congiunta i mezzi finanziari destinati agli armamenti (l\'Ue spende meno della meta\' degli Usa realizzando pero\', secondo stime, solo un decimo della potenza americana). Il ministro Kemp oggi ha lamentato carenze nell\'intelligence e nel trasporto di truppe e mezzi, un limite che potrebbero risultare grave qualora l\'Ue si dovesse impegnare in piu\' operazioni contemporaneamente. Queste dovrebbero essere condotte attraverso anche \'\'Battle groups\'\', unita\' interforze di impiego ultra-rapido (entro 15 giorni) composte da 1.500-2.000 uomini di cui i ministri tornano a discutere nel dettaglio domani.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>Kemp ha confermato che Francia e Gran Bretagna avranno \'\'sicuramente\'\' un proprio \'\'battle group\'\' mentre la Germania \'\'potrebbe averne uno\'\'. L\'obiettivo e\' quello di crearne a sufficienza nel 2007 puntando al numero di nove con una \'\'capacita\' iniziale\'\' disponibile gia\' l\'anno prossimo. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 1942, 1, 0, '2026-01-13 23:55:33', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(326, '2004-09-23 00:00:00', 'Nostro Servizio', 'PER I RAPPRESENTANTI COCER, COIR E COBAR DELLA GdF.\r\nE\' prossima una importante scadenza: il 30 settembre.IL COCER CARABINIERI AVVERTE', '', 'PER I RAPPRESENTANTI COCER, COIR E COBAR DELLA GdF.\r\nE\' prossima una importante scadenza: il 30 settembre.IL COCER CARABINIERI AVVERTE', '<P>Il prossimo 30 settembre scade il termine per la verifica che la rappresentanza della Guardia di Finanza si era data al termine della riunione congiunta degli Organismi, tenutasi il 30 e 31 marzo scorsoa Villa Spada.</P>\r\n<P>Ricordiamo che in quella sede, in un documento votato, i convenuti all\'incontro, chiedevano \"che si tenti la via di una mini riforma su sette punti irrinunciabili\". Si trattava di sette punti qualificanti ed imprescindibili per arrivare ad una riforma della 382 che desse dignità ai rappresentanti.</P>\r\n<P>In altro editoriale pubblicheremo l\'ultimo testo varato dalla Commissione Difesa della Camera dei Deputati con un ampio commento che, punto per punto elenca gli aspetti di arretramento rispetto, non solo alla attuale 382, non parliamo poi rispetto alla prassi che il tempo ha costruito, ma, addirittura, alle precedenti versioni predisposte dalla stessa Commissione nei mesi precedenti.</P>\r\n<P>Chi si è fatto giustamente sentire sono i delegati del COCER Carabinieri che sembrano voler scavalcare i rappresentanti del COCER Guardia di Finanza, visto che nell\'articolo apparso sul Sole 24 Ore di qualche giorno fà,fanno pressing ed avvertono \"<STRONG>SE NON PASSA la RIFORMA DELLA RAPPRESENTANZA MILITARE, CI COSTITUIREMO IN SINDACATO</STRONG>\".</P>', 92, '', '', '', 1926, 1, 0, '2026-01-07 17:36:53', '0000-00-00'),
(327, '2004-09-23 00:00:00', 'Nostro Servizio', 'COCER CARABINIERI AVVERTE:SE NON PASSA LA RIFORMA CI COSTITUIREMO IN SINDACATO', '', 'COCER CARABINIERI AVVERTE:SE NON PASSA LA RIFORMA CI COSTITUIREMO IN SINDACATO', '<P>Il&nbsp; 30 settembre prossimo&nbsp; scade un termine molto importante.</P>\r\n<P>Ricordiamo che i&nbsp; rappresentanti del COCER, dei COIR e dei COBAR della G. di F., a Villa Spada, il 30 e 31 marzo scorso, al termine di un incontro,, avevano stabilito questo termine per verificare se la riforma della 382, in corso alla Commissione Difesa della camera dei deputati, avesse colto sette punti qualificanti che avevano indicato in un documento.</P>\r\n<P>In altro editoriale indicheremo i punti rilevanti dell\'ultimo testo varato dalla Commissione e soprattutto evidenzieremo come, per molti aspetti, il testo sia arrettrato&nbsp; non solo rispetto al testo attuale, più ancora rispetto alla prassi che si è costruita nel tempo, ma, addirittura, rispetto ai testi licenziati dalla stessa Commissione nei mesi precedenti.</P>\r\n<P>Solo il COCER Carabinieri sembra essersene accorto ed evidenzia il proprio punto di vista in un articolo apparso nei giorni scorsi&nbsp; sul sole 24 ore nel&nbsp; quale avverte: <STRONG>SE IL DDL NON PASSA CI COSTITUIREMO IN SINDACATO\"</STRONG></P>', 93, '', '', '', 1990, 1, 0, '2026-01-14 00:29:17', '0000-00-00'),
(328, '2004-09-24 00:00:00', 'Nostro Servizio', 'COCER CARABINIERI AVVERTE:SE NON PASSA LA RIFORMA CI COSTITUIREMO IN SINDACATO', '', 'COCER CARABINIERI AVVERTE:SE NON PASSA LA RIFORMA CI COSTITUIREMO IN SINDACATO', '<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">Il&nbsp; 30 settembre prossimo&nbsp; scade un termine molto importante.<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">Ricordiamo che i&nbsp; rappresentanti del COCER, dei COIR e dei COBAR della G. di F., a Villa Spada, il 30 e 31 marzo scorso, al termine di un incontro,, avevano stabilito questo termine per verificare se la riforma della 382, in corso alla Commissione Difesa della camera dei deputati, avesse colto sette punti qualificanti che avevano indicato in un documento.<o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">In altro editoriale indicheremo i punti rilevanti dell\'ultimo testo varato dalla Commissione e soprattutto evidenzieremo come, per molti aspetti, il testo sia arrettrato&nbsp; non solo rispetto al testo attuale, più ancora rispetto alla prassi che si è costruita nel tempo, ma, addirittura, rispetto ai testi licenziati dalla stessa Commissione nei mesi precedenti.<o:p></o:p></SPAN></P>\r\n<P><SPAN style=\"FONT-SIZE: 10pt; COLOR: black; FONT-FAMILY: Verdana\">Solo il COCER Carabinieri sembra essersene accorto ed evidenzia il proprio punto di vista in un articolo apparso nei giorni scorsi&nbsp; sul sole 24 ore nel&nbsp; quale avverte: <STRONG><SPAN style=\"FONT-FAMILY: Verdana\">SE IL DDL NON PASSA CI COSTITUIREMO IN SINDACATO\"</SPAN></STRONG><o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>', 94, '', '', '', 2226, 1, 0, '2026-01-11 10:05:31', '0000-00-00'),
(329, '2004-09-26 00:00:00', 'Editoriale', 'Utimo testo della Riforma della rappresentanza - Nostro commento che evidenzia aspetti estremamente negativi.', '', 'Utimo testo della Riforma della rappresentanza - Nostro commento che evidenzia aspetti estremamente negativi.', '<P>Pubblichiamo, in allegato, il testo della riforma della legge 382, così come risulta dagli ultimi lavori della Commissione Difesa. In neretto le variazioni rispetto all\' ultimo testo.</P>\r\n<P>Assieme, un commento che evidenzia come non si sono minimamente accolte le proposte avanzate da più parti, in particolare dai COCER, per costruire un sistema di rappresentanza moderno e capace di tutelare il personale militare, anzi sono da registrare degli arretramenti rispetto, addirittura, al testo di novembre.</P>\r\n<P>&nbsp;</P>', 95, '', '', '', 2694, 1, 0, '2026-01-14 06:35:14', '0000-00-00'),
(330, '2004-09-26 00:00:00', 'Notizie Flash', 'PM CHIEDE 15 ANNI PER UFFICIALE GDF, INDAGO\' SU CARD GIORDANO - GDF: \'\'STASERA PAGHIAMO NOI\'\' PER FESTA PATRONO CON FIORELLO - ATENE 2004: FESTA GDF', '', 'PM CHIEDE 15 ANNI PER UFFICIALE GDF, INDAGO\' SU CARD GIORDANO - GDF: \'\'STASERA PAGHIAMO NOI\'\' PER FESTA PATRONO CON FIORELLO - ATENE 2004: FESTA GDF', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">PM CHIEDE 15 ANNI PER UFFICIALE GDF, INDAGO\' SU CARD GIORDANO<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">E\' IMPUTATO DI CONCUSSIONE COMMESSA CON METODO MAFIOSO<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - LAGONEGRO (POTENZA), 20 SET - La condanna a 15 anni di reclusione per l\' ex tenente della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza </SPAN></STRONG>Fiorenzo Fioravanti - che indago\' sul Cardinale di Napoli, Michele Giordano - e\' stata chiesta in serata al Tribunale di Lagonegro (Potenza) dal pubblico ministero della Direzione<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>distrettuale antimafia di Potenza, Vincenzo Montemurro.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Fioravanti, che fu arrestato due volte negli anni scorsi, e\' accusato di associazione per delinquere finalizzata alla commissione di numerosi episodi di concussione. Nella requisitoria, durata alcune ore, Montemurro ha evidenziato il legame della continuazione nei singoli episodi di concussione e l\' aggravante del metodo mafioso con il quale i reati vennero commessi. Secondo l\' accusa, Fioravanti - che comandava la tenenza di Lauria (Potenza) della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> - si fece consegnare denaro ed ottenne altri favori per evitare verifiche fiscali a diversi operatori economici della zona.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il processo proseguira\' nei prossimi giorni con le arringhe degli avvocati di parte civile e di Fioravanti, dopo di che il Tribunale si ritirera\' in camera di consiglio: la sentenza dovrebbe essere emessa all\' inizio della prossima settimana. (ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">GDF: \'\'STASERA PAGHIAMO NOI\'\' PER FESTA PATRONO CON FIORELLO<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - PALERMO, 21 SET - La <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> festeggia a Palermo il patrono, San Matteo, e per la manifestazione organizzata per oggi annunciano: \'\'Stasera paghiamo noi\'\'. Il titolo introduce lo spettacolo di Fiorello al Teatro di Verdura, alla presenza del Comandante generale, Roberto Speciale. <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Lo showman siciliano e\' molto legato alla <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza </SPAN></STRONG>per via del padre (deceduto nel 1990) che era un finanziere. <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Gli incassi saranno interamente devoluti alle associazioni \'\'Piera Cutino\'\', presieduta da Rita Borsellino, attiva nel settore dell\'assistenza sanitaria ai malati di talassemia ed emoglobinopatia, e \'\'Missione speranza e carita\'\'\', di Biagio Conte, operante nel campo dell\'accoglienza e assistenza dei senzatetto, degli indigenti e degli emarginati.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La manifestazione e\' organizzata dal Comando interregionale dell\'Italia Sud Occidentale guidato dal generale Umberto Fava. (ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">ATENE 2004: FESTA GDF; T CAGNOTTO,IN USA MA NON PER IMPIANTI<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">PREMIATI 42 ATLETI, IL COMANDANTE \'ESEMPIO SPORTIVO E MORALE\'<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 23 SET - Fiamme gialle fa rima con medaglie: alle Olimpiadi di Atene il medagliere italiano ha raggiunto quota 32. E di queste un oro, due argenti e cinque bronzi portano la firma di atleti appartenenti al gruppo sportivo della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>. <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Quarantadue atleti-finanzieri in divisa ufficiale, ovvero una scintillante tuta gialloverde, hanno ricevuto dal Comandante Generale Roberto Speciale ricompense di ordine morale ed encomi semplici, e da parte del presidente del CONI Giovanni Petrucci, elogi e ringraziamenti. Cerimonia rigorosa, come vuole la tradizione militare. Ma le emozioni, a volte, sono difficili da controllare . E allora ecco, a dispetto del cerimoniale, l\'abbraccio commosso fra il Comandante Speciale e la medaglia di bronzo nel salto con l\'asta, Giuseppe Gibilisco, che ha motivato il suo slancio d\'affetto affermando: \'\'Questo capita sempre fra due siciliani; c\'e\' grande intesa con il Comandante Generale. E poi e\' proprio a lui che avevo dedicato questa medaglia\'\' <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ma al Comandante, un bacio ed un abbraccio scappa pure al momento della premiazione della tuffatrice Tania Cagnotto; ma questa volta per motivazioni ben diverse: \'\'finalmente una della mia altezza\'\'.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>E proprio la Cagnotto, che nei giorni scorsi aveva dichiarato la sua intenzione di trasferirsi all\'estero a causa dell\'inadeguatezza degli impianti sportivi italiani, smorza qualsiasi polemica: \'\'la decisione di lasciare l\'Italia risale a molto tempo fa, anche se ancora non c\'e\' nulla di certo. Voglio fare un\'esperienza all\'estero, a Houston in particolare; sicuramente, pero\', non partiro\' prima di un anno. Il problema principale non riguarda tanto le strutture degli impianti quanto il poco tempo che abbiamo a disposizione per allenarci. Infatti, a Bolzano, la mattina non ci consentono di entrare in acqua; la piscina e\' a disposizione solo di pomeriggio\'\'. <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Le discipline sportive nelle quali gli atleti della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> hanno gareggiato sono le piu\' svariate: atletica leggera, judo, nuoto, canoa, canottaggio. Ma quel \'mostro\' di sport che e\' il calcio, anche quando non e\' direttamente contemplato, e\' sempre argomento di discussione. La medaglia d\'argento nel K2 Antonio Rossi, ironizza sul suo Milan e, riferendosi alla sconfitta subita in casa contro il Messina, afferma \'\'E\' tornata la squadra generosa che regala punti...A parte gli scherzi, il livello calcistico delle squadre del sud si e\' alzato molto\'\'.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Una vena polemica e\' invece celata nelle parole della judoka Lucia Morico, bronzo ad Atene: \'\'Noi atleti siamo un punto di riferimento per i bambini, sarebbe di buon gusto avere un certo tenore; e nel calcio c\'e\' un andamento che non va. Sarebbe bello se tutti fossero come Cristiano Lucarelli, uno dei pochi calciatori in grado di compiere gesti forti\'\'. <SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;</SPAN>(ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2957, 1, 0, '2026-01-14 01:38:57', '0000-00-00'),
(331, '2004-09-26 00:00:00', 'Notizie Flash', 'CONTRATTI: VIGILI FUOCO; CGIL, LEGGE E\' RITORNO AL PASSATO - CONTRATTI: VIGILI FUOCO; CONFSAL, PIENA SODDISFAZIONE', '', 'CONTRATTI: VIGILI FUOCO; CGIL, LEGGE E\' RITORNO AL PASSATO - CONTRATTI: VIGILI FUOCO; CONFSAL, PIENA SODDISFAZIONE', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">CONTRATTI: VIGILI FUOCO; CGIL, LEGGE E\' RITORNO AL PASSATO<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">PATTA, UNA GRAVISSIMA VIOLAZIONE DEL SISTEMA CONTRATTUALE<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 22 SET - Un ritorno al passato \'\'quando era la politica a presiedere ai trattamenti economici del personale\'\': questo il giudizio di Gian Paolo Patta, segretario confederale della Cgil, sull\' approvazione della legge delega che riformula il rapporto di lavoro dei <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">vigili</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">del</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">fuoco</SPAN></STRONG>.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Secondo il sindacalista, il provvedimento rappresenta \'\'una violazione del sistema contrattuale e delle relazioni sindacali nel pubblico impiego\'\'. Anche sui benefici nel servizio svolto per la cittadinanza che dovrebbero derivare dalla legge, Patta e\' perplesso: \'\'E\' tutto da dimostrare - spiega - che tale provvedimento rappresenti un intervento che migliora l\'efficienza del Corpo nazionale dei <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Vigili</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">del</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">fuoco</SPAN></STRONG>, operatori che i cittadini hanno imparato ad apprezzare quotidianamente\'\'.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Cgil e organizzazioni sindacali confederali preannunciano una \'\'piena e totale contrarieta\' se questo e\' l\'intendimento con il quale il governo intende affrontare la nuova stagione contrattuale\'\'.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Intanto - conclude Patta - tra i <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Vigili</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">del</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">fuoco</SPAN></STRONG> si procedera\' alle elezioni delle rappresentanze sindacali unitarie e la Cgil si impegnera\' in quella sede per affermare il pieno diritto alla contrattazione e al rinnovo del contratto di lavoro che la legge stessa demanda ad una lunghissima ed incerta stagione di applicazione\'\'. (ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">CONTRATTI: VIGILI FUOCO; CONFSAL, PIENA SODDISFAZIONE<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">RAGGIUNTO UN IMPORTANTE TRAGUARDO PER LA CATEGORIA<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 22 SET - Il sindacato Confsal-<STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Vigili</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">del</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">fuoco </SPAN></STRONG>esprime soddisfazione per l\'approvazione al Senato del disegno di legge delega che interessa la categoria. <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Attraverso tale provvedimento - dichiara Franco Giancarlo, segretario generale del sindacato - il trattamento economico dei <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Vigili</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">del</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Fuoco</SPAN></STRONG> viene equiparato a quello degli operatori della Polizia di Stato, ricompensandone la professionalita\' con una retribuzione maggiore\'\'.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'La nuova disciplina contrattuale - aggiunge Giancarlo - dimostra che le istituzioni avvertono la necessita\' di rafforzare e valorizzare le professionalita\' espresse dal Corpo dei <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Vigili</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">del</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Fuoco</SPAN></STRONG> che rappresenta il principale strumento per fronteggiare ogni tipo di emergenza e calamita\' naturale\'\'. <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il segretario della Confsal, ringrazia, infine, \'\'tutte le forze politiche di maggioranza e di opposizione che hanno spianato la strada per questo importante traguardo raggiunto dalla nostra categoria\'\'. (ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 1809, 1, 0, '2026-01-14 03:07:44', '0000-00-00'),
(332, '2004-09-26 00:00:00', 'Notizie Flash', 'FINANZIARIA: SINISCALCO A PALAZZO CHIGI INCONTRO CON SINDACATI POLIZIA E FORZE ARMATE - FINANZIARIA: SILP-CGIL,DA GOVERNO NIENTE SOLDI PER SICUREZZA - SICUREZZA: SAP, SLITTA TAVOLO TECNICO SU RIORDINO CARRIERE', '', 'FINANZIARIA: SINISCALCO A PALAZZO CHIGI INCONTRO CON SINDACATI POLIZIA E FORZE ARMATE - FINANZIARIA: SILP-CGIL,DA GOVERNO NIENTE SOLDI PER SICUREZZA - SICUREZZA: SAP, SLITTA TAVOLO TECNICO SU RIORDINO CARRIERE', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">FINANZIARIA: SINISCALCO A PALAZZO CHIGI<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">INCONTRO CON <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">SINDACATI</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">POLIZIA</SPAN></STRONG> E FORZE ARMATE<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 23 SET - Il ministro dell\'Economia, DomenicoSiniscalco, e\' da poco arrivato a Palazzo Chigi dove incontrera\' i rappresentanti dei <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">sindacati</SPAN></STRONG> della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">polizia</SPAN></STRONG> e delle forza armate in vista della messa a punto della Finanziaria 2005. (ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">FINANZIARIA: SILP-CGIL,DA GOVERNO NIENTE SOLDI PER SICUREZZA<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">ALLARMANTE VISTA LA CRESCITA DELLE MINACCE TERRORISTICHE<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 24 SET - Dal Governo niente soldi per la sicurezza. Lo afferma Claudio Giardullo, segretario generale Silp-Cgil, dopo l\' incontro a Palazzo Ghigi tra l\' Esecutivo e le rappresentanze sindacali e militari del Comparto sicurezza e difesa, sulle linee guida delle Finanziaria 2005. <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Abbiamo appreso - spiega Giardullo - che i capitoli di bilancio delle amministrazioni interessate potrebbero essere incrementati del 2,5%. Siccome il decreto dell\' allora ministro Giulio Tremonti ha tagliato dal 20 al 30% i fondi destinati a settori strategici dell\' attivita\' di polizia, tagli che non sono stati recuperati dalle ultime leggi finanziarie, ne consegue che il governo non intende investire nel potenziamento infrastrutturale delle forze dell\' ordine\'\'.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Che l\' Esecutivo non trovi i soldi necessari per la sicurezza - osserva il segretario della Silp-Cgil - e\' francamente allarmante, vista da una parte la crescita della minaccia terroristica e criminale nel nostro Paese e dall\' altra la volonta\' di costituire, con la devolution, le polizie amministrative regionali. Cioe\' - sottolinea - strutture dal costo rilevante, che accentrerebbero invece che decentrare le attivita\' di <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">polizia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">amministrativa</SPAN></STRONG>, e renderebbero meno efficace l\' azione del nostro sistema di sicurezza\'\'. (ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">SICUREZZA: SAP, SLITTA TAVOLO TECNICO SU RIORDINO CARRIERE<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">NON ESCLUSA MANIFESTAZIONE PROTESTA SOTTO VIMINALE<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 20 SET - Revocata la convocazione, prevista per domani, del tavolo tecnico che avrebbe affrontare il riordinamento delle carriere delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">polizia</SPAN></STRONG>. Lo fa sapere il Sap (Sindacato autonomo di polizia), che annuncia una mobilitazione per protestare contro questa decisione.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il 9 agosto scorso, spiega il sindacato, \'\'il Governo aveva assunto l\' impegno di affrontare e risolvere i temi del riordinamento delle carriere delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">polizia</SPAN></STRONG>, evitando la manifestazione che era stata organizzata dai sindacati per il giorno di Ferragosto. Le direttive impartite dal ministro dell\'Interno, Giuseppe Pisanu, per arrivare in tempi rapidissimi ad affrontare la questione a livello legislativo, a distanza di 40 giorni, rischiano di essere eluse. E\' stata infatti revocata la convocazione del tavolo tecnico delegato allo scopo\'\'. Una decisione che il Sap addebita \'\'ai componenti delle varie amministrazioni interessate, con l\' obiettivo di ostacolare l\'azione del Governo\'\'.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Per contrastare questa tendenza, e per sostenere l\'iniziativa dell\' intero Esecutivo e del ministro Pisanu - prosegue - il Sap ha convocato per giovedi\' prossimo a Roma tutti i segretari regionali e provinciali, senza escludere la possibilita\' di una manifestazione sotto il Viminale per riaffermare le regole della democrazia e il rispetto dei ruoli, anche di quelli della rappresentanza del personale\'\'.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il Sap ritiene che \'\'il rinvio del tavolo tecnico, ingiustificato sotto ogni aspetto, potrebbe cagionare danni irreparabili, facendo venir meno i tempi tecnici necessari per l\' approvazione della delega legislativa e per il provvedimento delegato\'\'.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il sindacato ricorda infine che \'\'la riforma che si vuole ostacolare arriva a due anni dall\' impegno contrattuale di rivedere la problematica legata alla professionalita\' delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">polizia</SPAN></STRONG>, sulla quale sono stati votati ordini del giorno in Parlamento e stanziate risorse non utilizzate\'\'. (ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2937, 1, 0, '2026-01-09 21:33:53', '0000-00-00'),
(333, '2004-09-26 00:00:00', 'Notizie Flash', 'RIFORME: CAMERA, SI\' A POLIZIA AMMINISTRATIVA REGIONALE\r\n\r\n', '', 'RIFORME: CAMERA, SI\' A POLIZIA AMMINISTRATIVA REGIONALE\r\n\r\n', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">RIFORME: CAMERA, SI\' A <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">POLIZIA</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">AMMINISTRATIVA</SPAN></STRONG> REGIONALE<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\">PASSA SUBEMENDAMENTO COMMISSIONE, MODIFICA ART. 117 COSTITUZIONE<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 22 SET - Si\' dell\'Aula di Montecitorio alla \'<STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">polizia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">amministrativa</SPAN></STRONG> regionale e locale\'. L\'Assemblea ha approvato il subemendamento all\'articolo 34 del testo di riforma della parte Seconda presentato dalla commissione Affari costituzionali che modifica l\'art. 117 della Costituzione.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>I si\' sono stati 253, 201 i no (il centrosinistra al completo), otto gli astenuti.(ANSA). <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black\"><o:p><FONT face=\"Times New Roman\">&nbsp;</FONT></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2792, 1, 0, '2026-01-09 18:27:27', '0000-00-00'),
(334, '2004-10-06 00:00:00', 'Notizie Flash', 'GUARDIA FINANZA: NASCE REPARTO RELAZIONI E COMUNICAZIONE - BOVINI IMPORTATI DA PAESI EST FRODANDO L\'UE, OPERAZIONE GDF', '', 'GUARDIA FINANZA: NASCE REPARTO RELAZIONI E COMUNICAZIONE - BOVINI IMPORTATI DA PAESI EST FRODANDO L\'UE, OPERAZIONE GDF', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">GUARDIA FINANZA: NASCE REPARTO RELAZIONI E COMUNICAZIONE<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">A DIRIGERLO E\' IL GENERALE GIORGIO TOSCHI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 30 SET - Novita\' ordinativa al Comando generale della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanza</SPAN></STRONG> in materia di comunicazione: e\' stato istituito il quinto Reparto \'\'Relazioni Esterne e Comunicazione\'\'. Il nuovo Reparto risponde all\' esigenza di ricondurre in modo unitario la responsabilita\' delle azioni di coordinamento e di gestione delle competenze in materia di informazione e comunicazione, interna ed esterna.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Nell\' ambito del quinto Reparto sono inquadrati l\' Ufficio stampa, l\' Ufficio relazioni esterne e cerimoniale, l\' Ufficio centrale relazioni con il pubblico, l\' Ufficio storico e l\' Ufficio assistenza e protezione sociale.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Principale obiettivo del nuovo Reparto e\' far conoscere la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> ai cittadini nelle funzioni di tutela della sicurezza economica e finanziaria del Paese e dell\' Unione Europea. A dirigerlo, da oggi, e\' il generale Giorgio Toschi. Continua a svolgere la funzione di portavoce il capo Ufficio stampa, colonnello Carlo Ricozzi. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il generale Toschi, nato a Chieti nel 1954, sposato, con un figlio, e\' laureato in giurisprudenza, economia e commercio e scienze della sicurezza economico-finanziaria. E\' entrato in Accademia nel 1974 ed ha rivestito, nel corso della sua carriera, prestigiosi incarichi di comando in numerosi Reparti del Corpo. Da ultimo ha retto, per due anni, il Nucleo speciale Polizia valutaria di Roma. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">BOVINI IMPORTATI DA PAESI EST FRODANDO L\'UE, OPERAZIONE GDF<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">7 DENUNCE TRA ABRUZZO E VENETO, SEQUESTRATI 700 CAPI BESTIAME<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - PESCARA, 30 SET - Avevano costituito almeno 300 societa\' fittizie, ciascuna delle quali importava fino a 100 capi di bestiame da Paesi dell\'Est, usufruendo in tal modo - piu\' volte e per piu\' quote - di agevolazioni dell\'80% sui dazi di importazione. La truffa ai danni dell\'Ue e\' stata scoperta grazie all\'operazione \'\'Cow Boy\'\', condotta dal Gruppo repressione frodi della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> di Pescara, che ha indagato tre pescaresi, quattro veneti e sequestrato 700 bovini in un pascolo di Rocca di Mezzo (L\'Aquila).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La base dell\'organizzazione era a Treviso. La costituzione di societa\' ad hoc consentiva di accedere a un elevato numero di titoli di importazione, eludendo i regolamenti comunitari destinati ad agevolare la libera concorrenza e il libero accesso di nuovi operatori al mercato. I bovini arrivavano da Polonia, Slovacchia e Romania. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">\'\'Dall\'attivita\' investigativa - ha riferito il comandante del Nucleo regionale di Polizia tributaria di Pescara, colonnello Marco Angeloni - sono emersi elementi precisi e concordanti su ditte abruzzesi titolari dei certificati di importazione, collegate alla capogruppo del Nord Est, che non avevano per oggetto sociale dell\'attivita\' l\'allevamento di animali. Erano di tutt\'altro genere: commercio al dettaglio di fiori, frutta e verdure, impresa edile\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Un\'ulteriore societa\' abruzzese, con sede fittizia in un mattatoio in disuso, era stata creata con il compito esclusivo di individuare siti nel territorio regionale, adatti al pascolo dei bovini importati. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\'evasione accertata dei soli dazi doganali ammonta a oltre un milione di euro. Il numero di societa\' fittizie e\' destinato ad aumentare: le indagini proseguono per meglio delineare il quadro investigativo e individuare ulteriori persone fisiche e giuridiche coinvolte nella truffa.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\'operazione e\' stata portata a termine dalla <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di finanza</SPAN></STRONG> la scorsa settimana - al termine di indagini avviate nel giugno 2003 -, con il sequestro dei 700 vitelli maschi tenuti allo stato brado, in un\'area dell\'Altopiano delle Rocche in grado di ospitarne 400. Alcuni versavano in evidente stato di denutrizione e disidratazione, secondo quanto ha verificato il Servizio veterinario e sanita\' animale del Dipartimento di prevenzione dell\'Aquila. I finanzieri, improvvisatisi mandriani, sono stati aiutati nelle operazioni di sequestro dall\'amministrazione comunale di Rocca di Mezzo.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>I reati contestati riguardano la truffa aggravata ai danni dell\'Unione europea, contrabbando aggravato, pascolo abusivo e violazione delle leggi sanitarie finalizzate alla tutela della salute pubblica. I sette indagati rischiano la pena della reclusione fino a un massimo di sei anni e la multa che va da due a dieci volte i diritti doganali evasi.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Le agevolazioni concesse dall\'Unione europea interessano complessivamente 123 mila capi, e sono destinate prevalentemente ai piccoli allevatori di Italia e Grecia, Paesi il cui fabbisogno di carne bovina non e\' coperto dalla produzione interna. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 3177, 1, 0, '2026-01-09 23:01:47', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(335, '2004-10-06 00:00:00', 'Notizie Flash', 'CONDANNATO TENENTE \'INDOMITO\', INDAGO\' SU CARD GIORDANO - COLONNELLO GDF ASSOLTO PER CONCUSSIONE - DROGA: CAFFE\' AL POSTO DI EROINA, ARRESTATO MARESCIALLO GDF - MAFIA: TALPE DDA; PROCURA DEPOSITA ATTO D\'ACCUSA A GUP', '', 'CONDANNATO TENENTE \'INDOMITO\', INDAGO\' SU CARD GIORDANO - COLONNELLO GDF ASSOLTO PER CONCUSSIONE - DROGA: CAFFE\' AL POSTO DI EROINA, ARRESTATO MARESCIALLO GDF - MAFIA: TALPE DDA; PROCURA DEPOSITA ATTO D\'ACCUSA A GUP', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">CONDANNATO TENENTE \'INDOMITO\', INDAGO\' SU CARD GIORDANO<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">12 ANNI ALL\' UFFICIALE GDF, COLPEVOLE DI CONCUSSIONE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">(di Mario Restaino)<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - POTENZA, 28 SET - Estate del 1998: i giorni \'\'caldi\'\' dell\' inchiesta sull\' usura nella quale era coinvolto il Cardinale di Napoli, Michele Giordano. A Lagonegro (Potenza) - sede della \'\'piccola Procura\'\' sul cui registro degli indagati era finito il porporato - circolava un settimanale locale. Titolo a tutta pagina: \'\'L\' indomito\'\'. Sotto, sempre a tutta pagina, la foto in alta uniforme del tenente della <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">di Finanza</SPAN></STRONG> Fiorenzo Fioravanti, l\' investigatore di punta dei magistrati che indagavano su Giordano.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Oggi, palazzo di giustizia di Lagonegro: dopo quasi quattro giorni di camera di consiglio, il Tribunale condanna Fioravanti a 12 anni e dieci mesi di reclusione, ritenendolo responsabile di concorso in concussione continuata.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Fioravanti non e\' in aula, al momento della lettura della sentenza: in quel palazzo di giustizia nel quale si muoveva con passo sicurissimo (dal dibattimento e\' emerso che avrebbe ricevuto da un coimputato 50 milioni di lire, frutto di tangenti, in una stanza della Procura della Repubblica, proprio nei giorni piu\' intensi dell\' inchiesta sull\' usura) oggi lui non e\' entrato.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\' ultima volta, ci era venuto la settimana scorsa, prima delle richieste del pubblico ministero (il pm della Direzione distrettuale antimafia di Potenza, Vincenzo Montemurro, aveva chiesto al Tribunale la condanna a 15 anni di reclusione) e le arringhe dei difensori. Aveva chiesto di parlare: una volta davanti al Tribunale, aveva negato di aver ricevuto mai alcuna tangente, una dichiarazione che aveva colpito (negativamente) quanto meno la pubblica accusa. In precedenza, durante il dibattimento, Fioravanti aveva ammesso di aver avuto dei \'\'regali\'\', mai delle tangenti.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Fiorenzo Fioravanti, che oggi ha 51 anni, fini\' agli arresti domiciliari il 27 aprile 2000 per concorso in tentativo di concussione ai danni di un ristoratore; il 13 luglio successivo, passo\' dalla sua abitazione al carcere di Santa Maria Capua Vetere (Caserta), con l\' accusa di vari episodi di concussione; il 20 novembre dello stesso anno, i Carabinieri bussano alla sua cella con un\' altra ordinanza di custodia cautelare, nella quale l\' accusa di concussione e\' accompagnata dalla contestazione dell\'associazione per delinquere; il 19 gennaio 2001, un altro arresto.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ma l\' \'\'indomito\'\' era crollato gia\' prima e a risollevare la sua situazione giudiziaria non basto\' la difesa del professor Carlo Taormina, poi sollevato dall\' incarico. Le testimonianze raccolte dal pubblico ministero, Vincenzo Montemurro, e l\'andamento del processo hanno riproposto lo schema di azione dell\' ufficiale delle fiamme gialle. Comandante della tenenza della <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> di Lauria (Potenza), guida degli investigatori della Procura che indagavano sull\' usura e sul Cardinale di Napoli, Fioravanti (che scortava costantemente il Procuratore della Repubblica, titolare dell\' inchiesta) \'\'incuteva timore\'\', come testimonio\' una delle vittime, anche per le persone che insieme a lui agivano (e che hanno scelto il rito abbreviato: sono state condannate e attendono ora il processo di appello).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Copione standard: individuato l\' imprenditore o il commerciante da colpire, lo si convocava con un pretesto in caserma, a Lauria, o direttamente al palazzo di giustizia, a Lagonegro. Nel frattempo, magari aveva ricevuto una visita in azienda e alcuni documenti erano stati acquisiti. La proposta, diretta o indiretta, era chiarissima: pagare (decine di milioni) o subire le conseguenze di una verifica fiscale. In qualche caso - secondo l\' accusa - il tenente ottenne l\' esecuzione di lavori - senza pagare - nella sua villa in costruzione, a Villapiana (Cosenza).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Uno provo\' a resistere e Fioravanti taglio\' corto: \'\'Ho capito a te ti devo far imparare la strada della caserma\'\'. <SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;</SPAN>Fu uno dei suoi complici, un impreditore, ad incastrare il tenente: registro\' ogni colloquio telefonico, con appuntamenti e accordi per dividere i soldi, poi nascose tutto in casa di una zia, in una valigetta 24 ore che - secondo alcuni - ha custodito anche \'\'carte\'\' pericolose per persone piu\' in alto di Fioravanti. Il quale tuttavia ha presentato ricorso contro il provvedimento di diniego della richiesta di rientrare nei ranghi della <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>: indomito.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">COLONNELLO GDF ASSOLTO PER CONCUSSIONE, PM IMPUGNA SENTENZA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">PER ACCUSA FRENO\' INDAGINI SU GUADAGNI EX SCIATORE ALBERTO TOMBA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - GENOVA, 1 OTT - Il pubblico ministero militare della Spezia Stefano Grillo ha impugnato la sentenza di assoluzione pronunciata dal tribunale militare spezzino nei confronti del colonnello della <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">finanza</SPAN></STRONG> Giuseppe Moscuzza, 52 anni, in servizio presso il comando di zona di Venezia. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Moscuzza era stato condannato a 2 anni il 22 giugno per frode al fisco ma assolto dall\' accusa di concussione formulata dall\'accusa per un presunto accordo con i manager che si occupavano dei beni dell\' ex campione di sci Alberto Tomba, accordo - secondo il pm - teso a favorire un rallentamento degli accertamenti sui guadagni \'in nero\' di Tomba. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>In sostanza, Moscuzza non solo avrebbe rivelato l\' esistenza di un\' indagine fiscale a carico dell\' ex campione di sci e di suo padre Franco, ma avrebbe di fatto concesso tempo a uno dei manager e all\' entourage di Alberto Tomba - sottoposti a indagine fiscale - per \'\'provare a sottrarre agli accertamenti i guadagni in nero\'\'. \'\'Di piu\' - scrive il pubblico ministero mella proposta di appello -, Moscuzza rappresento\' la possibilita\' che le indagini si attardassero su uno dei manager\'\' per rallentare fattivamente le indagini. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Secondo il pm Grillo, per la definizione di questo tipo di reato (la concussione, appunto) e\' sufficiente \'\'qualsiasi accordo tra finanziere e terzo per effetto del quale sia leso l\'interesse primario dello Stato alla normalita\' e alla sicurezza delle entrate indispensabili per il finanziamento dello Stato stesso\'\'. La collusione con estranei, prosegue il pubblico ministero, puo\' consistere inoltre \'\'in qualunque intesa, evidentemente non abbisognevole di consacrazione formale, che abbia per oggetto un contenuto determinato o determinabile dalla successiva volonta\' delle parti\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">DROGA: CAFFE\' AL POSTO DI EROINA, ARRESTATO MARESCIALLO GDF<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">SOSTITUITO PACCHETTO SEQUESTRATO DA CONSEGNARE A UFFICIO REPERTI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - MILANO, 1 OTT - Un maresciallo della <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">di Finanza</SPAN></STRONG> e\' stato arrestato oggi per peculato, detenzione illecita di sostanze stupefacenti e calunnia: e\' accusato di aver consegnato all\'ufficio reperti del palazzo di giustizia di Milano, al posto di un pacchetto che avrebbe dovuto contenere oltre 3 chili di eroina sequestrati durante una operazione, un pacchetto simile contenente pero\' caffe\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ad accorgersi di quanto era accaduto sono stati gli addetti dell\'ufficio reperti, una decina di giorni fa. Subito sono stati avvisati i militari del nucleo regionale della <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">di Finanza</SPAN></STRONG> della Lombardia, i quali dopo aver controllato l\'involucro, hanno spiegato che il pacchetto non era lo stesso confezionato e sigillato da loro. Poi la scoperta: all\'interno invece della droga c\'era caffe\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Da qui sono scattate indagini lampo, coordinate dal pm Alfredo Robledo. I militari del Gico della Gdf sono risaliti al<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>collega che aveva sostituito il pacchetto. Questo pomeriggio il maresciallo e\' stato arrestato dagli uomini del Gico e il pacchetto contenente gli oltre 3 chili di eroina e\' stato rinvenuto durante le perquisizioni.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\'ordinanza di custodia cautelare, richiesta dal pm Robledo, e\' stata firmata dal gip Maria Rispoli.(ANSA). <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">MAFIA: TALPE DDA; PROCURA DEPOSITA ATTO D\'ACCUSA A GUP<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - PALERMO, 2 OTT - Un atto d\' accusa che riepiloga fatti e circostanze per i quali e\' stato chiesto il rinvio a giudizio del presidente della Regione, Salvatore Cuffaro, dell\'imprenditore Michele Aiello, e dei marescialli Giorgio Riolo dei carabinieri e Giuseppe Ciuro della <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>, e\' stato depositato al gip dalla procura di Palermo. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>In oltre cento pagine i magistrati ripercorrono le indagini sulle \'\'talpe alla dda\'\', le fughe di notizie, i contatti fra uomini politici, imprenditori e affiliati a Cosa nostra. E\' una sorta di \'\'sentenza ordinanza\'\' che veniva fatta in passato dal giudice istruttore.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>All\' udienza preliminare il presidente della Regione, che deve rispondere solo di aver favorito Cosa nostra, potrebbe presentarsi con una richiesta di giudizio immediato, cosi\' come l\'imprenditore Michele Aiello, accusato di associazione mafiosa. L\' udienza e\' fissata per il 14 ottobre. Entrambi gli imputati vorrebbero saltare questa fase di giudizio e presentarsi direttamente davanti al tribunale.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\'indagine sulle talpe, come spiega la procura nella memoria depositata al gup, \'\'ha evidenziato la gravita\' del problema della rivelazione di notizie segrete sulle indagini alla mafia\'\'. Il documento e\' arricchito da dotte citazioni, come quelle di Leopoldo Franchetti e Sidney Sonnino, nella loro indagine sulla \'\'Sicilia nel 1876\'\', ma anche quelle della Commissione antimafia, di Luciano Violante e Giuseppe Lumia. <SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;</SPAN>I magistrati sottolineano \'\'la capacita\' di Cosa nostra di instaurare e mantenere rapporti con i piu\' svariati settori della societa\' civile e delle istituzioni\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'In questo procedimento - scrivono i pm al gup - sono stati dimostrati, come accade spesso in un unico contesto di indagine, i rapporti di una persona imputata di far parte<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">dell\'associazione mafiosa Cosa nostra ( Michele Aiello) e di una condannata con sentenza definitiva per questo reato (Giuseppe Guttadauro) con esponenti, anche al piu\' alto livello, della politica regionale, con imprenditori, professionisti e giornalisti, con impiegati, funzionari e dirigenti della pubblica amministrazione, con persone che lavorano negli uffici giudiziari e con appartenenti, di ogni ordine e qualifica, alle Forze di polizia\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>I magistrati cercano di spiegare le singole posizioni e connessioni, e sottolineano che \'\'solo in alcuni casi questi rapporti hanno integrato a parere della procura, fattispecie di reato\'\'. \'\'In tutti i casi pero\' - aggiungono - anche quando non hanno avuto, sotto il profilo oggettivo o sotto quello soggettivo le caratteristiche dell\'illecito penale, questi rapporti possono avere un effetto devastante sulla vita della societa\' siciliana\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La procura diretta da Pietro Grasso si rifa\' a quanto ha scritto nel 1993 dalla Commissione parlamentare antimafia, presieduta allora da Luciano Violante, quando dice: \'\'Risulta indispensabile che ogni settore delle istituzioni e della societa\' civile rompa i rapporti con Cosa nostra\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 8714, 1, 0, '2026-01-14 10:47:26', '0000-00-00'),
(336, '2004-10-06 00:00:00', 'Notizie Flash', 'FINANZIARIA: PER FORZE ARMATE E POLIZIA MAGGIORI RISORSE PER 20 MLN - FINANZIARIA: FISCO;DA NEO-CONCORDATO A LOTTA EVASIONE - FINANZIARIA: FISCO; MANCATI VERSAMENTI, RISCHIO MANETTE', '', 'FINANZIARIA: PER FORZE ARMATE E POLIZIA MAGGIORI RISORSE PER 20 MLN - FINANZIARIA: FISCO;DA NEO-CONCORDATO A LOTTA EVASIONE - FINANZIARIA: FISCO; MANCATI VERSAMENTI, RISCHIO MANETTE', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">FINANZIARIA: BOZZA; 56 MLN IN PIU\' PER CONTRATTI PUBBLICI<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">PER <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">FORZE</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">ARMATE</SPAN></STRONG> E POLIZIA MAGGIORI RISORSE PER 20 MLN<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 29 SET - La manovra economica \'rimpingua\' di 56 milioni lo stanziamento previsto per i rinnovi contrattuali nel pubblico impiego. E\' quanto contenuto nella bozza della Finanziaria che approda questa sera al consiglio dei ministri. <SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;</SPAN>Sempre all\'art.14 del testo si prevede anche un aumento di 22 milioni di euro per il personale statale in regime di diritto pubblico. A beneficiarne maggiormente saranno i lavoratori delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\">armate</SPAN></STRONG> e dei corpi di polizia a cui andranno 20 milioni dell\'incremento.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">FINANZIARIA: FISCO;DA NEO-CONCORDATO A LOTTA EVASIONE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">CONTROLLI INCROCIATI, NEL MIRINO AFFITTI E OPERAZIONI IVA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 29 SET - Arriva una nuova tipologia di concordato. Non sara\' la vecchia sanatoria e nemmeno il cosiddetto \'\'preventivo\'\' che consentiva di programmare le imposte per piu\' annualita\'. Sara\' invece una sorta di \'\'concordato a proposta\'\', perche\' spettera\' al Fisco l\' onere di avanzare, in base a specifici parametri raccolti dagli Studi di Settore, una proposta che il contribuente puo\' decidere di accogliere. E\' questa una delle novita\' che potrebbero approdare nella manovra finanziaria ora all\' esame del Consiglio dei Ministri. Ma, sul fronte fiscale, la manovra fiscale prevede anche una piu\' incisiva lotta all\' evasione: si concentrera\' in particolare sull\' Iva e sugli affitti.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>- CONCORDATO A RICHIESTA: E\' una delle misure che si basera\' sugli studi di settore e che quindi punta in particolare sulle categorie del lavoro autonomo e della piccola impresa. L\' ipotesi prevede una sorta di concordato a richiesta, diverso da quello preventivo che lo scorso anno ebbe una adesione inferiore alle attese, ma anche dal concordato-sanatoria previsto tra i condoni del passato. Si starebbe invece pensando ad una proposta che l\' amministrazione finanziaria avanzera\' in base ad alcuni parametri (gli \'\'studi di settore\'\' per appunto) molto probabilmente per il biennio 2004-5, uno modo di richiedere le imposte che consentirebbe una \'\'pianificazione\'\' da parte dei contribuenti degli importi dovuti.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>- AFFITTO E CODICE FISCALE: E\' tutta nel \'\'codice fiscale\'\' la chiave di volta sulla quale si basera\' la lotta all\' evasione nel settore degli affitti. Ma anche nella denuncia \'\'antimafia\'\' che ora si effettua alla questura in caso di locazione. In rampa di lancio sarebbe infatti una norma che prevede l\'obbligatorieta\' dell\' indicazione del codice fiscale per ogni tipo di pratica immobiliare (ad esempio per effettuare i lavori nell\' appartamento) per consentire poi gli incroci. I controlli telematici saranno poi estesi alle utenze con l\' obbligo per le societa\' di servizi di comunicare al fisco gli estremi domiciliari. Un\' ultima novita\', poi, potrebbe essere prevista per la domanda che viene presentata alle questure ai fini anti-mafia.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>- STUDI SETTORE: La manutenzione degli studi di settore, che riguardera\' 80 settori, era gia\' in programma e sara\' effettuata entro la fine dell\' anno per adeguare questi parametri, dai quali si risale ai ricavi, all\' impatto che l\' inflazione ha avuto sugli incassi. Ma la vera novita\' arriverebbe con l\' applicazione di questi strumenti anche per i contribuenti operano in regime di contabilita\' ordinaria che, pena un accertamento, dovranno adeguarsi e non potranno piu\' dichiarare importi inferiori a quelli ricavati dagli Studi.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>- EVASIONE IVA: Sarebbero in arrivo misure per contenere l\' evasione e le truffe Iva. La norma piu\' pungente e\' quella che estende anche all\' acquirente i rischi di sanzione in caso di mancata fatturazione nell\' acquisto di un bene. Per frenare i fenomeni di triangolazioni e frodi Iva, sarebbe prevista uno mappatura delle vetture acquistate negli altri paesi della comunita\', la reintroduzione dell\' elenco dei clienti e fornitori (in via telematica, per frenare il fenomeno delle false fatturazioni) e l\' obbligo di una comunicazione al fisco nel caso di acquisti fatti in sospensione d\' imposta. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">FINANZIARIA: FISCO; MANCATI VERSAMENTI, RISCHIO MANETTE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\">LA NUOVA NORMA RIGUARDA I SOSTITUTI DI IMPOSTA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 30 SET - L\'omesso versamento al fisco da parte dei sostituti di imposta di ritenute certificate puo\' essere sanzionato anche con la reclusione fino a due anni. A prevedere l\'introduzione di una pena detentiva e\' la nuova Finanziaria licenziata dal Consiglio dei ministri.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'E\' punito con la reclusione da sei mesi a due anni – si legge nella nuova norma - chiunque non versa entro il termine previsto per la presentazione della dichiarazione annuale di sostituto di imposta ritenute risultanti dalla certificazione rilasciata ai sostituti, per un ammontare superiore a cinquantamila euro per ciascun periodo di imposta\'\'.(ANSA). <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2344, 1, 0, '2025-12-19 23:17:38', '0000-00-00'),
(357, '2004-10-26 00:00:00', 'Libere manifestazioni di pensiero', 'RIORDINO DEI SOVRINTENDENTI, UNO SPECCHIETTO PER LE ALLODOLE SE NON SI RIQUALIFICANO LE FUNZIONI \r\ndi Dario Dal Mut', '', 'RIORDINO DEI SOVRINTENDENTI, UNO SPECCHIETTO PER LE ALLODOLE SE NON SI RIQUALIFICANO LE FUNZIONI \r\ndi Dario Dal Mut', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Accade che oggi a quasi dieci anni di distanza dall’approvazione del decreto legislativo 12 maggio 1995 numero 199 si faccia riecheggiare con insistenza la prospettiva di un nuovo riordino delle carriere. Si crea<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>nel personale<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>l’illusione di una facile progressione di carriera con l’intento di avvantaggiare pochi: anzi pochissimi. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Si usa il termine “RIFORMA DELLE CARRIERE” come specchietto per le allodole sostenendo che con provvedimenti da emanarsi entro breve termine finanzieri, appuntati, sovrintendenti, marescialli ed ufficiali potranno beneficiare di migliori posizioni e di uno stipendio adeguato. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Non credo che sia così semplice. Anzi sono sicuro che non è così semplice.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Siamo tutti d’accordo che la riforma del 1995 ha manifestato ad oggi tutto il suo limite e che comunque occorre mettere mano ad un nuovo riordino. Mi pare che oltre ai COCER siamo d’accordo su questo anche i sindacati della Polizia di Stato della Polizia Penitenziaria e del Corpo Forestale dello Stato. Mi pare che anche le forze politiche sia di maggioranza che di opposizione sono favorevoli ad un nuovo riordino. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Però anche qui occorre essere chiari.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Con l’attuale assetto del comparto sicurezza e del<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>comparto difesa ogni operazione ordinamentale per quanto circoscritta e limitata comporta costi altissimi tali da “bruciare” subito le scarse risorse a disposizione. Infatti oggi con il minimo intervento ad una sola qualifica o grado in base al legame contrattuale con le forze armate si generano riflessi a catena che comportano costi altissimi ed esborsi annui per decine di milioni di euro. Il riallineamento né è la prova più lampante. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Anche qui dunque matura la grande priorità: SEPARARE IMMEDIATAMENTE IL COMPARTO SICUREZZA DAL COMPARTO DIFESA. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Occorre poi essere chiari e comprendere bene che “riordinare” non significa solo cambiare nome alle qualifiche o attribuire finte progressioni di carriera o diventare tutti capitani e ispettori. Non vuol dire attaccare sulla divisa un gallone in più. RIORDINARE SIGNIFICA INDIVIDUARE UNA REALE CORRISPONDENZA TRA IL GRADO RIVESTITO E LA FUNZIONE ESERCITATA, VALORIZZANDO CONSEGUENZIALMENTE SUL PIANO ECONOMICO QUESTA CORRISPONDENZA. Non sono necessari generali con lo stipendio da soldato. La dignità di un lavoratore la si misura anche nel rapporto che esiste tra la ricompensa economica percepita e la funzione svolta. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Una valorizzazione ed una riqualificazione professionale quindi. Valorizzazione e riqualificazione che deve comprendere tutti anche quel ruolo che ancora oggi è troppo sottostimato e mi riferisco al ruolo sovrintendenti. Un ruolo che nasce nella Polizia di Stato nel 1981 e nella Guardia di Finanza con l’equiparazione del 1995 e che ancora dopo 10 anni aspetta una collocazione ed un riconoscimento. Ufficiali di polizia giudiziaria e tributaria impegnati ancora troppo spesso a fare i cucinieri, gli autisti, i piantoni. A che serve far fare sei mesi di corso ad un sovrintendente per poi lasciarlo ai compiti che aveva precedentemente. Riqualificazione professionale quindi riproponendo e ripartendo da quella che è la madre della riforma: la legge 1 aprile 1981 numero 121. Legge completamente dimenticata e disattesa. Se ci guardiano intorno vediamo che neppure i segni distintivi di grado sono uguali tra le forze di polizia che ai sensi dell’articolo 16 di quella legge fanno parte del COMPARTO SICUREZZA. Ognuno attento a difendere il proprio orticello. Il proprio potere. Ma credo che l’Europa ci costringerà ad attenuare se non ad annullare gli egoismi settoriali e, se vogliamo VERAMENTE costruire un futuro che non abbia più bisogno di altre riforme che significano altri costi, dobbiamo dare risposte certe e concrete e non aggiustamenti tanto per non scontentare nessuno.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Inoltre tenuto conto che non esiste nessun rappresentante della categoria dei sovrintendenti presso il COCER credo che sia OPPORTUNO, anzi DOVEROSO, che essi vengano convocati e sentiti in tutte le iniziative che il COCER intende promuovere nella discussione del nuovo riordino.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>DARIO DAL MUT</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">Componente del Comitato direttivo nazionale di Ficiesse<o:p></o:p></FONT></FONT></I></P>', 0, '', '', '', 4616, 1, 0, '2026-01-03 22:51:01', '0000-00-00'),
(358, '2004-10-27 00:00:00', 'Dal Parlamento', 'PASSA ANCHE ALLA CAMERA LA CONVERSIONE DEL DECRETO LEGGE SUGLI ISPETTORI DELLE FORZE DI POLIZIA E SULLA PROROGA DEI COCER A MAGGIO 2006', '', 'PASSA ANCHE ALLA CAMERA LA CONVERSIONE DEL DECRETO LEGGE SUGLI ISPETTORI DELLE FORZE DI POLIZIA E SULLA PROROGA DEI COCER A MAGGIO 2006', 'PRIMA NOTIZIA\r\nLa Camera ha appena approvato, con 416 voti a favore, la conversione del decreto legge 10 settembre 2004, n. 238, recante misure urgenti per il personale appartenente ai ruoli degli ispettori delle Forze di polizia e disposizioni in materia di trattamento giuridico ed economico dei dirigenti delle Forze armate e delle Forze di polizia.\r\nIl provvedimento - già approvato anche al Senato - prevede l\'inquadramento anche in soprannumero degli ispettori capo e dei periti tecnici capo della Polizia di Stato, Polizia penitenziaria e Corpo forestale dello Stato nelle rispettive qualifiche di ispettore-sostituto ufficiale di pubblica sicurezza e di perito tecnico superiore; un avanzamento di grado pari a quello gia\' conseguito dalle Forze armate e\' previsto anche per i marescialli sostituto ufficiale di Pubblica sicurezza di Carabinieri e Guardia di Finanza. Si proroga, inoltre, fino al 2006 il mandato dei componenti in carica dei consigli di Rappresentanza militare delle forze armate e di polizia.  \r\n', 0, '', '', '', 3097, 1, 0, '2026-01-12 10:21:13', '0000-00-00'),
(359, '2004-10-27 00:00:00', 'Documenti', 'APPROVATO DALLA CAMERA IL D.L. 10 settembre 2004, nr.238.\r\nATTI DELLE SEDUTE', '', 'APPROVATO DALLA CAMERA IL D.L. 10 settembre 2004, nr.238.\r\nATTI DELLE SEDUTE', '<P>Come abbiamo dato notizia a parte, in mattinata, la Camera con una votazione unanime ha&nbsp;convertito in legge con modificazioni,&nbsp;il D. L. 10 settembre 2004, nr. 238. In allegato, per capire l\'iter della discussione parlamentare, il testo discusso alla Camera, così come approvato dal Senato, la discussione in aula, l\' approvazione, nonchè, cosa interessante, gli ordini del giorno approvati dall\'aula che impegnano il Governo ad atti futuri.</P>\r\n<P>Pubblicheremo il testo integrale definitivo non appena disponibile.</P>', 100, '', '', '', 2265, 1, 1, '2026-01-11 20:54:04', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(360, '2004-10-31 00:00:00', 'Notizie Flash', 'COCER GDF, BENE PROROGA MANDATO', '', 'COCER GDF, BENE PROROGA MANDATO', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">FORZE ARMATE: COCER GDF, BENE PROROGA MANDATO<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">PARLAMENTO HA DIMOSTRATO SENSIBILITA\'<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 28 OTT - \'\'La proroga del mandato consentira\' di procedere nei lavori di riforma della rappresentanza militare, istituto ormai obsoleto e non piu\' adeguato alle esigenza di tutela del personale, nonche\' al riordino dei ruoli del personale non direttivo e non dirigente\'\'. Lo afferma il Cocer della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanza</SPAN></STRONG>, prendendo atto della \'\'sensibilita\' dimostrata dal Parlamento nei confronti del personale appartenente alle forze di polizia ad ordinamento militare\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ieri la Camera ha infatti definitivamente approvato il disegno di legge che proroga al 2006 il mandato dei componenti in carica dei consigli di rappresentanza militare delle forze armate e di polizia.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Con questo provvedimento, aggiunge il Cocer delle Fiamme gialle, \'\'viene assicurato al Parlamento ed a tutto il personale rappresentato l\' esperienza acquisita dai delegati Cocer del nono mandato, in vista di riforme di portata strutturale\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">FORZE POLIZIA: <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">COCER</SPAN></STRONG>, GRAZIE A PARLAMENTARI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 27 OTT - Il <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG> Carabineri \'\'ringrazia tutti i parlamentari\'\' per \'\'la sensibilita\', attenzione e disponibilita\' che hanno dimostrato\'\' nell\'approvare, prima al Senato, e poi oggi all\' unanimita\' alla Camera, in via definitiva il decreto legge sulle misure urgenti per gli ispettori delle forze di polizia e delle forze armate.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Il <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG> Carabinieri sottolinea inoltre la proroga del mandato degli organismi di rappresentanza \'\'cosa - aggiunge - che sta a significare la fiducia unanime che tutte le forze politiche ripongono in questa rappresentanza\'\'. Il <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG> \'\'ringrazia altresi\' il Governo e gli Stati maggiori per la vicinanza dimostrata nella circostanza. Questo organismo - conclude - e\' convinto che la proroga concessa dia la possibilita\' ai parlamentari tutti di un confronto costruttivo con gli organismi di rappresenta per approvare, in tempi brevi, la riforma della rappresentanza militare al fine di garantire a tutti i lavoratori a status militare dignita\' costituzionale\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">FORZE POLIZIA: DS, OK A DECRETO E\' ANCHE OCCASIONE PERSA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">LUCIDI, MA ANCHE PASSO AVANTI PER CARRIERE PIU\' EQUILIBRATE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 27 OTT - \'\'Un passo avanti per rendere piu\' equilibrate le carriere fra le diverse forze armate e di polizia. Positiva anche l\'estensione del contratto 2002-2003 ai dirigenti delle stesse forze\'\'. E\' il commento di Marcella Lucidi, deputato dei Ds, che ha svolto in Aula la dichiarazione di voto per i Ds.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Resta pero\' aperta - aggiunge la Lucidi - una questione non piu\' rinviabile: il riordino delle carriere di tutto il personale. Una riforma che il governo sta completamente trascurando come si vede dalla legge finanziaria. Si e\' infine persa l\'occasione - conclude - di disporre la rieleggibilita\' dei <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG>, necessaria per dare continuita\' in modo trasparente al loro mandato\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">FORZE ARMATE:<STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">COCER</SPAN></STRONG> AERONAUTICA, SI\' A RIFORMA RAPPRESENTANZA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">\'PERCHE\' <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">COCER</SPAN></STRONG> ABBIA EFFETTIVO RUOLO NEGOZIALE\'<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 28 OTT - Il <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG> dell\'Aeronautica militare esprime \'\'soddisfazione e apprezzamento\'\' per le prese di posizione di \'\'numerosissimi parlamentari\'\' che ieri - nel corso del dibattito per l\'approvazione definitiva del decreto che ha disposto misure urgenti per gli ispettori delle forze di polizia e delle forze armate - hanno sottolineato la \'\'necessita\' di giungere ad una riforma della rappresentanza militare\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Tutti - si legge in una nota dell\'organismo di rappresentanza dell\'Aeronautica - hanno rappresentato l\'opportunita\' che tale riforma debba dare al <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG> un effettivo ruolo negoziale, tale a garantire la tutela del personale rappresentato\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Ora il <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG> - conclude il comunicato - attende con fiducia che tali propositi parlamentari possano presto concretizzarsi all\'interno di un nuovo confronto con tutte le forze politiche e con l\'amministrazione della Difesa. Il <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG> non fara\' mancare il suo fattivo contributo\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">FORZE POLIZIA: FALLICA (FI), SODDISFAZIONE PER DECRETO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 27 OTT - \'\'Grande soddisfazione\'\' e\' stata espressa da Pippo Fallica (Fi) per l\'approvazione definitiva del decreto sulle misure urgenti per gli ispettori delle forze di polizia e delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">armate</SPAN></STRONG>.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Si tratta, ha spiegato Fallica, di un \'\'primo grande sforzo portato avanti dal Governo in merito al trattamento giuridico ed economico dei dirigenti delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">armate</SPAN></STRONG> e delle forze di polizia, anche se il Parlamento - ha sottolineato - dovra\' sicuramente predisporre, con la massima urgenza, un progetto di legge di riforma organica dei ruoli e delle carriere del personale non direttivo e non dirigente sia delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">armate </SPAN></STRONG>che delle forze di polizia\'\'.<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">FORZE POLIZIA: MOLINARI,SI\' DIELLE PER SENSO RESPONSABILITA\'<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 27 OTT - \'\'Il gruppo della Margherita ha votato a favore del decreto legge sugli ispettori di Polizia perche\' si e\' sanata una situazione da tempo aperta relativa ai dirigenti delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">armate</SPAN></STRONG> e delle forze di polizia\'\'. Lo afferma Giuseppe Molinari, capogruppo dei dielle in commissione Difesa alla Camera.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Il decreto - spiega - non ci soddisfa appieno ma per senso di responsabilita\' abbiamo detto si\'. Rimangono perplessita\' sul modo in cui e\' stata portata avanti questa azione legislativa. Si poteva fare meglio, ci si poteva confrontare con l\'opposizione e utilizzare l\'opportunita\' del provvedimento per dare altre risposte ad attese e istanze che mirano a risolvere altre situazioni di iniquita\' penalizzanti all\'interno delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">armate</SPAN></STRONG> e delle forze di polizia\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Penso ad esempio - aggiunge Molinari - al comparto autonomo per il contratto per i funzionari e i dirigenti delle forze di polizia. Torneremo da subito nelle commissioni di merito ad affrontare questi problemi e ci auguriamo che gli ordini del giorno accolti oggi dal governo agevolino l\'iter di una riforma necessaria\'\'.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">FORZE POLIZIA:SAPORITO,SODDISFAZIONE PER CONVERSIONE DECRETO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 27 OTT - Soddisfazione e\' stata espressa dal sottosegretario alla Funzione Pubblica Learco Saporito per la conversione all\'unanimita\' del decreto legge sulle misure urgenti per il personale appartenente ai ruoli degli ispettori delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">polizia</SPAN></STRONG>.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Saporito ha inoltre sottolineato che il \'\'problema di fondo del decreto-legge riguarda proprio il tema del riordino delle carriere\'\', ravvisando la necessita\' di introdurre \'\'entro la fine dell\'anno, strumenti normativi per l\'utilizzo dei fondi, gia\' previsti nella legge finanziaria per il 2004, per l\'avvio del processo di riordino\'\'. L\'intesa stabilita dal Governo nello scorso mese di agosto al Viminale, prosegue il sottosegretario, \'\'aveva tra gli altri lo scopo di avviare un procedimento legislativo proprio al fine di \'coprire\', per quest\'anno, le somme relative al riordino\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Saporito, infine, ha espresso l\'auspicio che possa esserci una \'\'convergenza tra tutte le forze politiche, con delle risorse dignitosamente elargite per il riordino delle carriere, pur nella consapevolezza delle difficolta\' che attraversa il paese\'\' ed ha ricordato come i problemi del comparto della sicurezza e delle <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">polizia</SPAN></STRONG> \'\'per la loro complessita\', vadano affrontati con le risorse adeguate\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2515, 1, 0, '2026-01-09 23:23:19', '0000-00-00'),
(361, '2004-11-01 00:00:00', 'Servizio di Ficiesse', 'IL CASO SANTORO ARRIVA SU \"STRISCIA LA NOTIZIA\"', '', 'IL CASO SANTORO ARRIVA SU “STRISCIA LA NOTIZIA”', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=3>Domenica scorsa, 31 ottobre 2004, il programma satirico ï¿½Striscia la notiziaï¿½ ha mandato in onda un servizio da Brindisi sul caso del maresciallo della Guardia di finanza di Brindisi, Marco Santoro.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=3>La vicenda, giï¿½ nota ai lettori del forum di Ficiesse per volontï¿½ del medesimo ispettore che ne&nbsp;ha pubblicato tutti i particolari, ï¿½ stato ora portato allï¿½attenzione del grande pubblico televisivo con lï¿½intervista che riportiamo in allegato e della quale abbiamo anche trascritto, di seguito, il testo.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><FONT face=\"Times New Roman\" size=3>Nei prossimi giorni i nostri commenti.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><B style=\"mso-bidi-font-weight: normal\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">Testo del servizio da Brindisi<o:p></o:p></FONT></FONT></I></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><B style=\"mso-bidi-font-weight: normal\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">andato in onda su ï¿½Striscia la notiziaï¿½ di domenica 31 ottobre 2004<o:p></o:p></FONT></FONT></I></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">UNZICHER: E Iniziamo subito dando la linea a Fabio e Mingo che oggi ci parlano di uno strano caso di follia che ha colpito nientepopodimenoche: la Guardia di Finanza<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">GREGGIO: Oh signur!<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO:<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Allora, amici di Striscia, noi oggi ci troviamo a Brindisi. State molto attenti a quello che stiamo per raccontarvi, eh. Ce lo racconta <SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>questo signore che ci ha<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>chiamato. Salve, lei ï¿½?<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Buongiorno, io sono Marco Santoro e sono un maresciallo della <SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>Guardia di Finanza<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Che cosa le ï¿½ successo?<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Eï¿½ una storia incredibile, premetto anche questo. Io mi sono arruolato nel 1996. Sono otto anni che presto<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>servizio. Nel ï¿½99 ho fatto un concorso per ufficiale della Guardia di Finanza, insieme a tutti gli altri concorrenti. Ho scoperto soltanto adesso, casualmente, che dagli esiti dei test che mi sono stati somministrati ï¿½<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: cioï¿½ avete fatto dei test durante il concorso<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Si. Quindi, test psicoattitudinali<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: E che cosa avete scoperto casualmente?<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Casualmente ho scoperto che io sono affetto da ï¿½turbe nevrotiche non strutturateï¿½. Ovvero da ï¿½isteria, paranoia e ideazioni bizzarre con potenziali aspetti di ansia, impazienza, irritabilitï¿½ï¿½.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Quindi? Cosa vuol dire che lei ï¿½<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: ï¿½ pazzo<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Lei ï¿½ pazzo.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: E non soltanto io<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Ah. E chi altro?.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Ci sono altri dodici colleghi finanzieri<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Di dove?<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Lido di Ostia, Milano, Como, Firenze e Roma.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Lo sa perchï¿½? Perchï¿½ ï¿½ scritto ï¿½<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Perchï¿½ ho acquisito della documentazione che era nel mio fascicolo e risulta tutto ufficialmente.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Quindi anche loro, hanno lo stesso suo risultato<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Turbe nevrotiche non strutturate<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Quindi; sono pazzi anche loro, in poche parole.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Si<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Eppure siete in servizio.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Certo<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Avete lï¿½arma di ordinanza.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Certo<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: E andate girando armati. Lei qui ha la pistola?<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Certamente. Sono armato<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO : E questo ï¿½ un pericolo pubblico<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Non ï¿½ bastato dichiararmi pazzo, in piï¿½ anche cavia umana<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Cioï¿½, che vuol dire, spieghiamo perchï¿½ uno si starï¿½ chiedendo cosa vuol dire adesso cavia umana<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Il pazzo andava controllato di giorno e di notte anche fuori dal servizio. E questo risulta eh? Cï¿½ï¿½ scritto qui.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Cioï¿½, lei attualmente viene controllato<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Si. Perchï¿½ pazzo anche se in servizio<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Anche chiaramente gli altri dodici.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Anche gli altri dodici. Perï¿½ la cosa piï¿½ strana ï¿½ che la Guardia di Finanza stessa mi dice che io sto bene, ovvero ï¿½<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Insomma, ecco ï¿½<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Con la visita ufficiale che si fa dopo quattro anni di servizio, per passare in ï¿½servizio permanente effettivoï¿½, si dice, ï¿½ Il medesimo Maresciallo Santoro conserva<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>idoneitï¿½ psicofisica al servizio incondizionatoï¿½.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Questo vuol dire che li ï¿½ sano, insomma, non ï¿½ pazzo<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Non ci sono dubbi<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Cosï¿½ come gli altri dodici.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Certo<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Quindi cosa succede, cosa sta succedendo. Perchï¿½ questi test dichiarano che lei e gli altri siete pazzi?<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Perchï¿½ questi test sono del 1943, un poï¿½ vecchiotti, siccome qualcuno pensa che non sono efficaci, che non riescono a giudicare se uno ï¿½ pazzo oppure non ï¿½ pazzo li devono sperimentare sui finanzieri stessi ï¿½<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Ah!<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Che risultano pazzi ma in servizio comunque<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: La cosa che perï¿½ ï¿½ ecco ï¿½ incredibile, pazzesca, ï¿½ che tutto questo viene mantenuto segreto.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Tutto segreto<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Fanno degli esperimenti su ï¿½<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Lï¿½esperimento ï¿½ segreto<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Insomma su di lei, su di altri in maniera segreta, insomma voi non sapete niente Lei sa questo perchï¿½ lï¿½ha<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>scoperto casualmente<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: per puro caso<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO Per puro caso. Viene controllato giorno e notte<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">SANTORO: Giorno e notte<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">MINGO: Amici di Striscia chiudiamo qua il servizio. Avete ascoltato questa storia e speriamo, insomma, che qualcuno dia delle risposte. Da Brindisi questo ï¿½ tutto. Mingo e il buon Fabio, a voi studio.<o:p></o:p></FONT></FONT></I></P>', 101, '', '', '', 11097, 1, 0, '2026-01-14 12:06:50', '2013-02-21'),
(362, '2004-11-03 00:00:00', 'Dalle autorità giurisdizionali', 'SENTENZA DEL T.A.R. LAZIO SU ANNULLAMENTO NOTE   CARATTERISTICHE', '', 'SENTENZA DEL T.A.R. LAZIO SU ANNULLAMENTO NOTE   CARATTERISTICHE', '<P>Dal socio PROMETEO riceviamo la segnalazione circa la sentenza che volentieri pubblichiamo.</P>\r\n<P>Ci sembra particolarmente interessante per due principali motivi:</P>\r\n<P>1.Riguardando un Brigadier Generale ci segnala la circostanza che le ingiustizie non conoscono gradi (naturalmente date le dovute proporzioni);</P>\r\n<P>2.L\' annullamento avviene in base a considerazioni profonde e&nbsp; motivate che, secondo noi potrebbero applicarsi a tanti dei casi che ci vengono segnalati da nostri soci o simpatizzanti appartenenti al Corpo della Guardia di Finanza, che si vedono abbassare il giudizio delle note caratteristiche sulla base, spesso, della semplice \"antipatia\" dei compilatori.</P>', 103, '', '', '', 1432, 1, 0, '2010-09-28 00:11:57', '0000-00-00'),
(364, '2004-11-03 00:00:00', 'Opinioni', 'L\'ERA DELLA PERSONA, EDITORIALE SUL CASO SANTORO - di Giuseppe Fortuna', '', 'L\'ERA DELLA PERSONA, EDITORIALE SUL CASO SANTORO\r\n   (di Giuseppe Fortuna)', '<font face=\"Times New Roman\" size=\"3\">\r\n<p class=\"ecxMsoBodyText\"><font face=\"Times New Roman\" size=\"3\">La vicenda del maresciallo della Guardia di finanza Marco Santoro è approdata, infine, a Striscia la notizia.</font></p>\r\n<p class=\"ecxMsoBodyText\"><font face=\"Times New Roman\" size=\"3\">Proviamo a riepilogarla. </font></p>\r\n<p class=\"ecxMsoBodyText\"><font face=\"Times New Roman\" size=\"3\">Santoro effettua un accesso al fascicolo personale. Viene in possesso di una corrispondenza risalente al 1999 e apprende che la sua esclusione da un concorso per l\'Accademia è stata causata, per lui e per altri dodici aspiranti in servizio nel Corpo, dalla rilevazione di una presunta patologia: &quot;TURBE NEVROTICHE NON STRUTTURATE&quot;.</font></p>\r\n<p class=\"ecxMsoBodyText\"><font face=\"Times New Roman\" size=\"3\">La malattia non è certa, anzi per gli stessi specialisti si tratta di &quot;<i>ipotesi interpretative da suffragare successivamente con ulteriori riscontri&quot;</i>.</font></p>\r\n<p class=\"ecxMsoBodyText\"><font face=\"Times New Roman\" size=\"3\">Ma il fatto è che i tredici finanzieri non vengono informati e continuano a essere regolarmente impiegati in servizio. In più, il Centro di reclutamento ha raccomandato &quot;<i>la più discreta azione di monitoraggio del comportamento dei soggetti segnalati&quot;,</i> sicchà© il superiore diretto è stato incaricato dal comandante provinciale &quot;<i>di seguire attentamente il maresciallo Santoro sia in servizio che fuori di esso&quot;</i>.</font></p>\r\n<p class=\"ecxMsoBodyText\"><font face=\"Times New Roman\" size=\"3\">Questi i fatti, stando ai documenti pubblicati in internet e sul forum della nostra associazione (www.ficiesse.it) dall\'ispettore in questione.</font></p>\r\n<p class=\"ecxMsoBodyText\"><font face=\"Times New Roman\" size=\"3\">Veniamo alle osservazioni.</font></p>\r\n<p class=\"ecxMsoNormal\" style=\"text-align: justify;\"><font face=\"Times New Roman\" size=\"3\">Innanzitutto, ci sembra incontestabile che il maresciallo Santoro abbia ragioni da vendere quando si indigna per non essere stato informato di un responso così delicato riguardante la sua salute. Circostanza, questa, che gli ha impedito di tutelare i propri legittimi interessi morali e materiali e che - diciamolo pure - quasi certamente lo ha anche messo in una luce non propriamente favorevole agli occhi dei superiori locali.</font></p>\r\n<p class=\"ecxMsoNormal\" style=\"text-align: justify;\"><font face=\"Times New Roman\" size=\"3\">Poi, c\'è quella frasetta un po\' inquietante: &quot;SEGUIRE ATTENTAMENTE LA PERSONA ANCHE FUORI DAL SERVIZIO&quot;, cioè nella vita privata.</font></p>\r\n<p class=\"ecxMsoBodyText\"><font face=\"Times New Roman\" size=\"3\">Che vuol dire, in concreto? Cosa fanno i comandanti quando ricevono un ordine del genere? Si tratta di prassi codificate da qualche parte, oppure ogni superiore è libero di agire come vuole? Ma, specialmente, ciò che più conta, su quali basi normative poggia tale singolare disposizione?</font></p>\r\n<p class=\"ecxMsoBodyText\"><font face=\"Times New Roman\" size=\"3\">Ebbene, noi crediamo che dietro la vicenda di Marco Santoro e anche dietro la formuletta poco rassicurante non ci sia altro che l\'approccio nella gestione del personale che noi da anni combattiamo.</font></p>\r\n<p class=\"ecxMsoNormal\" style=\"text-align: justify;\"><font face=\"Times New Roman\" size=\"3\">&quot;Meglio non dire nulla ai ragazzi - ci par di sentire -. Le diagnosi potrebbero essere sbagliate. Perchà© allarmarli? Meglio investire i loro comandanti. Sapranno loro come comportarsi se qualche sintomo dovesse davvero emergere.&quot;</font></p>\r\n<p class=\"ecxMsoNormal\" style=\"text-align: justify;\"><font face=\"Times New Roman\" size=\"3\">Nessuna volontà , quindi, ne siamo certi, di fare del male a Santoro, nessuna malafede degli specialisti, nessun malanimo dei comandanti dei diversi livelli. Solo paternalismo. Il solito stucchevole, immarcescibile paternalismo.</font></p>\r\n<p class=\"ecxMsoNormal\" style=\"text-align: justify;\"><font face=\"Times New Roman\" size=\"3\">Il problema vero, perciò, è che i problemi del personale non possono più essere affrontati con la retorica paternalista. Perchà© non lo permettono più quei processi di trasformazione che hanno investito la società  civile a partire dal 1990 e che da qualche anno sono giunti, con energia altrettanto dirompente, anche nelle organizzazioni militari.</font></p>\r\n<p class=\"ecxMsoNormal\" style=\"text-align: justify;\"><font face=\"Times New Roman\" size=\"3\">Una volta c\'era l\'organizzazione e basta. Oggi non più. Oggi ci sono anche gli individui, ai quali l\'ordinamento, a torto o a ragione, riserva una considerazione e una tutela che qualche volta prevalgono perfino su interessi rilevanti dell\'organizzazione. Persone con pienezza di diritti: di conoscenza, di informazione, di azione. Individui, perciò, che giustamente pretendono di essere trattati da adulti. Con i vantaggi e gli svantaggi che l\'essere adulti comporta.</font></p>\r\n<p class=\"ecxMsoNormal\" style=\"text-align: justify;\"><font face=\"Times New Roman\" size=\"3\">Signori, la politica del &quot;Ciao, caro&quot; è morta per sempre. Cerchiamo di rendercene conto prima che sia davvero troppo tardi.<br />\r\n<br />\r\n<br />\r\nGIUSEPPE FORTUNA</font><i style=\"mso-bidi-font-style: normal;\"><font size=\"3\"><font face=\"Times New Roman\"><br />\r\nPresidente Direttivo nazionale Ficiesse</font></font></i></p>\r\n</font>', 0, '', '', '', 4496, 1, 0, '2026-01-09 21:28:49', '2011-06-19'),
(365, '2004-11-04 00:00:00', 'Comunicato stampa', 'L\'ASSOCIAZIONE SI STRINGE ATTORNO AI FAMILIARI DI FRANCESCO', '', 'L\'ASSOCIAZIONE SI STRINGE ATTORNO AI FAMILIARI DI FRANCESCO', 'APPRESA LA TRISTE NOTIZIA DELLA DISGRAZIA CHE HA COLPITO IL COLLEGA FRANCESCO PLAZZOTTA, IL SEGRETARIO NAZIONALE DELL\'ASSOCIAZIONE, Carlo GERMI, I COMPONENTI DEL DIRETTIVO NAZIONALE, e I SOCI TUTTI SI STRINGONO AI FAMILIARI ED AI PARENTI DI FRANCESCO, PARTECIPANDO AL LORO IMMENSO DOLORE.', 104, '', '', '', 1077, 1, 0, '2010-09-17 06:20:26', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(366, '2004-11-07 00:00:00', 'Notizie Flash', 'RUBA PLAYSTATION IN CENTRO COMMERCIALE, FINANZIERE ARRESTATO - LA GDF DENUNCIA UNA SOCIETA\' PER VIOLAZIONE DELLA PRIVACY', '', 'RUBA PLAYSTATION IN CENTRO COMMERCIALE, FINANZIERE ARRESTATO - LA GDF DENUNCIA UNA SOCIETA\' PER VIOLAZIONE DELLA PRIVACY', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">RUBA PLAYSTATION IN CENTRO COMMERCIALE, <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">FINANZIERE</SPAN></STRONG> ARRESTATO<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - LANCIANO (CHIETI), 2 NOV - Ha rubato una playstation in un centro commerciale, ma e\' stato sorpreso e arrestato dai Carabinieri: protagonista dell\'episodio un <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanziere</SPAN></STRONG> di Castelfrentano (Chieti), che presta pero\' servizio a Roma, Marcello Di Giacomo, di 31 anni, accusato di furto aggravato.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il giovane e\' stato sorpreso all\'interno del centro commerciale Pianeta-Conad di Lanciano dopo essersi impossessato di una playstation. L\'arresto del <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanziere</SPAN></STRONG> e\' stato convalidato: il giovane e\' stato scarcerato. Il processo per direttissima si terra\' il prossimo 9 novembre.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">BUONGIORNO.IT:CROLLO BORSA SU ACCUSE VIOLAZIONE PRIVACY/ANSA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">TITOLO CEDE 6,6%. PM, PER 450MILA CASI ILLECITO TRATTAMENTO DATI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - MILANO, 3 NOV - Bufera in Borsa su Buongiorno Vitaminic dopo che oggi la societa\', denunciata dalla <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di finanza</SPAN></STRONG> e per la quale il pm Gianluca Bragho\' ha chiuso le indagini, ha ammesso l\' avviso giunto a carico dell\'amministratore delegato, del responsabile trattamento dati e della societa\' stessa in seguito agli accertamenti fatti dalla magistratura sul sito Buongiorno.it riferibile alla societa\'. Il titolo, quotato al Nuovo Mercato e al Nasdaq di Wall Street, ha ceduto in Piazza Affari il 6,62% sul prezzo di riferimento chiudendo a 1,693 euro (1,813 alla vigilia). Sono passate di mano 2,857 milioni di azioni rispetto ad una media giornaliera di 447.000: il 3,74% del capitale sociale.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Le accuse della magistratura riguardano la frode informatica e la violazione della privacy. Questo, mentre la societa\' come persona giuridica e\' stata indagata in base alla legge 231 del 2001 che impone la costituzione di determinati modelli organizzativi al fine di prevenire gli illeciti.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>In pratica, secondo le ipotesi mosse al termine dell\'operazione di accertamenti di polizia denominata \'\'Newsletter\'\', la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanza</SPAN></STRONG> avrebbe accertato un illecito trattamento dei dati personali di oltre 450 mila persone. Questo nell\'ambito di un\' inchiesta che sta proseguendo, visto che la societa\' oggetto dell\' indagine gestisce infatti in via diretta o indiretta altri database contenenti i dati personali di quasi 50 milioni di utenti italiani e stranieri iscritti a varie newsletter. Si tratta della prima operazione di questo genere dall\' entrata in vigore del nuovo testo unico sulla privacy.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>In particolare secondo gli accertamenti, e\' stato ricostruito che la societa\' era stata incaricata di gestire, per conto di un\' altra impresa on line, il database degli iscritti a una newsletter abbinata a un noto sito internet. Dopo la risoluzione del contratto con il titolare del sito, la societa\' secondo gliinvestigatori avrebbe continuato ad utilizzare il sistema informatico di gestione del servizio di newsletter inviando migliaia di e-mail pubblicitarie agli abbonati senza averne chiesto il preventivo consenso (il cosiddetto spamming). Secondo l\' accusa da qui e\' derivato un immediato e rilevante vantaggio per il mancato pagamento degli introiti al titolare del sito e per l\' abusiva diffusione dei messaggi pubblicitari. Inoltre, vi sono stati in base agli accertamenti problemi per gli utenti che in molti casi si sono trovati nell\' impossibilita\' di eliminare i messaggi non autorizzati dalla propria casella di posta elettronica.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Gli elementi acquisiti nell\' inchiesta sono stati comunicati dalle Fiamme Gialle anche al Garante per la protezione dei dati personali che dovra\' ora pronunciarsi su un\' eventuale sanzione amministrativa a titolo di risarcimento danni che puo\' arrivare fino a 250 euro per ogni comunicazione indesiderata.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Da parte sua, Buongiorno ha diramato una nota in serata dove si dichiara \'\'tranquilla di poter chiarire completamente presso le autorita\' competenti la posizione propria e dei propri manager\'\', che viene definita \'\'assolutamente corretta dal punto di vista dei rapporti contrattuali con terzi e del rispetto delle norme sulla privacy\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Buongiorno, inoltre, \'\'nega nel modo piu\' assoluto di aver continuato a gestire una lista di terzi dopo la risoluzione del contratto, voluto da Buongiorno stessa, nell\'ambito di un programma di miglioramento degli standard di gestione e tutela della privacy, imposto da Buongiorno alle terze parti ospitate sulla propria piattaforma\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La societa\' sostiene che \'\'le indagini nascono dalla denuncia di un terzo proprietario di una lista ospitata che si era rifiutato di uniformarsi alle best-practice indicate da Buongiorno\'\'. Una denuncia di cui \'\'Buongiorno sta valutando il contenuto\'\' per \'\'assumere le piu\' opportune iniziative giudiziali nei confronti del terzo denunciante\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 4831, 1, 0, '2026-01-13 21:20:29', '0000-00-00'),
(367, '2004-11-08 00:00:00', 'Servizio di Ficiesse', 'CASO SANTORO, LA REPLICA DEL COMANDO GENERALE GDF A STRISCIA LA NOTIZIA', '', 'CASO SANTORO, LA REPLICA DEL COMANDO GENERALE GDF A STRISCIA LA NOTIZIA', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><SPAN style=\"COLOR: black\"><FONT size=3><FONT face=\"Times New Roman\">Sabato 6 novembre 2004, il conduttore di Striscia la notizia, Ezio Greggio, ha letto in trasmissione la replica inviata dal Comando generale sul caso Santoro.<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"COLOR: black\">In allegato, il testo della replica e nei prossimi giorni i nostri commenti.</SPAN><SPAN style=\"COLOR: black; FONT-FAMILY: \'Arial Unicode MS\'; mso-fareast-font-family: \'Times New Roman\'; mso-bidi-font-family: \'Times New Roman\'\"><o:p></o:p></SPAN></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"COLOR: black\">&nbsp;</SPAN><SPAN style=\"COLOR: black; FONT-FAMILY: \'Arial Unicode MS\'; mso-fareast-font-family: \'Times New Roman\'; mso-bidi-font-family: \'Times New Roman\'\"><o:p></o:p></SPAN></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"COLOR: black\">&nbsp;</SPAN><SPAN style=\"COLOR: black; FONT-FAMILY: \'Arial Unicode MS\'; mso-fareast-font-family: \'Times New Roman\'; mso-bidi-font-family: \'Times New Roman\'\"><o:p></o:p></SPAN></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><B style=\"mso-bidi-font-weight: normal\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"COLOR: black\"><FONT size=3><FONT face=\"Times New Roman\">Testo della replica del Comando generale della Guardia di finanza<o:p></o:p></FONT></FONT></SPAN></I></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><B style=\"mso-bidi-font-weight: normal\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"COLOR: black\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>andata in onda su “Striscia la notizia” di sabato 6 novembre 2004</FONT></FONT></SPAN></I></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyText2 style=\"MARGIN: 0cm 0cm 6pt\"><EM><FONT face=\"Times New Roman\" size=3>GREGGIO: In relazione al nostro servizio sul maresciallo Marco Santoro, che era stato certificato pazzo da alcuni test attitudinali eseguiti per un concorso da ufficiale della Guardia di finanza, il Comando generale della stessa Guardia di finanza ci fa sapere che: </FONT></EM></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><EM><FONT size=3><FONT face=\"Times New Roman\">“Per gli accertamenti sanitari, il test usato è l’MMPI-2 risalente, sì, agli anni ’40 ma che oggi è stato completamente innovato risultando il questionario clinico più affidabile al mondo. Inoltre – ci scrivono -, gli esiti dei test sono da considerare solo come ipotesi e, come nel caso del maresciallo Santoro, da suffragare con ulteriori e successivi accertamenti”. <o:p></o:p></FONT></FONT></EM></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><I><FONT size=3><FONT face=\"Times New Roman\">Bene, noi ringraziamo la Guardia di finanza per queste puntuali precisazioni. Un applauso a loro che ci hanno raccontato queste cose. <o:p></o:p></FONT></FONT></I></P>', 105, '', '', '', 4766, 1, 0, '2026-01-14 11:38:51', '0000-00-00'),
(369, '2004-11-08 00:00:00', 'Documenti', 'IL COCER GDF CHIEDE AL COMANDANTE GENERALE DI ACCERTARE EVENTUALI VIOLAZIONI SUI CASI DI ACCENTUATA CONFLITTUALITA’ TRA SINGOLI E GERARCHIA', '', 'IL COCER GDF CHIEDE AL COMANDANTE GENERALE DI ACCERTARE EVENTUALI VIOLAZIONI SUI CASI DI ACCENTUATA CONFLITTUALITA’ TRA SINGOLI E GERARCHIA', '<P><EM><FONT face=\"times new roman, times, serif\" size=3><STRONG>Pubblichiamo la delibera del Cocer del 4 novembre 2004 sui sempre più numerosi casi di acentuata conflittualità tra singoli appartenenti alla Guardia di finanza e la gerarchia.</STRONG></FONT></EM></P>\r\n<P><STRONG><EM><FONT face=\"Times New Roman\" size=3>Nei prossimi giorni pubblicheremo le circolari del Comandante generale Speciale alle quali la delibera fa riferimento con i nostri commenti.</FONT></EM></STRONG></P>\r\n<P><STRONG><EM><FONT face=\"Times New Roman\" size=3></FONT></EM></STRONG>&nbsp;</P>\r\n<P class=MsoTitle style=\"MARGIN: 0cm -1cm 0pt 0cm\" align=center><SPAN style=\"FONT-SIZE: 12pt\"><STRONG><FONT face=\"Times New Roman\">COMANDO GENERALE DELLA GUARDIA DI FINANZA<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></STRONG></SPAN></P>\r\n<P class=MsoSubtitle style=\"MARGIN: 0cm 0cm 0pt\" align=center><SPAN style=\"FONT-SIZE: 12pt\"><STRONG><FONT face=\"Times New Roman\">Consiglio Centrale Di Rappresentanza<o:p></o:p></FONT></STRONG></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><FONT size=3><FONT face=\"Times New Roman\">Viale XXI Aprile, 51- 00162 Roma – Tel. 06/44242585 – Fax 06/44222633<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; tab-stops: 24.05pt right 347.15pt\" align=center><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; tab-stops: right 347.15pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; tab-stops: right 347.15pt\" align=center><o:p></o:p><SPAN style=\"FONT-SIZE: 12pt\"><U><FONT face=\"Times New Roman\">D<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>E<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>L<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>I<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>B<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>E<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>R<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>A<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>N.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>01/115/9°<o:p></o:p></FONT></U></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; tab-stops: 24.05pt right 347.15pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify; mso-pagination: none\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 70.9pt; TEXT-INDENT: -70.9pt; TEXT-ALIGN: justify; mso-pagination: none\"><FONT size=3><FONT face=\"Times New Roman\"><B>OGGETTO</B>:<SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp; </SPAN>Tutela del personale.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoHeading8 style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><B><U><SPAN style=\"FONT-STYLE: normal\"><FONT size=3><FONT face=\"Times New Roman\">I<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>L<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>C<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>O<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>C<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>E<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>R<o:p></o:p></FONT></FONT></SPAN></U></B></P>\r\n<P class=MsoBodyTextIndent style=\"MARGIN: 0cm 0cm 0pt 81pt; TEXT-INDENT: -81pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent style=\"MARGIN: 0cm 0cm 0pt 81pt; TEXT-INDENT: -81pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: -99pt; TEXT-ALIGN: justify; tab-stops: 99.0pt\"><FONT size=3><FONT face=\"Times New Roman\"><B><U>VALUTATE</U></B><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>con estrema positività le iniziative sino ad oggi messe in campo dal Comandante Generale concretizzatesi in diverse disposizioni ed in particolare nella circolare n. 334800/152 del 19 ottobre 2004 (<B><I>Allegato n. 1</I></B>), recante “Disposizioni concernenti la Rappresentanza Militare”;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyTextIndent style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: -99pt; TEXT-ALIGN: justify; tab-stops: 99.0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: -99pt; TEXT-ALIGN: justify; tab-stops: 99.0pt\"><FONT size=3><FONT face=\"Times New Roman\"><B><U>L E T T A</U></B><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>attentamente la circolare n. 284116 dell’1 settembre 2004, avente per oggetto: “Gli obiettivi della Guardia di Finanza per il quinquennio, 2004-2008. Linee di tendenza e relativi provvedimenti” di cui si riportano i seguenti passaggi salienti:<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyTextIndent style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: -99pt; TEXT-ALIGN: justify; tab-stops: 99.0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: 0cm; TEXT-ALIGN: justify; tab-stops: 108.0pt; mso-list: l2 level1 lfo1\"><FONT size=3><FONT face=\"Times New Roman\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"mso-list: Ignore\">1.</SPAN></B>“<I>[omissis]</I>…dobbiamo entrare, pertanto, nell’ordine di idee che, per quanto ci riguarda, è già iniziata una nuova era che ha, come chiave di volta, un nuovo approccio verso la soluzione dei problemi relativi al governo e impiego del personale…<I>[omissis]</I>”;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-ALIGN: justify; tab-stops: 108.0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: 0cm; TEXT-ALIGN: justify; tab-stops: 108.0pt; mso-list: l2 level1 lfo1\"><FONT size=3><FONT face=\"Times New Roman\"><B><SPAN style=\"mso-list: Ignore\">2.</SPAN></B>“<I>[omissis]</I>…necessita un nuovo approccio verso tutti i problemi che afferiscono il miglioramento della <B>funzionalità dello strumento operativo e della qualità della</B> <B>vita di tutte le Fiamme Gialle</B>. Semplificando notevolmente dobbiamo <B>immaginare di</B> <B>trovarci di fronte ad un bivio</B>: o continuiamo ad operare come se nulla fosse accaduto (e invece è già accaduto e, comunque, fatalmente dovrà accadere!) intorno a noi, oppure dobbiamo <B>cambiare il nostro approccio mentale, sforzandoci di compiere un vero e proprio salto culturale nella convinzione di far parte (tutti!) di un gruppo coeso con obiettivi condivisi in un contesto di valori comuni</B>…<I>[omissis]</I>”<B>;<o:p></o:p></B></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-ALIGN: justify; tab-stops: 108.0pt\"><B><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: 0cm; TEXT-ALIGN: justify; tab-stops: 108.0pt; mso-list: l2 level1 lfo1\"><FONT size=3><FONT face=\"Times New Roman\"><B><SPAN style=\"mso-list: Ignore\">3.</SPAN></B>“<I>[omissis]</I>…in tale quadro, se volgiamo lo sguardo, per l’appunto, ai problemi della funzionalità dello strumento operativo e della qualità della vita del personale, appare del tutto evidente che <B>nuove filosofie e paradigmi organizzativi devono sostituirsi a vecchi modelli mentali e procedurali.</B> Infatti, così come è vero <B>che il personale</B> <B>costituisce il bene più prezioso per ogni organizzazione</B> – tanto che spesso, le vere differenze tra strutture analoghe risiedono interamente nel personale- è altresì vero che <B>uomini motivati non si improvvisano</B>, ma si costruiscono con la cura attenta e continua di leaders capaci. L’arma vincente – che consente di acquisire una autorevolezza in grado di dare una spinta in più in questi <B>delicatissimi e vitali settori</B> è composta di un mix di ingredienti difficile da amalgamare: capacità di <B>coinvolgere i collaboratori</B>, di gestire i cambiamenti, di possedere una visione “<B>perfettamente a fuoco</B>” degli obiettivi della nostra istituzione, di creare, finalmente, un distacco tra “<B>quello che siamo o crediamo di essere” e “quello che invece dobbiamo essere”</B> …<I>[omissis]</I>”<B>;<o:p></o:p></B></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-ALIGN: justify; tab-stops: 108.0pt\"><B><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: 0cm; TEXT-ALIGN: justify; tab-stops: 108.0pt; mso-list: l2 level1 lfo1\"><FONT size=3><FONT face=\"Times New Roman\"><B><SPAN style=\"mso-list: Ignore\">4.</SPAN></B>“<I>[omissis]</I>…l’interesse dei cittadini e delle Istituzioni, sia stimolare, con iniezioni di credibilità via via crescenti, il nostro personale in servizio ad offrire una performance sempre migliore, con <B>soddisfazione del singolo e miglioramento, in termini di efficacia e di efficienza, dell’intera organizzazione;<o:p></o:p></B></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-ALIGN: justify; tab-stops: 108.0pt\"><B><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: 0cm; TEXT-ALIGN: justify; tab-stops: 108.0pt; mso-list: l2 level1 lfo1\"><FONT size=3><FONT face=\"Times New Roman\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"mso-list: Ignore\">5.</SPAN></B>“<I>[omissis]</I>…la maggiore diffusione possibile e vengano poste in atto tutte le necessarie misure di comando e controllo volte a <B>verificare la conoscenza delle sopra esposte linee guida</B>, quantomeno da parte dei responsabili di tutte le articolazioni dipendenti (fino ai minori livelli) …<I>[omissis]</I>”;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: -99pt; TEXT-ALIGN: justify; tab-stops: 99.0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: -99pt; tab-stops: 99.0pt\"><FONT size=3><FONT face=\"Times New Roman\"><B><U>ESAMINATA</U><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></B>la corrispondenza pervenuta dal personale, che sovente sollecita l’intervento (l’”aiuto” v. prott. 1178/COCER, 1035/COCER, 1073/COCER etc., <B><I>Allegato n. .2</I></B>) di questo Consiglio a fronte di situazioni che vengono rappresentate come estremamente gravi;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: -99pt; tab-stops: 81.0pt 99.0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: -99pt; tab-stops: 99.0pt\"><FONT size=3><FONT face=\"Times New Roman\"><B><U>CONSIDERATO</U><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp; </SPAN></B>che le situazioni rappresentate nelle missive pervenute, necessitano di accertamenti autorevoli ed ufficiali;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: -99pt; tab-stops: 81.0pt 99.0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: -99pt; tab-stops: 99.0pt\"><FONT size=3><FONT face=\"Times New Roman\"><B><U>A T T E S O</U><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></B>l’affidamento che larga parte del personale dimostra di riporre nella Rappresentanza ed in questo Consiglio in particolare;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: -99pt; tab-stops: 99.0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 99pt; TEXT-INDENT: -99pt; tab-stops: 99.0pt\"><FONT size=3><FONT face=\"Times New Roman\"><B><U>ASCOLTATE</U><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></B>con sgomento, da ultimo, le notizie pervenute dai mezzi di stampa,<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 0cm\"><B><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></B></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 0cm\"><B><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></B></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 81pt; TEXT-ALIGN: center\" align=center><B><U><FONT size=3><FONT face=\"Times New Roman\">D<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>E<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>I<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>B<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>E<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>R<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>A<o:p></o:p></FONT></FONT></U></B></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 81pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 81pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 18pt; TEXT-INDENT: -18pt; tab-stops: list 18.0pt; mso-list: l1 level1 lfo2\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>1.</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><FONT size=3>di chiedere al Comandante Generale:<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 81pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 36pt; TEXT-INDENT: -18pt; tab-stops: list 36.0pt; mso-list: l0 level1 lfo3\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>a.</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><FONT size=3>di approfondire le vicende relative a quei militari che hanno in atto vicende oltremodo conflittuali con la propria gerarchia al fine di valutare se effettivamente, alla luce anche delle direttive emanate, tali conflittualità siano da attribuire esclusivamente ai militari interessati;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 0cm\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 36pt; TEXT-INDENT: -18pt; tab-stops: list 36.0pt; mso-list: l0 level1 lfo3\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>b.</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><FONT size=3>di esaminare e chiarire le vicende rappresentate nell’intervista alla trasmissione “Striscia la Notizia” del 30 ottobre 2004, nell’interesse dell’Istituzione e del personale ad Essa appartenente;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 18pt; TEXT-INDENT: 0cm\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 36pt; TEXT-INDENT: -18pt; tab-stops: list 36.0pt; mso-list: l0 level1 lfo3\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>c.</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><FONT size=3>di monitorare l’attività amministrativa relativa alle circolari emanate, onde garantirne il rispetto e, laddove necessario, sanzionare le trasgressioni;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 18pt; TEXT-INDENT: 0cm\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt 36pt; TEXT-INDENT: -18pt; tab-stops: list 36.0pt; mso-list: l0 level1 lfo3\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>d.</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><FONT size=3>di farsi promotore delle aspettative dei delegati e del personale, chiedendo al Ministro ed al Governo di recepire, nella prossima riforma della Rappresentanza Militare, tutte quelle proposte in grado di rendere efficiente l’attuale modello di tutela del personale militare, e che possano contribuire ad evitare ulteriori spiacevoli incidenti all’immagine del Corpo;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 0cm\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 0cm; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent style=\"MARGIN: 0cm 0cm 0pt 144pt; TEXT-INDENT: -18pt; TEXT-ALIGN: justify; tab-stops: list 144.0pt; mso-list: l0 level4 lfo3\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>2.</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><FONT size=3>Nella circostanza, si stigmatizza il comportamento di taluni superiori che, nel motivare le sanzioni inflitte al personale dipendente, hanno fatto riferimento a posizioni assunte dal<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>COCER proprio a tutela di analoghe e specifiche situazioni, ingenerando così confusione e sfiducia nella Rappresentanza, ente referente delle istanze personali e collettive, come stabilito dalla legge.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyTextIndent style=\"MARGIN: 0cm 0cm 0pt 72pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent style=\"MARGIN: 0cm 0cm 0pt 72pt; TEXT-ALIGN: justify\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 0cm; TEXT-ALIGN: justify\"><FONT size=3><FONT face=\"Times New Roman\">La presente delibera, approvata all\'unanimità (11 votanti) in data 4 novembre 2004, viene inviata a stralcio verbale.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoBodyTextIndent2 style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoHeading7 style=\"MARGIN: 0cm 0cm 0pt; tab-stops: center 90.0pt 342.0pt\" align=center><B><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>IL SEGRETARIO<SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>IL PRESIDENTE DEL COCER<o:p></o:p></FONT></FONT></B></P>\r\n<P class=MsoHeading8 style=\"MARGIN: 0cm 0cm 0pt; tab-stops: center 90.0pt 342.0pt 387.0pt\" align=center><EM><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>App. Enrico ZACCARIA<SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Gen.B. Paolo POLETTI<o:p></o:p></FONT></FONT></EM></P>\r\n<P>&nbsp;</P>\r\n<P></P>', 0, '', '', '', 8050, 1, 0, '2026-01-14 10:24:42', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(370, '2004-11-09 00:00:00', 'Documenti', 'LA CIRCOLARE DEL COMANDANTE GENERALE SPECIALE SUL DOCUMENTO \"PER UNA ROTTA CONDIVISA\". ESPOSTI I MOTIVI PER I QUALI ORGANISMI INTERNI SAREBBERO SUFFICIENTI PER TUTELARE GLI INTERESSI DEL PERSONALE MILITARE', '', 'LA CIRCOLARE DEL COMANDANTE GENERALE SPECIALE SUL DOCUMENTO \"PER UNA ROTTA CONDIVISA\". ESPOSTI I MOTIVI PER I QUALI ORGANISMI INTERNI SAREBBERO SUFFICIENTI PER TUTELARE GLI INTERESSI DEL PERSONALE MILITARE', '<P><EM><FONT size=3><FONT face=\"times new roman, times, serif\">Pubblichiamo la</FONT> <FONT face=\"times new roman, times, serif\">circolare n. 334800 del 19 ottobre 2004 con la quale il generale di corpo d\'armata Roberto Speciale spiega&nbsp;cosa è stato fatto dal Comando generale del Corpo a seguito delle richieste avanzate&nbsp;nel documento unitario intitolato \"PER UNA ROTTA CONDIVISA\" licenziato dai Cobar, Coir e&nbsp;Cocer del Corpo riuniti a Roma nello scorso mese di marzo.</FONT></FONT></EM></P>\r\n<P><EM><FONT size=3><FONT face=\"times new roman, times, serif\">La tesi dell\'attuale Comandante generale - che&nbsp;come noto&nbsp;assolutamente NON condividiamo - è che per tutelare gli interessi dei finanzieri siano sufficienti&nbsp;organismi interni&nbsp;e comandanti militari sensibili ai problemi dei loro sottoposti.</FONT></FONT></EM></P>\r\n<P><EM><FONT face=\"Times New Roman\" size=3>Nei prossimi giorni i nostri commenti.</FONT></EM></P>\r\n<P><EM><FONT face=\"Times New Roman\" size=3></FONT></EM>&nbsp;</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt\"><FONT face=\"Times New Roman\">COMANDO GENERALE DELLA GUARDIA DI FINANZA<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt\"><FONT face=\"Times New Roman\">Ufficio Rappresentanza Militare<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 51.1pt 0cm 0pt; LINE-HEIGHT: 12.95pt; mso-line-height-rule: exactly\"><FONT face=\"Times New Roman\"><SPAN style=\"FONT-SIZE: 11pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; N.<SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-SIZE: 16pt\">334800/152</SPAN><SPAN style=\"FONT-SIZE: 18pt\"><SPAN style=\"mso-tab-count: 5\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-SIZE: 11pt\">Roma<SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp; </SPAN></SPAN><SPAN style=\"FONT-SIZE: 16pt\">19 OTT. 2004</SPAN><SPAN style=\"FONT-SIZE: 18pt\"><o:p></o:p></SPAN></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 51.1pt 0cm 0pt; LINE-HEIGHT: 12.95pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">OGGETTO: Disposizioni concernenti la Rappresentanza Militare.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.7pt 0cm 0pt; LINE-HEIGHT: 11pt; TEXT-ALIGN: center; mso-line-height-rule: exactly\" align=center><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.7pt 0cm 0pt; LINE-HEIGHT: 11pt; TEXT-ALIGN: center; mso-line-height-rule: exactly\" align=center><FONT face=\"Times New Roman\" size=3>(INDIRIZZI OMESSI)</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.7pt 0cm 0pt; LINE-HEIGHT: 11pt; TEXT-ALIGN: center; mso-line-height-rule: exactly\" align=center><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.7pt 0cm 0pt; LINE-HEIGHT: 11pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">e, per conoscenza:<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.95pt 0cm 0pt; TEXT-INDENT: 93.6pt; LINE-HEIGHT: 11pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">AL CONSIGLIO CENTRALE DI RAPPRESENTANZA ROMA<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-INDENT: 93.6pt; LINE-HEIGHT: 10.05pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">~ Sezione Guardia di finanza -<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 14.4pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">Seguito fogli nn. 427116 in data 22 dicembre 2003 e n. 123107 in data 16 aprile 2004 non<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 1.65pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">a tutti diretto<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.45pt 68.4pt 0pt 36pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.95pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">1. Con i fogli a seguito ho ritenuto, tra !\'altro, di richiamare l\'attenzione sulla necessità di instaurare uno spirito di collaborazione, leale e costruttivo, con gli Organismi di rappresentanza affiancati e con i militari dipendenti, nel superiore interesse del Corpo e dei suoi appartenenti.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.7pt 68.4pt 0pt 36pt; LINE-HEIGHT: 12.7pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">Tale armonia è indispensabile per assicurare le migliori condizioni di vita del personale onde, anche per questa via, perseguire in modo efficace gli obiettivi assegnati dalle Istituzioni alla Guardia di finanza e soddisfare le forti aspettative della collettività in termini di sicurezza ed equità sostanziale.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 14.15pt 68.4pt 0pt 36pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.95pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">2. La bontà di tale linea di azione è stata confermata a livello centrale. Infatti, grazie al contributo propositivo fornito nel tempo dal COCER, arricchito da taluni auspici contenuti nel \"documento unitario\" approvato nella riunione congiunta degli Organi di rappresentanza svoltasi a Roma lo scorso marzo, sono state risolte in via amministrativa, in quanto rientranti nella sfera di competenza dell\'Amministrazione, le seguenti problematiche di rilievo sul tappeto:<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.45pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12.95pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">a.<B style=\"mso-bidi-font-weight: normal\">impiego a \"domanda\" del personale dei ruoli ispettori, sovrintendenti,appuntati e finanzieri.<o:p></o:p></B></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 11.5pt 0cm 0pt 36pt; LINE-HEIGHT: 13.9pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">È stata emanata una nuova circolare (n. 255000 in data 2 agosto 2004) che, segnatamente, prevede:<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 10.3pt 0cm 0pt 54pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 13.9pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(1) unico procedimento per la formazione delle graduatorie, in cui si integrano le istanze di trasferimento in ambito nazionale con quelle a livello regionale;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.95pt 68.4pt 0pt 54pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 13.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(2) revoca per i trasferimenti a \"domanda\", sia dopo l\'eventuale proroga che a movimento già effettuato, con azzeramento del periodo di permanenza al reparto di provenienza;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.45pt 68.4pt 0pt 54pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 13.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(3) abbattimento del requisito della permanenza minima, in ambito nazionale (da sei a tre anni) e a livello periferico da (tre a un anno), per l\'accesso al piano di impiego a \"domanda\" in favore di militari che hanno maturato quindici anni di anzianità di servizio;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.95pt 0cm 0pt 54pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 13.2pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(4) pubblicazione delle vacanze organiche relative al contingente di mare distintamente per specializzazione;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 39.1pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><B style=\"mso-bidi-font-weight: normal\">b</B>. <B style=\"mso-bidi-font-weight: normal\">domande di trasferimento a tempo determinato</B>.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12pt 64.8pt 0pt 36pt; LINE-HEIGHT: 12.7pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">È stata snellita ulteriormente l\'istruttoria prevedendo che le istanze devono pervenire al Comando Generale entro 15 giorni dalla loro presentazione (messaggio n. 122748 in data 16 aprile <I>2004);<o:p></o:p></I></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 15.1pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12.7pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><B style=\"mso-bidi-font-weight: normal\">c</B>. <B style=\"mso-bidi-font-weight: normal\">impiego dei luogotenenti.</B><o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 13.4pt 68.4pt 0pt 36pt; LINE-HEIGHT: 12.7pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">Sono stati stabiliti organicamente taluni incarichi di comando cui preporre gli ispettori, compresi i luogotenenti (determinazioni nn. 227237 e 252236 in data, rispettivamente, 9 e 29 luglio 2004).<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.7pt 64.8pt 0pt 36pt; LINE-HEIGHT: 12.95pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">La manovra -che, dopo la disciplina delle funzioni della categoria (circolare del1\'11 aprile 2002), aggiunge un ulteriore tassello alla piena attuazione di quanto previsto dall\'art. 34, comma 4, del D.Lgs. 199/1995 sarà a breve completata con l\'emanazione di nuove disposizioni a livello nazionale;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 13.4pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12.95pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><B style=\"mso-bidi-font-weight: normal\">d. concorsi interni per l\'accesso ai ruoli sovrintendenti e ispettori</B>.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 11.75pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12.95pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">È stato stabilito che:<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 13.65pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12.95pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(1) in sede di prossimi bandi di concorso saranno rivisti i criteri di attribuzione dei<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 45pt; TEXT-INDENT: -9pt; LINE-HEIGHT: 12pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;</SPAN>punteggi ai titoli, privilegiando quelli connessi al servizio;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 15.1pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(2)<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>i neo sovrintendenti provenienti dal concorso riservato agli appuntati scelti<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0.95pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>saranno confermati al Comando Regionale di appartenenza, salvo diversa<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 1.2pt 0cm 0pt 54pt; LINE-HEIGHT: 12pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">istanza degli interessati al termine del corso. In tal caso, la richiesta verrà<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 54pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 13.4pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>valutata tenendo conto delle esigenze organiche e di servizio dell\'Amministrazione nonché<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>dell\'anzianità di servizio e delle motivazioni dei<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12.2pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>singoli espresse in sede di eventuali memorie.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 13.65pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12.2pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>I criteri innanzi citati sono stati già utilizzati, lo scorso mese di luglio, anche per<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0.45pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12.2pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>le assegnazioni dei neo marescialli provenienti dal concorso interno;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 13.9pt 0cm 0pt 54pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.2pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><FONT size=3><FONT face=\"Times New Roman\">e. aggiornamento degli incarichi di comando validi ai fini del conferimento della medaglia di lungo<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>comando.<o:p></o:p></FONT></FONT></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 14.4pt 0cm 0pt 45pt; LINE-HEIGHT: 12.2pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">È in corso di elaborazione una nuova determinazione;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.95pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12.2pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><FONT size=3><FONT face=\"Times New Roman\">f. adozione distintivi di grado almeno uguali per tutte le Forze di polizia.<o:p></o:p></FONT></FONT></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 13.9pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">Sono stati assunti contatti con le altre Amministrazioni per riattivare il \"tavolo tecnico\"<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0.45pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">interforze , avviato all\'indomani dell\'entrata in vigore del D.Lgs. 199/1995, per<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 1.2pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">individuare una soluzione condivisa.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.7pt 68.4pt 0pt 36pt; LINE-HEIGHT: 13.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">Nel frattempo, sono state emanate nuove disposizioni transitorie volte a chiarire le<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>caratteristiche dei distintivi oggi in uso (determinazione n. 177527 in data 26 maggio<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0.7pt 0cm 0pt 18pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 13.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><I>2004 </I>);<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 26.4pt 0cm 0pt; TEXT-INDENT: 36pt; LINE-HEIGHT: 12pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><FONT size=3><FONT face=\"Times New Roman\">g. maggiore disponibilità di alloggi di servizio.<o:p></o:p></FONT></FONT></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.95pt 3.6pt 0pt 45pt; LINE-HEIGHT: 13.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">La legge 28/1999 prevede apposito finanziamento pluriennale per la realizzazione di un programma di potenziamento delle infrastrutture del Corpo che, per alterne vicende, non è stato ancora avviato.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 13.65pt 0cm 0pt 45pt; LINE-HEIGHT: 12.2pt; mso-line-height-rule: exactly; tab-stops: 45.0pt\"><FONT size=3><FONT face=\"Times New Roman\">Da ultimo, il Ministro dell\'economia e delle finanze ha emanato due decreti, in attuazione della legge 326/2003, con i quali ha:<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.7pt 3.6pt 0pt 63pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 13.2pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(1) autorizzato l\'impiego delle quote annuali del finanziamento 2000 non impegnate entro il 31 dicembre 2003 (24 milioni di euro) per il pagamento di canoni di<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 1.2pt 0cm 0pt 63pt; LINE-HEIGHT: 13.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">locazione;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.2pt 3.6pt 0pt 63pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(2) approvato il programma relativo al triennio 2000/2002 predisposto dal Comando Generale, concernente la realizzazione di caserme ed alloggi di servizio sul territorio nazionale, autorizzando l\'impegno dei relativi fondi (247 milioni di euro);<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 14.85pt 7.2pt 0pt 54pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.7pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><FONT size=3><FONT face=\"Times New Roman\">h. unificazione delle mense, \"esternalizzazione\" del servizio di vettovagliamento, allineamento del trattamento alimentare gratuito a quello delle altre Forze di polizia e maggiore ricorso ai \"buoni-pasto\".<o:p></o:p></FONT></FONT></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.7pt 0cm 0pt 45pt; TEXT-INDENT: 9pt; LINE-HEIGHT: 12.7pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">In materia:<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 13.65pt 3.6pt 0pt 72pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.95pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(1) è stato disposto che la fruizione del vitto avvenga in tutti i locali a ciò destinati senza distinzioni di categoria, fatte salve particolari esigenze di alta rappresentanza, per le quali il servizio a tavola sarà, comunque, affidato a ditte esterne (circolare n. 115032 in data 8 aprile 2004).<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.95pt 7.2pt 0pt 72pt; LINE-HEIGHT: 12.95pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">Nell\'occasione, è stato anche precisato che le attività di confezionamento e distribuzione dei pasti, nei casi di appalto a privati, devono essere svolte esclusivamente dal personale delle imprese;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.45pt 7.2pt 0pt 72pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.95pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(2) è in atto il processo di \"privatizzazione\" del servizio di vettovagliamento sulla base delle disponibilità finanziarie sullo specifico capitolo. Ad oggi, su 832 mense complessive solo 126 (pari al 15,1% del totale) sono ancora a \"gestione diretta\".<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 72pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.95pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>La piena realizzazione del progetto è subordinata, però, ad un\'integrazione delle risorse, più volte richiesta ai competenti organi;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 15.1pt 7.2pt 0pt 72pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.7pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(3) è stata avviata la revisione delle norme amministrative di settore, risalenti al 1992. In tale contesto verranno, altresì, modificate le disposizioni vigenti nel Corpo in materia di trattamento alimentare gratuito, influenzate dalla insufficienza dei fondi, per adeguarle a quelle delle altre Forze di polizia;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12pt 7.2pt 0pt 72pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 13.4pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(4) per agevolare il ricorso all\'istituto del \"buono-pasto\" in luogo dell\'accesso all\'esercizio privato convenzionato, è stato abolito il vincolo (distanza del reparto dal punto di ristoro ovvero scarsa qualità del vitto oppure motivi di opportunità) contenuto nella circolare del 2002 (circolare n. 296491 in data 15 settembre (2004 );<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 23.25pt 0cm 0pt 45pt; TEXT-INDENT: 9pt; LINE-HEIGHT: 12pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><FONT size=3><FONT face=\"Times New Roman\">i. istituzione delle \"Segreterie COIR\".<o:p></o:p></FONT></FONT></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.95pt 0cm 0pt 54pt; LINE-HEIGHT: 12.95pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">Sono state istituite le Segreterie permanenti anche per tutti i COIR (determinazione n. 123280 in data 16 aprile 2004);<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.95pt 0cm 0pt 45pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 13.2pt; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><FONT size=3><FONT face=\"Times New Roman\">I. maggiore comunicazione fra i Comandanti ed il personale nonché iniziative formative e di informazione sul fenomeno del \"mobbing\".<o:p></o:p></FONT></FONT></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 13.2pt 3.6pt 0pt 45pt; LINE-HEIGHT: 12.95pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">Con la mia \"lettera aperta\" inviata lo scorso dicembre, tutti i Comandanti di reparto sono stati, tra l\'altro, sensibilizzati a ricercare maggiore collaborazione e comunicazione con il personale dipendente. Da ultimo (circolare n. 284116 in data 1 settembre 2004), ho rimarcato, tra l\'altro, la necessità del coinvolgimento dei collaboratori per innescare un \"circolo virtuoso\" capace di generare quel potenziale energetico idoneo al cambiamento mentale ed organizzativo avviato nell\'ambito del Corpo.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.95pt 0cm 0pt 45pt; LINE-HEIGHT: 12.95pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">Sull\'argomento, inoltre:<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 13.9pt 3.6pt 0pt 63pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(1) è stato costituito a livello Stato Maggiore apposito gruppo di lavoro, al quale partecipa anche uno psicologo di fama, per l\'esame, fra le altre, della problematica del <I>mobbing </I>e l\'individuazione delle pertinenti iniziative di prevenzione;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 14.4pt 0cm 0pt 63pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.95pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(2) sono state svolte lezioni nell\'ambito dei corsi di qualificazione per dirigenti e sensibilizzazioni in occasioni dei rapporti con i Comandanti Regionali;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 13.2pt 0cm 0pt 45pt; LINE-HEIGHT: 12.95pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(3) sarà distribuito un volume della collana \"Progetti ed azioni\";<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.45pt 0cm 0pt 45pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 13.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><B style=\"mso-bidi-font-weight: normal\"><FONT size=3><FONT face=\"Times New Roman\">m. riconoscimento economico della funzione dj polizia economico-finanziaria e parità di trattamento con le Agenzie fiscali in materia di \"premio incentivante\".<o:p></o:p></FONT></FONT></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 13.2pt 7.2pt 0pt 45pt; LINE-HEIGHT: 12.95pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">La valorizzazione economica delle funzioni è stata awiata con il noto \"fondo efficienza\" (art. 53 del D.P.R. 254/1999) alimentato dalle risorse contrattuali provenienti dal riequilibrio finanziario.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.7pt 7.2pt 0pt 45pt; LINE-HEIGHT: 13.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">La perequazione con il peculiare trattamento incentivante previsto dalle vigenti norme a favore del personale dell\'Amministrazione finanziaria è da tempo perseguita dal Comando Generale.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 13.2pt 7.2pt 0pt 45pt; LINE-HEIGHT: 12.7pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">Tuttavia, risulta difficile realizzare autonome iniziative di estensione a carattere legislativo, tenuto conto che il trattamento economico costituisce materia di \"concertazione\" e, pertanto, registra la \"sensibilità\" e la contrarietà delle altre Forze di polizia ad azioni unilaterali per il Corpo.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.7pt 7.2pt 0pt 45pt; LINE-HEIGHT: 12.95pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">I consensi delle altre Amministrazioni del comparto potrebbero essere ottenuti a condizione che i \"premi\" e i proventi derivanti dalle rispettive attività istituzionali confluiscano in un \"fondo\" comune dal quale prelevare le risorse da assegnare a ciascuna Istituzione. La problematica viene tenuta in evidenza per la sua definizione alla prima favorevole occasione.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 25.2pt 0cm 0pt 45pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 13.2pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">3. La soluzione delle suesposte questioni testimonia la validità e l\'idoneità della Rappresentanza Militare nel concorso alla tutela degli interessi del personale.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 10.05pt 61.2pt 0pt 45pt; LINE-HEIGHT: 13.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">La vigente normativa di settore va, tuttavia, aggiornata e migliorata al fine di confermare l\'istituto quale punto di riferimento catalizzatore delle istanze provenienti dalla \"base\", operante all\'interno dell\'Amministrazione militare, idoneo a stimolare le azioni delle Autorità competenti e concorrere a realizzare il benessere, morale e materiale, dei militari.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 11.5pt 64.8pt 0pt 45pt; LINE-HEIGHT: 12.7pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">In questa prospettiva, come ho anticipato con le note a seguito, la IV Commissione (Difesa) della Camera ha elaborato un progetto di riforma, approvato lo scorso mese di luglio (disponibile sul sito <I>INTRANET, </I>pagina <I>web </I>dell\'Ufficio Rappresentanza Militare) e attualmente all\'esame delle altre competenti Commissioni permanenti. Tale articolato reca ora, rispetto alla precedente versione, diverse \"aperture\" volte a rendere più incisivo il ruolo svolto dagli Organi di rappresentanza, sebbene necessiti di ulteriori interventi migliorativi, già segnalati dal Comando Generale e dal COCER/Guardia di finanza nelle competenti sedi.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.2pt 0cm 0pt 45pt; LINE-HEIGHT: 12.7pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">Il provvedimento contiene le seguenti novità di rilievo, che recepiscono buona parte<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 45pt; LINE-HEIGHT: 12.7pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">delle richieste indicate anche nel già citato \"documento unitario\":<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 11.5pt 64.8pt 0pt 63pt; TEXT-INDENT: -36pt; LINE-HEIGHT: 12.95pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>a. conferimento al COCER della fisionomia di \"parte\" nell\'ambito della \"concertazione\",<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>in posizione, cioè, distinta dai Ministri di riferimento, dei quali è diretto interlocutore.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 11pt 0cm 0pt 45pt; TEXT-INDENT: 18pt; LINE-HEIGHT: 12.95pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">Tale ruolo di \"parte\" consentirà al Consiglio Centrale, in concreto, di:<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12pt 0cm 0pt 81pt; TEXT-INDENT: -54pt; LINE-HEIGHT: 12.7pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>(1) partecipare alle attività in posizione distinta dai Ministri di riferimento e dagli Stati<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Maggiori/Comandi Generali.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.45pt 64.8pt 0pt 72pt; TEXT-INDENT: -45pt; LINE-HEIGHT: 12.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>In tale contesto, al fine di esercitare al meglio detta funzione, è consentito al<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>COCER di avvalersi del contributo tecnico delle Amministrazioni di appartenenza;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.95pt 64.8pt 0pt 81pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.45pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">(2) presentare, ove dissenziente sugli schemi di provvedimento \"concertati\",<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>osservazioni al Ministro per la funzione pubblica ed adire, laddove non ancora soddisfatto nei testi definiti, il Presidente del Consiglio dei Ministri;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.45pt 64.8pt 0pt 63pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.7pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">b. maggiori tutele per i delegati (partecipazione ai turni di servizio in proporzione alla<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>loro presenza ai reparti, assenso del Ministro per i trasferimenti d\'autorità in caso di discordanza tra Amministrazione e delegato);<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 11.75pt 0cm 0pt 45pt; LINE-HEIGHT: 12.7pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">c. facoltà per il COCER ed i COIR di effettuare visite conoscitive presso i reparti di<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0.2pt 0cm 0pt 45pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.7pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>pertinenza, incontrare il relativo personale e partecipare alle riunioni dei GaSAR;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 11.25pt 0cm 0pt 45pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.7pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>d. incarico a tempo pieno per i rappresentanti COCER;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 11.25pt 0cm 0pt 45pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 13.4pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>e. estensione della durata del mandato a quattro anni, con possibilità di immediata rieleggibilità dei delegati;<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.2pt 64.8pt 0pt 36pt; TEXT-INDENT: -9pt; LINE-HEIGHT: 12.2pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">f. ampliamento delle materie di competenza (articolazione dell\'orario di lavoro obbligatorio, criteri generali relativi ai trasferimenti, disciplina generale della qualificazione del personale) salute del personale, parere preventivo e obbligatorio<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0.7pt 0cm 0pt 45pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>sugli schemi dei provvedimenti legislativi e regolamentari).<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.95pt 68.4pt 0pt 45pt; TEXT-INDENT: -9pt; LINE-HEIGHT: 12.95pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">Per consentire agli Organi in carica di seguire l\'iter e la conclusione del suddetto processo di riforma, il Senato ha approvato un emendamento, in sede di conversione del decreto-legge n. 238/2004, che proroga il mandato in corso fino al15 maggio 2006.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 13.2pt 68.4pt 0pt 45pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.95pt; TEXT-ALIGN: justify; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">4. Confido sulla sensibilità, sul consueto spirito di collaborazione e sulla sapiente opera delle SS.LL. affinché si prosegua nella linea di azione tracciata con i fogli a seguito e sia data la massima diffusione possibile della presente fra tutto il personale dipendente, fino ai minori livelli, attivando anche le necessarie misure di comando e controllo volte a garantire la conoscenza del quadro di situazione innanzi delineato.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 12.2pt 0cm 0pt 45pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 12.95pt; mso-line-height-rule: exactly\"><FONT size=3><FONT face=\"Times New Roman\">5. Prego assicurare.<o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P>&nbsp;</P>', 106, '', '', '', 2569, 1, 0, '2026-01-10 07:14:05', '0000-00-00'),
(372, '2004-11-10 00:00:00', 'Notizie Flash', 'DIFFAMAZIONE E CALUNNIA A GENERALE CC, SEQUESTRATO SITO UNAC', '', 'DIFFAMAZIONE E CALUNNIA A GENERALE CC, SEQUESTRATO SITO UNAC', '<FONT size=3>(ANSA) - BARI, 27 OTT - Contestando i reati di calunnia e<BR>diffamazione nei confronti del generale dei carabinieri Michele<BR>Franze\', la Procura della Repubblica presso il Tribunale di Bari<BR>ha sottoposto a sequestro preventivo il sito nazionale dell\'<BR>Unione nazionale Arma carabinieri \'www.unionecarabinieri.it\'. Il<BR>provvedimento e\' gia\' stato convalidato dal gip di Bari Chiara<BR>Civitano.<BR>&nbsp;&nbsp; Secondo quanto rende noto l\' <STRONG><FONT color=#ff0000>Unac</FONT></STRONG>, il sequestro sarebbe<BR>scaturito dopo la pubblicazione di una notizia relativa \'\'ad un<BR>mega ricevimento di \'gala\' tenutosi all\' interno della Regione<BR>carabinieri Puglia di Bari per un \'saluto di commiato\'. Fatto<BR>che avrebbe suscitato la \'sensibilita\' di un alto ufficiale del<BR>posto, che avrebbe inoltrato una segnalazione, il cui seguito ha<BR>portato al blocco dell\' intero sito nazionale dell\' <STRONG><FONT color=#ff0000>Unac</FONT></STRONG>\'\'. Il<BR>saluto di commiato a cui fa riferimento l\' <STRONG><FONT color=#ff0000>Unac</FONT></STRONG> e\' quello del<BR>gen.Franze\' che ha da poco lasciato il comando della regione<BR>carabinieri Puglia. Il gen.Franze\' ha ora il Comando della<BR>Divisione Unita\' Mobili Carabinieri, con sede a Treviso ,che<BR>sovrintende alle attivita\' che l\' Arma svolge sul territorio<BR>nazionale per il mantenimento dell\' ordine pubblico e all\'<BR>estero attraverso i reparti destinati al mantenimento della pace<BR>nei vari teatri operativi, dal Kosovo all\'Iraq.<BR>&nbsp;&nbsp;&nbsp; Tuttavia, a quanto si e\' appreso da fonti giudiziarie, nella<BR>motivazione del provvedimento di sequestro sono riportati una<BR>serie di episodi relativi a calunnia e diffamazioni sia ai danni<BR>del gen.Franze\' sia nei confronti di alti ufficiali dell\' Arma,<BR>tutti avvenuti negli ultimi anni.<BR>&nbsp;&nbsp; Proprio per calunnia nei confronti di appartenenti all\' Arma,<BR>qualche tempo fa, il presidente dell\' <STRONG><FONT color=#ff0000>Unac</FONT></STRONG>, Antonio Savino, fu<BR>posto agli arresti domiciliari su richiesta dello stesso pm<BR>Rossi. Il provvedimento cautelare fu pero\' annullato dopo pochi<BR>giorni dal Tribunale del Riesame di Bari ma confermato, in sede<BR>di appello, dalla Cassazione. All\' indagato il magistrato ha<BR>fatto recentemente notificare un avviso di conclusione delle<BR>indagini preliminari. (ANSA).<BR></FONT>', 0, '', '', '', 3736, 1, 0, '2026-01-09 21:28:17', '0000-00-00'),
(373, '2004-11-12 00:00:00', 'Notizie Flash', 'FUMO: LE REGOLE CHE ENTRERANNO IN VIGORE IL 10 GENNAIO', '', 'FUMO: LE REGOLE CHE ENTRERANNO IN VIGORE IL 10 GENNAIO', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><FONT size=5><STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\"><FONT face=\"Courier New\">FUMO</FONT></SPAN></STRONG><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\">: LE REGOLE CHE ENTRERANNO IN VIGORE IL 10 GENNAIO <?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></FONT></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p><FONT size=5>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 11 NOV - Ci sono dodici giorni in piu\' per i locali per mettersi in regola con le nuove leggi antifumo che scatteranno il 10 gennaio prossimo, anziche\' il 29 dicembre . <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>Insieme alle regole in vigore dal 1975, le nuove regole che entreranno in vigore tra poco ridisegnano tutti i requisiti da rispettare per difendere i non fumatori, rendendo sempre piu\' difficile la vita dei fumatori e riducendo il piu\' possibile l\'esposizione ai pericoli del <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">fumo</SPAN></STRONG> passivo: <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5>- DIVIETO ASSOLUTO DI <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">FUMO</SPAN></STRONG>: c\'era gia\' in ospedali, scuole, autobus e metropolitane, sale di attesa delle stazioni, sui compartimenti ferroviari riservati ai non fumatori, nei cinema e nei teatri, in musei e biblioteche. Adesso le sigarette vengono bandite anche dagli esercizi aperti al pubblico e dai luoghi di lavoro, con particolare riguardo per alberghi, ristoranti, bar ed esercizi di pubblico ristoro in genere, uffici e altri luoghi di lavoro. I divieti valgono anche per gli spazi pubblici destinati ad attivita\' ricreative e per i circoli privati nei quali lo statuto lo preveda.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5>- ZONE RISERVATE AI FUMATORI: nei bar, ristoranti e alberghi che vorranno accogliere i fumatori, cosi\' come nei luoghi di lavoro, gli spazi riservati a chi non riesce a separarsi dalle sigarette dovranno essere contrassegnati. Dovranno inoltre essere delimitati e separati con pareti dagli spazi in cui non si puo\' fumare. Se vi e\' un solo locale, o se non e\' possibile assicurare una idonea separazione degli ambienti, il divieto di <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">fumo</SPAN></STRONG> e\' assoluto. Lo spazio destinato ai fumatori dovra\' essere inferiore alla meta\' della superficie complessiva aperta al pubblico.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5>- CARTELLI LUMINOSI: le zone riservate ai fumatori dovranno essere indicate da cartelli luminosi e dovranno riportare la scritta \'\'Area per fumatori\'\'. In caso di guasto all\'impianto di ventilazione, un altro cartello luminoso dovra\' accendersi automaticamente indicando l\'eventuale divieto di <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">fumo</SPAN></STRONG>. Nei locali per non fumatori basta esporre il cartello \'\'Vietato fumare\'\', con il riepilogo delle sanzioni per i trasgressori. <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5>- IMPIANTI DI VENTILAZIONE: in tutti gli esercizi pubblici e i luoghi di lavoro dovranno essere installati impianti per la ventilazione e il ricambio dell\'aria. Questi dovranno garantire una quantita\' di aria supplementare minima di 22 litri al secondo per ogni persona che puo\' essere ospitata nel locale, sulla base di un indice di affollamento di 0,7 persone per ogni metro quadrato. I locali per i fumatori devono essere in depressione non inferiore a 5 Pa (Pascal) rispetto alle zone circostanti e l\'aria proveniente da questi dovra\' essere espulsa all\'esterno. Gli impianti dovranno infine essere verificati annualmente ed i relativi certificati dovranno essere sempre disponibili per i controlli. (ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p><FONT size=5>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><FONT size=5><STRONG><SPAN style=\"FONT-WEIGHT: normal; COLOR: black; mso-bidi-font-weight: bold\"><FONT face=\"Courier New\">FUMO</FONT></SPAN></STRONG><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\">: MINISTERO A FIPE; PER DIVIETO BASTA CARTELLO QUALSIASI<o:p></o:p></SPAN></FONT></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p><FONT size=5>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 11 NOV - Il ministero della Salute risponde alle preoccupazione della Fipe, la federazione dei pubblici esercenti. Il decreto in arrivo non e\' indispensabile per l\'entrata in vigore della legge, ha spiegato in una nota, e basta un cartello qualsiasi per indicare il divieto, sul quale vigileranno i carabinieri dei Nas.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Il decreto attuativo del comma 7 dell\'articolo 51 della legge n. 3/2003 - ha spiegato il ministero - e\' appena pervenuto dal Ministero dell\'Economia e, pur non essendo indispensabile per l\'applicazione del divieto di <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">fumo</SPAN></STRONG>, e\' in via di trasmissione alla Conferenza Stato-Regioni per l\'approvazione. Il Provvedimento indica le procedure per l\'accertamento delle infrazioni, i soggetti legittimati a contestare i conseguenti processi verbali e ad irrogare le relative sanzioni. Nelle strutture pubbliche e private soggette al divieto di fumare le infrazioni al divieto saranno accertate oltre che dal personale appositamente incaricato a norma della Direttiva del Presidente del Consiglio dei Ministri del 14 dicembre 1995, dal personale dei Corpi di polizia locale, dalle guardie giurate espressamente adibite a tale servizio e, di propria iniziativa, dagli ufficiali ed agenti delle<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Forze di polizia presenti nei locali e nei luoghi soggetti al divieto di fumare\'\'.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Per quanto riguarda, invece, i cartelli di divieto, non esistendo alcun modello definito dalla norma, \'\'coloro cui spetta per legge, regolamento o disposizioni di autorita\' di assicurare l\'ordine all\'interno di tutti i locali nei quali e\' vietato fumare, devono comunque esporre un cartello di qualsiasi tipo recante le indicazioni richieste dalla legge\'\'. <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Dalla data di entrata in vigore del divieto di <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">fumo</SPAN></STRONG>, infine, i Nas vigileranno sull\'applicazione della legge.(ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p><FONT size=5>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p><FONT size=5>&nbsp;</FONT></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2377, 1, 0, '2026-01-13 13:04:14', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(374, '2004-11-12 00:00:00', 'Notizie Flash', 'CARABINIERI: COCER, SUBITO LEGGE RIFORMA RAPPRESENTANZA', '', 'CARABINIERI: COCER, SUBITO LEGGE RIFORMA RAPPRESENTANZA', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5>CARABINIERI: <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">COCER</SPAN></STRONG>, SUBITO LEGGE RIFORMA RAPPRESENTANZA<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN><o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 11 NOV - Calendarizzare ed approvare rapidamente il disegno di legge di riforma della rappresentanza militare. Lo chiede il <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG> dei carabinieri, riunito oggi in congresso, cui hanno partecipato anche i sottosegretari alla Difesa, Salvatore Cicu ed alla Funzione Pubblica, Learco Saporito.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>I delegati del <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG> si sono dichiarati \'\'ottimisti sull\'anno di proroga ottenuto e richiesto come garanzia per l\' approvazione della legge di riforma della rappresentanza\'\'. Cio\', spiegano, \'\'consentirebbe all\' Italia, anche in questo campo, di entrare a pieno titolo in Europa\'\'. <o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Cocer</SPAN></STRONG> ha esaminato poi il provvedimento di riordino delle carriere ed ha sottolineato \'\'l\' assoluta necessita\' di definire subito la legge delega per stabilire i limiti e le procedure. Il personale attende con impazienza il varo di un provvedimento che si tradurra\' in una maggiore efficienza a favore della collettivita\' degli strumenti per la lotta alla criminalita\' ed al terrorismo\'\'.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>I lavori si sono conclusi con l\' invito al comandante generale \'\'di adoperarsi nei confronti delle autorita\' interessate affinche\' le legittime aspettative dei carabineri e dell\' istituzione possano essere esaudite\'\'. (ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2296, 1, 0, '2026-01-12 14:56:56', '0000-00-00'),
(375, '2004-11-12 00:00:00', 'Notizie Flash', 'FISCO: BERLUSCONI,SE TASSE OLTRE 1/3 TI INGEGNI PER ELUSIONE - RISPARMIO: BENVENUTO, E\' SPARITO IL RUOLO GUARDIA DI FINANZA', '', 'FISCO: BERLUSCONI,SE TASSE OLTRE 1/3 TI INGEGNI PER ELUSIONE - RISPARMIO: BENVENUTO, E\' SPARITO IL RUOLO GUARDIA DI FINANZA', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5>FISCO: BERLUSCONI,SE TASSE OLTRE 1/3 TI INGEGNI PER ELUSIONE<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5>PREMIER VISITA COMANDO GDF E SCHERZA, MA NON SIA RICAMBIATA...<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p><FONT size=5>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 11 NOV - \'\'Vi ringrazio per quel che fate. Agite con grande equilibrio e rispetto dei cittadini, nei confronti di chi si vuole sottrarre ad un obbligo che qualche volta si avverte come eccessivo...\'\'. Con queste parole Silvio Berlusconi ha esordito intervenendo alla cerimonia in occasione dei 230 anni della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'C\'e\' una norma di diritto naturale - ha aggiunto, rivolgendosi ai vertici delle Fiamme Gialle - che dice che se lo Stato ti chiede un terzo di quello che con tanta fatica hai guadagnato sembra una richiesta giusta e glielo dai in cambio di servizi. Se ti chiede di piu\' o molto di piu\', c\'e\' una sopraffazione dello Stato nei tuoi confronti e allora ti ingegni per trovare dei sistemi elusivi o addirittura evasivi che senti in sintonia con il tuo intimo sentimento di moralita\' e che non ti fanno sentire colpevole\'\'.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>Berlusconi ha quindi espresso la sua soddisfazione per il fatto di poter partecipare alla cerimonia alla <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di Finanza</SPAN></STRONG>, alla quale partecipava anche il ministro dell\'Economia Domenico Siniscalco. \'\'Sono contento di essere qui per questa visita speciale... Certo - ha detto con una battuta – il sottoscritto non potrebbe dire altrettanto se fosse la <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> a fargli visita a casa sua\'\'.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>(ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p><FONT size=5>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5>RISPARMIO: BENVENUTO, E\' SPARITO IL RUOLO <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">GUARDIA</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">DI</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">FINANZA</SPAN></STRONG><o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5>C\'E\' DA PREOCCUPARSI PER INTENZIONI DELLA CDL<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p><FONT size=5>&nbsp;</FONT></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 11 NOV - Giorgio Benvenuto, capogruppo dei Ds nella commissione finanze della Camera, sottolinea che \'\'dal nuovo testo-base sulla tutela del risparmio e\' sparito l\'art. 21 del testo Gambini-Conte di maggio, che assegnava alla <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di Finanza</SPAN></STRONG> uno specifico ruolo nell\'assistenza investigativa, alle autorita\' di vigilanza (Banca d\'Italia, Consob, Isvap, Covip, Autorita\' antitrust).<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Se due indizi fanno - afferma Benvenuto - una prova, allora c\'e\' davvero da essere preoccupati sull\'effettiva volonta\' dell\'attuale maggioranza di tutelare i risparmiatori e gli investitori\'\', perche\' \'\'questa maggioranza ha gia\' presentato - ricorda il parlamentare dei Ds - emendamenti alla disciplina del \'market abuse\', che tendono a svilire il ruolo della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di finanza</SPAN></STRONG> anche nella repressione degli abusi di mercato finanziario\'\'.<o:p></o:p></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=5><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Li\' abbiamo gia\' presentato i nostri emendamenti e, come ho fatto presente stamani in commissione, altrettanto faremo - conclude Benvenuto - anche in sede di discussione del disegno di legge di tutela del risparmio\'\'.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>(ANSA).<o:p></o:p></FONT></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2911, 1, 0, '2026-01-14 02:40:34', '0000-00-00'),
(376, '2004-11-15 00:00:00', 'Dalla rappresentanza militare', 'INTERVENTO DEL COCER IN MATERIA DI DISCIPLINA DELLA TUTELA DEL RISPARMIO', '', 'INTERVENTO DEL COCER IN MATERIA DI DISCIPLINA DELLA TUTELA DEL RISPARMIO', '<P>In questi giorni è forte la polemica sorta per la presentazione di alcuni emendamenti&nbsp; all\' Atto Camera con il quale il Parlamento&nbsp; intende disciplinare la tutela del mercato e del risparmio. Questi emendamenti tendono a ridimensionare il ruolo del Corpo della Guardia di Finanza nello specifico settore.</P>\r\n<P>Sull\'argomento, oggetto, peraltro, di molti interventi sul forum del sito, abbiamo già segnalato un\' Ansa con una dichiarazione&nbsp;dell\'Onorevole BENVENUTO, Presidente della Commissione finanze della Camera che di seguito riproponiamo:</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 18pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=2><FONT face=\"Verdana, Arial, Helvetica, sans-serif\">\"\"Giorgio Benvenuto, capogruppo dei Ds nella commissione finanze della Camera, sottolinea che \'\'dal nuovo testo-base sulla tutela del risparmio e\' sparito l\'art. 21 del testo Gambini-Conte di maggio, che assegnava alla <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di Finanza</SPAN></STRONG> uno specifico ruolo nell\'assistenza investigativa, alle autorita\' di vigilanza (Banca d\'Italia, Consob, Isvap, Covip, Autorita\' antitrust).<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 18pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=2><FONT face=\"Verdana, Arial, Helvetica, sans-serif\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Se due indizi fanno - afferma Benvenuto - una prova, allora c\'e\' davvero da essere preoccupati sull\'effettiva volonta\' dell\'attuale maggioranza di tutelare i risparmiatori e gli investitori\'\', perche\' \'\'questa maggioranza ha gia\' presentato - ricorda il parlamentare dei Ds - emendamenti alla disciplina del \'market abuse\', che tendono a svilire il ruolo della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di finanza</SPAN></STRONG> anche nella repressione degli abusi di mercato finanziario\'\'.\"\"</FONT></FONT></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 18pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=2><FONT face=\"Verdana, Arial, Helvetica, sans-serif\"></FONT></FONT></SPAN>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 18pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><FONT size=2><FONT face=\"Verdana, Arial, Helvetica, sans-serif\">Sul tema dobbiamo altresì registrare il deciso intervento del COCER della Guardia di Finanza, che, con una delibera dell\'11 novembre,( che pubblichiamo in allegato )&nbsp;ha preso posizione&nbsp; chiedendo, tra l\'altro, un incontro con il Ministro, nonchè una presa di posizione dei sindacati delle varie Forze di Polizia.</FONT></FONT></SPAN></P>\r\n<P>&nbsp;</P>', 108, '', '', '', 2279, 1, 0, '2026-01-09 20:40:50', '0000-00-00'),
(377, '2004-11-16 00:00:00', 'Notizie Flash', 'SICUREZZA: SILP-CGIL, BLOCCO ASSUNZIONI SCELTA GRAVE\r\n\r\n', '', 'SICUREZZA: SILP-CGIL, BLOCCO ASSUNZIONI SCELTA GRAVE\r\n\r\n', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SICUREZZA: SILP-CGIL, BLOCCO ASSUNZIONI SCELTA GRAVE<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 15 NOV - Il blocco delle assunzioni per quanto riguarda le forze di polizia \'\'e una scelta grave per la sicurezza\'\'. A sostenerlo e\' il segretario generale Silp-Cgil, Claudio Giardullo.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>\'\'Non sostituire gli operatori della Polizia di Stato, dell\'Arma dei Carabinieri e della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG> che andranno in pensione nei prossimi tre anni - dice - e\' una scelta grave per la sicurezza del paese\'\'. <SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>\'\'Di fronte alla crescente minaccia terroristica internazionale, ed alle emergenze criminalita\', come quella di Napoli, ridurre gli addetti alle forze dell\'ordine per rastrellare risorse ci sembra un\'operazione azzardata, in contrasto con gli stessi programmi annunciati dal Governo, e frutto di una concezione ragionieristica nella gestione delle istituzioni pubbliche\'\'. Per Giardullo \'\'il sistema di sicurezza del nostro paese non puo\' sopportare altri tagli alle risorse finanziarie, umane e tecniche impegnate su questo versante. Per questo nei prossimi giorni i poliziotti del Silp-Cgil manifesteranno per sensibilizzare l\'opinione pubblica e le forze politiche sui rischi di una politica di progressiva riduzione delle risorse a favore delle forze di polizia\'\'. <SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;</SPAN>(ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2221, 1, 0, '2026-01-13 06:17:22', '0000-00-00'),
(378, '2004-11-16 00:00:00', 'Notizie Flash', 'CRIMINALITA\': SPARANO CONTRO FINANZIERE A NAPOLI\r\n\r\n', '\r\n\r\n', 'CRIMINALITA\': SPARANO CONTRO FINANZIERE A NAPOLI\r\n\r\n', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">CRIMINALITA\': SPARANO CONTRO <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">FINANZIERE</SPAN></STRONG> A NAPOLI<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">NON E\' RIMASTO FERITO, ED HA RISPOSTO AL FUOCO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>(ANSA) - NAPOLI, 14 NOV - Una sparatoria e\' avvenuta poco fa alla periferia orientale di Napoli.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Secondo le prime notizie un <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanziere</SPAN></STRONG> e\' stato fatto oggetto di alcuni colpi di arma da fuoco in via Gianturco.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanziere</SPAN></STRONG>, che non e\' rimasto ferito, ha risposto al fuoco.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">Secondo una prima ricostruzione della dinamica della sparatoria il <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanziere</SPAN></STRONG>, che era fuori servizio ed in abiti civili, stava percorrendo via Gianturco, una strada della zona orientale di Napoli a bordo di una \'\'Punto\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Poco prima delle 21 e\' stato affiancato da un fuoristrada di colore nero con due uomini a bordo. Il passeggero gli ha esploso contro alcuni colpi di pistola, ma senza raggiungerlo.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanziere</SPAN></STRONG> ha sparato a propria volta, colpendo il fuoristrada, che si e\' allontanato.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Non si sa se i suoi occupanti siano stati colpiti. Finora nessun ferito da arma da fuoco si e\' presentato negli ospedali cittadini.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Secondo una prima ipotesi degli investigatori il <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanziere </SPAN></STRONG>potrebbe essere stato scambiato per un\' altra persona, forse un nemico, da appartenenti alla criminalita\' organizzata.<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN></SPAN><SPAN lang=DE style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-ansi-language: DE\"><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">E\' la seconda volta in meno di due settimane che appartenenti alle forze dell\'ordine vengono fatti segno di colpi di arma da fuoco a Napoli. Il 2 novembre quattro carabinieri che viaggiavano in abiti civili su un auto nel quartiere periferico di Scampia furono colpiti da sicari del clan camorristico Di Lauro. Tre militari rimasero feriti, uno dei quali gravemente. Uno dei feritori, un giovane di 19 anni ha confessato di aver scambiato i carabinieri per appartenenti al gruppo degli \'\'scissionisti\'\' del clan di Lauro. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">CRIMINALITA\': SPARANO CONTRO FINANZIERE, POSSIBILE AGGUATO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 14 NOV - I colpi d\'arma da fuoco, probabilmente di pistola sparati questa sera in via Granturco contro un maresciallo dei baschi verdi del gruppo della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">guardia di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">finanza</SPAN></STRONG> di Napoli, potrebbero essere stati un avvertimento contro l\'investigatore ed essere quindi collegati ad una delicata indagine in corso.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Lo si apprende da fonti investigative che, in serata hanno chiarito le modalita\' dell\'agguato al maresciallo della Gdf. L\'investigatore, si e\' appreso, stava tornando in caserma, da solo con la sua auto, ed e\' stato prima affiancato e poi distratto dalla guida da un fuoristrada scuro che lo ha accecato con gli abbaglianti. Contemporaneamente dal fuoristrada una delle due persone che si trovavano all\'interno, che secondo la ricostruzione era seduta nel sedile posteriore, ha cominciato a fare fuoco contro il maresciallo. La prontezza di riflessi e l\'immediata risposta sembra abbia messo in fuga gli occupanti della jeep che pero\' potrebbe essere stata colpita da uno dei proiettili sparati dal maresciallo della finanza. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SPARI A NAPOLI CONTRO FINANZIERE, INDAGA SU CONTRABBANDO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SI STUDIANO CONNESSIONI CON SUA ATTIVITA\' MA ANCHE ALTRE PISTE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - NAPOLI, 15 NOV - Si indaga a trecentosessanta gradi sulla sparatoria nella quale e\' rimasto coinvolto ieri sera in via Gianturco a Napoli, un militare dei baschi verdi del pronto impiego in servizio al comando provinciale della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di finanza</SPAN></STRONG> di Napoli.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Sono al vaglio degli investigatori eventuali connessioni con l\'attivita\' svolta dal maresciallo che, come gli altri 200 baschi verdi di stanza a Napoli, si occupa prevalentemente di traffici illeciti, contrabbando di merci dall\'estero e contraffazione di prodotti; non si escludono altre piste come il tentativo di rapina o l\'errore di persona. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La ricostruzione della dinamica dell\'agguato ha evidenziato che i sicari hanno sparato colpi d\'arma da fuoco proprio all\'indirizzo del finanziere: l\'investigatore stava tornando in caserma da solo con la sua auto ed e\' stato prima affiancato e poi distratto dalla guida da un fuoristrada scuro che lo ha accecato con gli abbaglianti. Una delle due persone che si trovavano all\'interno del fuoristrada - che secondo la ricostruzione era seduta sul sedile posteriore - ha cominciato a fare fuoco contro il maresciallo senza ferirlo. La prontezza di riflessi e l\'immediata risposta ha messo in fuga gli occupanti della jeep che pero\' potrebbe essere stata colpita da uno dei proiettili sparati dal maresciallo della finanza.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">Non appena rientrato in caserma - si sottolinea in una nota del Comando provinciale di Napoli della Finanza - il militare ha messo al corrente dell\'accaduto gli ufficiali del Comando che hanno fatto scattare immediatamente le ricerche dei malviventi, attivando tutte le pattuglie del dispositivo di controllo del Corpo e delle altre<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">forze di polizia.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Le indagini, scattate in raccordo con la procura della Repubblica, hanno portato all\'esecuzione di una serie di interventi in citta\' e nell\'hinterland. La Finanza ha precisato che le investigazioni \'\'procedono in ogni direzione concentrando la massima attenzione sull\'attivita\' di contrasto ai traffici illeciti portati avanti dalla Compagnia Pronto Impiego nell\'ultimo periodo, in modo da far luce su possibili connessioni dell\'attacco intimidatorio con ritorsioni violente alle pressioni esercitate contro le organizzazioni criminali\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\">SPARI A FINANZIERE: COMANDO,CONTROLLI A NAPOLI E HINTERLAND<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 15 NOV - Una serie di controlli a Napoli e nell\'hinterland sono stati eseguiti oggi dalla <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di Finanza</SPAN></STRONG> dopo che ieri sera alcuni malviventi a bordo di un\'auto hanno sparato ad un finanziere, senza pero\' riuscire a colpirlo.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Lo rende noto il comando generale della <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>, ricostruendo l\'episodio avvenuto a Gianturco sostenendo le indagini proseguono e che \'\'non puo\' escludersi il fatto che l\'episodio possa rappresentare una violenta ritorsione in risposta alle \'pressioni\' esercitate dalla <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza </SPAN></STRONG>sulle organizzazioni criminali\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Nella serata di ieri, afferma il comando, \'\'un militare appartenente alla <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal; mso-bidi-font-weight: bold\">Finanza</SPAN></STRONG>, mentre stava rientrando in caserma alla guida della propria autovettura, e\' stato affiancato da un fuoristrada dal quale sono stati esplosi alcuni colpi d\'arma da fuoco che, fortunatamente, non hanno raggiunto il bersaglio\'\'. Il militare ha risposto all\'aggressione con la pistola d\'ordinanza sparando alcuni colpi verso il fuoristrada ma non e\' riuscito a bloccarlo. Per risalire agli autori dell\'aggressione, prosegue il comando \'\'sono state attivate tutte le pattuglie del dispositivo di controllo delle Fiamme Gialle e delle altre forze di Polizia e contemporaneamente, sono state avviate le prime indagini che, in raccordo con la Procura della Repubblica di Napoli, hanno portato all\'esecuzione di una serie di interventi in citta\' e nell\'hinterland napoletano\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 3455, 1, 0, '2026-01-09 20:00:15', '0000-00-00'),
(379, '2004-11-19 00:00:00', 'Dai media', 'MONDO, “SE SILVIO SPEGNE LE FIAMME GIALLE , PRIME IPOTESI DI RIFORMA DELLA GUARDIA DI FINANZA”\r\n', '', 'IL MONDO, VENERDI’ 19 NOVEMBRE 2004, PAGINA 13, PRIMO PIANO\r\n “SE SILVIO SPEGNE LE FIAMME GIALLE\"\r\n\r\n\r\n', '<H1 style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-SIZE: 14pt; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: IT; mso-fareast-language: IT; mso-bidi-language: AR-SA\"><FONT face=\"Times New Roman\">\r\n<H1 style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 14pt; mso-bidi-font-weight: bold\"><EM>Prime ipotesi di riforma della Guardia di finanza. Una super agenzia sorveglierà il fisco. Ai finanzieri mare e immigrati.</EM></SPAN></H1>\r\n<H1 style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 14pt; mso-bidi-font-weight: bold\">\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><SPAN style=\"FONT-SIZE: 14pt\"></SPAN>&nbsp;</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><SPAN style=\"FONT-SIZE: 14pt\">(di Pietro Romano)<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><SPAN style=\"FONT-SIZE: 14pt\"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><SPAN style=\"FONT-SIZE: 14pt\">Silvio Berlusconi ha deciso di tirarlo fuori dal cassetto solo a riforma fiscale avviata. Ma, secondo quanto risulta al <EM>Mondo</EM>, lo ritiene uno dei principali tasselli della sua rivoluzione tributaria. Si tratta del progetto di super-Agenzia delle entrate, modellata sull’Internal revenue service, il potentissimo Fisco federale americano. Per creare questo organismo il premier sarebbe deciso a riformare <?xml:namespace prefix = st1 ns = \"urn:schemas-microsoft-com:office:smarttags\" /><st1:PersonName ProductID=\"la Guardia\" w:st=\"on\">la Guardia</st1:PersonName> di finanza, con un piano che avrebbe avuto il via libera preventivo anche da esponenti dell’opposizione. La super-Agenzia dovrebbe partire dal 2006 o dal 2007 e non sarebbe un caso che la programmazione delle attività della Gdf si arresti appunto a fine <st1:metricconverter ProductID=\"2006. In\" w:st=\"on\">2006. In</st1:metricconverter> sostanza, le Fiamme gialle perderebbero le competenze tributarie, destinate a confluire nella super-Agenzia, e le attività nautiche. La riforma della Gdf prevede infatti anche la nascita di un Servizio guardiacoste modellato sull’esperienza americana. Incaricato della lotta al contrabbando, all’immigrazione clandestina e soprattutto al terrorismo, questo servizio coprirebbe le spalle nelle acque territoriali italiane alla Marina militare. Alla quale buona parte dei 25 Paesi rivieraschi, su impulso degli Usa, ha affidato, sia pure in sordina, il controllo del traffico marittimo nel Mediterraneo. Alla Guardia di finanza, che potrebbe essere smilitarizzata, rimarrebbero il controllo delle frontiere terrestri e la lotta ai crimini economici, in particolare le falsificazioni di merci, in più stretto collegamento con le Dogane.<o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><SPAN style=\"FONT-SIZE: 14pt\"><EM>Pietro Romano<o:p></o:p></EM></SPAN></P></SPAN></H1>\r\n<H1 style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 14pt; mso-bidi-font-weight: bold\"></SPAN>&nbsp;</H1>\r\n<H1 style=\"MARGIN: 0cm 0cm 0pt\"><SPAN><o:p></o:p></SPAN>&nbsp;</H1></FONT></SPAN></H1>', 0, '', '', '', 4606, 1, 0, '2026-01-12 19:17:25', '0000-00-00'),
(382, '2004-11-22 00:00:00', 'Dai media', 'IL MESSAGGERO: \"GOLA, ADDIO DOPO 15 ANNI\"', '', 'IL MESSAGGERO: \"GOLA, ADDIO DOPO 15 ANNI\"', '<P><FONT size=3>LUNEDI\' 22 NOVEMBRE 2004, PAGINA 38</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\"><FONT size=3><SPAN style=\"mso-bidi-font-weight: bold\">ROMA - Quindici anni in sella, un’eredità scomoda perché Gianni Gola, ex martellista mantovano, generale della Guardia di finanza, grande capo dello sport mondiale con le stellette senza dimenticare il suo ruolo di comando nelle Fiamme Gialle, è arrivato alla Fidal dopo l’epoca di Primo Nebiolo. Per chiunque fare meglio del dirigente torinese sarebbe stato se non impossibile complicato assai. Gola ha preso in mano la Fidal nel 1989, all’indomani delle dimissioni di Nebiolo dopo lo scandalo Evangelisti ai Mondiali di Roma ’87. Gola ha operato non senza polemiche nel suo mondo pur lavorando moltissimo per l’atletica. Di certo il generale, al quale molto probabilmente verrà offerto il ruolo di presidente onorario della Fidal, ha incontrato sul suo cammino molti ostacoli. La scuola non ha più risposto agli appelli, la vocazione sportiva si è dissolta, il volontariato ha attraversato un periodo di crisi, il Coni ha distribuito molti meno denari che in passato e, quindi, l’attività, soprattutto quella giovanile, è stata ridimensionata. Aggiungiamo poi che grandi atleti non sono arrivati, niente più uomini o donne con il potenziale atletico e carismatico di un Mennea o di una Simeoni. Gola ha sempre combattuto in prima fila, qualche volta con durezza come nei confronti del doping, inflessibile nel mantenere la squalifica a quattro anni anche quando la Federatletica mondiale era scesa a due. Le colpe di Gola? Una senz’altro: non essere mai riuscito a costruirsi, nell’ambito del consiglio federale, una squadra di alleati. Non senza tristezza un mese fa ha deciso di non ricandidarsi lasciando la strada libera per Franco Arese. <BR>C. S.</SPAN> <?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></P>\r\n<P><FONT size=3></FONT>&nbsp;</P>', 0, '', '', '', 3196, 1, 0, '2026-01-11 05:00:48', '0000-00-00'),
(381, '2004-11-22 00:00:00', 'Dai media', 'LA GAZZETTA DEL MEZZOGIORNO: \"I CONTROLLI FISCALI IN CASA NON SONO SEMPRE VALIDI\"', '', 'LA GAZZETTA DEL MEZZOGIORNO: \"I CONTROLLI FISCALI IN CASA NON SONO SEMPRE VALIDI\"', '<P><FONT face=\"times new roman, times, serif\" size=3>LA GAZZETTA DEL MEZZOGIORNO: \"I CONTROLLI FISCALI IN CASA NON SONO SEMPRE VALIDI\", LUNEDI\' 22.11.2004</FONT></P>\r\n<P><EM><FONT face=\"times new roman, times, serif\" size=3>La Cassazione ha fatto il punto sulle ipotesi di lecità.</FONT></EM></P>\r\n<P><SPAN style=\"FONT-SIZE: 7pt; COLOR: black; FONT-FAMILY: Verdana; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: IT; mso-fareast-language: IT; mso-bidi-language: AR-SA; mso-bidi-font-family: \'Times New Roman\'\"><FONT face=\"times new roman, times, serif\" size=3>Sono inutilizzabili, in quanto invalide, le prove acquisite dal Fisco presso l\'abitazione del contribuente se l\'autorizzazione del Procuratore della Repubblica è stata rilasciata «in assenza di gravi indizi di violazione di norme tributarie». La Cassazione con recentissima e corretta sentenza ha fatto il «punto» sulle diverse ipotesi di accesso e ispezione regolate dall\'art.52 dpr 633/72 escludendo che i controlli fiscali siano automaticamente leciti nel caso in cui i locali del contribuente (azienda ed abitazione) siano promiscui. Nel caso sottoposto alla Corte il ricorrente lamentava, tra le altre, la nullità delle riprese fiscali (riguardanti gli accreditamenti ed addebitamenti bancari considerati ingiustificati dalla Guardia di finanza) per illegittimità dell\'acquisizione documentale. Infatti, sia la richiesta della Guardia di finanza alla Procura della Repubblica che il provvedimento del pubblico ministero che autorizzava l\'accesso domiciliare risultavano immotivati in relazione ai gravi indizi di violazione della norme tributarie di cui all\'art.52 comma 2 del dpr 633/72, richiamato dall\'art.33 del dpr 600/73. Il contribuente contestava, anche, la sentenza del giudice di appello in quanto era stata ritenuta coincidente la residenza con l\'esercizio dell\'impresa. Nella realtà tale coincidenza era insussistente in quanto l\'ufficio e l\'abitazione, pur avendo lo stesso indirizzo, erano nettamente separati e corrispondenti a due distinte unità immobiliari. Tant\'è che «nello stesso giorno ed alla stessa ora due distinte pattuglie di militari (rispettivamente unità 3 e 5) hanno effettuato due distinti accessi all\'ufficio ed all\'abitazione... verbalizzando la documentazione rinvenuta in ciascuna unità immobiliare». La Suprema Corte ha ritenuto effettivamente sussistente la eccepita illegittimità del provvedimento di autorizzazione all\'accesso domiciliare. Con la conseguenza della inutilizzabilità, a sostegno dell\'accertamento tributario, delle prove reperite nel corso della perquisizione illegale. Atteso che, come evidenziato dalle sezioni unite della Cassazione, «detta inutilizzabilità non abbisogna di una espressa disposizione sanzionatoria, derivando dalla regola generale secondo cui l\'assenza del presupposto di un procedimento amministrativo, infirma tutti gli atti nei quali si articola». E tale illegittimità non può essere sanata nemmeno dalla circostanza che dal processo verbale non risultava esservi stata opposizione all\'accesso da parte del contribuente, e comunque non è rilevante e/o veridico che tale accesso sia avvenuto con il consenso dello stesso. Ciò «perché la mancata opposizione del contribuente non equivale a consenso all\'accesso né rende legittimo un accesso operato al di fuori delle previsioni legislative e, comunque, perché l\'eventuale consenso o dissenso dello stesso contribuente all\'accesso, legittimo od illegittimo che sia, è del tutto privo di rilievo giuridico non essendo richiesto e/o preso in considerazione da alcuna norma di legge». Ma anche nel merito la censura è stata accolta dalla Corte perché fondata, in aderenza ai principi costituzionali (art.14) e alla corretta interpretazione dell\'art.52 dpr 633/72. La Corte Suprema ha innanzitutto precisato che «l\'autorizzazione del procuratore della Repubblica è stata prevista dal legislatore come opportuno filtro preventivo dell\'azione degli inquirenti fiscali in entrambe le fattispecie perché queste coinvolgono sempre quel domicilio del contribuente che la vigente Costituzione dichiara comunque inviolabile...». Comunque, dalle disposizioni succitate si ricava che per l\'accesso ai locali adibiti «anche ad abitazione» del contribuente da parte del fisco in effetti è sufficiente l\'autorizzazione del procuratore della Repubblica. Al contrario, per l\'accesso in locali diversi da quelli indicati nel primo comma (quindi per l\'accesso in locali destinati esclusivamente ad abitazione) l\'autorizzazione «motivata» del procuratore della Repubblica può essere richiesta e rilasciata soltanto «in presenza di gravi ed espressi indizi di violazione delle norme fiscali». Ed allo scopo esclusivo di reperire libri, registri, documenti, scritture ed altre prove delle violazioni.<BR style=\"mso-special-character: line-break\"></FONT><BR style=\"mso-special-character: line-break\"></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2687, 1, 0, '2026-01-14 12:13:03', '0000-00-00'),
(383, '2004-11-25 00:00:00', 'Nostro Servizio', '“SE SILVIO SPEGNE LE FIAMME GIALLE”, PUBBLICHIAMO LA REPLICA COMANDO GENERALE AL SETTIMANALE “IL MONDO”', '', '“SE SILVIO SPEGNE LE FIAMME GIALLE”, PUBBLICHIAMO LA REPLICA COMANDO GENERALE AL SETTIMANALE “IL MONDO”', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Il Comando generale della Guardia di finanza ha inviato al direttore de “Il Mondo” la lettera che di seguito riproduciamo con la quale viene smentita l’esistenza di progetti di riforma del Corpo.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\"><FONT size=3>&nbsp;<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 6pt 0cm 0pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>&nbsp;<o:p></o:p></FONT></SPAN></I></P>\r\n<H1 style=\"MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: normal\"><EM><FONT face=Arial size=3>LETTERA DEL PORTAVOCE DELLA GUARDIA DI FINANZA</FONT></EM></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-FAMILY: Arial\"><EM><FONT size=3>AL DIRETTORE DEL SETTIMANALE “IL MONDO”<o:p></o:p></FONT></EM></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>&nbsp;<o:p></o:p></FONT></SPAN></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 6pt 0cm 0pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>Le scrivo a nome del Comandante Generale della Guardia di Finanza, Generale di Corpo d’Armata Roberto Speciale.<o:p></o:p></FONT></SPAN></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 6pt 0cm 0pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>In relazione all’articolo apparso sul settimanale “Il Mondo” nr. 47, del 26 novembre 2004, pag. 13, dal titolo “PRIME IPOTESI DI RIFORMA DELLA GUARDIA DI FINANZA – SE SILVIO SPEGNE LE FIAMME GIALLE”, si smentisce l’avvio di qualsiasi piano diretto ad una revisione organizzativa<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>della Guardia di Finanza.<o:p></o:p></FONT></SPAN></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 6pt 0cm 0pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Il Presidente del Consiglio dei Ministri, On. Silvio Berlusconi, appena lo scorso 11 novembre ha fatto visita al Comando Generale ed ha pubblicamente elogiato il Corpo per l’efficacia e l’efficienza dell’azione svolta a tutela delle libertà economiche del Paese.<o:p></o:p></FONT></SPAN></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 6pt 0cm 0pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Del resto questo giudizio di valore è confermato dalla cronaca, che registra quotidianamente l’impegno della Guardia di Finanza in tutti i settori di competenza istituzionale e dal prestigio di cui il Corpo gode presso le forze di polizia in tutto il mondo.<o:p></o:p></FONT></SPAN></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 6pt 0cm 0pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Infine, si precisa che il processo di pianificazione, programmazione e controllo di gestione della Guardia di Finanza prevede l’elaborazione di documenti programmatici riferiti massimo al triennio.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>L’attuale piano riguarda il periodo 2004 – 2006;<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>il prossimo sarà focalizzato sul 2005 – 2007 e così per gli anni a venire. Dunque, è normale che “la programmazione delle attività della GdF si arresti appunto a fine 2006”.<o:p></o:p></FONT></SPAN></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 6pt 0cm 0pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Nel ringraziarLa per la cortese attenzione Le chiedo di pubblicare questa lettera nel prossimo numero de “Il Mondo”. <o:p></o:p></FONT></SPAN></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 31.9pt 0pt 45pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>&nbsp;<o:p></o:p></FONT></SPAN></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 31.9pt 0pt 45pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"FONT-FAMILY: Arial\"><FONT size=3>&nbsp;<o:p></o:p></FONT></SPAN></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 31.9pt 0pt 45pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"FONT-FAMILY: Arial\"><SPAN style=\"mso-tab-count: 1\"></SPAN><SPAN style=\"mso-tab-count: 1\"></SPAN><SPAN style=\"mso-tab-count: 1\"></SPAN><SPAN style=\"mso-tab-count: 1\"></SPAN><SPAN style=\"mso-tab-count: 1\"></SPAN><SPAN style=\"mso-tab-count: 1\"></SPAN><SPAN style=\"mso-tab-count: 1\"></SPAN><FONT size=3>Colonnello Carlo Ricozzi<o:p></o:p></FONT></SPAN></I></P>', 0, '', '', '', 3513, 1, 0, '2026-01-14 04:36:23', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(384, '2004-11-26 00:00:00', 'Nostro Servizio', 'ARTICOLO DE IL MONDO, ARRIVA ANCHE LA SMENTITA DEL GOVERNO: \"NESSUN PROGETTO DI SUPER AGENZIA DELLE ENTRATE\"', '', 'ARTICOLO DE IL MONDO, ARRIVA ANCHE LA SMENTITA DEL GOVERNO: \"NESSUN PROGETTO DI SUPER AGENZIA DELLE ENTRATE\"', '<P>Sul sito interenet ufficiale della Presidenza del Consiglio dei Ministri, <A href=\"http://www.governo.it\">www.governo.it</A>, è comparso da oggi&nbsp;lo stringato comunicato stampa che appresso pubblichiamo.</P>\r\n<P>&nbsp;</P>\r\n<P align=center><EM>COMUNICATO STAMPA DELLA PRESIDENZA DEL CONSIGLIO DEI MINISTRI</EM></P>\r\n<P align=center><EM>\"NESSUN PROGETTO DI SUPER AGENZIA DELLE ENTRATE\"</EM></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"COLOR: #003366\"><FONT face=\"Times New Roman\" size=4><EM>Il Presidente del Consiglio Silvio Berlusconi non ha mai, assolutamente mai, neanche pensato ad un progetto di super Agenzia dell\'Entrate. La voce pubblicata sull\'ultimo numero de \"Il Mondo\" e circolata in ambienti dell\'informazione è non solo falsa ma destituita di ogni e qualsiasi fondamento.</EM></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"COLOR: #003366\"><EM><FONT face=\"Times New Roman\" size=4></FONT></EM></SPAN>&nbsp;</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><SPAN style=\"COLOR: #003366\"><EM><FONT face=\"Times New Roman\" size=4>Venerdì 26 novembre 2004</FONT></EM></SPAN></P>', 0, '', '', '', 3085, 1, 0, '2026-01-13 14:38:03', '0000-00-00'),
(385, '2004-11-26 00:00:00', 'Notizie Flash', 'GUARDIA FINANZA: MFD, ENTUSIASMO PER RIFORMA DEL CORPO\r\n', '', 'GUARDIA FINANZA: MFD, ENTUSIASMO PER RIFORMA DEL CORPO\r\n', '<P>&nbsp;</P>\r\n<H1 style=\"MARGIN: 0cm 0cm 0pt\" align=center><FONT face=\"Times New Roman\" size=3>GUARDIA FINANZA: MFD, ENTUSIASMO PER RIFORMA DEL CORPO</FONT></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">(ANSA) - TRIESTE, 20 NOV - Il Movimento dei finanzieri democratici (Mfd) accoglie \'\'con entusiasmo\'\' l\'ipotesi di riforma della Guardia di Finanza che il presidente del Consiglio, Silvio Berlusconi, intenderebbe attuare, secondo quanto pubblicato sul settimanale \'\'Il Mondo\'\' di questa settimana. Il Corpo, cosi\' come oggi e\' strutturato - afferma in una nota diffusa a Trieste il presidente nazionale dell\' Mfd, Lorenzo Lorusso - \'\'appare infatti ampiamente sotto utilizzato\'\' e \'\'rappresenta una vera e propria anomalia tra le polizie finanziarie europee, l\'unica ad essere organizzata militarmente e ad occuparsi delle piu\' svariate mansioni\'\'. Lorusso si augura che il progetto di Berlusconi venga accolto con il consenso trasversale anche di quella componente della Sinistra (Margherita e Ds) \'\'che sempre ha rifiutato di prendere in esame l\' ipotesi di riforma del Corpo\'\'. (ANSA). <o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"COLOR: black; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 4141, 1, 0, '2026-01-14 11:35:42', '0000-00-00'),
(389, '2004-11-26 00:00:00', 'Notizie Flash', 'SENATO: SI\' A REVISIONE LEGGI E CODICI MILITARI\r\n', '', 'SENATO: SI\' A REVISIONE LEGGI E CODICI MILITARI\r\n', '<P>&nbsp;</P>\r\n<H1 style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>SENATO: SI\' A REVISIONE LEGGI E CODICI MILITARI</FONT></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">(ANSA) - ROMA, 18 NOV - L\'assemblea di Palazzo Madama ha approvato, in prima lettura, l\'ampio ddl che delega al governo entro 12 mesi dalla definitiva approvazione della legge per la revisione delle leggi penali militari di pace e di guerra nonche\' per il complessivo adeguamento dell\' ordinamento giudiziario militare.L\'assemblea del Senato ha approvato in prima lettura il ddl delega per la revisione delle leggi penali militari di pace e di guerra, nonche\' per l\'adeguamento dell\'ordinamento giudiziario militare, con 132 si\' e 45 no. Il ddl di delega rivede, in sostanza, tutta la materia fissando i criteri dei successivi decreti legislativi per modificare i due codici penali militari e lo stesso ordinamento giudiziario militare. L\'obiettivo del provvedimento e\' quello di rendere l\'intera legge militare \'\'armonica\'\' con lo spirito dell\'ordinamento repubblicano, i valori prevalenti nella cultura giuridica italiana in questo inizio di secolo e le nuove realta\' di fatto sviluppatesi nella politica internazionale, che oggi propone un vastissimo spettro di situazioni intermedie rispetto alla classica contrapposizione tra periodi di pace e di guerra. In particolare il provvedimento trova radice nel fatto che per la prima volta dal 1945, l\'Italia ha applicato il codice militare di guerra durante la missione militare di Enduring Freedom con un decreto del 1 dicembre 2001, scelta piu\' volte confermata da allora e prevista anche per la missione Antica Babilonia. In quel contesto si decise di applicare il codice militare di guerra, opportunamente emendato, ma anche di intervenire con una piu\'organica e complessiva riforma della legge penale militare. Il testo della riforma e\' infatti l\'esito dei lavori di una Commissione insediata dai dicasteri della Giustizia e della Difesa oltre al contributo di altri 5 ddl di iniziativa parlamentare. La legge penale di guerra, quindi, tornera\' ad essere il regime giuridico normale per i contingenti italiani inviati all\'estero e trovera\' applicazione ogni qualvolta le forze armate italiane si trovino ad intervenire in situazione di aperto conflitto e comunque, in caso di attacco armato allo stato italiano. Per quanto riguarda il codice penale di guerra, infatti, le innovazioni piu\' importanti riguardano la volonta\' di renderlo applicabile alle nuove situazioni di conflitto armato, seguendo gli indirizzi consolidatesi negli ultimi due anni con la partecipazione italiana alla campagna contro il terrorismo internazionale e alla stabilizzazione della situazione in Iraq. (ANSA). <o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></B></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><FONT face=\"Times New Roman\" size=3>CODICE GUERRA PER MILITARI ESTERO,DA SENATO OK A DELEGA/ANSA IL NONNISMO DIVENTA REATO, SI RISCHIANO FINO A CINQUE ANNI</FONT></STRONG></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">(ANSA) - ROMA, 18 NOV - Applicazione del Codice penale militare di guerra per i soldati italiani che operano, a diverso titolo, all\' estero; applicabilita\' nel processo penale militare delle norme del Codice di procedura penale, salvo qualche specificita\' relativa direttamente alla vita con le stellette, e previsione del reato di \'nonnismo\' tra quelli specificatamente militari, prevedendo una pena dai sei mesi ai cinque anni di reclusione militare. Non solo: si ampliano le \'maglie\' di intervento per le querele di parte quando si e\' sotto le armi, permettendo quindi concretamente di non far rientrare nel \'sommerso\' gli episodi di angheria e sopraffazione durante la vita militare, ormai professionale dopo la fine della naja. Sono queste solo alcune delle importanti novita\' contenute nel ddl di delega al governo per la riforma dei Codici militari e dell\' ordinamento giudiziario militare, che trova la sua origine nel fatto che dal 2001, cioe\' dalla missione Enduring Freedom, i nostri militari all\' estero operano applicando il Codice militare di guerra, rivisto all\' epoca per l\'occasione, ma in una situazione di incertezza normativa e comunque di razionalizzazione annunciata. E\' stata infatti creata dopo il 2001 una commissione per rivedere la materia dai due dicasteri interessati, Giustizia e Difesa, e il testo approvato oggi in prima lettura dall\' aula del Senato e\' il frutto diretto di quella commissione, oltre che di cinque progetti di iniziativa parlamentare. La legge delega dovra\' mutarsi in decreti legislativi entro un anno dalla definitiva approvazione. Il testo approvato rivede di fatto quello originario del 1941, soprattutto nelle parti che fanno diretto riferimento all\' adeguamento al Codice di procedura penale ordinario e alla parte di novita\' delle missioni all\' estero che rientrano sotto la definizione \'Operations other than war\', operazioni diverse dalla guerra. Al suo interno si trovano tutte le tipologie di interventi militari italiani autorizzati dal Parlamento dal 1992 ad oggi: missioni militari strettamente umanitarie, interventi di mantenimento, consolidamento e costruzione della pace, nonche\' il cosiddetto \'peace enforcement\', che si attua quando la comunita\' internazionale raggiunge un consenso circa le necessita\' di imporre, con le armi, il ripristino della pace violata, come accadde nel \'91 nei confronti dell\' Iraq che aveva invaso il Kuwait. Il provvedimento e\' molto ampio e contiene sei articoli, piu\' altri tre aggiuntivi. Sono fissati i principi e i criteri direttivi generali cui il governo si dovra\' attenere nell\' esercizio dei poteri conferitigli dal Parlamento, nonche\' quelli che ispireranno le modificazioni del Codice militare di pace e dell\' ordinamento giudiziario militare. Per la legge penale militare di pace, in particolare si prevede di ridurre al minimo le deroghe apportate al regime penale ordinario. Quelle previste fanno diretto riferimento alla \'\'logica militare\'\'. Tra l\'altro, si applichera\' il Codice militare penale di pace anche ai militari stranieri, qualora sia in atto una cooperazione internazionale e cio\' sia previsto dalle convenzioni che regolano tale cooperazione. Per il Codice penale militare di guerra l\'innovazione piu\' importante riguarda la volonta\' di renderlo applicabile alle nuove situazioni di conflitto armato, seguendo gli indirizzi consolidati negli ultimi due anni con la partecipazione italiana alla campagna contro il terrorismo internazionale e alla stabilizzazione dell\' Iraq. L\'art.4 della legge delega da\' la definizione di conflitto armato, rilevante ai fini dell\' applicazione della legge di guerra, inserendovi, oltre ai conflitti armati internazionali di tipo tradizionale, \'\'i conflitti interni tra gruppi di persone organizzate, che si svolgano all\' interno del territorio dello Stato, e raggiungendo la soglia di una guerra civile o di una insurrezione armata; i conflitti interni prolungati tra le forze armate dello Stato e i gruppi armati organizzati, o tra tali gruppi\'\'. La legge penale militare di guerra si applichera\' comunque a tutti coloro che, durante un conflitto armato cosi\' definito, violino le leggi e gli usi di guerra a danno dello Stato italiano, del territorio della Repubblica o delle stesse forze armate rischierate all\'estero. Quindi la legge penale di guerra tornera\' a essere il regime giuridico normale per i contingenti all\' estero e trovera\' applicazione ogni qual volta le forze armate italiane si trovino a intervenire in situazioni di aperto conflitto e comunque in caso di attacco armato allo Stato italiano. Tra le novita\' rilevanti, in questo campo, l\'attribuzione al personale in missione abilitato a svolgere funzioni di polizia giudiziaria militare, del potere di procedere d\'iniziativa al compimento di tutti gli atti di polizia, compresi quelli che normalmente sono svolti solo su delega del Pm, incluso l\'interrogatorio dei militari arrestati o fermati, in circostanze ben definite e limitare, o in zone di operazione, o in presenza di divieto di comunicazioni con il comando in Italia dettato da ragioni di sicurezza oppure nel caso di reparti isolati o di aeromobili in navigazione quando non siano possibili i collegamenti. (ANSA). <o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<H1 style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>DIFESA: NIEDDU, RIFORMA CODICI SBAGLIATA E TROPPO ONEROSA</FONT></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><FONT face=\"Times New Roman\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\">(ANSA) - ROMA, 18 NOV - \'\'Era necessario mettere mano alla giurisdizione militare, ma rendendola una sezione \'esperta\' di quella ordinaria\'\' dice il senatore Gianni Nieddu, capogruppo Ds nella commissione Difesa, commentando il via libera del Senato alla legge delega che riforma i codici e l\'ordinamento militare. \'\'Il nostro punto di partenza e\' che la riforma della giurisdizione militare era necessaria, ma quella approvata oggi e\' una riforma sbagliata in radice. Pur di non affrontare la questione vera, ovvero il problema dell\'esistenza di una giurisdizione militare che non trova oggi ragione di esistere, la Destra ha pensato bene di dilatare i reati militari\'\'. La strada che \'\'avremmo voluto percorrere, e che e\' la stessa gia\' affrontata da tutti i Paesi d\'Europa e della Nato, e\' quella - spiega l\'esponente ds - di creare una sezione speciale militare all\'interno della giurisdizione ordinaria. La scelta di mantenere invece una giurisdizione speciale, operata dalla Destra, e\' tutt\'altro che razionale e non risponde tra l\'altro al criterio del migliore utilizzo delle scarse risorse finanziarie . La giurisdizione militare, infatti, e\' composta da circa 103 magistrati, di cui solo 80 realmente operanti, che hanno la competenza su appena 340 mila cittadini. La dilatazione dei reati militari e\' prevista anche quando si tratta in modo chiaro di un reato civile, ricadente nella giurisdizione ordinaria, che e\' trasformato in reato militare per il solo fatto che a compierlo e\' un cittadino con le stellette. In questo modo si creano una serie di problemi. Solo per fare un esempio, se un poliziotto - che e\' un pubblico ufficiale - commette il reato di concussione viene giudicato dal giudice ordinario, mentre se a compiere lo stesso reato sono pubblici ufficiali con le stellette come un carabiniere o un </SPAN><SPAN style=\"FONT-SIZE: 12pt; COLOR: red; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\">finanziere</SPAN><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\">, il giudice competente - conclude - e\' militare\'\'. (ANSA). <o:p></o:p></SPAN></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></B></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><FONT face=\"Times New Roman\" size=3>DIFESA: AN, RIFORMA NECESSARIA PER ESERCITO PROFESSIONALE E PER LE MISSIONI ALL\'ESTERO DEI NOSTRI SOLDATI</FONT></STRONG></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">(ANSA) - ROMA, 18 NOV - Una riforma necessaria per adeguare i codici militari ad un esercito di professionisti a gli obblighi che vedono i 10 mila nostri soldati operare in missioni all\'estero. Questi, per Mario Palombo, responsabile dell\'ufficio di An per la Difesa, i due principali obiettivi \'\'centrati\'\' dal ddl di delega sulla riforma dei codici e dell\'ordinamento militare approvato stamane in prima lettura dal Senato. \'\'I nostri militari, in patria e nelle missioni di pace all\'estero, avranno una legge militare moderna ed adeguata alle loro esigenze operative\'\', sottolinea il senatore di An. \'\'I nuovi codici - aggiunge subito dopo il voto - tuteleranno nel rispetto della Costituzione l\'uomo in divisa fornendo tutte le garanzie necessarie per un esercito moderno. Il centrosinistra avrebbe voluto abolire la giustizia militare, ampliando le competenze della giustizia ordinaria, ma non sarebbe stata la risposta piu\' adatta. E\' stato meglio aggiornare i codici. Cosi\' ora questa riforma in ossequio al principio di simmetria, assicurera\', da un lato, alla giustizia militare assoluta autonomia e, dall\'altro, agli uomini in uniforme la garanzia dei tre gradi di giudizio, qualora imputati di reati militari\'\'. In particolare, i nuovi codici \'\'salvaguarderanno la coesione interna delle Forze Armate, tutelando l\'uomo in divisa dagli abusi autoritari nell\'esercizio della professione militare, e proteggeranno, anche nelle operazione di peace keeping, i prigionieri, i feriti e la popolazione civile inerme. Quindi - conclude Palombo - il consolidato vanto del nostro esercito di leva, nel rispetto del diritto umanitario, sara\' acquisito anche dal nostro esercito professionale\'\'. (ANSA). <o:p></o:p></FONT></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2558, 1, 0, '2026-01-11 04:30:58', '0000-00-00'),
(390, '2004-11-26 00:00:00', 'Notizie Flash', 'SICUREZZA: SILP-CGIL IN PIAZZA CONTRO FINANZIARIA MANIFESTAZIONE DAVANTI A PREFETTURE E QUESTURE', '', 'SICUREZZA: SILP-CGIL IN PIAZZA CONTRO FINANZIARIA MANIFESTAZIONE DAVANTI A PREFETTURE E QUESTURE', '<P>&nbsp;</P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><FONT face=\"Times New Roman\" size=3>SICUREZZA: DOMANI SILP-CGIL IN PIAZZA CONTRO FINANZIARIA MANIFESTAZIONE DAVANTI A PREFETTURE E QUESTURE</FONT></STRONG></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">(ANSA) - ROMA, 24 NOV - Domani i poliziotti del Silp-Cgil manifesteranno davanti a tutte le prefetture e le questure d\' Italia \'\'contro una Finanziaria che riduce la sicurezza dei cittadini\'\'. Un volantinaggio in ogni capoluogo di provincia ed una fiaccolata a Palermo, spiega il sindacato, \'\'per protestare contro la scelta del Governo di tagliare ancora i fondi alle forze di polizia e bloccare il turn over per tre anni, esattamente mentre sale l\' allarme terrorismo e si aggrava l\' emergenza criminalita\'\'\'. \'\'Il rischio di un pericoloso peggioramento dei livelli di sicurezza del Paese - sostiene il Silp - potra\' essere scongiurato a condizione che il governo rinunci a rastrellare risorse a scapito della lotta al terrorismo ed alla criminalita\' e decida di mantenere gli impegni assunti con il Paese e con gli operatori delle forze dell\' ordine\'\'. (ANSA). <o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></B></P>\r\n<P class=MsoBodyText style=\"MARGIN: 0cm 0cm 0pt\"><STRONG><FONT face=\"Times New Roman\" size=3>POLIZIA: CGIL, CISL E UIL PROCLAMANO STATO AGITAZIONE CONTRO ATTO CHE DESTINA 25.000 AGENTI A MANSIONI AMMINISTRATIVE</FONT></STRONG></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">(ANSA) - ROMA, 17 NOV - Cgil, Cisl e Uil ministero dell\' Interno proclamano lo stato di agitazione del personale dell\' amministrazione civile dell\' Interno. Nel mirino dei sindacati, la direttiva del capo della polizia del 10 novembre scorso, che, spiegano, \'\'annulla con un tratto di penna la riforma della polizia di Stato, destinando agli uffici amministrativi personale che verra\' sottratto sia all\' attivita\' di controllo del territorio che all\' attivita\' investigativa\'\'. Con questa operazione, sottolineano i sindacati, \'\'oltre 25.000 poliziotti verranno utilizzati non per contrastare la criminalita\' ed il terrorismo, ma per svolgere mansioni amministrative contabili e patrimoniali\'\'. Per il 26 novembre sono state convocate le segreterie nazionali per decidere \'\'le opportune iniziative di lotta\'\'. Cgil, Cisl e Uil fanno presente \'\'il danno economico di tale scelta, in un momento in cui a tutti i cittadini vengono richiesti enormi sacrifici in considerazione dei costi di equipaggiamento, addestramento e trattamento economico di un poliziotto che impropriamente viene adibito a funzioni meramente amministrative\'\'. I sindacati chiedono poi \'\'come siano conciliabili con tali direttive le dichiarazioni del ministro dell\' Interno, Giuseppe Pisanu che, richiamando i cittadini napoletani ai propri doveri, li rassicurava sulla presenza di un poliziotto ogni 238 abitanti nel momento in cui si decide di destinare tali poliziotti a servizi strategici quali mense, zone benessere, strutture sportive e centri balneari\'\'. (ANSA). <o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></B></P>\r\n<H1 style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>CRIMINALITA\': NAPOLI; SILP, AVREMO PIU\' MEZZI E RISORSE</FONT></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">(ANSA) <B style=\"mso-bidi-font-weight: normal\">- </B>NAPOLI, 18 NOV - \'\'La complessa vicenda dell\'ordine e della sicurezza pubblica ha richiesto ancora una volta nel giro di qualche settimana la presenza del capo della polizia prefetto De Gennaro che mantenendo l\'impegno assunto ha di nuovo incontrato a Napoli i sindacati di polizia. Lo ha detto Paolo Masia, segretario nazionale del sindacato di polizia Silp. Il sindacato ha fornito \'\'dati numerici e statistici rispetto alle risorse utilizzate per la polizia napoletana\'\'. De Gennaro ha reso noto arriveranno entro qualche mese nuove autovetture coi colori della polizia ed altre civili \'\'per le esigenze connesse ai servizi investigativi\'\'. Le moto in dotazione ai Nibbio \'\'verranno equipaggiate cosi\' come chiede da tempo il Silp e rese funzionali all\'esigenze del servizio antiscippo e antirapina\'\'. Per le questioni logistiche \'\'oltre ai miglioramenti complessivi che arriveranno dalla costruenda cittadella della Polizia, vi sara\' un impegno per la verifica di quei commissariati che sono in condizioni precarie\'\'. Inoltre \'\'gli emolumenti accessori tra i quali lo straordinario verranno aumentati e, corrisposti con maggiore puntualita\'\'\'. \'\'Il Silp pur apprezzando quanto riferito dal prefetto De Gennaro - si legge nella nota - si e\' riservato la verifica dei dati forniti, verificando nel contempo che gli impegni oggi assunti verranno rispettati in tempi brevi\'\'. Sul piano delle risorse economiche e umane, il Silp \'\'ha ribadito al Capo della Polizia di farsi interprete di tutto il nostro disagio - e\' scritto nel documento - nei confronti di una legge finanziaria che non investe sulla sicurezza, ma fa intravedere ulteriori tagli tra i quali l\'assunzione di nuovo personale.E\' stato inoltre stigmatizzato il comportamento delle forze di governo che con le operazioni alto impatto e poliziotto di quartiere hanno illuso i cittadini e sprecato soldi dei contribuenti; e\' stato richiesto sul piano operativo un aumento dei servizi investigativi ad iniziare dai Commissariati. Su questo progetto il confronto continuera\' con il questore di Napoli Malvano\'\'.(ANSA). <o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><B style=\"mso-bidi-font-weight: normal\"><SPAN style=\"FONT-SIZE: 12pt; COLOR: black; mso-bidi-font-size: 10.0pt; mso-ansi-language: IT\"><FONT face=\"Times New Roman\">&nbsp;<o:p></o:p></FONT></SPAN></B></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 1913, 1, 0, '2026-01-11 20:12:50', '0000-00-00'),
(391, '2004-11-26 00:00:00', 'Notizie Flash', 'CRIMINALITA\': MARESCIALLO GDF, COSI\' MI SONO SALVATO A NAPOLI- SICUREZZA: STORACE,MODELLO CATALANO PER POLIZIA REGIONE - PREMI: \'PAOLO BORSELLINO\' A GENERALE SPECIALE', '', 'CRIMINALITA\': MARESCIALLO GDF, COSI\' MI SONO SALVATO A NAPOLI - TANGENTI: APPALTI MILITARI; COMINCIATO PROCESSO A ROMA - SICUREZZA: STORACE,MODELLO CATALANO PER POLIZIA REGIONE - PREMI: \'PAOLO BORSELLINO\' A GENERALE SPECIALE', '<p style=\"text-align:justify\"><strong><span style=\"color:black; font-size:12pt\">&nbsp;<!--?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /--></span></strong></p>\r\n\r\n<p><strong>CRIMINALITA&#39;: MARESCIALLO GDF, COSI&#39; MI SONO SALVATO DOMENICA SERA A NAPOLI FU BERSAGLIO SPARI</strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:black; font-size:12pt\">&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:black; font-size:12pt\">(ANSA) - NAPOLI, 16 NOV - Il maresciallo della Guardia di Finanza finito nel mirino dei killer nella serata di domenica a Napoli e&#39; sicuro di non essere stato scambiato per qualche altra persona. Lo ha detto nel corso di un&#39;intervista al TG3, commentando con soddisfazione come la citta&#39; sia con lui: &#39;&#39;La cosa piu&#39; brutta che puo&#39; capitare a un&#39;unita&#39; impegnata e&#39; sentirsi sola&#39;&#39;. Il sottufficiale delle Fiamme Gialle, che secondo quanto finora trapelato e&#39; impegnato in una delicata inchiesta sul contrabbando, ha ricostruito quanto avvenuto quella sera, in via Gianturco, mentre stava tornando in caserma: la macchina del commando che si avvicina, il braccio di un uomo che spunta da un finestrino impugnando una pistola. &#39;&#39;Ha puntato verso di me e ha fatto fuoco&#39;&#39;, ha raccontato il finanziere. Momenti di grande tensione: &#39;&#39;Sono riuscito a mantenere la calma&#39;&#39; ha aggiunto il sottufficiale, spiegando come e&#39; riuscito a mettere in fuga gli uomini del commando rispondendo al fuoco. Il maresciallo non ha saputo specificare se ha ferito le persone a bordo dell&#39;auto.(ANSA)</span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"color:black; font-size:12pt\">&nbsp;</span></strong></p>\r\n\r\n<h1><strong style=\"font-size:13px\">SICUREZZA: STORACE,MODELLO CATALANO PER POLIZIA REGIONE/ANSA A BRUXELLES PER STUDIARE MODELLI EUROPEI IN VISTA DEVOLUTION</strong></h1>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:black; font-size:12pt\">&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:black; font-size:12pt\">(ANSA) - BRUXELLES, 17 NOV - I poliziotti regionali come i &#39;mossos de squadra&#39; catalani o i &#39;flich&#39; parigini. La regione Lazio pensa gia&#39; all&#39;entrata in vigore della riforma costituzionale, che le assegnera&#39; poteri di legislazione esclusiva in materia di polizia locale, e a Bruxelles studia i vari modelli di autonomia federale in termini di sicurezza, realizzati in Europa. Devolution e Unione europea non sono in contraddizione, fa&#39; capire dal cuore d&#39;Europa il governatore del Lazio Francesco Storace che oggi ha incontrato tecnici ed esperti catalani e parigini per mutuare il modello di polizia regionale quando la riforma federale entrera&#39; in vigore in Italia. E dal confronto tra varie esperienze europee, Storace ha gia&#39; scelto il suo poliziotto preferito: gli agenti che vegliano sulla sicurezza in Catalogna, una delle regioni europee da anni emblema di autonomia. &#39;&#39;Non sono un innamorato dell&#39;idea di polizia regionale ma in vista dell&#39;entrata in vigore della riforma costituzionale stiamo studiando le caratteristiche e i compiti del poliziotto regionale che avra&#39; aree di competenza specifica senza sovrapporsi all&#39;ordine pubblico garantito dalla polizia di Stato&#39;&#39;. E proprio dalla Catalogna vengono i campi di intervento del futuro poliziotto regionale: la prevenzione nelle scuole &#39;&#39;ad esempio - ha spiegato il governatore del Lazio - per prevenire fenomeni di bullismo e di spaccio di droga&#39;&#39;, la vigilanza fuori da night e discoteche, compiti di polizia ambientale, tributaria e amministrativa. C&#39;e&#39; da chiedersi se trovera&#39; attuazione anche nel Lazio uno dei compiti degli agenti catalani, ovvero la vigilanza su fenomeni di devianza religiosa e esoterica. &#39;&#39;Oggi ad esempio - ha affermato Storace - spetta alla </span><span style=\"color:red; font-size:12pt\">Guardia di Finanza </span><span style=\"color:black; font-size:12pt\">l&#39;esclusivo compito di polizia tributaria mentre in un prossimo futuro potra&#39; essere un poliziotto regionale a verificare se un cittadino paga o meno i contributi. Gli agenti regionali saranno armati &#39;&#39;perche&#39; certo non basteranno per difendersi - ha evidenziato Storace - corsi di judo o di karate&#39;&#39;. Base di partenza per la futura creazione della polizia regionale sara&#39; la legge di riforma delle polizie locali, che approdera&#39; la prossima settimana in Consiglio regionale. Obiettivo della legge, ha detto Storace, &#39;&#39;sara&#39; uniformare tutte le forme di polizia locale sia negli aspetti esteriori, come la divisa sia in questioni piu&#39; delicate come gli armamenti&#39;&#39;. Cuore del coordinamento tra le varie forze di polizia locale sara&#39; la creazione di una centrale operativa unica e di un sistema radio oltre all&#39;apertura di una scuola di formazione. Ma in attesa di norme comuni, i pizzardoni oggi e i poliziotti regionali di domani sanno gia&#39; quando potranno festeggiare il loro giorno: &#39;&#39;il 20 gennaio istituiremo la festa del corpo della polizia locale nel Lazio&#39;&#39; ha annunciato il governatore del Lazio. (ANSA). </span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"color:black; font-size:12pt\">&nbsp;</span></strong></p>\r\n\r\n<p><strong>PREMI: &#39;PAOLO BORSELLINO&#39; A GENERALE SPECIALE A AGRIGENTO, PER IMPEGNO FIAMME GIALLE NELLA LOTTA ALLA MAFIA</strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:black; font-size:12pt\">&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:black; font-size:12pt\">(ANSA) - AGRIGENTO, 19 NOV - Il premio &#39;Paolo Borsellino&#39; dell&#39; accademia degli studi mediterranei di Agrigento e&#39; stato consegnato al comandante generale della guardia di finanza, generale Roberto Speciale, per &#39;&#39;l&#39; impegno delle Fiamme gialle nella lotta alla mafia&#39;&#39;. Il riconoscimento e&#39; stato consegnato dalla sorella del magistrato assassinato da Cosa nostra, Rita Borsellino. (ANSA). </span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"color:black; font-size:12pt\">&nbsp;</span></strong></p>', 0, '', '', '', 4064, 1, 0, '2026-01-09 14:07:40', '2017-08-08'),
(11160, '2017-08-10 00:00:00', 'Dai media', 'REPUBBLICA.IT: MIO PADRE STAVA MORENDO E AL 118 RISPONDEVA SOLO UN DISCO', '', '', '<p style=\"text-align: justify;\">Questo racconto &egrave; perch&eacute; nessun altro padre, marito o figlio, possa morire con una voce che ti dica &quot;Rimanga in attesa&quot;</p>\r\n\r\n<p style=\"text-align: justify;\">di VALENTINA RUGGIU</p>\r\n\r\n<p style=\"text-align: justify;\">09 agosto 2017</p>\r\n\r\n<p style=\"text-align: justify;\">&quot;RIMANGA in attesa&quot;. Una cordiale voce di donna me lo ripete in italiano, inglese e spagnolo. Il telefono &egrave; tra orecchio e spalla, mentre con tutta la forza cerco di sollevare mio padre che &egrave; mezzo steso a terra, una gamba piegata sotto l&#39;addome, l&#39;altra tesa indietro. Respira, si lamenta e dal viso scendono a terra gocce di sangue. &quot;Rimanga in attesa&quot;. Dentro di me sono convinta di poterlo rialzare, ma il solo sforzo per impedirgli di scivolare ancora &egrave; enorme, soprattutto per me che sono uno scricciolo e lui un omone. Gli dico che gli voglio bene, che andr&agrave; tutto bene e che arriver&agrave; presto qualcuno ad aiutarci.<br />\r\n<br />\r\n&quot;Rimanga in attesa&quot;. Sono passati pi&ugrave; di due minuti ed &egrave; la seconda chiamata al 118. Attacco e riprovo a chiamare: &quot;Rimanga in attesa &quot;. La terza chiamata la faccio dal mio cellulare e parte alle 3:19. Nel frattempo arrivano mio fratello e la compagna. &quot;Rimanga in attesa &quot;. Lo sollevano, lo poggiano sul letto e vedo mio padre che si sta spegnendo. La chiamata &egrave; ancora aperta, sotto le grida di mia madre sento la voce registrata: &quot;Rimanga in attesa&quot;. Non so cosa fare, vorrei solo un&#39;ambulanza, qualcuno che ci aiuti. Urlo contro la voce registrata. Prendo una spugnetta bagnata e gliela passo sul viso, provo a mettergli qualche goccia d&#39;acqua in bocca. Poi il dubbio: &quot;Forse non dovevo farlo, forse non pu&ograve; ingoiare. E se soffoca?&quot;. Ma a suggerirmi cosa fare non c&#39;&egrave; nessuno, al telefono ho solo la voce di donna. Mio fratello nel frattempo va in cerca di un&#39;ambulanza al pronto soccorso di Albano Laziale, il paese in provincia di Roma in cui ci siamo trasferiti per fuggire dal caos della Capitale.<br />\r\n<br />\r\nLa cosa buffa &egrave; che da casa mia si pu&ograve; quasi vedere nelle camere per la degenza perch&eacute; abitiamo nella via proprio sotto l&#39;entrata principale della struttura. In totale ci separano 300 metri, praticamente un minuto di macchina. Mio fratello per&ograve; torna a mani vuote, dal pronto soccorso dicono che &quot;non hanno ambulanze a disposizione al momento&quot;. &quot;Rimanga in attesa&quot;, continua la voce. Questa volta per&ograve; decido che in attesa non rimango pi&ugrave;: lascio la chiamata aperta e corro fuori. Intanto, alle 3:26 parte un&#39;altra chiamata al 118 dal telefono della ragazza di mio fratello. La sua attesa si aggiunge alla mia.<br />\r\n<br />\r\nFuori, scalza, suono ai vicini. In casa c&#39;&egrave; solo la figlia minore. Le chiedo di aiutarmi a chiamare i soccorsi e anche lei ci prova. Poi, d&#39;improvviso la vocina dal mio smartphone si interrompe, mi rispondono. All&#39;operatore dico dove abito, gli spiego del rumore tremendo che mi ha svegliata e di come ho trovato mio padre. Gli dico che &egrave; ancora vivo, ma che sta per morire. Gliel&#39;ho visto in faccia. Serve un&#39;ambulanza urgentemente. Mi dice &quot;Ok, trasferisco la chiamata alla centralina del 118 pi&ugrave; vicina a lei&quot;. E anche qui la beffa, uno dei punti da cui partono &egrave; a pochi minuti da casa. Ritorno in attesa, di nuovo la voce cordiale di donna.<br />\r\n<br />\r\nUrlo, mi sembra un incubo. Al telefono della vicina risponde un altro operatore: gli spiego tutto di nuovo. Sottolineo che ho gi&agrave; parlato con loro, che mi hanno gi&agrave; messo in attesa con il 118, ma che mio padre non ha pi&ugrave; tempo, morir&agrave; se non si sbrigano. Torna la voce di donna. Mollo il telefono con la chiamata aperta alla vicina, le dico di non riagganciare e di ripetere cosa ho detto io casomai qualcuno dovesse rispondere. Corro in mezzo alla strada e comincio a urlare aiuto. Anche la vicina urla, vede un uomo uscire dalla casa di fronte. Lo raggiungo gli dico di entrare in casa mia, che deve correre perch&eacute; pap&agrave; sta morendo e il 118 non risponde e devo portarlo al pronto soccorso.<br />\r\n<br />\r\n&quot;Rimanga in attesa&quot;, continua la voce dal telefono della mia vicina. Quella del mio cellulare si &egrave; zittita, non so se ho riagganciato io o lo hanno fatto loro. Continuo a urlare ed esce un altro uomo. Imploro aiuto anche a lui mentre alla vicina il numero per l&#39;emergenza sanitaria riaggancia il telefono. L&#39;attesa &egrave; finita, ma in tutti i sensi: pap&agrave; &egrave; morto. Alle 3:34 e alle 3:36 mi chiama un numero privato: &quot;Signora se la vuole ancora, le mando un&#39;ambulanza &quot;. Volevo aiuto e ho avuto solo una voce registrata. L&#39;autopsia forse dir&agrave; che si &egrave; trattato di un ictus o di un&#39;ischemia, in ogni caso dar&agrave; una spiegazione a quel tonfo che ho sentito. Forse per&ograve; non sapr&ograve; mai perch&eacute; ho atteso cos&igrave; tanto una risposta dal centralino unico del 112, perch&eacute; abbiamo dovuto chiamare in tre, perch&eacute; mi hanno rimesso altri minuti in attesa dopo aver parlato con l&#39;operatore. Mi domando se fosse accaduto mentre non c&#39;era mia madre che correva a prendere il telefono o mentre non c&#39;ero io che mi sono svegliata e che, dopo averla calmata, le ho detto di prenderlo quel telefono. O se non ci fossero stati, nella casa accanto, mio fratello e la ragazza. Mi chiedo a quante persone quella vocina abbia detto di rimanere in attesa, a quanti quei minuti sarebbero potuti servire per non perdere la vita.<br />\r\n<br />\r\nPer mio padre forse non avrebbero potuto fare nulla, ma una voce umana mi avrebbe almeno aiutata, guidata, supportata. Ho dovuto caricare mio padre in macchina. Mio fratello ha dovuto guidare con le gambe tremolanti. Alle 3:34 o alle 3:36, quell&#39;ambulanza a noi non serviva pi&ugrave;. Eravamo gi&agrave; al pronto soccorso, qualche minuto pi&ugrave; tardi ci hanno ufficializzato la morte.<br />\r\n<br />\r\nMio padre si chiamava</p>\r\n\r\n<p style=\"text-align: justify;\">Gianfranco e faceva il cameriere, era un uomo devoto al suo lavoro. Un padre e un marito con i suoi pregi e i suoi difetti. E questo racconto &egrave; perch&eacute; nessun altro padre, marito o figlio, nessun altro amico o cugino, possa morire con una voce che ti dica &quot;Rimanga in attesa&quot;.</p>', 0, '', '', '', 2196, 1, 0, '2026-01-13 16:36:57', '2017-08-10'),
(392, '2004-11-30 00:00:00', 'Servizio di Ficiesse', 'DELIBERE DEL COCER GUARDIA DI FINANZA: Università di Torino, Schema di concertazione, Articolo del settimanale \"Il Mondo\"', '', 'DELIBERE DEL COCER GUARDIA DI FINANZA: Università di Torino, Schema di concertazione, Articolo del settimanale \"Il Mondo\"', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Pubblichiamo in allegato tre interventi del COCER della G. di F., oggetto di delibere in data 25 novembre scorso.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>La prima si riferisce ad una situazione anomala che si è determinata alla Università di Torino che per alcuni corsi frequentati da militari del Corpo non riconosce crediti. Il Cocer chiede quindi l’integrazione della convenzione.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>La seconda contiene il parere favorevole alla circolare del Servizio Amministrativo emanata a seguito del recepimento dell’accordo- schema di concertazione del personale delle Forze di Polizia ad ordinamento militare, biennio Economico 2004/2005.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>La terza si inserisce nella vicenda sollevata dall’articolo apparso sul settimanale il “ Mondo” circa una riforma che dovrebbe interessare la G. di F.. Il Cocer<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>chiede un incontro urgente con il Comandante Generale.</FONT></P>', 109, '', '', '', 5086, 1, 0, '2026-01-10 01:22:13', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(393, '2004-12-04 00:00:00', 'Notizie Flash', 'IMPRENDITORE SUICIDA: GDF, \'ISPEZIONE IN CLIMA SERENITA\'\' - SIAE: DIRITTI AUTORE, GDF CHIUDE TRASMISSIONI MUSICALI TV - URNA CINERARIA SEQUESTRATA DA GDF IN UFFICIO DI SINDACO DEL COMASCO', '', 'IMPRENDITORE SUICIDA: GDF, \'ISPEZIONE IN CLIMA SERENITA\'\' - SIAE: DIRITTI AUTORE, GDF CHIUDE TRASMISSIONI MUSICALI TV - URNA CINERARIA SEQUESTRATA DA GDF IN UFFICIO DI SINDACO DEL COMASCO', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">IMPRENDITORE SUICIDA, IN AZIENDA C\'ERA STATA GUARDIA FINANZA<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">I CONOSCENTI, ERANO NORMALI CONTROLLI MA LUI APPARIVA TURBATO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - CHIETI, 30 NOV - Si e\' procurato una corda, una scala a libretto e con determinazione, probabilmente dopo essersi stordito ingerendo del whisky, si e\' impiccato a una trave nel locale destinato all\'archivio, nella sua azienda a Vasto (Chieti). Cosi\' e\' morto un imprenditore di 48 anni, Nicola Bastonno, sposato e padre di due figli, molto stimato nella sua citta\', dove dal nulla aveva tirato su una torneria-carpenteria di cui era amministratore unico. Nei giorni scorsi nella ditta c\'era stato un sopralluogo della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di Finanza</SPAN></STRONG>.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>A trovare Bastonno e\' stato uno dei due fratelli, dipendente della fabbrica (l\'altro e\' contitolare). Lo aveva cercato tutto il pomeriggio nel capannone, poi lo aveva chiamato al cellulare, che pero\' squillava a vuoto. Quando pero\' ha sentito il trillo provenire dall\'archivio e ha trovato la porta chiusa si e\' preoccupato; e\' salito sul tetto del capannone, lo ha scoperchiato e ha trovato il fratello, ormai morto. Ai piedi della scala, una bottiglia di whisky completamente vuota.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Nessun problema economico, assicurano parenti e conoscenti, assolutamente stabile mentalmente; pensava al futuro della sua famiglia, visto che stava costruendo una villa nei pressi di Vasto. Da qualche giorno, pero\', \'\'appariva molto agitato, dopo che in azienda aveva ricevuto la visita della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza </SPAN></STRONG>- raccontano persone a lui vicine -. Si trattava di normali controlli, ma lui sembrava turbato\'\'. Gli investigatori avrebbero ricevuto dai famigliari una lettera, lasciata dall\'imprenditore in un cassetto della sua scrivania in ufficio, ma del contenuto non si sa ancora nulla.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Intanto, il sostituto procuratore della Repubblica di Vasto, Annarita Mantini, ha incaricato l\'anatomopatologo Ivan Melasecca di effettuare una ricognizione cadaverica ed eventualmente, in caso di dubbi, un\'autopsia. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">IMPRENDITORE SUICIDA: GDF, \'ISPEZIONE IN CLIMA SERENITA\'\'<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">INTERVENTO COMANDO FINANZA PROVINCIA CHIETI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - CHIETI, 1 DIC - Un\'ispezione svoltasi \'\'in un clima di assoluta serenita\' e di collaborazione\'\'. Cosi\' il Comando di Chieti della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG> descrive il sopralluogo effettuato nei giorni scorsi nell\'azienda di Nicola Bastonno, l\'imprenditore di Vasto (Chieti) suicidatosi lunedi\'. Lo stesso Comando in una nota afferma che \'\'da contatti con la Procura di Vasto, sarebbe da escludere la paventata correlazione tra l\'estremo gesto e l\'attivita\' ispettiva\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Alcuni conoscenti dell\'uomo lo avevano visto turbato dopo la visita dei militari. \'\'Il Comando Tenenza di Vasto - scrive il tenente colonnello Alessandro Primavera - aveva avviato da pochi giorni un\'attivita\' di verifica fiscale nei confronti della societa\' Tecnometal Srl di cui il signor Bastonno era rappresentante legale. L\'unico contatto avuto con lui e\' stato in azienda, in occasione dell\'inizio delle operazioni ispettive, svoltesi anche alla presenza di altre figure deputate allo svolgimento dell\'attivita\' amministrativa e contabile della societa\', in un clima di assoluta serenita\' e di collaborazione da parte dei responsabili aziendali\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La documentazione acquisita, spiega ancora la nota della Finanza, \'\'e\' stata portata negli uffici della Tenenza del Corpo, per il prosieguo delle operazioni di controllo\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\'ipotesi di correlazione tra ispezione e suicidio \'\'e\' smentita, peraltro - conclude Primavera - anche dal rinvenimento di una lettera in cui il signor Bastonno indica ai famigliari le motivazioni che l\'avrebbero indotto al suicidio, senza fare alcun riferimento al controllo in atto della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG>, ma riferendosi esclusivamente ad altre vicende e circostanze personali\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">SIAE: DIRITTI AUTORE, GDF CHIUDE TRASMISSIONI MUSICALI TV<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">NEL BARESE TRASMETTEVA DA ANNI MUSICHE SENZA REGOLARE CONTRATTO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - MONOPOLI (BARI), 2 DIC - Nell\' ambito delle attivita\' per tutelare i diritti d\' autore, militari della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">finanza</SPAN></STRONG> della compagnia di Monopoli hanno inibito le trasmissioni musicali di una emittente radiofonica pugliese. Questa potra\' continuera\' la sua normale attivita\' in altri settori, tra i quali quello dell\' informazione, per il quale e\' in possesso della regolare concessione ministeriale.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>Per anni - e\' stato reso noto - l\' emittente aveva messo in onda programmi radiofonici musicali, spot pubblicitari e altro pur in mancanza di un contratto specifico con la Siae. Una volta emerso che l\'emittente non era in regola con gli adempimenti previsti dalla legge, i militari hanno sequestrato attrezzature per la radiodiffusione e la trasmissione di brani musicali (computer, equalizzatore, sintonizzatore), migliaia tra Cd e Lp in vinile, oltre a centinaia di files in formato Mp3 e Wave. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>Il legale rappresentante della societa\' proprietaria dell\' emittente e\' stato denunciato. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">URNA CINERARIA PER 2 ANNI IN UFFICIO DI SINDACO DEL COMASCO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - COMO, 2 DIC - Per due anni l\'urna con le ceneri di un\'anziana comasca, morta in una casa di riposo della Valle Intelvi, e\' rimasta nell\'ufficio del sindaco di Pellio Intelvi, senza che nessuno provvedesse alla sepoltura.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La singolare vicenda e\' finita sul tavolo della procura di Como, dal momento che la <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG> alcuni giorni fa aveva provveduto a sequestrare l\'urna cineraria e tutta la documentazione relativa. E\' stata aperta un\'inchiesta, che verra\' archiviata perche\' non vi sono estremi di reato, ma almeno l\'intervento delle Fiamme Gialle consentira\' di tumulare le ceneri in un loculo del cimitero.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\'anziana, E.F., era morta due anni fa in stato di indigenza, lasciando la volonta\' di essere cremata e sepolta a Pellio: alle rette della casa di riposo e alla cremazione aveva provveduto il Comune di Como, che poi aveva inviato l\'urna in valle Intelai per il funerale. Le ceneri, pero\', non sono state mai tumulate perche\' in Comune nessuno aveva mai deliberato la spesa per la sepoltura.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Dopo l\'intervento della Finanza, il sostituto procuratore di Como, Claudio Galoppi, ha esaminato la vicenda, ha aperto un fascicolo senza indagati, e ha concluso che non sussistono i presupposti per contestare l\'occultamento o la sottrazione di cadavere. Tutto, insomma, e\' stato fatto in regola, dal riconoscimento alla cremazione al funerale. Per il pm potrebbero al limite sussistere gli estremi per una sanzione amministrativa, che e\' pero\' venuta meno, perche\' nel frattempo - cambiata anche l\'amministrazione comunale - il Comune di Pellio ha deciso di farsi carico di tutte le spese. (ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 3964, 1, 0, '2026-01-13 07:31:43', '0000-00-00'),
(394, '2004-12-04 00:00:00', 'Notizie Flash', 'TAVOLO TECNICO PER PREVIDENZA INTEGRATIVA COMPARTO SICUREZZA - POLIZIA: ASSOCIAZIONE FUNZIONARI CHIEDE DIRITTO SCIOPERO - GIUSTIZIA: MAGISTRATI MILITARI, INADEGUATO DDL RIFORMA PER GDF E CC - LEGGE COMUNITARIA: CONSOB PIU\' FORTE', '', 'TAVOLO TECNICO PER PREVIDENZA INTEGRATIVA COMPARTO SICUREZZA - POLIZIA: ASSOCIAZIONE FUNZIONARI CHIEDE DIRITTO SCIOPERO - GIUSTIZIA: MAGISTRATI MILITARI, INADEGUATO DDL RIFORMA PER GDF E CC - LEGGE COMUNITARIA: CONSOB PIU\' FORTE', '<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">TAVOLO TECNICO PER PREVIDENZA INTEGRATIVA COMPARTO SICUREZZA<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">SAPORITO, IN POCHI MESI CREARE FONDO COMUNE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 2 DIC - Alla presenza del sottosegretario alla Funzione pubblica, Learco Saporito, si e\' aperto oggi, nella sede del Dipartimento, un tavolo tecnico per la previdenza integrativa del comparto sicurezza. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Hanno partecipato i rappresentanti sindacali della Polizia di Stato, della Polizia penitenziaria, del Corpo forestale e i Cocer dell\' Esercito, dell\' Aeronautica, della Marina e della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG>.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il sottosegretario ha sottolineato l\' importanza che riveste sia per il Governo che per le categorie interessate l\' apertura della discussione sul tema della previdenza in un comparto di interesse vitale per il Paese, facendo seguito anche a quanto gia\' sta avvenendo per il settore scolastico. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'E\' necessario - ha detto Saporito - usare al meglio le risorse disponibili ed affrontare insieme le difficolta\' in modo che, con l\' ausilio di tutte le forze sindacali, si giunga in pochi mesi al raggiungimento dell\' obiettivo della creazione di un fondo comune per tutto il comparto sicurezza\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Dopo il 9 dicembre prossimo e\' prevista la prima riunione del tavolo tecnico a Palazzo Vidoni. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">POLIZIA: ASSOCIAZIONE FUNZIONARI CHIEDE DIRITTO SCIOPERO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">PER PROTESTA CONTRO DISINTERESSE MINISTRO INTERNO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 29 NOV - Diritto di sciopero anche per i poliziotti. Lo chiede l\' Associazione nazionale funzionari di polizia (Anfp), al termine di una riunione al ministero dell\' Interno, cui hanno partecipato centinaia di funzionari. \'\'Il diritto di sciopero - sottolinea l\' associazione - come unica credibile tutela rimasta per ottenere il riconoscimento dei piu\' elementari diritti\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Si tratta, spiega il segretario nazionale dell\' Anfp, Giovanni Aliquo\', \'\'di un diritto che, in molti Paesi avanzati, e\' stato riconosciuto alle <STRONG><SPAN style=\"FONT-WEIGHT: normal\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">polizia</SPAN></STRONG> e che, stante il disinteresse del ministro dell\' Interno e del Dipartimento della Pubblica sicurezza per le questioni degli appartenenti alla carriera, diventa oggi, per la maggioranza dei funzionari, forse l\' unico strumento credibile di rivendicazione\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Le retribuzioni dei funzionari di polizia appiattite su quelle delle qualifiche esecutive, il loro trattamento giuridico, penalizzato nei confronti di altri funzionari dell\' Amministrazione civile, il disconoscimento dei loro diritti sindacali, usurpati da organizzazioni che non ne rappresentano gli interessi - spiega Aliquo\' - sono i principali motivi di uno scontento che, secondo quanto deliberato oggi, motivano ad una stagione di mobilitazione e di denuncia\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Nel corso della riunione, inoltre, prosegue il segretario dell\' Anfp, \'\'e\' emerso che i tagli ingiustificati ai fondi della sicurezza renderanno, di fatto, quasi impossibile il lavoro di prevenzione e repressione del crimine. Ad una manifestazione, che si terra\' il 9 dicembre dinanzi al Senato della Repubblica, si intende, percio\', aggiungere anche una raccolta di firme per un\' iniziativa di provocazione, per abrogare la norma che vieta agli appartenenti alle <STRONG><SPAN style=\"FONT-WEIGHT: normal\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di polizia</SPAN></STRONG> di scioperare\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">GIUSTIZIA: MAGISTRATI MILITARI, INADEGUATO DDL RIFORMA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ANM, GIUDICE MILITARE PER REATI DI MILITARI IN ATTIVITA\'SERVIZIO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>(ANSA) - PADOVA, 1 DIC - Il disegno di legge delega governativo per la riforma della legislazione penale militare, approvato dal Senato, \'\'appare per piu\' aspetti inappagante e inadeguato\'\'. E\' la posizione dell\' Associazione Nazionale Magistrati Militari (Anmm) che, pur dicendosi soddisfatta dell\' iniziativa, esprime una serie di controproposte tecniche in una lettera del presidente, Sergio Dini, a Gaetano Pecorella, presidente della Commissione Giustizia della Camera. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'I reati contro l\'Amministrazione militare posti in essere da militari - rileva Dini, della Procura militare di Padova - continuano ad essere per la gran parte sottratti alla naturale giurisdizione militare\'\'. \'\'Basti pensare - prosegue - che il richiedere che i fatti debbano essere \'commessi da militari nel corso o in funzione di attivita\' di carattere militare\' comportera\' la sottrazione alla giurisdizione militare di tutti i reati contro l\'Amministrazione posti in essere da appartenenti all\' Arma dei Carabinieri e alla <STRONG>Guardia</STRONG> <STRONG>di</STRONG> <STRONG>Finanza</STRONG>, <B>posto che gli uni e gli altri non svolgono oggigiorno quasi mai attivita\' di carattere militare\'\'.<o:p></o:p></B></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><B><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Pare strano - rileva ancora il presidente dell\'Anmm – che non ci si sia resi conto che in tal modo si viene una volta di piu\' ad operare nel senso di un sostanziale riconoscimento di \'non militarita\' \' delle due forze di polizia ad ordinamento militare, con tutte le possibili ricadute in tal senso per coloro che ne desiderano la totale \'smilitarizzazione\'\'\'. <o:p></o:p></SPAN></B></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Difficilmente inoltre - segnala inoltre Dini in merito all\' art. 2 lettera 1) - saranno ricompresi nella categoria di reati militari fatti come la turbata liberta\' degli incanti e la stessa corruzione posta in essere in attivita\' amministrative come le gare per la fornitura di beni e servizi non attinenti a funzioni prettamente militari.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Dini suggerisce dunque di riformulare il testo, prevedendo come reato militare ogni violazione della legge penale costituente delitto da parte del Pubblico Ufficiale contro la Pubblica Amministrazione \'\'se commessa da militare nel corso o in funzione della propria attivita\' di servizio\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'La riduzione a cinque (al massimo) tribunali militari - sottolinea ancora Dini - non permettera\' un proficuo funzionamento di tali organi, creando inoltre notevoli problemi per i fruitori del servizio pubblico \'giustizia militare\' \'\'. E\' questo, spiega, sia alla luce dei prevedibili disagi per avvocati, imputati e testimoni convocati, per esempio, in un\' unica sede del Nord Italia per illeciti compiuti in un\'area che vada da Trieste ad Aosta a Ventimiglia, sia per le maggiori difficolta\' di controllo della legalita\' su territori cosi\' vasti. Tra l\'altro, osserva il presidente dell\'Anmm, \'\'questa operazione di ridimensionamento comporterebbe notevoli oneri finanziari (cui non pare essere data copertura nel testo approvato al Senato) sia per le varie spese necessarie per far fronte ai trasferimenti d\'ufficio del personale, sia per lo necessita\' di reperire nuove e piu\' ampie sedi per i Tribunali Militari mantenuti in funzione\'\'. Ma nel caso si dovesse confermare questa riduzione, suggerisce, sarebbe necessario prevedere, a favore dei magistrati e del personale di cancelleria attualmente in servizio, un meccanismo di transito in altra magistratura.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il presidente dell\' Anmm propone infine che (all\'art. 3 lettera V), venga stabilita in maniera inequivoca l\' abrogazione dell\' art.264<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>c.p.m.p. ed il principio della separazione dei procedimenti di competenza della magistratura ordinaria e di quella militare in tutti i casi di connessione, ivi compresi i casi di \'\'concorso di militari e civili nel reato militare\'\' ipotesi attualmente non disciplinata in maniera espressa - rileva- dall\'art.13 del codice di procedura penale.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Il rafforzamento della presenza dei magistrati di cassazione nell\' organo di autogoverno (art.5, punto 1)-B.) non appare giustificato - evidenzia ancora Dini - tanto piu\' alla luce della prevista soppressione (art.5 punto 1)- E bis) delle sezioni di Corte D\'appello di Verona e Napoli. Considerazioni critiche critiche sono infine avanzate anche sulla prevista sezione disciplinare (art.5 punto 1 lettera B, bis). (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">LEGGE COMUNITARIA: CONSOB PIU\' FORTE, STOP NORME VIGILANZA BOND - RECEPITA DIRETTIVA MARKET ABUSE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 2 DIC - La legge comunitaria approvata oggi alla Camera, che recepisce la direttiva Ue sul market abuse, da\' vita ad una Consob con poteri molto rafforzati, ma non recepira\' anche le norme relative ai prospetti finanziari. Si sposta in altro luogo, dunque, forse in sede di riforma del risparmio, il dibattito su un tema che aveva suscitato grandi polemiche, per un emendamento presentato dal governo sulla questione. Emendamento che affidava alla Banca d\'Italia la vigilanza<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">esclusiva sulle emissioni dei prodotti finanziari.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>E\' stato Giorgio La Malfa, presidente della commissione Finanze, a chiedere lo stralcio dell\' articolo 9 della legge comunitaria che conteneva il recepimento della direttiva sui prospetti finanziari. La direttiva prevede tra l\'altro norme sulla vigilanza per le emissioni dei bond, alle quali il governo aveva presentato l\'emendamento contestato durante il precedente passaggio della Comunitaria in Aula. Le polemiche erano state tali da indurre ad un rinvio del testo in commissione, per una correzione.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>La richiesta di stralcio e\' stata motivata con il fatto che in commissione Finanze e Attivita\' produttive sta per ripartire l\'iter del disegno di legge sul risparmio, che dovrebbe comprendere tutta la materia. Per lo stesso motivo La Malfa aveva chiesto di stralciare anche la norma della direttiva sul market abuse che prevede il rafforzamento dei poteri e della struttura della<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>della Consob. Questa seconda richiesta, pero\' e\' stata respinta e il recepimento della direttiva e\' stata votata, oltre che dalla maggioranza, anche dall\'opposizione. Dunque, via libera all\'articolo otto della Comunitaria che, con il recepimento della direttiva europea sul market abuse, prevede l\'inasprimento delle sanzioni per gli abusi di mercato e il rafforzamento della Consob, che potra\' avvalersi del supporto della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG> per il controllo degli abusi, e alla quale sara\' consentita l\'assunzione di 150 nuovi dipendenti. La Consob potra\' inoltre accedere alla centrale rischi della Banca d\'Italia. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>La commissione di borsa, dunque, potra\' richiedere notizie, dati o documenti sotto qualsiasi forma, richiedere registrazioni telefoniche, procedere ad audizioni di personale, al sequestro dei beni che possono formare oggetto di confisca, a ispezioni e perquisizioni. Chi ostacola le funzioni di vigilanza attribuite alla Consob sara\' punito con la reclusione fino a due anni e con la multa 10mila a 200mila euro.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>La Consob potra\' chiedere la collaborazione delle pubbliche amministrazioni, che dovranno fornire dati e informazioni; potra\' accedere al sistema informativo dell\'anagrafe tributaria, richiedere la comunicazione di dati personali anche in deroga ai<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">divieti previsti, avvalersi, ove necessario, dei dati contenuti nell\'anagrafe dei conti e dei depositi. La maggior parte di questi poteri saranno esercitati dalla Consob su autorizzazione del procuratore della Repubblica. (ANSA).<o:p></o:p></SPAN></P>', 0, '', '', '', 2438, 1, 0, '2026-01-05 09:02:37', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(395, '2004-12-04 00:00:00', 'Notizie Flash', 'BERLUSCONI E SINISCALCO INCONTRANO VERTICI GDF A CENA PRIVATA - FISCO: GDF, NESSUNA SUPER AGENZIA DELLE ENTRATE; GEN. SPECIALE, SMENTISCO RISTRUTTURAZIONE CORPO - GUARDIA FINANZA: CORPO SI RINNOVA, MENO BUROCRAZIA', '', 'BERLUSCONI E SINISCALCO INCONTRANO VERTICI GDF A CENA PRIVATA - FISCO: GDF, NESSUNA SUPER AGENZIA DELLE ENTRATE; GEN. SPECIALE, SMENTISCO RISTRUTTURAZIONE CORPO - GUARDIA FINANZA: CORPO SI RINNOVA, MENO BUROCRAZIA', '<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">BERLUSCONI E SINISCALCO INCONTRANO VERTICI GDF<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">CENA PRIVATA, RICAMBIATA VISITA PREMIER A COMANDO GENERALE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 2 DIC - Il premier Silvio Berlusconi ed il ministro dell\'Economia Domenico Siniscalco hanno incontrato questa sera a Palazzo Chigi i vertici della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza </SPAN></STRONG>per una cena privata che ricambia la visita istituzionale fatta dal presidente del Consiglio al Comando generale delle Fiamme gialle nei giorni scorsi. All\'incontro hanno partecipato anche i sottosegretari alla presidenza del Consiglio Gianni Letta e Paolo Bonaiuti. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">FISCO: GDF, NESSUNA SUPER AGENZIA DELLE ENTRATE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">GEN. SPECIALE, SMENTISCO RISTRUTTURAZIONE CORPO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 1 DIC - \'\'Sono assolutamente prive di fondamento le voci riportate da un settimanale nazionale circa un piano di ristrutturazione (o destrutturazione) della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">finanza</SPAN></STRONG>, in vista della nascita di una super-Agenzia delle Entrate\'\'. Lo ha detto il comandante generale della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di finanza</SPAN></STRONG>, Roberto Speciale, durante il suo intervento di inaugurazione dell\'anno accademico 2004-05 delal scuola di polizia tributaria. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">GUARDIA FINANZA: CORPO SI RINNOVA, MENO BUROCRAZIA / ANSA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">GEN. SPECIALE, IMPORTANTE RIFORMA REPARTI SPECIALI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 1 DIC - Meno burocrazia, piu\' intelligence e maggiore efficacia nelle azioni di prevenzione, grazie anche alla riforma dei reparti speciali. Il generale Roberto Speciale, comandante della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">finanza</SPAN></STRONG>, parlando all\' inaugurazione del nuovo anno di studi della Scuola di polizia tributaria, ha tratteggiato le linee del rinnovamento in atto nelle Fiamme Gialle. Presente alla cerimonia anche il ministro dell\' Economia, Domenico Siniscalco.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Per adempiere al meglio alla missione istituzionale che le e\' stata affidata - ha spiegato il generale Speciale – la <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG> ha riorientato i suoi interventi operativi in modo da affiancare alla tradizionale attivita\' di verifica fiscale, riguardante sostanzialmente le entrate dello Stato, anche efficaci azioni a tutela delle uscite e dei mercati\'\'. Il comandante ha poi definito \'\'assolutamente prive di fondamento le voci riportate da un settimanale nazionale circa un piano di ristrutturazione della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG> in vista della nascita di una super Agenzia delle Entrate\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Lo spettro delle attivita\' operative del corpo, ha osservato il gen. Speciale, \'\'va dalle verifiche fiscali alla tutela del bilancio dell\'Unione Europea attraverso la prevenzione e repressione delle indebite percezioni degli aiuti comunitari; dalla tutela della spesa sanitaria a carico del servizio nazionale ai danni erariali, dalla lotta alla criminalita\' organizzata alle indagini antiriciclaggio\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il nuovo modello organizzativo, ha proseguito il comandante, \'\'si accompagna ad un rinnovamento culturale orientato verso la sburocratizzazione delle attivita\', lo sviluppo delle competenze dirigenziali e l\' affinamento delle capacita\' di leadership e di comando\'\'. Tra i punti di forza di questo processo, ha sottolineato, \'\'c\' e\' la riforma dei reparti speciali. Con il nuovo assetto abbiamo operato una scelta organizzativa precisa che passa per la frammentazione della fusione della polizia economica e finanziaria in 4 segmenti: le entrate e le uscite per l\'area della finanza pubblica; il mercato dei capitali ed il mercato dei beni e servizi per l\'area economica\'\'. Per ognuno di questi segmenti, ha aggiunto \'\'abbiamo quindi costituito un\'unita\' speciale che svolge una duplice funzione: di analisi del segmento di competenza, ai fini preventivi e per orientare i controlli; di elaborazione di progetti formativi, da realizzare attraverso l\'impiego di risorse dei reparti territoriali. Dunque - ha concluso - ci aspettiamo un\' azione preventiva piu\' efficiente ed efficace\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il ministro Siniscalco si e\' soffermato sull\' importanza della preparazione dei finanzieri. \'\'Negli ultimi anni – ha osservato - c\' e\' stato un cambiamento tumultuoso nell\' economia mondiale e quando un sistema e\' cosi\' complesso, per capirlo serve una preparazione molto particolare. Una preparazione – ha detto rivolgendosi gli allievi della scuola - che e\' tecnica e questo corso ve la fornisce ma si richiedono anche competenze generali. Questi corsi - ha aggiunto - sono ben disegnati ma la loro riuscita dipende dall\' interazione con gli allievi\'\'. Siniscalco ha poi toccato uno dei settori di intervento della Gdf, quello della contraffazione, che, ha rilevato, \'\'si fa sempre piu\' sofisticata\'\'. Il ministro ha quindi fatto un sempio per spiegare quanto sia difficile distinguere tra prodotti che hanno il marchio made in Italy. \'\'Come distinguere - ha affermato - fra un bene prodotto a Biella, interamente<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">italiano, da uno prodotto nelle Marche, fatto costruire in Romania e poi reimportato?\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">GUARDIA FINANZA:GEN SPECIALE, VIA A RIFORMA REPARTI SPECIALI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">INAUGURATO ANNO STUDI SCUOLA POLIZIA TRIBUTARIA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 1 DIC - Uno dei punti di forza del nuovo modello organizzativo della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG> e\' rappresentato dalla riforma dei reparti speciali. Lo ha detto il comandante della Fiamme gialle, generale Roberto Speciale, intervenendo all\'inaugurazione del nuovo anno di studi della scuola di polizia tributaria.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Con il nuovo assetto, ha spiegato il generale Speciale \'\'abbiamo operato una scelta organizzativa precisa che passa per la frammentazione della fusione della polizia economica e finanziaria in 4 segmenti: le entrate e le uscite per l\'area della finanza pubblica; il mercato dei capitali ed il mercato dei beni e servizi per l\'area economica\'\'. Per ognuno di questi segmenti ha aggiunto \'\'abbiamo quindi costituito un\' unita\' speciale che svolge una duplice funzione: di analisi del segmento di competenza, ai fini preventivi e per orientare i controlli; di elaborazione di progetti formativi, da realizzare attraverso l\'impiego di risorse dei reparti territoriali. Dunque - ha sottolineato il comandante generale della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di finanza</SPAN></STRONG> - ci aspettiamo un\'azione preventiva piu\' efficiente ed efficace\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">\'\'Per adempiere al meglio alla missione istituzionale che le e\' stata affidata - ha proseguito il comandante delle fiamme gialle - la <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG> ha riorientato i suoi interventi operativi in modo da affiancare alla tradizionale attivita\' di verifica fiscale, riguardante sostanzialmente le entrate dello Stato, anche efficaci azioni a tutela delle uscite e dei mercati\'\'. Lo spettro delle attivita\' operative del corpo, ha osservato il gen. Speciale \'\'va dunque dalle verifiche fiscali alla tutela del bilancio dell\'Unione Europea attraverso la prevenzione e repressione delle indebite percezioni degli aiuti comunitari; dalla tutela della spesa sanitaria a carico del servizio nazionale ai danni erariali, dalla lotta alla criminalita\' organizzata alle indagini antiriciclaggio\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Nella cerimonia di oggi e\' stato anche presentato il calendario storico del corpo, dedicato all\'istituzione del soccorso alpino della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG>. Il soccorso alpino, ha affermato il comandante delal GdF, \'\'opera fin dal 1965 per la tutela e salvaguardia della vita umana in montagna ed in occasioni di calamita\' naturali. Si tratta di una funzione importante interpretata con generoso altruismo spesso in lotta contro il tempo, le asperita\' del terreno e le avversita\' atmosferiche in un quadro di intensa solidarieta\' umana\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Alla presentazione erano presenti anche il brigadiere della GdF, Silvio Mondinelli, che ha di recente scalato il K2 e le famiglie del vicebrigadiere Francesco Plazzotta ed il finanziere scelto Stefano Gottardi, che hanno perso la vita nell\'adempimento dei propri compiti di servizio. (ANSA). <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>', 0, '', '', '', 3867, 1, 1, '2026-01-11 22:40:49', '0000-00-00'),
(396, '2004-12-08 00:00:00', 'Dai media', 'IL TEMPO: “GDF, RAFFICA DI PROMOZIONI – CON SPECIALE TUTTI GENERALI ALLE FIAMME GIALLE”', '', 'IL TEMPO: “GDF, RAFFICA DI PROMOZIONI – CON SPECIALE TUTTI GENERALI ALLE FIAMME GIALLE”', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><B style=\"mso-bidi-font-weight: normal\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">Il Tempo, mercoledì 8 dicembre 2994, pagina 2<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></FONT></I></B></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 6pt\"><FONT size=3><FONT face=\"Times New Roman\">Raffica di promozioni ai vertici della Guardia di Finanza presieduta dal comandante generale <B style=\"mso-bidi-font-weight: normal\">Roberto Speciale. </B>Diventano generali di corpo d’armata l’attuale capo di stato maggiore generale di divisione <B style=\"mso-bidi-font-weight: normal\">Nino Di Paolo</B> e il comandante interregionale Venezia, generale di divisione <B style=\"mso-bidi-font-weight: normal\">Lucio Macchia</B>; le nomine a generale di divisione hanno invece interessato quattro generali di brigata: <B style=\"mso-bidi-font-weight: normal\">Emilio Spaziante </B>(regione Lombardia),<B style=\"mso-bidi-font-weight: normal\"> Vito Bardi </B>(1° reparto), <B style=\"mso-bidi-font-weight: normal\">Pasquale Debidda </B>(3° reparto) e <B style=\"mso-bidi-font-weight: normal\">Cosimo Sasso</B>. Diventano invece generali 8 colonnelli: <B style=\"mso-bidi-font-weight: normal\">Luciano Carta</B> ( Livorno ) <B style=\"mso-bidi-font-weight: normal\">Mario D’Alonzo</B> (Catania), <B style=\"mso-bidi-font-weight: normal\">Michele Calandro</B> (Genova), <B style=\"mso-bidi-font-weight: normal\">Giorgio Bartoletti </B>(scuola Pt), <B style=\"mso-bidi-font-weight: normal\">Francesco Attardi</B> (antifrodi),<B style=\"mso-bidi-font-weight: normal\"> Renato Russo </B>(Genova), <B style=\"mso-bidi-font-weight: normal\">Maurizio Raponi</B> (Bologna) e <B style=\"mso-bidi-font-weight: normal\">Giuseppe Alineri </B>(Napoli).<o:p></o:p></FONT></FONT></P>', 0, '', '', '', 12998, 1, 0, '2026-01-09 23:20:36', '0000-00-00'),
(397, '2004-12-12 00:00:00', 'Notizie Flash', 'CONTRAFFAZIONE: INTESA GDF-CONFINDUSTRIA - FINANZIARIA: GIARETTA, INVADENZA FISCO SU CONTI CORRENTI - FISCO: AGENZIA ENTRATE, ARRIVA UNA GUIDA SU FISCO E MOTORI - EDITORIA: ESCE \'TUTTO FISCO 2005\' DEL SOLE 24 ORE', '', 'CONTRAFFAZIONE: INTESA GDF-CONFINDUSTRIA - FINANZIARIA: GIARETTA, INVADENZA FISCO SU CONTI CORRENTI - FISCO: AGENZIA ENTRATE, ARRIVA UNA GUIDA SU FISCO E MOTORI - EDITORIA: ESCE \'TUTTO FISCO 2005\' DEL SOLE 24 ORE', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">CONTRAFFAZIONE: INTESA GDF-CONFINDUSTRIA, MARTEDI\' LA FIRMA<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">SCAMBIO INFORMAZIONI PER LOTTA A CONTRAFFAZIONE E A PIRATERIA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 10 DIC - <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG> e Confindustria stipuleranno un protocollo d\'intesa volto a regolamentare i flussi informativi per la lotta alla contraffazione e alla pirateria.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\' accordo verra\' firmato martedi\' 14 dicembre 2004, alle 18,00, nella sede del Comando Generale delle Fiamme Gialle, Viale XXI Aprile 51, a Roma dal Comandante Generale, Roberto Speciale, e dal Presidente di Confindustria, Luca Cordero di Montezemolo, alla presenza del Ministro dell\' Economia e delle Finanze, Domenico Siniscalco.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Nell\'occasione saranno divulgati i dati sull\'attivita\' svolta dalla <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG> nel 2004 a contrasto della contraffazione e della pirateria.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>I giornalisti, solo se accreditati, potranno accedere da Viale XXI Aprile 55, dalle 17 alle 17,30. Per gli accrediti, e\' possibile rivolgersi all\' Ufficio Stampa þ prima sezione Pubblica Informazione - ai numeri 06/44223534 þ 3543, fax 06/44223535 þ 3545, e-mail: accrediti.stampa@gdf.it, entro e non oltre le 13,00 del 14 dicembre.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">FINANZIARIA: GIARETTA, INVADENZA FISCO SU CONTI CORRENTI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">PER COPRIRE IRAP, VANTAGGIO AZIENDE DI SOLI 22 EURO L\'ANNO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 10 DIC - Tra le coperture dell\'emendamento sugli sgravi Irap \'\'c\'e\' una norma antielusiva che e\' di fatto un\'invadenza della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG> sui conti correnti\'\'. Lo riferisce Paolo Giaretta della Margherita. L\'esponente dell\'opposizione contesta anche che, sempre per coprire l\'Irap, i tagli alla Tabella C (spese correnti dei ministeri) arrivano al 10%. \'\'E il risultato - sottolinea il senatore della Margherita - e\' un regalo di Natale, anzi di San Valentino visto che a Natale si fanno regali piu\' grossi: un\'azienda risparmiera\' infatti 22 euro l\'anno\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Per quanto riguarda le coperture \'\'si rafforzano le norme - spiega Giaretta - che consentono l\'accesso ai dati degli intermediari finanziari, come banche e poste\'\'.(ANSA). <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">FISCO: AGENZIA ENTRATE, ARRIVA UNA GUIDA SU FISCO E MOTORI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">SPIEGA SCADENZE E AGEVOLAZIONI, VERRA\' PRESENTATA AL MOTOR SHOW<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 6 dic - Arriva una nuova guida sui motori. Ma non riguarda le tecniche di guida o i consigli per affrontare i guasti durante un viaggio. E\' invece incentrata su un connubio nuovo: l\' auto e il fisco. A stilarla e\' stata l\' Agenzia delle Entrate che, in occasione del Motor Show di Bologna, presenta un volume sul trattamento fiscale dei veicoli.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Non sara\' pero\' un manuale per addetti ai lavori. La guida, scritta in linguaggio comprensibile all\' \'\'automobilista medio\'\', spiega come affrontare le principali scadenze, come ad esempio il bollo auto, ma anche le agevolazioni che la legge riconosce ad alcune categorie di contribuenti, ad esempio agli handicappati.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>La guida non tralascia alcun aspetto. Esamina il trattamento fiscale sui mezzi utilizzati nell\'impresa e dai professionisti ma anche la disciplina dei costi e il regime di detraibilita\' dell\'Iva. Vengono illustrate, inoltre, le modifiche normative del 2004 e i principali adempimenti in materia di tasse automobilistiche.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Alle agevolazioni ed esenzioni previste dalla normativa per i portatori di handicap e\' dedicato un intero capitolo mentre, per aiutare i cittadini a districarsi tra le norme, sono stati pubblicati i quadri riassuntivi, i quesiti piu\' frequenti e i riferimenti di legge e amministrativi.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">MOTOR SHOW:\'FISCO E MOTORI\',TUTTE LE ISTRUZIONI IN UNA GUIDA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - BOLOGNA, 7 DIC - Tutto cio\' che c\'e\' da sapere su tasse e motori: il manuale \'\'Fisco e Motori: istruzioni di guida\'\', pubblicato in 150 mila copie e disponibile anche in internet, e\' stato presentato al Motor Show di Bologna dall\'Agenzia regionale delle Entrate che lo ha realizzato d\'intesa con l\'Osservatorio regionale per l\'educazione stradale e la sicurezza, la Direzione regionale delle risorse finanziarie e strumentali dell\'Emilia-Romagna e con la Direzione nazionale delle Entrate.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Le 38 pagine della guida, che fa parte della serie di periodici bimestrali \'L\'Agenzia informa\' e si trova anche on line sul sito www.agenziaentrate.it, contengono tutte le informazioni sul trattamento fiscale dei veicoli: \'\'Sappiamo quanto le automobili siano importanti per gli italiani e abbiamo voluto fare chiarezza sia per i privati sia per chi utilizza i mezzi per lavoro - ha spiegato il direttore regionale dell\'Agenzia Entrate dell\'Emilia-Romagna, Villiam Rossi – Il manuale contiene tutte le informazioni pratiche utili con, tra l\'altro, una parte rivolta alle persone disabili e alle agevolazioni previste per l\'acquisto dell\'auto\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Anche il vicepresidente della Regione Flavio Delbono la giudica un bella iniziativa per migliorare i rapporti tra i cittadini e il fisco: \'\'Noi abbiamo collaborato anche in questa occasione con l\'Agenzia delle Entrate - ha sottolineato Delbono - come abbiamo sempre fatto per tutti gli aspetti che riguardano la fiscalita\' regionale per migliorare i rapporti tra i cittadini e il fisco\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>Il manuale e\' stato tenuto a battesimo dal comico bolognese Vito, che si e\' prestato a fare da \'cavia\'. \'\'Se capisco io, capiscono tutti. Fiscalmente parlando - ha scherzato - Sono qui per imparare e questo opuscolo rivolto a tutti i cittadini, in particolare a quelli che come me per le questioni fiscali non ricorrono ai professionisti, ti dice in modo chiaro quello che c\'e\' da fare\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">EDITORIA: ESCE \'TUTTO FISCO 2005\' DEL SOLE 24 ORE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 9 DIC - In vista del nuovo anno fiscale Il Sole 24 Ore pubblica \'Tutto Fisco 2005\', una guida con Cd-Rom che analizza, categoria per categoria, gli adempimenti contabili, previdenziali ed amministrativi e le scadenze fiscali previste per il 2005. La guida sara\' in edicola con il quotidiano per un mese a partire da venerdì 10 dicembre. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La trattazione di \'Tutto Fisco 2005\' si articola in due sezioni, dedicate rispettivamente all\'inquadramento giuridico e fiscale delle categorie di contribuenti e all\'approfondimento della disciplina tributaria secondo i diversi regimi impositivi.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>All\'interno dell\'opera sono, inoltre, illustrati istituti, tributi ed adempimenti fiscali comuni a tutte le categorie di contribuenti, nonché i testi integrali della normativa di riferimento.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il Cd-Rom allegato alla pubblicazione contiene utili formule e programmi di calcolo per le varie imposte, uno scadenziario fiscale interattivo, un\'agenda elettronica e un \'Prontuario IVA\' per il corretto ed automatico inquadramento degli adempimenti relativi alle operazioni soggette al regime dell\'IVA.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'Tutto Fisco 2005\' con il CD-Rom sarà in edicola fino a venerdì 14 gennaio 2005 al prezzo di 10 Euro oltre al costo del quotidiano. (ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 1848, 1, 0, '2026-01-13 17:47:50', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(398, '2004-12-12 00:00:00', 'Notizie Flash', 'FINANZIARIA:FUNZIONARI PS CONTRO TAGLI, PROTESTA AL SENATO - FINANZIARIA: SIT IN POLIZIOTTI A PALAZZO CHIGI CONTRO TAGLI - SICUREZZA: PISANU,IN FINANZIARIA TUTTE LE RISORSE NECESSARIE - POLIZIA: SINDACALISTA \'ESPROPRIA\' VETTURA PER PARCO VOLANTI', '', 'FINANZIARIA:FUNZIONARI PS CONTRO TAGLI, PROTESTA AL SENATO - FINANZIARIA: SIT IN POLIZIOTTI A PALAZZO CHIGI CONTRO TAGLI - SICUREZZA: PISANU,IN FINANZIARIA TUTTE LE RISORSE NECESSARIE - POLIZIA: SINDACALISTA \'ESPROPRIA\' VETTURA PER PARCO VOLANTI', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">FINANZIARIA: ANFP, TAGLIATI 60,5 MLN EURO PER FORZE ORDINE<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><STRONG><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">SINDACATO</SPAN></STRONG><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">POLIZIA</SPAN></STRONG>, E SI TRATTA DI NUMERI SOTTOSTIMATI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 9 DIC - La Finanziaria del 2005 prevede tagli per le forze dell\'ordine quantificabili in oltre sessanta milioni e mezzo di euro, dei quali piu\' di 38 milioni vengono sottratti dal bilancio della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">polizia</SPAN></STRONG> e oltre 22 da quello dei carabinieri. A fornire i dati inseriti nel bilancio di previsione dello Stato al vaglio del Parlamento, e\' il segretario dell\'Associazione nazionale funzionari di <STRONG><SPAN style=\"FONT-WEIGHT: normal\">polizia</SPAN></STRONG>, Giovanni Aliquo\' sottolineando che si tratta di numeri \'\'sottostimati\'\' e che quindi i tagli potrebbero avere una valenza ancora maggiore.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Nello specifico, per quanto riguarda la <STRONG><SPAN style=\"FONT-WEIGHT: normal\">polizia</SPAN></STRONG> - ma sul capitolo gravano anche le spese per il funzionamento del Viminale, del dipartimento di pubblica sicurezza e quelle periferiche delle questure e prefetture - i tagli sono mediamente del 19% rispetto allo stanziamento del 2003. Il taglio piu\' consistente, quasi 14 milioni di euro, riguarda la \'\'manutenzione, noleggio e gestione automezzi, natanti, officine per gli automezzi\'\': si e\' passati dai 73.089.701 del 2003 ai 59.179.054 del 2005 (nel 2004 erano 66 milioni). Per le \'\'spese di riscaldamento, illuminazione, forza motrice della caserme, questure e gli altri locali in uso alla <STRONG><SPAN style=\"FONT-WEIGHT: normal\">polizia</SPAN></STRONG>\'\', si passa da 46.511.628 del 2003 ai 37.659.368 del 2005 (nel 2004 erano 42 milioni). Per l\' acquisto, la gestione e la manutenzione dei cosiddetti etilometri sono stanziati 541.861 euro, contro i 645.867 del 2003. Si passa poi al \'\'noleggio, installazione, gestione, manutenzione impianti ed attrezzature\'\', dove dei 22.148.394 del 2003 si e\' scesi ai 17.933.047 (nel 2004 erano 20 milioni di euro) e alle \'\'spese di manutenzione per adattamento locali: da 18.826.135 a 15.243.090 (17 mln nel 2004). Il taglio per l\'adattamento e la riparazione dei locali e dei poligioni di tiro e\' invece di 7.587.626 (da 39.867.110 a 32.279.484). <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Anche per quanto riguarda i carabinieri il taglio e\' del 19% dello stanziamento del 2003. Le spese per le trasferte, ad esempio, passano da 17.209.216 a 13.933.907 (-3.275.307); quelle per il telefono la trasmissione dei dati da<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>19.826.135 a 16.052.767 (-3.777.368); quelle per i servizi di pulizia delle caserme da 28.792.913 a 23.312.961 (-4.879.952), quelle per il casermaggio da 22.148.394 a 17.933.047 (-4.215.347); quelle per il riscaldamento<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>delle caserme da 13.289.037 a 10.759.828 (-2.529.209); quelle per la manutenzione di fabbricati e impianti<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>da 12.109.635 a 9.804.893 (-2.304.742); quelle per l\'adattamento e la manutenzione dei locali in uso da 7.534.884 a 6.100.822 (-1.434.062). (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">FINANZIARIA:FUNZIONARI PS CONTRO TAGLI, PROTESTA AL SENATO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">ANFP, SI DANNO FONDI A REGIONI CHE MIRANO A \'NUOVE POLIZIE\'<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 8 DIC - I funzionari di polizia - e quelli del corpo forestale dello Stato - manifesteranno domani davanti al Senato contro il governo e quelli che loro stessi definiscono \'\'i pesanti tagli\'\' nella Finanziaria. Una protesta insolita anche per denunciare come i tagli servano in realta\' a \'\'rimpinguare le casse delle Regioni\'\' che a loro volta \'\'mirano per motivi politici a creare \'nuove polizie\'\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Proseguendo in una non condivisibile politica di penalizzazione dei fondi destinati al potenziamento delle forze di polizia - afferma il segretario dell\'Associazione nazionale funzionari di polizia (Anfp) Giovanni Aliquo\' - il governo intende ulteriormente praticare pesanti pesanti tagli sulle risorse destinate alla sicurezza interna\'\'. A pagare il conto delle scelte del governo, spiega Aliquo\', non saranno soltanto i funzionari \'\'in attesa di una riforma coerente ed organica delle carriere e dei ruoli\'\' e coloro che attendono il riequilibrio delle retribuzioni dirigenziali, ma anche \'\'i fondi necessari ad assicurare la funzionalita\' minima dei servizi e delle strutture di polizia\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Consapevoli che la sicurezza del Paese e la lotta alla criminalita\' comune ed organizzata sono obiettivi per i quali e\' indispensabile una chiara visione strategica e che la politica degli annunci debba essere seguita dai fatti - prosegue il segretario dell\'Anfp - i funzionari di polizia sottolineano come la questione delle risorse finanziarie sia la migliore cartina al tornasole per giudicare le intenzioni del Governo in materia\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\'Anfp, infine, si dice \'\'oltremodo sorpresa\'\' di constatare che nel 2005 le risorse pubbliche necessarie per far funzionare polizia, carabinieri, <STRONG><SPAN style=\"FONT-WEIGHT: normal\">guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">finanza</SPAN></STRONG>, corpo forestale e polizia penitenziaria, \'\'siano distratte, mentre si rimpinguano, in una grande partita di giro, i bilanci delle Regioni che, per motivi politici e senza alcuna visone armonica e razionale della sicurezza, mirano a \'nuove polizie\', ipertrofiche e delle quali non si comprende ne\' il ruolo ne\' la reale ragion d\'essere\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">SICUREZZA:SILP-CGIL, SIT-IN P CHIGI CONTRO TAGLI FINANZIARIA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">IN PROGRAMMA DOPODOMANI; 23 MLN EURO IN MENO RISPETTO A 2004<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 7 DIC - Contro \'\'un\' incomprensibile e pericolosa politica dei tagli alla sicurezza pubblica\'\', il Silp-Cgil ha organizzato un sit-in davanti a Palazzo Chigi alle ore 14 di dopodomani, in concomitanza con la discussione al Senato della Finanziaria.<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>\'\'In un primo momento - ha spiegato il segretario generale del <STRONG><SPAN style=\"FONT-WEIGHT: normal\">sindacato</SPAN></STRONG>, Claudio Giardullo - con la prima nota di variazione di bilancio, poi con il maxiemendamento, il Governo ha tagliato i cosiddetti consumi intermedi delle forze di <STRONG><SPAN style=\"FONT-WEIGHT: normal\">polizia</SPAN></STRONG> (praticamente tutte le spese tranne quelle obbligatorie come gli stipendi) per un importo pari a 121 milioni di euro. Inoltre - ha aggiunto - ha tagliato altri 30 milioni di euro dalle spese correnti del ministero dell\' Interno, che erano destinate soprattutto a rafforzare il poliziotto di quartiere ed il fondo antiterrorismo ed anticrimine\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il risultato, ha proseguito Giardullo, \'\'e\' che in questo momento la Finanziaria 2005 riduce le spese per le forze dell\'ordine del 2,2% rispetto alla Finanziaria 2004, per un importo pari a 23 milioni di euro, con un taglio ai fondi per la motorizzazione e la formazione che potra\' arrivare anche al 20%. Il che, mentre sale l\' allarme terrorismo e si aggrava l\'emergenza criminalita\' in citta\' come Napoli e Bari, ci sembra francamente inaccettabile\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Se a tutto questo - ha concluso il segretario del Silp – si aggiungono le voci insistenti di un vero e proprio svuotamento della Dia, con il trasferimento di 300 operatori e la chiusura di diversi centri e sezioni operative, si comprendono le preoccupazioni degli operatori di <STRONG><SPAN style=\"FONT-WEIGHT: normal\">polizia</SPAN></STRONG><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>per la sicurezza dei cittadini e le condizioni di lavoro degli operatori\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">FINANZIARIA: SIT IN POLIZIOTTI A PALAZZO CHIGI CONTRO TAGLI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">GIARDULLO (SILP-CGIL), POLITICA FRANCAMENTE INCOMPRESIBILE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 9 DIC - Dopo la fiaccolata di Palermo e le manifestazioni delle scorse settimane davanti alle questure, i poliziotti del Silp-Cgil sono nuovamente scesi in piazza in un sit-in davanti a Palazzo Chigi per protestare contro i tagli al settore sicurezza previsti in Finanziaria.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Quello all\'esame del Parlamento, afferma il segretario Claudio Giardullo, \'\'finira\' inevitabilmente per ridimensionare e non rafforzare la capacita\' operativa delle <STRONG><SPAN style=\"FONT-WEIGHT: normal\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">polizia</SPAN></STRONG>. E\' francamente incomprensibile una politica di sicurezza del governo che, mentre si aggrava l\'emergenza criminalita\' in citta\' come Napoli e Bari e resta alto l\'allarme terrorismo, propone una legge finanziaria che taglia i fondi alle <STRONG><SPAN style=\"FONT-WEIGHT: normal\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di polizia</SPAN></STRONG>\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Alla crescente domanda di sicurezza dei cittadini e al giustificato disagio del personale - aggiunge Giardullo - in un paese normale si investe nell\'attivita\' delle forze dell\'ordine e non si tenta la semplice carta della rassicurazione che, come e\' noto, non basta da sola a contrastare i fenomeni criminali, e finisce, nel tempo, per non rassicurare nessuno\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">SICUREZZA: PISANU,IN FINANZIARIA TUTTE LE RISORSE NECESSARIE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">RESTRIZIONI HANNO FATTO SALVE ESIGENZE FORZE ORDINE<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 7 DIC - \'\'In finanziaria ci sono tutte le risorse necessarie: le restrizioni che si sono rese necessarie per il taglio delle tasse, faranno salve le esigenze dei nostri apparati di sicurezza\'\'. Lo ha assicurato il ministro dell\'Interno, Giuseppe Pisanu, nel corso di una conferenza stampa all\' Istituto Superiore di Polizia.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">(ANSA). <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">POLIZIA: SINDACALISTA \'ESPROPRIA\' VETTURA PER PARCO VOLANTI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">QUESTURA VENEZIA LA RESTITUISCE A CONCESSIONARIO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - VENEZIA, 10 DIC - Un esproprio a fin di bene, per una maggiore sicurezza dei cittadini: e\' quello che ha provocatoriamente compiuto un agente di polizia, nelle vesti di esponente sindacale del Coisp, prelevando una vettura da un concessionario di Mestre e consegnandola alla questura di Venezia perche\' venisse aggiunta alla dotazione delle Volanti. Ma la Questura, preso atto del valore dimostrativo del gesto, ha prontamente restituito l\'auto ai legittimi proprietari. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Protagonista dell\' episodio Franco Maccari, segretario generale aggiunto del Coordinamento per l\'indipendenza sindacale delle <STRONG><SPAN style=\"FONT-WEIGHT: normal\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">polizia</SPAN></STRONG>. Oggi Maccari si e\' recato dal concessionario, ha chiesto di provare una Fiat Idea e l\'ha guidata fino alla caserma di Santa Chiara, sede della Questura di Venezia, dove ha lasciato le chiavi.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Non ho mai ritenuto che fossero giusti comportamenti come gli espropri proletari compiuti da Casarini e dai suoi amici - spiega Maccari - tuttavia quegli atti improbi hanno sollevato un notevole interesse politico e mediatico\'\'. Quanto all\'esproprio compiuto, spiega ancora Maccari, e\' un atto con cui si vuole segnalare il problema della scarsita\' dei mezzi a disposizione delle forze della polizia per garantire una adeguato servizio di controllo del territorio. Maccari solleva in particolare il problema delle donazioni di mezzi alle forze dell\'ordine (e\' di qualche anno fa, dice, il caso di una vettura donata dal consiglio comunale di Treviso che non sarebbe stata accettata), e della necessita\' di un provvedimento legislativo che \'\'che preveda e codifichi cosa la Polizia possa ricevere in regalo\'\'. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>Nessun provvedimento disciplinare e\' stato preso nei confronti di Maccari, dato che - ha spiegato il questore di Venezia Salvatore Presenti - la sua e\' da ritenersi un\' azione solo provocatoria, compiuta nel suo ruolo di rappresentante sindacale. (ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2072, 1, 0, '2026-01-14 05:50:23', '0000-00-00'),
(399, '2004-12-12 00:00:00', 'Notizie Flash', 'MAFIA: ESTORSIONI; PISANU,RUOLO INSOSTITUIBILE FIAMME GIALLE - MAFIA: ESTORSIONI; FINANZIERE INDAGATO PER FAVOREGGIAMENTO - MAFIA:SCICO SI RIORGANIZZA,PER COLPIRE PATRIMONI COSA NOSTRA - TRUFFE: TRASFERIMENTO QUOTE SRL, GICO ARRESTA 2 IMPRENDITORI', '', 'MAFIA: ESTORSIONI; PISANU,RUOLO INSOSTITUIBILE FIAMME GIALLE - MAFIA: ESTORSIONI; FINANZIERE INDAGATO PER FAVOREGGIAMENTO - MAFIA:SCICO SI RIORGANIZZA,PER COLPIRE PATRIMONI COSA NOSTRA - TRUFFE: TRASFERIMENTO QUOTE SRL, GICO ARRESTA 2 IMPRENDITORI', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">MAFIA: ESTORSIONI; PISANU,RUOLO INSOSTITUIBILE FIAMME GIALLE<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 9 DIC - \'\'Il ruolo insostituibile delle Fiamme Gialle nella lotta all\'estorsione\'\' e\' stato sottolineato dal ministro dell\'Interno, Giuseppe Pisanu che ha espresso il suo \'\'vivo apprezzamento\'\' al comandante generale della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di Finanza</SPAN></STRONG>, generale Roberto Speciale, ed ai suoi \'\'eccellenti collaboratori del gico\'\' per gli importanti risultati conseguiti con l\'operazione \'cantiere aperto\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\'operazione contro il pizzo a Palermo, che ha portato all\'emissione di 35 ordini di custodia cautelare \'\'conferma – ha aggiunto il ministro - il ruolo insostituibile delle Fiamme Gialle nell\' attacco ai patrimoni illecitamente costituiti e, piu\' in generale, nel contrasto al crimine organizzato\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>\'\'Il coordinamento sempre piu\' stretto con la Polizia di Stato e con l\' Arma dei Carabinieri esalta questo ruolo nella sua specificita\' e, allo stesso tempo, rende molto piu\' incisiva l\'azione dello Stato in difesa della legalita\' \'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">MAFIA: ESTORSIONI; FINANZIERE INDAGATO PER FAVOREGGIAMENTO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - PALERMO, 9 DIC - Un sottufficiale della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di Finanza</SPAN></STRONG> e\' indagato per favoreggiamento nell\'ambito dell\'inchiesta del Gico, coordinata dalla Dda di Palermo, denominata \'\'Cantiere aperto\'\', che oggi ha portato all\' arresto di 35 persone, accusate a vario titolo di associazione mafiosa e estorsione.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Il finanziere, il cui ruolo non sembra centrale, secondo gli investigatori avrebbe passato alle famiglie mafiose informazioni sull\'indagine che ha portato all\'arresto del boss latitante Cosimo Vernengo, cugino e omonimo di una delle persone finite in manette oggi, condannato all\' ergastolo per la strage di via D\'Amelio. Vernengo e\' stato catturato, dopo una breve latitanza, a marzo di quest\' anno.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il finanziere, secondo quanto emerso da alcune intercettazioni di conversazioni tra gli indagati, avrebbe partecipato alle indagini per la cattura del capomafia ma non avrebbe potuto dare ai boss notizia del blitz imminente perche\', a causa di un incidente stradale, non era in servizio.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ma alcuni particolari emersi nel dialogo tra i boss, lascerebbero perplessi gli inquirenti, che ipotizzano un ruolo marginale del finanziere indagato.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">MAFIA:SCICO SI RIORGANIZZA,PER COLPIRE PATRIMONI COSA NOSTRA<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 9 DIC - Lo Scico della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG>, il servizio centrale investigativo sul crimine organizzato, si sta riorganizzando per aggredire, in maniera piu\' incisiva, i patrimoni dei mafiosi. Lo ha detto il colonnello dello Scico Orazio Gibilasco, intervenendo al convegno promosso da Libera sui beni sequestrati a \'cosa nostra\'. Gibilasco ha aggiunto che \'\'sono ormai lontani i tempi eroici, quando riuscivamo a fornire al giudice Falcone le prove delle transazioni bancarie che evidenziavano il legame mafioso tra soggetti che dicevano di non<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>conoscersi: quel metodo ha funzionato perche\' era caratterizzato dalla novita\' e dalla sorpresa\'\'. \'\'Oggi la realta\' e\' diversa e siamo lontani anni luce da quei risultati anche perche\' i patrimoni dei mafiosi si sono smaterializzati, passano attraverso canali e strumenti telematici. Le organizzazioni criminali si muovono su piazze internazionali: non voglio essere pessimista ma la nostra e\' una rincorsa\'\'. E\' quasi \'\'inevitabile\'\', ha concluso Gibilasco, che il nostro \'\'impegno sia rappresentato da un diagramma con fasi alterne: ci stiamo pero\' attrezzando per combattere questo avversario su scala globale\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\'intervento del colonnello Gibilasco e\' stato molto apprezzato. Tra gli altri hanno preso la parola i parlamenti Giovanni Russo Spena e Vittorio Agnoletto, il sindaco di San Giuseppe Jato Giuseppe Tiriu, don Lo Bue e molti soci e rappresentanti delle cooperative che gestiscono i beni, soprattutto terreni, confiscati ai mafiosi. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">TRUFFE: TRASFERIMENTO QUOTE SRL, GICO ARRESTA 2 IMPRENDITORI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">OPERAZIONE GDF MESSINA E MILANO, SPARITI 2,5 MLN DI EURO<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - PALERMO, 7 DIC - Due imprenditori, uno di Messina e uno originario di Trieste e di cui non sono stati resi noti i nomi, sono stati arrestati per truffa ieri dal Gico del nucleo provinciale di polizia tributaria della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">finanza</SPAN></STRONG> di Messina e dalle Fiamme gialle di Milano. L\' ordinanza di custodia cautelare notificata ai due imprenditori, arrestati a Messina e a Viareggio, e\' stata emessa dal gip di Messina al termine di una complessa indagine, scaturita dalla denuncia di un imprenditore di Catania.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\' imprenditore catanese ha raccontato agli inquirenti che un notaio di Francavilla di Sicilia avrebbe rogato, a sua totale insaputa, la vendita delle quote di una societa\', di cui e\' amministratore delegato, a un soggetto da lui mai visto ne\' conosciuto. La societa\', una Srl, e\' in possesso dei diritti per la costruzione di un parco commerciale e di un ipermercato di 56.000 metri quadri nella zona di Misterbianco, in provincia di Catania. I finanzieri del Gico avrebbe accertato che la fittizia cessione di quote societarie aveva gia\' permesso ai due imprenditori arrestati di stipulare un compromesso di vendita con una nota catena commerciale, ottenendo un anticipo di 2 milioni e mezzo di euro. La somma e\' stata accreditata su un conto corrente aperto in una banca a Messina, che e\' stato immediatamente svuotato. Il denaro ha preso diverse destinazioni sulle quali il nucleo di polizia tributaria della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di finanza</SPAN></STRONG> di Messina sta ancora indagando.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Del provento della truffa, per ora, sono stati recuperati una Ferrari 360 modena, una Bmw x5, 160 mila euro ed alcuni saldi attivi su conti correnti in uso agli indagati.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>I due soggetti si trovano agli arresti domiciliari in attesa dell\' interrogatorio. Sono in corso approfondite indagini bancarie per tentare di recuperare almeno un parte della somma ottenuta tramite il raggiro.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2549, 1, 0, '2026-01-11 23:36:43', '0000-00-00'),
(400, '2004-12-13 00:00:00', 'Ordine del giorno', 'E\' CONVOCATO IL DIRETTIVO NAZIONALE DELL\'ASSOCIAZIONE FINANZIERI CITTADINI E SOLIDARIETA\' PER SABATO 18 DICEMBRE, ORE 11, A BOLOGNA', '', 'E\' CONVOCATO IL DIRETTIVO NAZIONALE DELL\'ASSOCIAZIONE FINANZIERI CITTADINI E SOLIDARIETA\' PER SABATO 18 DICEMBRE, ORE 11, A BOLOGNA', '<H1 style=\"MARGIN: 0cm 0cm 0pt\" align=center><U><FONT face=\"Times New Roman\" size=3>ORDINE DEL GIORNO</FONT></U></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36pt; TEXT-INDENT: -18pt; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>1)</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><FONT size=3>PUNTO DI SITUAZIONE DEL PROCESSO LEGISLATIVO DI RIFORMA DELLA RAPPRESENTANZA MILITARE E DECISIONI SULLE INIZIATIVE DA ASSUMERE</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36pt; TEXT-INDENT: -18pt; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>2)</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><FONT size=3>ARTICOLO DEL SETTIMANALE “IL MONDO” E IPOTESI DI RIFORMA&nbsp;DELLA GDF</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt 36pt; TEXT-INDENT: -18pt; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>3)</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><FONT size=3>IL FENOMENO DEL MOBBING</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><FONT face=\"Times New Roman\" size=3>IL PRESIDENTE DEL DIRETTIVO</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><FONT face=\"Times New Roman\" size=3>(Giuseppe Fortuna)</FONT></P>', 0, '', '', '', 2091, 1, 0, '2026-01-10 03:20:00', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(401, '2004-12-21 00:00:00', 'Notizie Flash', 'CONTRAFFAZIONE: ACCORDO CONFINDUSTRIA-GUARDIA DI FINANZA - TESORO: PRONTA SCIP3, IN FONDO IMMOBILI DA 4 MLD UFFICI MA ANCHE CASERME GDF', '', 'CONTRAFFAZIONE: ACCORDO CONFINDUSTRIA-GUARDIA DI FINANZA - TESORO: PRONTA SCIP3, IN FONDO IMMOBILI DA 4 MLD UFFICI MA ANCHE CASERME GDF', '<P>&nbsp;</P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">CONTRAFFAZIONE: ACCORDO CONFINDUSTRIA-<STRONG><SPAN style=\"FONT-WEIGHT: normal\">GUARDIA</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">DI</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">FINANZA</SPAN></STRONG><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 14 DIC - Un accordo per combattere la contraffazione e la pirateria: e\' quanto hanno siglato la <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG> e Confindustria. Il protocollo d\'intesa prevede l\'instaurazione di un processo collaborativo basato sullo scambio di informazioni in tema di importazione, produzione, distribuzione e commercializzazione di prodotti contraffatti o realizzati in violazione delle normative di settore.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La Confindustria, in base all\' accordo firmato, promuovera\' ogni iniziativa utile a far conoscere alla <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza </SPAN></STRONG>ipotesi di violazione in materia di contraffazione marchi e di pirateria audiovisiva, informatica ed intellettuale. Il protocollo e\' stato siglato dal comandante generale della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG>, generale di Corpo d\' Armata Roberto Speciale, e dal presidente di Confindustria, Luca Cordero di Montezemolo, alla presenza del ministro dell\' Economia e delle Finanze, Domenico Siniscalco. </SPAN><SPAN lang=DE style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-ansi-language: DE; mso-bidi-font-size: 10.0pt\">(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">\'\'La contraffazione e\' un tema di scottante attualita\'\'\', ha detto il comandate generale della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG>, sottolineando come abbia \'\'fra i suoi effetti perversi anche quello di scoraggiare creativi e inventori, annullando i vantaggi dell\' innovazione\'\'.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Secondo Speciale, la contraffazione provoca chiaramente un danno economico diretto alle imprese, producendo fra l\' altro effetti distorsivi che ricadono sull\' erario. \'\'<STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di Finanza</SPAN></STRONG> e Confindustria - ha precisato riferendosi all\' accordo appena siglato - hanno finalita\' istituzionali convergenti nel contrasto del fenomeno della produzione e del commercio dei beni contraffatti e possono insieme migliorare l\'azione preventiva e repressiva attraverso la standardizzazione dei flussi informativi reciproci\'\'.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Speciale ha infatti spiegato come nell\' ambito della collaborazione, \'\'Confindustria comunichera\' alla <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di Finanza</SPAN></STRONG> notizie ed informazioni utili per gli sviluppi operativi. Il Corpo, nel caso di riscontri positivi, offrira\' a Confindustria il feedback, anche al fine di una valutazione piena sull\' affidabilita\' delle fonti informative\'\'.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Soddisfatto dell\' intesa raggiunta anche il presidente di Confindustria, Luca Cordero di Montezemolo. \'\'Innanzitutto vorrei ringraziare la <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG> per la collaborazione e la disponibilita\' dimostrata sul tema della contraffazione – ha esordito Montezemolo - la contraffazione e\' un problema grosso che coinvolge i prodotti del Made in Italy, conosciuto nel mondo come sinonimo di qualita\'\'\'. Il presidente di Confindustria si e\' detto particolarmente compiaciuto del fatto che l\' accordo sia \'\'un\' operazione di sistema, e questo e\' un fatto estremamente importante\'\'.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Non sono qui tanto per presiedere alla firma, ma soprattutto perche\' questo accordo segna una collaborazione attiva e proattiva fra Stato ed imprese\'\', ha detto invece il ministro dell\' Economia Domenico Siniscalco, sottolineando come la competitivita\' sia un problema dell\' Azienda Italia, anche se c\'e\' \'\'un impegno a sederci attorno ad un tavolo per ricercare la soluzione migliore\'\'.<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">TESORO: PRONTA SCIP3, AL VIA IN 2005, VALE 1 MLD<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">IN FONDO IMMOBILI DA 4 MLD UFFICI MA ANCHE CASERME<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 14 DIC - I tecnici del ministero dell\'Economia stanno predisponendo \'\'la terza operazione di cartolarizzazione di immobili, prevalentemente residenziali, pubblici che dovrebbe prendere il via all\'inizio del 2005 con la vendita di oltre 6500 unita\' immobiliari per un valore stimato in circa 1 miliardo di Euro\'\'. Lo ha detto il sottosegretario all\'economia, Maria Teresa Armosino, nel corso di un\'audizione alla Commissione di vigilanza sugli enti previdenziali.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il sottosegretario ha anche parlato del fondo comune di investimento immobiliare denominato Fip. \'\'E\' stato identificato un portafoglio di immobili - ha riferito Armosino - con un valore di mercato di circa 4 miliardi di euro quale oggetto dell\'operazione composto per circa il 70% di immobili ad uso governativo (Agenzie fiscali, sedi del ministero dell\'Economia e delle Finanze, sedi del ministero delle Infrastrutture e dei Trasporti, sedi del ministero del Lavoro e <B>caserme della <STRONG>guardia di</STRONG></B> <STRONG>finanza</STRONG>) e per circa il 30% da immobili degli enti previdenziali (Inps, Inail, Inpdap)\'\'. <o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Sempre per quanto riguarda le operazioni immobiliari la Armosino ha citato un altro fondo comune di investimento immobiliare di tipo chiuso, quello promosso da Patrimonio Spa. \'\'E\' stato identificato un portafoglio di immobili – ha sottolineato a proposito di questo fondo - con un valore di mercato di circa 800 milioni di euro quale oggetto dell\'operazione, composto per circa il 60% da immobili di enti previdenziali (Inps, Inail, Inpdap) e per il 40 % da immobili di altri soggetti controllati dallo stato. Gli immobili – ha precisato Armosino - sono prevalentemente localizzati in zone centrali e semi centrali del centro-nord Italia.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Nel corso dell\' audizione alla commissione di Vigilanza sugli enti previdenziali, Armosino ha anche quantificato \'\'l\' attivo pubblico e il suo reale valore di mercato\'\', che e\' \'\'ampiamente superiore al debito, oltre il 125% del Pil, pur rimanendo attualmente poco noto, spesso mal gestito, e con rendimenti - ha rilevato il sottosegretario - quasi nulli\'\'.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il sottosegretario ha anche spiegato che l\' onere medio anno sostenuto dalla pubblica amministrazione per detenere la proprieta\' di un immobile e\' pari al 9-10% del valore dell\' immobile stesso. Per questo, l\' avvio del fondo immobili con uffici pubblici dovrebbe rendere da subito il 3-3,5% del portafoglio (alla minore spesa di manutenzione del 10% bisogna infatti sottrarre la spesa per l\' affitto dell\' immobile stesso calcolata in 7-7,5% del valore).<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Armosino ha anche riferito che la terza operazione di cartolarizzazione (Scip3) che partira\' all\' inizio del 2005, \'\'sara\' costruita principalmente su immobili dello Stato e non degli enti previdenziali\'\'.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il sottosegretario si e\' anche soffermato sulla struttura dell\' operazione riguardante il fip \'\'dopo la costituzione del fondo ed il collocamento delle quote sul mercato - ha spiegato - la Pa vende al fondo l\' ulteriore 60% del portafoglio attraverso un decreto ministeriale. Per finanziare l\' acquisto la Sgr sottoscrive un prestito per conto del fondo. Il prestito che sara\' valutato dalle Agenzie di rating sara\' inizialmente sottoscritto dalla cassa depositi e Prestiti e dalle banche a ranger a condizioni di mercato\'\'.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Il sottosegretario ha assicurato che da questa operazione riguardante il fondo non ci saranno problemi per gli enti previdenziale ne\' per quanto riguarda l\' equilibrio economico ne\' per il godimento del bene. \'\'L\' equilibrio patrimoniale degli enti - ha detto Armosino - e\' in ogni caso garantito dallo Stato attraverso trasferimenti annui, al ripianamento del deficit degli enti, del valore di circa 60 miliardi di euro\'\'. Per quanto riguarda invece l\' utilizzo dei beni, gli enti hanno comunque assicurato la ossibilita\' di riaffitto \'\'per un minimo di 30 anni, oltre al diritto di prelazione in caso di vendita\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2264, 1, 0, '2026-01-14 02:16:05', '0000-00-00'),
(402, '2004-12-21 00:00:00', 'Notizie Flash', 'GUARDIA FINANZA: 2004; 6.800 EVASORI TOTALI - ISPETTORE GDF SALVA AUTOMOBILISTA DA VETTURA IN MARE -  TRUFFE: FALSI BOLLETTINI RAI A FERRARA', '', 'GUARDIA FINANZA: 2004; 6.800 EVASORI TOTALI - ISPETTORE GDF SALVA AUTOMOBILISTA DA VETTURA IN MARE -  TRUFFE: FALSI BOLLETTINI RAI A FERRARA', '<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"></SPAN>&nbsp;</P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">GUARDIA FINANZA: 2004; 6.800 EVASORI TOTALI,</SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">9 MILIARDI EURO RECUPERATI A TASSAZIONE<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 20 DIC - Sono stati 6.800 gli evasori totali individuati dalla <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG> nel corso del 2004. Nello stesso periodo sono state eseguite 65.000 verifiche fiscali e 540.000 intervelli tra controlli incrociati, scontrini, ricevute e beni viaggianti. Lo rendono noto le stesse Fiamme gialle anticipando alcuni dei dati che verranno illustrati mercoledi\' prossimo - alle 11, presso la caserma \'Piave\' di Roma – nel corso della tradizionale conferenza stampa di fine anno.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Nove miliardi di euro sono stati recuperati a tassazione ai fini delle imposte sui redditi (il 42% dell\'importo si riferisce ai controlli svolti sulle aziende con giro d\'affari superiore ai 25 milioni di euro).<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">Ai 6.800 evasori totali individuati viene contestato un reddito non dichiarato per 5,2 miliardi di euro.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>All\'incontro con la stampa di mercoledi\', durante<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>il quale verra\' fatto il consuntivo<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>di un anno di attivita\', sara\' presente il comandante generale della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG>, il generale Roberto Speciale, che traccera\' le linee di indirizzo per il 2005.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>L\'intervento introduttivo, con l\'illustrazione dei dati, sara\' a cura del generale Pasquale Debidda, capo del 3/o Reparto operazioni. Verranno analizzati tutti i settori di intervento delle Fiamme gialle: dalle verifiche fiscali alla tutela del bilancio dell\'Unione Europea attraverso la prevenzione e la repressione delle illecite percezioni di aiuti comunitari; dalla tutela della spesa sanitaria a carico del servizio nazionale ai danni erariali; dalla lotta alla criminalita\' organizzata alle indagini antiriciclaggio.<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp; </SPAN>(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">SOTTUFFICIALE GDF SALVA AUTOMOBILISTA DA VETTURA IN MARE<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - TARANTO, 18 DIC - Un sottufficiale della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di finanza</SPAN></STRONG> ha tratto in salvo un automobilista finito in mare con la sua vettura. L\' episodio - informa una nota del comando generale delle Fiamme gialle - e\' avvenuto su una litoranea alla periferia di Taranto. Il militare, fuori servizio, dopo aver forzato la portiera dell\'automobile, ha estratto l\'automobilista che e\' stato poi soccorso dal 118. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">L\'incidente, a quanto si e\' appreso, e\' avvenuto sulla strada che circumnaviga il mar Piccolo. La vettura, guidata da Angelo Simonetti, di 34 anni, di Taranto, e\' finita in mare per cause in corso d\'accertamento. In quel tratto il fondale e\' basso. Il maresciallo Antonio Barbato, che transitava, si e\' fermato ed ha soccorso l\'uomo che e\' stato trasportato in ospedale per accertamenti. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">TRUFFE: FALSI BOLLETTINI RAI; SOLO OGGI 100 DENUNCE A FERRARA<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - FERRARA, 18 DIC - E\' una truffa senza precedenti, a Ferrara, visto l\'assalto dei ferraresi agli uffici della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG>: solo nella giornata di oggi piu\' di un centinaio di ferraresi, abbonati della Rai, hanno denunciato agli uffici delle Fiamme gialle di Ferrara la tentata truffa subita, presentando false lettere per il pagamento del canone e bollettini artefatti.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Tutti inviati da una fantomatica azienda di Milano, la Sat, Servizi di assistenza televisiva, casella postale 1717 – 20121 Milano, come riportato nell\'intestazione della busta. I centralini della caserma sono stati intasati dalle chiamate per informazioni, code agli uffici, con in alcuni casi un rinvio<SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>a lunedi\' prossimo per le denunce.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Le indagini erano scattate ieri dopo le prime denunce e sono coordinate dal pm Simone Purgato e seguite in primo piano dai comandanti Claudio Pesole e Fulvio Bernabei: la finanza sta raccogliendo in questi giorni tutte le denunce e segnalazioni inviate ai carabinieri e alla polizia. Secondo la prima stima i bollettini falsi inviati arriverebbero anche alle migliaia: si tratta di una tentata truffa colossale di cui tuttavia non si conoscono al momento ancora i contorni, soprattutto se e\' estesa anche a livello nazionale. Sono in corso accertamenti sul conto corrente postale su cui gli abbonanti erano invitati a versare i soldi del canone. Buone notizie, comunque, per i molti ferraresi che hanno gia\' pagato, da quanto si e\' appreso: potrebbero essere rimborsati perche\' le Poste di Ferrara hanno stoppato il pagamento su quel numero di conto corrente, su segnalazione della stessa Finanza. Tutti coloro, invece, che si presentano per pagare il falso bollettino vengono informati del raggiro in corso. (ANSA).<o:p></o:p></SPAN></P>', 0, '', '', '', 1995, 1, 0, '2026-01-14 09:18:05', '0000-00-00'),
(403, '2004-12-21 00:00:00', 'Notizie Flash', 'SICUREZZA: CONTRATTO; CORTE DEI CONTI REGISTRA DECRETI - SICUREZZA: SIULP, MOBILITAZIONE CONTRO PARTITO DEI GENERALI - FINANZIARIA: OK ASSUNZIONE 2.724 CARABINERI E POLIZIOTTI - SICUREZZA: SILP-CGIL, NESSUN INCREMENTO FONDI IN FINANZIARIA', '', 'SICUREZZA: CONTRATTO; CORTE DEI CONTI REGISTRA DECRETI - SICUREZZA: SIULP, MOBILITAZIONE CONTRO PARTITO DEI GENERALI - FINANZIARIA: OK ASSUNZIONE 2.724 CARABINERI E POLIZIOTTI - SICUREZZA: SILP-CGIL, NESSUN INCREMENTO FONDI IN FINANZIARIA', '<P>&nbsp;</P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">SICUREZZA: CONTRATTO; CORTE DEI CONTI REGISTRA DECRETI<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">BACCINI, PIENA SODDISFAZIONE PER ESAURIMENTO ITER<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 DIC - La Corte dei Conti ha registrato oggi i decreti del presidente della Repubblica che recepiscono l\'accordo sindacale e gli schemi di concertazione che hanno portato al rinnovo del contratto delle forze di polizia ad ordinamento civile e delle <STRONG><SPAN style=\"FONT-WEIGHT: normal\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">armate</SPAN></STRONG> siglato lo scorso 13 ottobre.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Lo rende noto il Dipartimento della Funzione Pubblica, precisando che i decreti del Presidente della Repubblica saranno ora inviati alla Gazzetta Ufficiale per la loro pubblicazione e riguardano il secondo biennio economico (2004 - 2005) per il personale non dirigente del comparto Sicurezza (326.132 unita\' dei Corpi di polizia e 126.591 unita\' delle <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">armate</SPAN></STRONG>). <o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Piena soddisfazione\'\' e\' stata espressa dal ministro per la Funzione Pubblica Mario Baccini per il \'\'positivo esaurimento dell\'iter negoziale che comportera\' un incremento medio mensile di 111 euro lordi\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">SICUREZZA: SIULP, MOBILITAZIONE CONTRO PARTITO DEI GENERALI<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><STRONG><SPAN style=\"FONT-WEIGHT: normal; FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">SINDACATO</SPAN></STRONG><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">, SEPARARE COMPARTO DA DIFESA<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 DIC - \'\'Una mobilitazione contro il partito dei generali\'\' e\' stata messa in campo \'\'con un blitz\'\' presso le questure italiane dal Siulp (<STRONG><SPAN style=\"FONT-WEIGHT: normal\">sindacato</SPAN></STRONG> italiano unitario lavoratori <STRONG><SPAN style=\"FONT-WEIGHT: normal\">polizia</SPAN></STRONG>). <o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Motivo della manifestazione, spiega il <STRONG><SPAN style=\"FONT-WEIGHT: normal\">sindacato</SPAN></STRONG>, \'\'alcune recenti scelte governative in tema di sicurezza e difesa, che da una parte hanno comportato maggiori investimenti a favore di esercito, aviazione, marina, a scapito delle forze di <STRONG><SPAN style=\"FONT-WEIGHT: normal\">polizia</SPAN></STRONG>. Dall\'altro - prosegue - hanno creato numerose disparita\' di trattamento tra poliziotti e militari\'\'. <o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Secondo il Siulp, \'\'il rischio e\' che venga per sempre insabbiata la gestione civile della sicurezza e si ritorni verso una militarizzazione dell\'ordine pubblico\'\'.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Per questo, il <STRONG><SPAN style=\"FONT-WEIGHT: normal\">sindacato</SPAN></STRONG> chiede \'\'che vengano separati i comparti sicurezza e difesa perche\' - sostiene - il modello dei militari non puo\' piu\' essere ritenuto valido per la <STRONG><SPAN style=\"FONT-WEIGHT: normal\">polizia</SPAN></STRONG> di Stato; che venga ripristinata presso il parlamento la commissione Interni per trattare le iniziative legislative in tema di sicurezza, cosi\' come fa la commissione Difesa per le materie di competenza; che vengano eliminate le sperequazioni economiche ed ordinamentali\'\', conclude il Siulp, sottolineando che nei prossimi giorni \'\'saranno varate altre iniziative\'\'.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>La mobilitazione e\' stata accompagnata da un manifesto nel quale un carro armato di ultimA generazione viene fermato da un poliziotto della Stradale, con la scritta \'Fermiamoli\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">SICUREZZA: BRESSA (DL), GOVERNO CERCA MILITARIZZARE POLIZIA<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 13 DIC - \'\'La militarizzazione dell\'ordine pubblico appare sempre piu\' come l\'obiettivo vero del governo Berlusconi\'\'. Lo afferma il deputato della Margherita, Giancarlo Bressa, definendo \'\'un\'iniziativa chiara e coraggiosa quella del Siulp che - dice - mira a squarciare le nebbie del governo e della maggioranza sulla questione della sicurezza\'\'.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>Secondo Bressa, \'\'la richiesta di separare i comparti sicurezza e difesa perche\' il modello dei militari non puo\' piu\' essere valido per la polizia di Stato cosi\' come previsto da una mia proposta di legge presentata oltre due anni fa, costituisce il vero banco di prova per il governo. Senza questo necessario chiarimento - conclude - si vive nella confusione e nella demagogia del giorno per giorno e la confusione serve solo a mascherare l\'insufficienza politica del governo e la mancanza di risorse vere per il comparto sicurezza\'\'.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">FINANZIARIA: OK ASSUNZIONE 2.724 CARABINERI E POLIZIOTTI<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">POTENZIATI FONDI ANTI-TERRORISMO E PER RISCHI NON CONVENZIONALI<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 14 DIC - L\' assunzione di 1.324 agenti di Polizia e di 1.400 carabinieri per un incremento complessivo del personale di 2.724 unita\' da realizzare entro la fine del 2006. E\' quanto prevede uno dei commi del maxi emendamento del governo sul quale e\' stata posta la Fiducia.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>La norma fa parte di un \'\'pacchetto sicurezza\'\' che prevede l\' assunzione di forze polizia, dagli ausiliari (1.500 posti) e dai volontari di truppa delle <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">armate</SPAN></STRONG> (1.224). Le norme prevedono anche il potenziamento del sistema di difesa contro i rischi non convenzionali e un incremento degli stanziamenti per esigenze straordinarie anti-terrorismo. (ANSA).<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">SICUREZZA: SILP-CGIL, NESSUN INCREMENTO FONDI IN FINANZIARIA<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">CONFERMATI TAGLI, PER ASSUNZIONI SOLO OPERAZIONE CONTABILE<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 15 DIC - \'\'Con il maxiemendamento al Senato, che sostituisce l\' intera manovra 2005, il Governo conferma completamente i tagli alla sicurezza attuati dalla Finanziaria in discussione\'\'. Lo sostiene Claudio Giardullo, segretario generale del Silp-Cgil.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Al di la\' degli annunci altisonanti - spiega Giardullo - siamo davanti, in realta\', ad una semplice operazione contabile. Le risorse che erano previste per il ministero dell\' Interno nella tabella A, quella delle spese correnti, sono state in parte spalmate sulla spesa per investimenti e sul testo della Finanziaria, facendo sparire la sicurezza da quella tabella\'\'. Dunque, sottolinea, \'\'nessun incremento di fondi per la sicurezza e\' stato deciso dal Governo con il maxiemendamento presentato al Senato, neanche per le sbandierate assunzioni, che saranno finanziate sempre con le stesse risorse, a scapito, quindi, degli altri capitoli di bilancio del ministero dell\'Interno\'\'.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Il risultato finale - secondo il segretario del Silp - e\' che la Finanziaria di quest\' anno ridurra\' dal 10 al 20% i fondi per le <STRONG><SPAN style=\"FONT-WEIGHT: normal\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">polizia</SPAN></STRONG>, rispetto a quella del 2004. Il che ci sembra grave e incomprensibile, visto il reiterato allarme terrorismo e le emergenze criminalita\' di importanti citta\' del nostro Paese\'\'.<o:p></o:p></SPAN></P>\r\n<P class=WEB style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>\'\'Di pessimo gusto sarebbe, infine - conclude Giardullo - dare la sensazione ai cittadini che si aumentano le imposte per finanziare un incremento di risorse per le <STRONG><SPAN style=\"FONT-WEIGHT: normal\">forze</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">polizia</SPAN></STRONG>, che in realta\' non esiste\'\'. (ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 2694, 1, 0, '2026-01-14 00:46:04', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(404, '2004-12-22 00:00:00', 'Nostro Servizio', 'TROPPE ASSENZE AL DIRETTIVO DI FICIESSE. FORTUNA LO RICONVOCA E SI DIMETTE DA PRESIDENTE.', '', 'TROPPE ASSENZE AL DIRETTIVO DI FICIESSE. FORTUNA LO RICONVOCA E SI DIMETTE DA PRESIDENTE.', '<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Sabato scorso, 18 dicembre 2004, si è riunito a Bologna il Comitato direttivo nazionale dell’Associazione Finanzieri Cittadini e Solidarietà, ma la riunione non ha potuto avere regolare svolgimento per mancanza del numero minimo di componenti previsto dalle norme regolamentari.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Non è stato possibile, pertanto, procedere all’esame dei punti all’ordine del giorno e assumere le decisioni conseguenti.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Oggi il presidente del Direttivo, Giuseppe Fortuna, lo ha riconvocato per sabato 15 gennaio, ore 11, sempre a Bologna, annunciando contemporaneamente le sue dimissioni dalla carica. </FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=3>Pubblichiamo di seguito la lettera inviata in data odierna da Fortuna a tutti i componenti del Comitato.</FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></P>\r\n<DIV style=\"BORDER-RIGHT: windowtext 1pt solid; PADDING-RIGHT: 4pt; BORDER-TOP: windowtext 1pt solid; PADDING-LEFT: 4pt; PADDING-BOTTOM: 1pt; BORDER-LEFT: windowtext 1pt solid; PADDING-TOP: 1pt; BORDER-BOTTOM: windowtext 1pt solid; mso-border-alt: solid windowtext .5pt\">\r\n<P class=MsoNormal style=\"BORDER-RIGHT: medium none; PADDING-RIGHT: 0cm; BORDER-TOP: medium none; PADDING-LEFT: 0cm; PADDING-BOTTOM: 0cm; MARGIN: 0cm 0cm 0pt; BORDER-LEFT: medium none; PADDING-TOP: 0cm; BORDER-BOTTOM: medium none; TEXT-ALIGN: center; mso-border-alt: solid windowtext .5pt; mso-padding-alt: 1.0pt 4.0pt 1.0pt 4.0pt\" align=center><I style=\"mso-bidi-font-style: normal\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></I></P>\r\n<P class=MsoNormal style=\"BORDER-RIGHT: medium none; PADDING-RIGHT: 0cm; BORDER-TOP: medium none; PADDING-LEFT: 0cm; PADDING-BOTTOM: 0cm; MARGIN: 0cm 0cm 0pt; BORDER-LEFT: medium none; PADDING-TOP: 0cm; BORDER-BOTTOM: medium none; TEXT-ALIGN: center; mso-border-alt: solid windowtext .5pt; mso-padding-alt: 1.0pt 4.0pt 1.0pt 4.0pt\" align=center><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">LETTERA<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"BORDER-RIGHT: medium none; PADDING-RIGHT: 0cm; BORDER-TOP: medium none; PADDING-LEFT: 0cm; PADDING-BOTTOM: 0cm; MARGIN: 0cm 0cm 0pt; BORDER-LEFT: medium none; PADDING-TOP: 0cm; BORDER-BOTTOM: medium none; TEXT-ALIGN: center; mso-border-alt: solid windowtext .5pt; mso-padding-alt: 1.0pt 4.0pt 1.0pt 4.0pt\" align=center><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">DEL PRESIDENTE DEL DIRETTIVO NAZIONALE DI FICIESSE,<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"BORDER-RIGHT: medium none; PADDING-RIGHT: 0cm; BORDER-TOP: medium none; PADDING-LEFT: 0cm; PADDING-BOTTOM: 0cm; MARGIN: 0cm 0cm 0pt; BORDER-LEFT: medium none; PADDING-TOP: 0cm; BORDER-BOTTOM: medium none; TEXT-ALIGN: center; mso-border-alt: solid windowtext .5pt; mso-padding-alt: 1.0pt 4.0pt 1.0pt 4.0pt\" align=center><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">GIUSEPPE FORTUNA,<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"BORDER-RIGHT: medium none; PADDING-RIGHT: 0cm; BORDER-TOP: medium none; PADDING-LEFT: 0cm; PADDING-BOTTOM: 0cm; MARGIN: 0cm 0cm 0pt; BORDER-LEFT: medium none; PADDING-TOP: 0cm; BORDER-BOTTOM: medium none; TEXT-ALIGN: center; mso-border-alt: solid windowtext .5pt; mso-padding-alt: 1.0pt 4.0pt 1.0pt 4.0pt\" align=center><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">AI COMPONENTI DEL COMITATO<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"BORDER-RIGHT: medium none; PADDING-RIGHT: 0cm; BORDER-TOP: medium none; PADDING-LEFT: 0cm; PADDING-BOTTOM: 0cm; MARGIN: 0cm 0cm 0pt; BORDER-LEFT: medium none; PADDING-TOP: 0cm; BORDER-BOTTOM: medium none; TEXT-ALIGN: center; mso-border-alt: solid windowtext .5pt; mso-padding-alt: 1.0pt 4.0pt 1.0pt 4.0pt\" align=center><I style=\"mso-bidi-font-style: normal\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></I></P></DIV>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 6pt 27pt; TEXT-ALIGN: right\" align=right><I style=\"mso-bidi-font-style: normal\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 6pt 27pt; TEXT-ALIGN: right\" align=right><I style=\"mso-bidi-font-style: normal\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 6pt 27pt; TEXT-ALIGN: right\" align=right><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">Roma, 22 dicembre 2004<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 6pt 27pt\"><I style=\"mso-bidi-font-style: normal\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></I></P>\r\n<H1 style=\"MARGIN: 0cm 22.9pt 0pt 27pt\"><SPAN style=\"FONT-WEIGHT: normal\"><EM><FONT size=3><FONT face=\"Times New Roman\">Ai Componenti del Direttivo nazionale<o:p></o:p></FONT></FONT></EM></SPAN></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 0pt 27pt; TEXT-ALIGN: right\" align=right><EM><FONT size=3><FONT face=\"Times New Roman\">dell’Associazione<o:p></o:p></FONT></FONT></EM></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 0pt 27pt; TEXT-ALIGN: right\" align=right><I><FONT size=3><FONT face=\"Times New Roman\">“Finanzieri Cittadini e Solidarietà”<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 0pt 27pt; TEXT-ALIGN: right\" align=right><I><FONT size=3><FONT face=\"Times New Roman\">LORO SEDI<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 0pt 27pt; TEXT-ALIGN: right\" align=right><I><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></I></P>\r\n<P class=MsoBodyTextIndent style=\"MARGIN: 0cm 22.9pt 12pt 108pt; TEXT-INDENT: -81pt; LINE-HEIGHT: 150%\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">OGGETTO:<SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Convocazione del Direttivo nazionale per sabato 15 gennaio 2005 e dimissioni dalla carica di presidente.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 12pt 27pt; LINE-HEIGHT: 150%\"><I style=\"mso-bidi-font-style: normal\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 12pt 27pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">Cari amici,<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoBlockText style=\"MARGIN: 0cm 22.9pt 12pt 27pt; TEXT-INDENT: 45pt; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">come sapete, per sabato 18 dicembre era stato convocato un Direttivo per affrontare questioni di rilievo centrale per la nostra Associazione, quali il processo di riforma della rappresentanza militare (che sembra ormai in dirittura d’arrivo), il prolungamento del mandato degli attuali Consigli della rappresentanza, le ipotesi di riforma del Corpo apparse sul settimanale “Il Mondo”, il fenomeno del mobbing.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoBlockText style=\"MARGIN: 0cm 22.9pt 12pt 27pt; TEXT-INDENT: 45pt; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">Temi sui quali l’Associazione, da qualche tempo, nonostante l\'obbligo statutario, mostra difficoltà ad esprimersi e ad assumere quel ruolo nel dibattito pubblico che rappresenta una delle sue principali ragioni d’essere.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoBlockText style=\"MARGIN: 0cm 22.9pt 12pt 27pt; TEXT-INDENT: 45pt; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">Ebbene, nonostante fossero pervenute alla segreteria di Roma numerose assicurazioni di partecipazione, il Direttivo non si è svolto per mancanza del numero legale.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoBlockText style=\"MARGIN: 0cm 22.9pt 12pt 27pt; TEXT-INDENT: 45pt; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">In qualità di presidente del Direttivo nazionale, mi sento personalmente responsabile di questo momento di difficoltà e ritengo necessario svolgere con urgenza una analisi delle ragioni del rallentamento al fine di consentire all’Associazione di riprendere, subito dopo, slancio e motivazioni.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoBlockText style=\"MARGIN: 0cm 22.9pt 12pt 27pt; TEXT-INDENT: 45pt; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">Per questi motivi, convoco, con la presente, una nuova riunione del Comitato direttivo nazionale per sabato 15 gennaio 2005, ore 11, sempre a Bologna, e rassegno fin da ora le mie dimissioni dalla carica di presidente.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoBlockText style=\"MARGIN: 0cm 22.9pt 12pt 27pt; TEXT-INDENT: 45pt; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">In merito alla convocazione, ho concordato con il Segretario generale, Carlo Germi, il seguente <U>ORDINE DEL GIORNO</U>:<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 12pt 90pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 90.0pt\"><FONT face=\"Times New Roman\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>1)</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN></I><I style=\"mso-bidi-font-style: normal\"><FONT size=3>Elezione del nuovo presidente del Comitato direttivo nazionale;<o:p></o:p></FONT></I></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 12pt 90pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify; mso-list: l0 level1 lfo1; tab-stops: list 90.0pt\"><FONT face=\"Times New Roman\"><I style=\"mso-bidi-font-style: normal\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>2)</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN></I><I style=\"mso-bidi-font-style: normal\"><FONT size=3>Analisi delle cause del rallentamento delle attività associative e decisioni conseguenti.<o:p></o:p></FONT></I></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 12pt 27pt; TEXT-INDENT: 45pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">Ovviamente, mi asterrò da questo momento in poi dal porre in essere qualunque iniziativa di carattere politico od organizzativo.<o:p></o:p></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 12pt 27pt; TEXT-INDENT: 45pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><o:p><FONT face=\"Times New Roman\" size=3>&nbsp;</FONT></o:p></I><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">Un caro saluto </FONT></FONT></I></P><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\">\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 12pt 27pt; LINE-HEIGHT: 150%\"><I style=\"mso-bidi-font-style: normal\"><o:p>&nbsp;</o:p></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 12pt 27pt; LINE-HEIGHT: 150%; TEXT-ALIGN: right\" align=right><I style=\"mso-bidi-font-style: normal\">(Giuseppe Fortuna)</I></FONT></FONT></I></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 12pt 27pt; LINE-HEIGHT: 150%; TEXT-ALIGN: justify\"><I style=\"mso-bidi-font-style: normal\"><FONT size=3><FONT face=\"Times New Roman\"><o:p></o:p></FONT></FONT></I>&nbsp;</P>', 0, '', '', '', 2886, 1, 0, '2026-01-14 08:21:05', '0000-00-00'),
(405, '2004-12-22 00:00:00', 'Ordine del giorno', 'E\' CONVOCATO IL DIRETTIVO NAZIONALE DELL\'ASSOCIAZIONE FINANZIERI CITTADINI E SOLIDARIETA\' PER SABATO 15GENNAIO 2005, A BOLOGNA', '', 'E\' CONVOCATO IL DIRETTIVO NAZIONALE DELL\'ASSOCIAZIONE FINANZIERI CITTADINI E SOLIDARIETA\' PER SABATO 15GENNAIO 2005, A BOLOGNA', '<P class=MsoBlockText style=\"MARGIN: 0cm 22.9pt 12pt 27pt; TEXT-INDENT: 45pt; TEXT-ALIGN: center\" align=center><FONT size=3><FONT face=\"Times New Roman\"><U></U></FONT></FONT>&nbsp;</P>\r\n<P class=MsoBlockText style=\"MARGIN: 0cm 22.9pt 12pt 27pt; TEXT-INDENT: 45pt; TEXT-ALIGN: center\" align=center><FONT size=3><FONT face=\"Times New Roman\"><U>ORDINE DEL GIORNO</U></FONT></FONT></P>\r\n<P class=MsoBlockText style=\"MARGIN: 0cm 22.9pt 12pt 27pt; TEXT-INDENT: 45pt; TEXT-ALIGN: center\" align=center>&nbsp;</P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 12pt 90pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 150%; mso-list: l0 level1 lfo1; tab-stops: list 90.0pt 98.25pt\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>1)</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><FONT size=3>Elezione del nuovo presidente del Comitato direttivo nazionale;</FONT></FONT></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 22.9pt 12pt 90pt; TEXT-INDENT: -18pt; LINE-HEIGHT: 150%; mso-list: l0 level1 lfo1; tab-stops: list 90.0pt 98.25pt\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-list: Ignore\"><FONT size=3>2)</FONT><SPAN style=\"FONT: 7pt \'Times New Roman\'\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN></SPAN><FONT size=3>Analisi delle cause del rallentamento delle attività associative e decisioni conseguenti.</FONT></FONT></P>', 0, '', '', '', 1783, 1, 0, '2026-01-13 03:00:50', '0000-00-00'),
(406, '2004-12-23 00:00:00', 'Editoriale', 'AUGURI DI BUONE FESTE', '', 'AUGURI  DI  BUONE FESTE', 'LA SEGRETERIA NAZIONALE&nbsp;E I COMPONENTI DEL DIRETTIVO NAZIONALE AUGURANO A TUTTI I SOCI, A TUTTI I SIMPATIZZANTI, NONCHE\' A TUTTI I FREQUENTATORI DEL SITO UN LIETO NATALE ED UN SERENO ANNO NUOVO ', 0, '', '', '', 2149, 1, 0, '2026-01-12 16:52:44', '0000-00-00'),
(407, '2004-12-27 00:00:00', 'Notizie Flash', 'GUARDIA DI FINANZA: IL BILANCIO DEL 2004, LE PRIORITA\' PER IL 2005', '', 'GUARDIA DI FINANZA: IL BILANCIO DEL 2004, LE PRIORITA\' PER IL 2005', '<P>&nbsp;</P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">GUARDIA FINANZA:7.500 EVASORI IN 2004,OCCULTATI 8,9 MLD<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">33.000 CONTROLLI CONTRO CARO-VITA, LE PRIORITA\' PER IL 2005<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 22 DIC - Sono 7.584 gli evasori fiscali scoperti dalla <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">finanza</SPAN></STRONG> da gennaio a novembre di quest\'anno. Erano stati 7.073 nello stesso periodo del 2003. Complessivamente la base imponibile occultata al fisco ammonta a 8,9 miliardi di euro, 230 milioni in piu\' rispetto allo scorso anno. Il bilancio e\' stato fatto oggi dal comandante delle Fiamme Gialle, generale Roberto Speciale e dal capo del terzo Reparto operazioni del Comando generale, generale Pasquale Debidda. E per il 2005, l\'attenzione dei finanzieri sara\' rivolta ai fruitori di regimi fiscali privilegiati, alle operazioni societarie straordinarie ed a quelle di fiscalita\' internazionale.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>EVASORI IN AUMENTO - Gli evasori totali – soggetti completamente sconosciuti al fisco - sono 6.800 (5.943 nel 2003) e hanno occultato 5,2 miliardi di euro di base imponibile (3,3 nel 2003). Sono stati individuati soprattutto nel settore dele costruzioni, del commercio al dettaglio e delle riparazioni. Veneto, Campania e Lazio le principali regioni di provenienza. Le Fiamme Gialle hanno identificato, inoltre, 26.300 lavoratori irregolari, di cui piu\' di 17.400 completamente sconosciuti agli enti previdenziali ed assistenziali.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>65.000 VERIFICHE FISCALI - Nei primi 11 mesi dell\'anno sono state concluse 65.000 verifiche fiscali, mentre 544.000 sono stati i controlli su scontrini, ricevute e circolazione dei beni. Le persone denunciate all\'autorita\' giudiziaria per reati tributari sono state 4.200. Nel settore dell\'imposizione diretta sono stati occultati al fisco 8,9 miliardi di euro (8,6 nel 2003); nel campo dell\'imposizione indiretta, ammontano invece a 2,1 miliardi di euro i recuperi proposti in materia di Iva. Questi risultati, ha spiegato il generale Debidda, \'\'sono particolarmente significativi se si tiene conto dell\'accessibilita\', fino al 16 aprile di quest\'anno, alle varie forme di condono introdotte dalla Finanziaria 2003, con effetti limitativi all\'azione di accertamento\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>33.000 CONTROLLI CONTRO IL CARO-VITA - Nel 2004 la <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di finanza</SPAN></STRONG> ha fatto oltre 33 mila controlli nei settori commerciali e ha riscontrato circa 2.200 violazioni alla disciplina dei prezzi. I controlli sono stati eseguiti per concretizzare la norma della legge Finanziaria per il 2004 che prevedeva un intervento specifico della Gdf sul versante dei prezzi. I settori interessati dai controlli sono stati il commercio al dettaglio di frutta, verdura e pesce; la ristorazione; gelaterie e bar e stabilimenti balneari.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>SEQUESTRATI 110 MILIONI DI PEZZI CONTRAFFATTi - Nel 2004 la <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">finanza</SPAN></STRONG> ha operato circa 20.000 interventi nel settore della contraffazione, sequestrando oltre 110 milioni di pezzi contraffatti, di cui circa 60 milioni costituiti da giocattoli. La contraffazione, ha spiegato il generale Debidda, \'\'ormai si muove secondo logiche d\'impresa anche di tipo internazionale e sena limitazione sui prodotti da copiare, interessando sempre di piu\' quelli \'made in Italy\'\'\'\'. Per arginare il fenomeno, le Fiamme Gialle sono intervenute in tutte le fasi della filiera produttivo-distributiva, aumentando i controlli soprattutto nelle zone portuali e nelle aree piu\'<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">sensibili in termini di stoccaggio e commercializzazione (Milano, Roma, Napoli).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>INDIVIDUATI 7.200 IMMIGRATI CLANDESTINI - L\'azione di contrasto all\'immigrazione clandestina, operata prioritariamente nelle acque dello stretto di Sicilia e nelle vicinanze delle coste di Malta, ha consentito di individuare oltre 7.200 clandestini, 230 dei quali arrestati.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>LOTTA AL TERRORISMO - Negli ultimi 10 anni la Gdf ha sviluppato 22.000 segnalazioni di operazioni finanziarie sospette. Si tratta, ha osservato il comandante Speciale, \'\'di uno strumento formidabile per la lotta al terrorismo internazionale\'\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>LE PRIORITA\' PER IL 2005 - La <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">finanza</SPAN></STRONG>, ha poi sottolineato il generale Speciale, \'\'proseguira\' anche nel 2005 il piano di contrasto all\'economia sommersa, mentre, per i contribuenti noti al fisco la programmazione delle verifiche e<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">dei controlli sara\' elaborata in modo tale da indirizzare gli interventi soprattutto nei confronti delle categorie economiche per le quali non sono stati ancora elaborati i relativi studi di settore e di quei contribuenti nei cui confronti sono stati rilevati profili di incoerenza o incongruita\'\'\'. Le verifiche, ha proseguito, \'\'saranno mirate al controllo dei soggetti fruitori di regimi fiscali privilegiati (enti non profit, agriturismi, eccetera); all\'esame delle operazioni societarie straordinarie (fusioni, scambi di partecipazioni, cessioni d\'azienda, ecc.) che si prestano maggiormente alle manovre elusive; alle operazioni di fiscalita\' internazionale (paradisi fiscali e societa\' off-shore)\'\'. Sara infine rafforzato l\'impegno per il contrasto ai giochi e alle scommesse clandestine e sara\' avviata una campagna di controlli ad hoc per scoraggiare l\'evasione nel campo degli immobili.(ANSA).<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">GUARDIA FINANZA:IN 2005 OCCHIO SU FUSIONI E PARADISI FISCALI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">CONTROLLI ANCHE SU FRUITORI REGIMI FISCALI PRIVILEGIATI<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\">&nbsp;<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 22 DIC - Nel 2005 le verifiche fiscali della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG> saranno mirate al controllo dei soggetti fruitori di regimi fiscali privilegiati (enti non profit, agriturismi, eccetera); all\'esame delle operazioni societarie straordinarie (fusioni, scambi di partecipazioni, cessioni d\'azienda, ecc.) che si prestano maggiormente alle manovre elusive; alle operazioni di fiscalita\' internazionale (paradisi fiscali e societa\' off-shore). E\' quanto e\' emerso oggi alla presentazione del bilancio del 2004 della <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG>, presentato dal comandante del corpo, generale Roberto Speciale. La <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Guardia</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">di</SPAN></STRONG> <STRONG><SPAN style=\"FONT-WEIGHT: normal\">Finanza</SPAN></STRONG>, alla luce delle linee guida del ministro dell\'Economia, Domenico Siniscalco, proseguira\' l\'anno prossimo a svolgere azioni a tutela della finanza pubblica e dell\'economia. Le fiamme gialle continueranno - e\' stato detto - nell\'attuazione del piano di contrasto all\'economia sommersa. Per i contribuenti noti al fisco la programmazione delle verifiche e dei controlli sara\' elaborata in modo tale da indirizzare gli interventi soprattutto nei confronti delle categorie economiche per le quali non sono stati ancora elaborati i relativi studi di settore e di quei contribuenti nei cui confronti sono stati rilevati profili di incoerenza o incongruita\'.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Sara\' confermato e potenziato l\'impegno per il contrasto al fenomeno dei giochi e delle scommesse clandestine. E\' inoltre previsto l\'avvio di una campagna di controlli ad hoc per scoraggiare l\'evasione fiscale nel settore degli immobili.<o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Per quanto riguarda le uscite di bilancio aumenteranno del 25%, rispetto al 2004, le risorse impiegate per la tutela della spesa pubblica. Nel campo della lotta al riciclaggio di capitali verra\' privilegiata l\'attivita\' di approfondimento delle segnalazioni di operazioni finanziarie sospette. Sara\' confermato anche il piano di vigilanza sul fenomeno del \'carovita\', attuato attraverso rilevamenti periodici dei prezzi al consumo direttamente presso i pubblici esercizi. I controlli presso i distributori stradali di carburante potranno poi rappresentare un\'efficace deterrente verso forme di abuso o di frode nel settore delle accise. <o:p></o:p></SPAN></P>\r\n<P class=web style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; COLOR: black; FONT-FAMILY: \'Times New Roman\'; mso-bidi-font-size: 10.0pt\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Infine, sempre piu\' determinata, sara\' l\'azione svolta dalle fiamme gialle a tutela della contraffazione e della pirateria. (ANSA).<o:p></o:p></SPAN></P>\r\n<P>&nbsp;</P>', 0, '', '', '', 3043, 1, 0, '2026-01-14 11:06:32', '0000-00-00');
INSERT INTO `t_articoli` (`id_articolo`, `data`, `argomento`, `titolo_articolo`, `sotto_titolo`, `TITLE`, `testo_articolo`, `nr_attach`, `titolo_foto`, `foto_path`, `link_esterno`, `contatore_visite`, `attivo`, `id_forum`, `ultimo_accesso`, `scadenza`) VALUES
(408, '2005-01-05 00:00:00', 'Notizie Flash', 'FISCO: CON IL NUOVO ANNO ECCO L\'IRPEF A TRE ALIQUOTE - FISCO: OCCHIO A SCONTI IVA,RISCHIA SANZIONE ANCHE CHI COMPRA - FISCO: TORNANO SCONTRINI FISCALI, FINE EFFETTI CONCORDATO', '', 'FISCO: CON IL NUOVO ANNO ECCO L\'IRPEF A TRE ALIQUOTE - FISCO: OCCHIO A SCONTI IVA,RISCHIA SANZIONE ANCHE CHI COMPRA - FISCO: TORNANO SCONTRINI FISCALI, FINE EFFETTI CONCORDATO', '<P>&nbsp;</P>\r\n<H1 style=\"MARGIN: 0cm 0cm 0pt\"><FONT face=\"Times New Roman\" size=5>FISCO: CON IL NUOVO ANNO ECCO L\'IRPEF A TRE ALIQUOTE </FONT></H1>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: center\" align=center><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">&nbsp;<?xml:namespace prefix = o ns = \"urn:schemas-microsoft-com:office:office\" /><o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>(ANSA) - ROMA, 1 GEN - Al via con il nuovo anno il calo delle tasse: arrivano, cosi\' come previsto dalla Finanziaria 2005, le tre aliquote e i maggiori sconti fiscali per la famiglia. Un passo lungo verso il completamento di quella riforma fiscale che a regime dovrebbe portare l\'Ire (l\'imposta sul reddito che sostituisce la vecchia Irpef) a due sole aliquote: 23 e 33%. <o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Si amplia la fascia della \'no tax area\', la soglia che resta esente dalle tasse. Tutto questo grazie alla trasformazione delle vecchie detrazioni per i figli a carico in deduzioni.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ma ecco come cambia il fisco delle famiglie da questo 2005 e come pesera\' sulle tasche dei cittadini, a seconda dei vari scaglioni di reddito.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>- LA NUOVA IRE (IRPEF): Arrivano le tre aliquote, o quasi. Si applichera\' un prelievo del 23% fino a 26.000 euro, del 33% tra i 26.000 e i 33.500 euro, del 39% oltre. Ma ci sara\' anche un contributo di solidarieta\' del 4% oltre i 100.000 euro: si tratta in pratica di una quarta aliquota del 43%. Gli effetti? Saranno attorno ai 570 euro per chi guadagna 25.000 euro, per salire sui 870 euro per i redditi attorno ai 35.000 euro.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>- FIGLI E NONNI, CAMBIA LO SCONTO: Addio detrazioni per i figli a carico. Al loro posto arrivano deduzioni che abbatteranno il reddito che poi viene tassato. Saranno pari a 3.200 euro per il coniuge a carico e di 2.900 per ciascun figlio a carico. Saliranno a 3.450 euro per i figli con meno di 3 anni e a 3.700 euro per figli con handicap. Il loro valore calera\' con l\'aumentare del reddito fino ad azzerarsi a 78.000 euro. Di fatto per una famiglia con 2 figli o con un figlio e un nonno a carico ci sara\' un\' esenzione dall\' Irpef fino a 14.000 euro.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>- LO SCONTO PER BADANTE: Arriva una deduzione specifica di 1.820 euro, decrescente al crescere del reddito, delle spese per la badante per i soggetti non autosufficienti. Per accedere al super-sconto fiscale sara\' pero\' necessaria una certificazione della Asl che attesti la non auto-sufficienza e dunque la necessita\' dell\'assistenza della badante.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>- RISPARMI DA 331 A 29.330 EURO. Si attesta in una forbice tra i 331 e 29.330 euro il risparmio fiscale che otterra\' dal taglio delle aliquote Irpef una famiglia media italiana, composta da un lavoratore dipendente con un coniuge e due figli a carico (o un figlio e un nonno). Sono questi i calcoli fatti dai tecnici governativi che hanno partecipato all\'elaborazione del secondo modulo della riforma fiscale. Lo sconto tiene conto non solo delle nuove aliquote ma anche del nuovo regime di deduzioni che sostituisce le detrazioni.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp; </SPAN>- PREMIATE FAMIGLIE OPERAI. In termini percentuali percentuali (-42,60% di tasse) viene premiata la famiglia di un operaio (15.000 euro); mentre per le fasce dei redditi medi (dai 20.000 ai 40.000) il risparmio percentuale e\' mediamente stabile intorno al 10%. Per i redditi alti il risparmio va poi calando in termini percentuali assestandosi intorno a 4%, anche se ovviamente in valore assoluto sono proprio i \'\'ricchi\'\' quelli che si ritroveranno piu\' soldi.<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\"><SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Ecco uno schema di risparmi per fasce di reddito, secondo i calcoli fatti dai tecnici del governo. <o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">Reddito<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Irpef 2003<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Irpef 2005<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp; </SPAN>Risparmio<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>Percentuale<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">5.000<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>0<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>0<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>0<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</SPAN><SPAN style=\"mso-tab-count: 1\">&nbsp;</SPAN><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>0,00%<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">10.000<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>0<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>0<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>0<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</SPAN><SPAN style=\"mso-tab-count: 1\">&nbsp;</SPAN><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>0,00%<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">15.000<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>778<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp; </SPAN><SPAN style=\"mso-spacerun: yes\">&nbsp;</SPAN>447<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>331<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</SPAN><SPAN style=\"mso-tab-count: 1\"> </SPAN>42,60%<o:p></o:p></FONT></SPAN></P>\r\n<P class=MsoNormal style=\"MARGIN: 0cm 0cm 0pt; TEXT-ALIGN: justify\"><SPAN style=\"FONT-SIZE: 14pt; mso-bidi-font-size: 12.0pt\"><FONT face=\"Times New Roman\">20.000<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>2.421<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</SPAN><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</SPAN>2.106<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><SPAN style=\"mso-tab-count: 1\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>315<SPAN style=\"mso-spacerun: yes\">&nbsp;&nbsp;&nbsp;&n